INSERT INTO app_hist.mandado VALUES (9, '1752-06-16', 1752, 1, 217, 'Mandado para D. Estevão de Meneses, presidente do conselho ultramarino, pelo Diogo de Mendonça Corte Real, ministro e secretário de estado dos negócios da marinha e domínios ultramarinos, informando que D. Jose I ordenou que se reformasse a consulta que se fizera sobre o requerimento do bacharel João Vieira de Andrade para poder nomear oficiais para a diligência a que era mandado à vila de Santos, de 16/06/1752.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (10, '1771-11-29', 1771, 1, 97, 'Consulta da mesa de consciência e ordens de 29/11/1771 sobre o requerimento do frei Manuel da Ressureição, bispo de São Paulo, a D. Maria I pedindo-lhe licença para nomear as dignidades, conezias, igrejas, benefícios e mais cargos eclesiásticos no seu bispado. Esta consulta foi reformada por despacho da mesa de consciência e ordens, de 18/07/1778. Contém, ainda, um alvará de D. Jose I concedendo ao frei Manuel da Ressureição permissão de prover dignidades, conezias, vigararias e mais benefícios e cargos eclesiásticos no seu bispado, com a condição de as ditas nomeações não recaírem em indivíduos incultos e de raça impura, de 08/01/1751. O traslado em pública-forma é de Lisboa, de 07/04/1778.', 'Mesa de consciência e ordens ', 'Mesa de consciência e ordens ', '', '');
INSERT INTO app_hist.mandado VALUES (11, '1751-05-26', 1751, 5, 511, 'Aviso da junta dos três estados, solicitando ao conselho ultramarino, que passe por ele, ordem aos provedores das vilas de São João Del-Rei e São José, para que informem a respeito dos rendimentos dos ofícios de escrivão dos ausentes, meirinho das execuções, porteiro dos auditórios e escrivão dos órfãos.', 'Conselho ultramarino e provedores', 'junta dos três estados', '', '');
INSERT INTO app_hist.mandado VALUES (12, '1751-05-26', 1751, 5, 512, 'Aviso da junta dos três estados, solicitando ao conselho ultramarino, que passe por ele, ordem ao provedor da fazenda de Vila Rica, para que informe sobre os rendimentos do ofício de porteiro, guarda-livros e solicitador.', 'Conselho Ultramarino e provedor de Vila Rica', 'junta dos três estados', '', '');
INSERT INTO app_hist.mandado VALUES (7, '1755-10-25', 1755, 1, 236, 'Ordem do secretário de estado da marinha e ultramar, Diogo de Mendonça Corte Real, ao presidente do conselho ultramarino, o marquês de Penalva, Estevão de Menezes, ordenando que se reforme a consulta de um ano sobre o requerimento do antigo governador da praça de Santos, Luis Antonio de Sa e Queiroga, em que solicita a patente e o soldo de brigadeiro para se aposentar, pois já estava muito velho e pobre.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (8, '1753-05-09', 1753, 1, 276, 'Ordem do secretário de estado da marinha e ultramar, Diogo de Mendonça Corte Real ao presidente do conselho ultramarino, o Marquês de Penalva, Estevão de Menezes, ordenando que se consulte o caso do requerimento do corretor e dos religiosos de São Francisco de Paula e que dê o referido parecer. ', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (13, '1751-09-13', 1751, 5, 601, 'Aviso do Conselho Ultramarino, ordenando ao Marques de Penalva, presidente do referido conselho, que consulte a representação da câmara da vila de São João.', 'Marques de Penalva', 'Conselho Ultramarino', '', '');
INSERT INTO app_hist.mandado VALUES (20, '1760-08-15', 1760, 5, 932, 'Aviso de Francisco Xavier de Mendonça Furtado a Alexandre Metelo de Sousa e Meneses, para que remeta ao Conselho Ultramarino os autos de residência de Domingos Nunes Vieira, intendente da comarca do Sabará, tirados pelo seu sucessor.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (19, '1759-09-05', 1759, 5, 808, 'duvida Aviso do secretário de estado Tome Joaquim da Costa Corte Real dirigido a Alexandre Metelo de Sousa e Meneses, ordenando que se consulte a petição de Pedro Dias Paes Leme.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (18, '1758-10-16', 1758, 5, 771, 'Aviso de Tome Joaquim da Costa Corte Real a Alexandre Metelo de Sousa e Meneses, para que mande o seu tesoureiro embarcar, na presente frota do Rio de Janeiro, duzentos pares de pistolas para os Dragões de Minas Gerais.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (17, '1758-10-13', 1758, 5, 770, 'Aviso de Tome Joaquim da Costa Corte Real a Alexandre Metelo de Sousa e Meneses, ordenando passar provisão ao bacharel Jose Gomes de Araujo, para que seja nomeado intendente de Sabará e possa embarcar na frota para o Rio de Janeiro.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (14, '1758-05-29', 1758, 5, 750, 'Aviso de Tome Joaquim da Costa Corte Real ao Marques de Penalva, ordenando que mande a folha dos ordenados de Manuel Pinto de Azevedo, oficial da secretaria do governo das Minas Gerais.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (15, '1758-10-01', 1758, 5, 768, 'Aviso de Tome Joaquim da Costa Corte Real a Alexandre Metelo de Sousa e Meneses, ordenando que proceda a arrematação dos contratos das entradas para as minas.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
INSERT INTO app_hist.mandado VALUES (16, '1758-10-05', 1758, 5, 769, 'Aviso de Tome Joaquim da Costa Corte Real a Alexandre Metelo de Sousa e Meneses, ordenando arrematar o contrato dos dízimos das Minas Gerais pelo maior lanço.', 'Conselho Ultramarino', 'Secretário de Estado', '', '');
