INSERT INTO app_hist.provocacao VALUES (198, 'Requerimento de João Ferreira de Oliveira, sargento-mor da ordenança das cidade de São Paulo, a Manuel Mendes de Almeida, provedor, pedindo que lhe seja passada certidão dos cargos que desempenhou. Consta um despacho do provedor citado, deferindo o pedido. Contém outros documentos, como carta patente de confirmação do posto de sargento-mor ao requerente, requerimentos, certidões e uma carta. ', 174, '1752-07-24', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (22, 'Requerimento do tenente de dragões das Minas Novas do Aracuai e Fanados, Jose Inacio da Fonseca, pedindo para que lhe seja concedido um hábito de Cristo, com oitenta mil reis de tença. Em anexo se encontra uma junta de papéis de serviço do tenente da tropa. ', 34, '1760-05-05', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (27, 'Requerimento de Baltasar Rodrigues Borba a D. Jose I pedindo confirmação do posto de capitão de cavalaria de auxiliares na freguesia de Santo Amaro, capitania de São Paulo. Consta o despacho do conselho ultramarino de 12/03/1770 deferindo o pedido. Contém, ainda, uma carta patente de 31/05/1766, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando e promovendo ao posto de capitão Baltasar Rodrigues Borba.', 43, '1770-03-17', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (214, 'O bacharel Manuel Caetano, nomeado intendente das Minas de Paracatu, pede para que vossa majestade providencie ajuda de custo no valor de duzentos e cinquenta mil reis para servir de ajuda aos intendentes da provedoria da fazenda para auxilia-los nas viagens de longa distância e ao mesmo tempo para evitar despesas desnecessárias as quais fazem os mesmos intendentes. ', 204, '1750-01-11', 1750, 5, 'R', '', '1 ');
INSERT INTO app_hist.provocacao VALUES (708, 'Requerimento do bacharel José de Sousa Monteiro, ex-ouvidor da comarca do Rio das Mortes, solicitando alvará de fiança para poder defender-se da acusação que impede sobre a sua pessoa', 773, '1755-07-17', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (33, 'Queixa de Luis Antonio de Sousa a Sebastião Jose de Carvalho e Melo, conde de Oeiras, contra Jose Honorio de Valadares e Aboim, provedor da fazenda real da capitania de São Paulo, que duvida de todos os despachos e expede ordens contrárias às suas determinações. ', 49, '1770-07-06', 1770, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (400, 'Requerimento de Jose de Godoi Moreira, provedor e contador da fazenda real da vila e praça de Santos, a D. Jose I pedindo duas vias da provisão de 1744 para que se possa aumentar o ordenado. Consta um despacho do conselho ultramarino, mandando passar, de 08/08/1754. Contém um outro requerimento, assim como uma certidão. ', 382, '1754-08-08', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (28, 'Requerimento de Pedro da Silva Chaves solicitando a D. Jose I a confirmação de sesmaria nas terras de uma légua de "terras de testada com outra de sertão". Consta um despacho do conselho ultramarino de 07/08/1770, mandando passar carta de confirmação. Contém, ainda, uma carta de sesmaria de 17/01/1764 por D. Antonio Alvares da Cunha, vice-rei do estado do Brasil. Segue-se no mesmo documento uma certidão de 10/04/1767 do tabelião Inacio Jose Ferreira Coutinho e um bilhete do secretário do conselho ultramarino de 17/08/1770 mandando passar a carta de confirmação de sesmaria à Pedro da Silva Chaves.', 44, '1770-05-14', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (499, 'Sebastião Francisco Bandeira pede ao rei a licença para se deslocar ao reino com sua família', 473, '1750-10-26', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (25, 'Requerimento de Antonio Blanco Ribeiro, capitão de auxiliares de infantaria da freguesia de Santo Amaro, para D. Jose solicitando a confirmação do posto de capitão. Consta o despacho do conselho ultramarino de 13/03/1770 deferindo o pedido. Contém, ainda, uma carta patente de 30/05/1766, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, tornando público que nomeou Antonio Blanco Ribeiro como capitão de auxiliares de infantaria da freguesia de Santo Amaro.', 40, '1770-03-13', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (722, 'Requerimento de Alexandre Luis de Sousa e Meneses, capitão dos dragões solicita prorrogação de licença para permanecer no Reino e dar continuidade a seus tratamento. ', 798, '1755-09-02', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (29, 'Requerimento de Paulino Aires de Aguirre a D. Jose I pedindo confirmação do posto de capitão de infantaria de auxiliares, na vila de Sorocaba do regimento de serra acima. Consta o despacho do conselho ultramarino de 07/06/1770 deferindo o pedido. Contém, ainda, uma carta patente de 28/06/1762, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão.', 45, '1770-06-07', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (23, 'Requerimento de Raimundo Jose de Sousa, alferes da infantaria paga da Companhia de Bastos da Praça de Santos, ao rei pedindo que lhe conceda um ano de licença para vir ao reino tratar de assuntos particulares, vencendo o soldo relativo ao posto que ocupar. Junto, há um requerimento encaminhado para Luis Antonio de Sousa Mourão, governador e capitão-general da capitania de São Paulo, no qual pede que não demore a informação que provavelmente lhe será pedida para tanto. Igualmente uma carta de Luis Antonio de Sousa Mourão, de 14/02/1770, confirmando que o alferes é merecedor da licença que requere. Sobre esta carta, há um despacho da provisão requerida pelo conselho ultramarino, do dia 12 de setembro de 1770[?].
INSERT INTO app_hist.provocacao VALUES (30, 'Requerimento de Francisco Gonçalves Pereira Belem a D. Jose I pedindo confirmação do posto de capitão auxiliar de infantaria da companhia da vila de Paranaguá. Consta o despacho do conselho ultramarino de 15/06/1770 deferindo o pedido. Contém, ainda, uma carta patente de 20/05/1760, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão.', 46, '1770-06-15', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (24, 'Requerimento de Antonio Lopes de Azevedo, tenente da infantaria da praça de Santos, ao rei pedindo que lhe conceda um ano de licença, com o soldo pago. Junto, há um requerimento encaminhado para Luis Antonio de Sousa, governador e capitão-general da capitania de São Paulo, no qual pede que o governador informe o que for conveniente para que se possa deferir o requerimento. Igualmente uma carta de Luis Antonio de Sousa Mourão, de 15/02/1770, favorável a que seja concedido um ano de licença a Antonio Lopes de Azevedo. Sobre esta carta, há um despacho da provisão concedida pelo conselho ultramarino, de 17/09/1770.', 39, '1770-02-15', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (20, 'Requerimento da viúva Josefa Teresa Ramos solicitando não ser concedida a mercê a Jose Manuel de Sousa, que em vida de seu marido, Domingos da Costa Guimarães, foi contratador do contrato das aguardentes e de saída dos escravos e das passagens do rio São Francisco. A justificativa é que ela havia sido desligada dos negócios. ', 35, '1760-05-05', 1760, 5, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (26, 'Requerimento de Jeronimo da Costa Guimarães solicitando a D. Jose I a confirmação de sesmaria nas terras no sítio de Jacareupava, na Vila de Sorocaba. Consta um parecer do procurador da fazenda e três despachos do conselho ultramarino de 01/08/1770, apenas um deles favorável. Contém, ainda, uma carta de sesmaria de 15/09/1768 pela qual Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, concedeu ao requerente terras de lavradio e sertão.', 41, '1770-03-16', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (633, 'Requerimento do coronel Antonio Pires de Campos solicitando ajuda de custo para continuar na "destruição" do indio Caiapó. Contem a certidão do escrivão das diligencias do sindicante, desembargador Manuel da Fonseca Brandão, José Pereira da Cunha, referente ao requerimento. ', 665, '1755-03-21', 1755, 28, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (35, 'Requerimento de Bernardo de Araujo a D. Jose I pedindo que lhe mande passar uma provisão permitindo que ele venha de São Paulo com Maria Pedroso Sanches, sua mulher, e seus filhos fixar residência em Portugal. Sobre este requerimento recaiu um despacho do conselho ultramarino de 28/07/1770 declarando que Luis Antonio de Sousa Botelho e Mourão, governador e capitão general da capitania de São Paulo, deve dar o seu parecer sobre o conteúdo deste requerimento.', 51, '1770-07-28', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (153, 'D. Lourenço de Almeida, ex governador das Minas Gerais e de Pernambuco, pede a vossa majestade e ao conselho ultramarino para que junte em um só lugar os papeis que o mesmo havia enviado em uma petição anterior. Essa petição se refere a um pedido anterior a qual o suplicante havia feito aonde requeria a concessão de uma mercê como recompensa por seus serviços prestados. Este pedido teve uma resposta requirindo o envio de certos documentos como a residência de seu governo, este o enviou porém no processo estes acabaram indo para outro lugar. ', 158, '1750-01-01', 1750, 5, 'R', '', '1');
INSERT INTO app_hist.provocacao VALUES (36, 'Requerimento de Joaquim Jose Freire da Silva a D. Jose I pedindo confirmação da provisão comprovativa da sua profissão a qual lhe foi dada por Luis Antonio de Sousa Botelho Mourão. Consta dois despachos do conselho ultramarino e um parecer do procurador da fazenda dizendo que o requerente deve apresentar por certidão as confirmações passadas aos seus antecessores. Contém um lembrete informando que o requerimento foi deferido; uma provisão de Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, provendo no cargo de médico do presídio e tropas desta capitania; uma certidão de fé de ofícios; uma provisão de Luis Antonio de Sousa Botelho Mourão provendo no cargo de médico do presídio e tropas da capitania de São Paulo, de 04/10/1769.', 52, '1770-08-07', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (659, 'Representação dos oficiais da câmara de vila Real de Nossa Senhora da Conceição do Sabará, a D. Jose I, informando a falta de segurança dos corregedores da comarca, e solicitando cartas de seguro para os referidos oficias.', 715, '1757-07-11', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (37, 'Requerimento de Inacio Francisco Xavier a D. Jose I pedindo confirmação de patente no posto de capitão  da companhia dos homens pardos forros na vila de São Vicente. Consta o despacho do conselho ultramarino de 09/09/1770 indeferindo o pedido. Contém, ainda, uma carta patente de 16/10/1769, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente para o posto de capitão da companhia de homens pardos forros pelo falecimento de Caetano da Silva Ferreira.', 53, '1770-09-09', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (32, 'Requerimento de Antonio Ferrão de Carvalho solicitando a D. Jose I que lhe mande passar carta de confirmação da terra, situada em Itacuruça. Constam dois despachos do conselho ultramarino e um parecer do procurador da fazenda, no qual pontua ser necessário pedir informações ao ouvidor da comarca sobre a legalidade do aforamento. Um dos despachos concorda com o parecer do procurador da fazenda, do dia 29/01/1771. Contém, ainda, uma escritura de aforamento de uma légua de terra em quadra concedida pelos oficiais da câmara da vila de Taubaté a Antonio Ferrão de Carvalho, do dia 25/05/1769.', 48, '1770-06-19', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (56, 'Cópia do requerimento de Domingos Fernandes Lima, morador em São Paulo, a D. Jose I pedindo licença para sair para o reino por motivos de doença, com sua mulher e duas criadas. Contém uma cópia de uma provisão de D. Jose I ordenando a Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, que o informe com o seu parecer, de 06/09/1769. Sobre este documento, recaiu um despacho do conselho ultramarino. Ainda, contém uma carta de Luis Antonio de Sousa Botelho Mourão dizendo não ver inconveniente que Domingos Fernandes Lima siga para o Reino com sua mulher e criadas, de 25/04/1771.', 68, '1771-04-25', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (548, 'Carta do desembargador João Cardoso de Azevedo para D. Jose I queixando-se da não execução da ordem régia para serem soltos os oficiais judiciais da cidade de Mariana que o juiz de fora mandara prender.', 552, '1757-01-15', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (551, 'Requerimento de Amaro dos Santos, ermitão da capela de Nossa Senhora de Nazaré, em São João Del Rei, comarca do Rio das Mortes, solicitando a prorrogação da licença para poder pedir esmolas para a referida capela. Consta bilhete do conselho ultramarino ordenando passar provisão por tempo de um ano.', 555, '1757-01-18', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (550, 'Requerimento do capitão Felisberto Caldeira Brant a D. Jose I solicitando suspensão das ordens de cobrança dos diamantes e seus fiadores referente ao contrato de 1749. Consta parecer do conselho ultramarino, sugerindo consultar o provedor da fazenda.', 554, '1757-01-17', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (34, 'Requerimento de Antonio Luis Pereira, capitão de auxiliares de infantaria da vila de Ubatuba, para D.. Jose solicitando a confirmação da patente do seu posto. Consta o despacho do conselho ultramarino de 10/07/1770 deferindo o pedido. Contém, ainda, uma carta patente de 16/02/1776, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando Antonio Luis Pereira capitão de auxiliares de infantaria da vila de Ubatuba.', 50, '1770-07-10', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (215, 'Manuel Antunes Viana, capitão da ordenança de uma companhia da Vila Real do Sabará, requere a vossa majestade a permissão para continuar no dito cargo. ', 205, '1750-07-13', 1750, 5, 'R', '', '2 ');
INSERT INTO app_hist.provocacao VALUES (158, 'Requerimento de Antonio da Silva de Araujo a D. Jose I em que diz que foi nomeado para os ofícios de escrivão da câmara e almoçataria da cidade de São Paulo e no de escrivão da câmara, almoçataria, órfãos e tabelião do público judicial e notas da vila de Guaratinguetá. Como era contra as ordens régias, passar-se à mesma pessoa provimentos de mais de um ofício, pede ao rei que os mande passar com declaração de que os referidos ofícios serão servidos por diversas pessoas. Consta um decreto de D. Jose I nomeando o requerente para lugar de escrivão, de 12/11/1750; um despacho do conselho ultramarino, mandando- cumprir, de 17/11/1750. Contém alguns decretos de D. Jose I, uma certidão e um bilhete do secretário do conselho ultramarino.', 164, '1750-11-11', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (302, 'Francisco Gomes da Cruz, preso na cadeia de Vila Rica devido a discórdias religiosas, solicita ao rei a mercê do pagamento de fiança para poder cuidar de seus bens', 293, '1760-08-21', 1750, 5, 'R', '', '2-3');
INSERT INTO app_hist.provocacao VALUES (303, 'Francisco Xavier e sua esposa Tereza de Purilicação, moradores da freguesia de Piracicaba, solicitam ao rei licença para irem ao reino junto de seus filhos.  ', 296, '1750-08-20', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (552, 'Requerimento dos oficiais da câmara da vila de São Sebastião da comarca de São Paulo a D. Jose I pedindo que se ordenasse a Rodrigo Cesar de Meneses que não se recrutasse mais soldados da vila de São Sebastião e restituísse todos os que de lá foram tirados. Consta um despacho do conselho ultramarino, mandando informar sobre, de 13/01/1756.', 564, '1756-01-13', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (723, 'Requerimento do capitão Manuel Dias Veloso, morador das Minas de Paracatu, comarca dos Rio das Velhas, solicitando provisão para que o ouvidor-geral da comarca a que pertence, Sabará, possa ser juiz da causa em que contende com Antonio de Aguiar Freire.', 799, '1755-09-09', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (59, 'Requerimento de Jose Felipe Santiago a D. João V solicitando mais tempo para que consiga levantar as certidões de fiança que fez à Cecilia Joana, referente a cobrança da herança de seu pai, o fundidor da casa de fundição de São Paulo, Francisco Pinheiro de Abreu, pois foi notificado a pouco tempo. Consta parecer positivo do conselho ultramarino de 14/06/1737.', 71, '1737-05-07', 1737, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (146, 'Requerimento de dom frei Antonio da Madre de Deus Galrão, bispo de São Paulo, a D. Jose I pedindo que lhe seja assinado um termo da divisão do bispado em conformidade com a bula do santo padre, e que Goiás e Cuiabá deixem de ser governados pelo bispado de Rio de Janeiro e passem a sê-lo pelo de São Paulo. Consta uma resolução de D. Jose I ordenando que se veja no conselho ultramarino e que se lhe consulte o que parecer, de 20/08/1750.', 151, '1750-08-20', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (395, 'Carta de Felisberto Caldeira Brant, contratador dos diamantes, ao secretário de Estado, informando ter recebido os despachos de suas petições sobre a demarcação de terras, em que se queixa que sua demarcação foi tão retardada que não resolve a falta de terras que tinha para dar a fazer os negros. Queixa dos oficiais da intendëncia dos diamentes que o querem prejudicar e solicita sua intervenção. ', 375, '1751-01-03', 1751, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (553, 'Requerimento dos oficiais da câmara da vila e praça de Santos para D. Jose I pedindo  para lhes mandar passar por certidão o teor das ordens régias expedidas pelo conselho ultramarino ao governador para não permitir que se recrutassem como soldados os filhos dos moradores da vila de Santos e São Vicente. Consta um despacho do conselho ultramarino, deferindo-o, de 16/10/1755. Segue-se no documento a certidão pedida. ', 564, '1755-10-16', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (259, 'Requerimento de João Teixeira Carvalho, capitão da fortaleza da Itapema da praça de Santos, a D. Jose I pedindo que lhe seja dada autorização para passar à vila de Santos, sem que para isso tenha de pedir licença aos cabos maiores, salvo em ocasião de guerra. Consta um despacho do conselho ultramarino, indeferindo o pedido, de 24/01/1753.', 248, '1753-01-24', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (39, 'Requerimento de Manuel Teixeira Coelho, procurador da câmara de São Paulo, pedindo que se lhe passe por certidão e o teor do requerimento feito a Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, para aplicação de novos impostos que se cobram das vendas desta cidade para a feitura da Ponte Grande de Nossa Senhora da Cruz, com resposta do provedor da fazenda real. Contém certidão pedida; requerimento dos oficiais da câmara de São Paulo a Luis Antonio de Sousa Botelho Mourão; informação do escrivão da fazenda real de 27/10/1770; informação do provedor da fazenda real, de 28/10/1770.', 55, '1770-10-29', 1770, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (101, 'Requerimento do governador de São Paulo, D. Luis de Mascarenhas, a D. João V solicitando prover os ofícios de São Paulo por tempo de um ano, devido à encontrar-se distante nas minas de Goiás.', 111, '1739-09-30', 1739, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (218, 'Requerimento dos oficiais da câmara de São Paulo a D. Jose I para que lhes mande passar certidão da provisão de D. João V de 10 de novembro de 1726 aos oficiais da câmara de São Paulo. Contém uma certidão da provisão de D. João V aos oficiais da câmara de São Paulo, de 11/02/1752; cópias de provisões autenticadas pelo cavaleiro professo da ordem de cristo e tabelião público Joaquim Jose Vieira Henriques, estas se referem aos privilégios concedidos para os oficiais da câmara do Rio de Janeiro e cidadãos do Porto, de 21/02/1752, 22/02/1752; uma carta régia em que D. Jose I manda ao juiz de fora da cidade do Rio de Janeiro que guarde e faça guardar aos oficiais da câmara e moradores daquela capitania os privilégios que até ali lhe foram concedidos, de 21/02/1752; e um bilhete do secretário do conselho ultramarino Joaquim Miguel Lopes do Lavre dizendo que se passe alvará aos oficiais da câmara da cidade de São Paulo em virtude de D. Jose I lhe ter feito os mesmos privilégios que tinham os oficiais da câmara do Rio de Janeiro, de 31/03/1752. Consta a consulta do conselho ultramarino para que fossem concedidos os mesmos privilégios que tem os oficiais da câmara do Rio de Janeiro que por sua vez são os mesmos que foram concedidos aos cidadãos do Porto, de 08/03/1752.', 208, '1752-03-08', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (40, 'Requerimento de João Antunes de Sousa a D. Jose I pedindo confirmação de patente do posto de sargento-mor de ordenanças dos sertões, das minas da Ribeira, Paranapanema, Apiaí e Nossa Senhora da Guia de Xiririca e sertões do mesmo distrito. Consta o despacho do conselho ultramarino de 30/10/1770 indeferindo o pedido. Contém, ainda, uma carta patente de 26/05/1769, passada anteriormente por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como sargento-mor; e o termo de posse e juramento.', 56, '1770-10-30', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (367, 'Carta dos oficiais da câmara de Santos para D. Jose I informando o recebimento de sua carta, na qual o rei deixava no arbítrio da câmara os meios de se estabelecer ao povo daquela vila um donativo gratuito, feito sem opressão do povo e com seu inteiro agrado. Consta certidão do escrivão da câmara da vila de Santos, Jacinto Tavares Amado, na qual comunica que os almotacés que não cobrassem o novo imposto receberiam pena de multa, assim como os capitães de navios.', 352, '1759-04-26', 1759, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (62, 'Requerimento de Garcia Rodrigues Pais Colaça, capitão-mor regente dos sertões e minas da Ribeira, Paranapanema, Apiaí e Nossa Senhora da Guia de Xiririca, pelo qual pede a  a D. Jose I a sua confirmação do posto que foi provido por Luis Antonio de Sousa Botelho Mourão. Consta despacho do conselho ultramarino, mandando o governador e capitão-general dar o seu parecer e enviar cópias das ordens pelas quais tem a faculdade de o prover, de 17/06/1771. Contém, ainda, uma carta patente de 21/03/1768 passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, pela qual nomeia o requerente para o posto de capitão-mor. ', 74, '1771-06-17', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (154, 'Requerimento de Alexandre Gomes Arevolo de Leão a D. Jose I pedindo que lhe fizesse mercê do ofício de escrivão da câmara da vila de Taubaté por seis anos. Contém dois despachos do conselho ultramarino, o último determinado que o juiz da Índia e Mina informasse com o seu parecer, de 16/10/1750. Contém uma carta do juiz da Índia e Mina dizendo que o requerente tinha capacidade para exercer a dita ocupação, de 23/09/1750, e um aviso do secretário do estado dos negócios da marinha e domínios ultramarinos ao presidente do conselho ultramarino dizendo que D. Jose I mandava que o conselho ultramarino fizesse o que lhe parecesse sobre a petição. ', 160, '1750-09-28', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (401, 'Requerimento de Jose de Godoi Moreira, provedor e contador da fazenda da vila e praça de Santos, a D. Jose I pedindo que lhe mande passar certidão da dita provisão. Consta um despacho do conselho ultramarino, dizendo que se podia passar sem inconveniente, de 03/08/1754. Segue-se uma certidão. Consta um outro requerimento.', 382, '1754-08-03', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (724, 'Requerimento do Padre Manuel Ferreira de Ascensão solicitando alvará de mantimento para poder cobrar sua congrua. ', 800, '1755-09-09', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (102, 'Requerimento de Jose Soares de Barros a D. João V, solicitando a restituição da quinta parte de seu ordenado, que o superintendente da casa de fundição de São Paulo o cobrou indevidamente. Consta provisão ordenando ao provedor da fazenda real de Santos que apresente seu parecer.', 112, '1739-10-06', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (155, 'Requerimento de Fernando Leite Guimarães, capitão de artilharia da guarnição da praça de Santos, a D. Jose I em que tendo ele matrícula anterior à do capitão de infantaria da praça de Santos, Silvestre Teixeira Pinto pedia que fosse ele o preferido no serviço. Consta um despacho do conselho ultramarino, ordenando que o governador informasse com o seu parecer, de 13/10/1750. Contém um outro requerimento deste suplicante pedindo uma certidão, um despacho do provedor da fazenda real ordenando que se lhe passasse a respectiva certidão (de 09/03/1750), a certidão pedida (de 09/03/1750); um outro requerimento deste suplicante também pedindo certidão, na qual um despacho do conselho ultramarino ordenando que se passasse o que constasse (de 20/08/1750) e a certidão pedida (de 20/08/1750).', 161, '1750-10-13', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (156, 'Requerimento do bispo de São Paulo, Antonio da Madre de Deus Galvão, a D. Jose I pedindo que lhe mandasse passar ordem para que lhe dessem quatro mil cruzados de ajudas de custo para o seu transporte, pois assim costumava mandar dar a todos os bispos que se nomeavam para as terras do ultramar. Constam três despachos do conselho ultramarino, o último dos quais ordenando que se satisfizesse o pedido na forma consultada, de 06/11/1750.', 162, '1750-10-14', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (52, 'Requerimento de Cipriano Gomes Veiga pedindo a D. Jose I confirmação do posto de sargento-mor das ordenanças da vila de Taubaté. Consta despacho do conselho ultramarino, indeferindo, de 12/01/1771. Contém, ainda, uma carta patente de 18/10/1765 passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente no posto citado. ', 63, '1770-01-12', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (245, 'Representação dos oficiais da câmara de São Paulo a D. João V informando que lhes competia pagar ao pároco determinada quantia por cada escravo falecido. O então pároco aumentou a importância estabelecida, dizendo ser ordem do bispo falecido. Solicitam ao monarca que ele ordene aos párocos e vigários daquele bispado que se regulem pelo estabelecido na cidade do Rio de Janeiro; bem como, ao provedor da misericórdia de São Paulo se nomeie um sacerdote que tivesse sido capelão daquela casa e que assistisse aos enterros à semelhança do observado no Rio de Janeiro. Consta dois despachos do conselho ultramarino, o segundo determinando que o bispo de São Paulo informe com o seu parecer, de 20/10/1750, além de outro procurador da coroa segundo o qual o novo bispo deve informar, se ainda não existir, sobre o governador do bispado. Contém uma carta do bispo de São Paulo, D. Frei Antonio da Madre de Deus Galvão, para D. Jose I, de 20/08/1752.', 231, '1748-12-30', 1748, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (170, 'Representação dos oficiais da câmara da vila de Guaratinguetá também em nome dos das câmaras vizinhas a D. Jose I pedindo que se proíba a plantação de tabaco em todas as Minas que prejudica os rendimentos da coroa e os dos habitantes daquelas vilas. Constam dois despachos do conselho ultramarino, um dos quais dizendo que informe o governador e capitão general da capitania do Rio de Janeiro com o seu parecer, de 03/09/1751. Contém uma carta do governador e capitão general da capitania do Rio de Janeiro para D. Jose I dizendo que não não devem privar dessa concessão os moradores das Minas, de 10/08/1752, e um despacho do conselho ultramarino e um parecer do procurador da fazenda, considerando inatendível a súplica; e uma provisão de D. Jose I para o governador e capitão general da capitania do Rio de Janeiro ordenando-lhe que dê o seu parecer acerca da representação, de 04/02/1752.', 175, '1751-04-25', 1751, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (660, 'Representação dos oficiais da câmara de de Vila Real de Nossa Senhora da Conceição do Sabará a D. Jose I, solicitando que envie os devidos alvarás e provisões pedidos pela Secretaria de Estado às referidas autoridades, para evitar que as ordens se suprimam e cause desordens na comarca.', 716, '1757-07-11', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (44, 'Representação dos oficiais da câmara de São Paulo a D. Jose I expondo-lhe a falta de vassalos nos empregos da câmara, o que motiva a desordem conforme se expressa na cópia que enviam dos seus antecessores, de 05/12/1770. Consta dois despachos do conselho ultramarino e um parecer do procurador da fazenda.  Contém uma cópia da representação dos oficiais da câmara de São Paulo pedindo providências sobre a isenção que conseguiam de Luis Antonio de Sousa, governador e capitão-general da capitania de São Paulo, para não servirem os cargos da república os que eram oficiais e soldados dos regimentos de auxiliares, de 19/12/1767.', 60, '1770-12-05', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (178, 'Requerimento de Diogo Pinto do Rego, mestre-de-campo e escrivão da ouvidoria e correição da cidade de São Paulo, dizendo que por muitos anos padecia de moléstias que o impediam de fazer a sua vida normal e de servir o dito ofício. Pedia ao rei que lhe conceda alvará a fim de poder nomear outra pessoa para servir o seu lugar. Contém dois despachos do conselho ultramarino, o último dos quais mandando que o ouvidor informe com o seu parecer, de 17/04/1752, um parecer do procurador da fazenda segundo o qual as causas alegadas pelo requerente não eram das que se costumava atender para deferir. Contém requerimentos anteriores do mesmo, assim como certidões, alvarás e um aviso de Diogo de Mendonça Corte Real informando o marquês de Penalva que D. Jose I ordenava que o dito conselho informasse com o seu parecer sobre o requerimento de Diogo, de 29/10/1751.', 183, '1751-10-29', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (159, 'Requerimento de Jose de Sousa e Silva a D. Jose I em que diz que foi nomeado para os ofícios de escrivão da câmara, almoçataria, órfãos e tabelião do público judicial e notas da vila de algumas vilas. Como era contra as ordens régias, passar-se à mesma pessoa provimentos de mais de um ofício, pede ao rei que os mande passar com declaração de que os referidos ofícios serão servidos por diversas pessoas. Consta um decreto de D. Jose I nomeando o requerente para lugar de escrivão, de 12/11/1750; um despacho do conselho ultramarino, mandando- cumprir, de 17/11/1750. Contém alguns decretos de D. Jose I, uma certidão e um bilhete do secretário do conselho ultramarino.', 165, '1750-11-12', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (554, 'Requerimento dos oficiais da câmara da vila de São Sebastião para a câmara da vila de São Vicente pedindo o traslado da ordem real que proibia o recrutamento de soldados naquela vila. Consta um despacho da dita câmara, deferindo-o, de 09/09/1754. Segue-se no documento o traslado pedido.', 564, '1754-09-09', 1754, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (669, 'Requerimento de Antonio Vidal a D. Jose I, solicitando confirmação no exercício do posto de capitão da companhia da ordenança de pé no distrito do Mato, caminho novo que vai de Minas Gerais ao Rio de Janeiro. Consta referida carta patente de nomeação do governador de Minas Gerais, Jose Antonio Freire de Andrade.	', 725, '1757-08-12', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (701, 'Requerimento do Padre Gaspar dos Reis Silveira, vigario encomendado da freguesias de Antonio Pereira e de Carijos, no bispado de Mariana, solicitando alvara de mantimento para receber a sua congrua. ', 761, '1755-05-15', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (725, 'Requerimento do Padre Manuel Ferreira de Ascensão solicitando alvará de mantimento para poder cobrar sua congrua. ', 800, '1755-09-09', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (784, 'maisa Requerimento de Batista Pereira da Costa, capitão da ordenança de pé do distrito da capela de Nossa Senhora da Glória da Ressaca, termo da Vila de São José, comarca de São João Del Rei do Rio das Mortes, solicitando a confirmação no exercício do referido posto. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 865, '1760-01-21', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (256, 'Requerimento de João Ferreira de Oliveira, sargento-mor das ordenanças da cidade de São Paulo, a D. Jose I, em que pede que lhe seja passada provisão para ser isento de servir os cargos da república, em virtude de padecer duma grande doença nos olhos. Consta um despacho do conselho ultramarino, mandando que se passe provisão para que o suplicante seja isento de servir, visto estar servindo o posto, de 09/11/1752. Contém um outro requerimento do mesmo requerente anterior a este e um bilhete do conselheiro Diogo Rangel de Almeida Castelo Branco. ', 244, '1752-11-09', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (57, 'Requerimento do governador de São Paulo, Antonio Luis de Tavora, a D. João V solicitando a permanência do secretário do governo de São Paulo, Antonio da Silva de Almeida, por mais três anos, devido ao seu bom serviço.', 69, '1736-08-04', 1736, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (202, 'Requerimento de Diogo Pinto do Rego dizendo ao juiz ordinário, João do Prado Camargo, que lhe era necessário justificar que era escrivão da ouvidoria geral e correição da cidade de São Paulo e que padecia de males que o não deixavam exercer o seu ofício. Contém requerimentos do mesmo, assim como certidões, alvarás e avisos.', 183, '1750-03-10', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (55, 'Carta de Luis Antonio de Sousa Mourão, governador e capitão-general da capitania de São Paulo, para Martinho de Melo e Castro, ministro e secretário de estado dos negócios da marinha e domínios ultramarinos, propondo Jose Gomes Pinto de Morais, juiz de fora da vila de Santos, para o cargo de ouvidor da capitania de São Paulo ou a qualquer outro compatível com as suas habilitações. Contém a mesma carta duplicada. ', 67, '1771-04-21', 1771, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (46, 'Requerimento de Simão Rodrigues Cardoso, soldado auxiliar da companhia do capitão Antonio de Sousa Morais, da vila de Mogi das Cruzes, ao governador pedindo que os oficiais da câmara daquela vila procedam e elejam outra pessoa para o lugar do vereador. Consta um despacho do dito governador, de 07/11/1770.', 62, '1770-11-07', 1770, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (172, 'Requerimento de Manuel Francisco de Figueiredo, ajudante supra de infantaria da praça de Santos, pedindo a D. Jose I licença para ir ao reino. Consta um despacho do conselho ultramarino dizendo que se passe provisão de licença por um ano, de 17/09/1751. Contém um bilhete do conselheiro. ', 177, '1751-09-17', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (176, 'Requerimento de Manuel da Silva, alferes da guarnição da praça de Santos, a D. Jose I pedindo a sua nomeação para o lugar de capitão da praça de Santos ou para o mesmo posto na ilha de Santa Catarina. Consta um despacho do conselho ultramarino, indeferindo o pedido, de 28/09/1751.  Contém folha de serviços do requerente.', 181, '1751-09-28', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (49, 'Requerimento de Antonio de Sousa de Morais, capitão de auxiliares da freguesia de Mogi das Cruzes da capitania de São Paulo, ao governador pedindo que se proceda a nova eleição de barrete, em virtude de não poder exercer ao mesmo tempo aquela ocupação e a de juiz ordinário para que tinha sido nomeado. Consta um despacho do governador, de 07/11/1770.', 62, '1770-11-07', 1770, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (613, 'CONSULTA Consulta do conselho ultramarino a respeito da petição de Bernardo Joaquim Pessoa de Lemos, morador em Vila Rica do Ouro Preto, na qual ele solicitava permissão para se deslocar ao reino com sua família. Em anexo um bilhete de ordem de provisão', 642, '1751-10-16', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (174, 'Requerimento de Manuel Correa Quintana a D. Jose I pedindo que fosse nomeado para o lugar vago de capitão da praça de Santos ou para o mesmo lugar na ilha de Santa Catarina. Consta um despacho do conselho ultramarino, indeferindo-o, de 22/09/1751. Contém uma folha de serviços do requerente feitos na praça de Santos. ', 179, '1751-09-22', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (349, 'Requerimento do bacharel João Evangelista de Maris Sarmento, ouvidor da comarca do Serro Frio, pedindo a concessão de 200 mil reis de ajuda de custo de que tem direito. O pedido foi deferido.', 327, '1755-02-25', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (276, 'A junta dos três estados solicita ao conselho ultramarino a avaliação dos rendimentos do ofício de partidor e avaliador de Sabará, que são feitos por Antonio Ferreira de Araujo Braga.', 269, '1750-03-03', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (50, 'Requerimento de Antonio Fernandes de Brito do termo de Emboaçaca, pedindo que o isente do cargo de vereador que devia ocupar. Consta um despacho do governador, de 11/12/1770.', 62, '1770-12-11', 1770, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (384, 'Carta de João Gonçalves Seixas a Sebastião Jose de Carvalho e Melo queixando-se contra o governador de Santos, Inacio Eloi de Madureira, que se serviu do pretexto de mandar levar uma carta às minas de Goiás para seduzir a sua mulher. ', 366, '1754-06-08', 1754, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (594, 'Requerimento de Francisco Lobo Rios, assistente nas minas do Rio das Mortes, a D. Jose I, solicitando licença para regressar ao reino de Portugal com sua mulher, filhos e uma cunhada.', 613, '1757-03-02', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (51, 'Carta de Salvador Pereira da Silva, ouvidor da comarca de São Paulo, dizendo que havia muitos desentendimentos na administração da justiça de toda a comarca de São Paulo por não servirem nas câmaras pessoas competentes, mas pessoas indignas, de 15/12/1770. Noticiou-se a Luis Antonio de Sousa Mourão, governador da capitania, mas ele nada resolveu. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, de 17/08/1771.', 62, '1770-12-15', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (502, 'Teresa de Fonseca Magalhães, viuva do capitão Tome Fernandes do Vale, pede ao rei licença para viajar ao Reino com seus filhos. ', 476, '1750-10-27', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (47, 'Requerimento de Antonio Leite de Siqueira, soldado da companhia do capitão Panteleão Pedroso de Pontes, da vila de Mogi das Cruzes pedindo que seja isento de ocupar o lugar de procurador do conselho, para que tinha sido notificado. Consta um despacho do governador, de 07/11/1778.', 62, '1770-11-07', 1770, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (54, 'Representação dos oficiais da câmara de São Paulo a D. Jose I solicitando que as tropas auxiliares voltem a ser constituídas na forma como haviam pedido na cópia que enviaram os antigos oficiais, estando prontos a servir sem vencimento de soldo algum, de 05/12/1770.  Constam dois despachos do conselho ultramarino e um parecer do procurador da fazenda. Contém, ainda, uma cópia da representação dos oficiais da câmara de São Paulo expondo a falta de exercício nas vilas da comarca em que se deviam ocupar os sargentos-mores e ajudantes das tropas auxiliares evitando parte da grande despesa anual. ', 66, '1770-12-05', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (726, 'Requerimento do Padre Manuel Ferreira de Ascensão solicitando alvará de mantimento para poder cobrar sua congrua. ', 800, '1755-09-09', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (786, 'maisa Requerimento de Manuel Gomes Ribeiro, Francisco Gomes Ribeiro e Antonio da Costa de Araujo a D. Jose I, solicitando confirmação da carta de sesmaria de meia légua de terra em quadra situada no caminho de Minas Gerais, na paragem chamada Pão Grande da capitania do Rio de Janeiro.', 872, '1760-01-24', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (58, 'Requerimento de Antonio dos Santos Pereira a D. João V solicitando uma certidão para comprovar que foi concedido uma prorrogação de seu oficio de escrivão do registro de Paraibuna por tempo de um ano. Consta parecer positivo do conselho ultramarino.', 70, '1737-03-11', 1737, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (149, 'Requerimento do capitão Miguel Pinheiro de Resende e de Eusebio da Cunha e Sousa, moradores na vila de Taubaté, a D. Jose I pedindo que, em virtude do governador de São Paulo, D. Luis Mascarenhas lhe ter feito doação de uma légua de terras de testada, lhes passasse confirmação da dita doação. Consta três despachos do conselho ultramarino, o último dos quais ordenando que requeressem com carta de sesmaria, 29/10/1750. Contém carta de sesmaria do governador aos requerentes concedendo-lhes a terra no distrito de Taubaté, de 08/01/1749. Há. ao final, uma nota dizendo que fora escusada por despacho de 29/10/1749.', 154, '1750-09-11', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (157, 'Requerimento de Francisco Caetano de Almeida, bacharel, a D. Jose I pedindo que lhe passassem por certidão o traslado das provisões que, pelo conselho ultramarino. se tinham mandado passar ao seu antecessor. Consta um despacho do conselho ultramarino, ordenando que se passassem os ditos traslados, de 28/07/1750. Contém certidões, provisões e um bilhete do secretário do conselho ultramarino. Além disso há um outro requerimento no qual pede que, em virtude de ter sido provido no lugar de juiz de fora da vila de santos, lhe concedesse as mesmas graças e ajudas de custo que tinham sido concedidas ao seu antecessor e lhe mandassem passar as mesmas provisões. Consta um despacho do conselho ultramarino determinando que se passassem as provisões que constassem da certidão junta, e que se tinham passado a seu antecessor, de 27/10/1750.', 163, '1750-10-27', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (260, 'Requerimento do capitão Miguel Pinheiro de Resende e de Eusebio da Cunha e Santos, solicitando a D. Jose I confirmação da carta de sesmaria concedida pelo governador do distrito e comarca de São Paulo. Consta um parecer do procurador a fazenda, outro do procurador da coroa e três despachos do conselho ultramarino, o último dos quais a indeferir, de 13/,02/1754.', 249, '1753-01-30', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (71, 'Requerimento de Domingos Gomes da Costa, contratador de uma fábrica de pesca de baleias em São Paulo, a D. João V solicitando provisão para mandar dois navios contendo azeite para Lisboa e depois para os mesmos voltarem com os rendimentos. Contém consulta e parecer positivo do conselho ultramarino.', 83, '1738-10-30', 1738, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (69, 'Requerimento do capitão da praça de Santos, Jose Rodrigues da Silva a D. João V solicitando a criação de mais uma companhia de infantaria na praça de Santos, por julgar as existentes insuficientes. Solicita também destacamento da ilha de Santa Catarina, para evitar deslocamento de soldados de outros lugares para lá.', 80, '1738-09-17', 1738, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (68, 'Requerimento de Manuel da Costa Ferreira, Eusebio de Oliveira Magalhães, Inacio Cabral, Jose Martins de Brito e Jose Dias Leite a D. Jose I pedindo a confirmação de sesmaria de meia légua de terras que Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, lhes concedem. Consta um parecer do procurador da fazenda e três despachos do conselho ultramarino, o último dos quais manda passar a cada um dos requerentes carta de confirmação de sesmaria, de 19/02/1772.  Contém, ainda, um bilhete do secretário do conselho ultramarino mandando passar a Manuel da Costa Ferreira carta de confirmação da sesmaria na capitania de São Paulo, de 16/03/1772.
INSERT INTO app_hist.provocacao VALUES (710, 'maisa duvida Requerimento do bacharel Custodio da Costa Souto Veloso e de suas irmãs solicitando a D. Jose I o pagamento do dinheiro que ficou em poder de Manuel João Baltasar pela venda da casa de seu pai. O pedido foi escusado. ', 776, '1758-10-26', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (104, 'Requerimento do sargento-mor Claro Francisco Nogueira a D. João V, solicitando provisão que lhe permita mover uma causa de perdas e danos contra o juiz de fora da vila de Santos, Gaspar da Rocha Pereira, e que possa mover essa ação durante sua judicatura.', 114, '1740-11-26', 1740, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (304, 'Manuel da Silva Vila Fria requer a confirmação da sesmaria que havia recebido, a qual teria  meia légua de terra em quadra no sítio Conceição da Barra. ', 297, '1750-08-22', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (185, 'FABI Requerimento do capitão de infantaria de uma das guarnições da praça de santos, Francisco Fernandes Montanha ao governador-geral de São Paulo dizendo que tendo o falecido o alferes João Francisco Maltes nomeara para este posto o sargento Francisco João, em virtude de ele já servir há mais de 30 anos com todo o zelo. Por isso, pedia que deferisse a dita nomeação. Contém um grande conjunto de documentos, como certidões e requerimentos.', 146, '1734-06-08', 1734, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (278, 'Gregório Maia Neves e seu sócio Joao Correia Campos requerem a vossa majestade a confirmação da sesmaria que haviam recebido do governador das Minas Gerais. ', 272, '1750-08-13', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (477, 'Antonio Carvalho Guimarães e Manuel Alves Coelho solicitam ao rei a confirmação de meia légua de terra.', 448, '1750-09-17', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (288, 'Queixa do escrivão da matrícula da gente de guerra da praça de Santos, Miguel das Aguias Cordeiro, a vários militares das famílias altivas e soberba que servem naquela praça, afirmando que os mesmos estão prejudicando a Fazenda Real, recebendo soldos não devidos e cometendo outras irregularidades.', 283, '1759-04-26', 1759, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (525, 'Manuel Alves Correa, soldado, pede ao rei licença para viajar ao reino ', 501, '1750-12-03', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (296, 'Requerimento do padre Jose Jacinto Ferraz, clérigo, pedindo a D. Jose I alvará de mantimento para cobrar a sua côngrua por ter sido nomeado para uma conesia da sé do bispado de São Paulo. Consta um despacho favorável do conselho ultramarino, de 09/05/1753.', 289, '1753-05-09', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (727, 'Requerimento de Manuel Fernandes da Silva solicitando confirmação de sesmaria de meia légua de terra que possui junto a lagoa dourada na comarca do Rio das Mortes', 801, '1755-09-13', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (277, 'Sancho de Andrade Castro e Lanções, intendente dos diamantes do Serro do Frio, solicita à vossa majestade para que seus ordenados sejam recebidos desde o dia do embarque.  ', 270, '1750-08-13', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (600, 'Requerimento do bacharel Jose Pinto Ferreira, da vila do Sabará, a D. Jose I, solicitando licença pra transportar sua mulher e seus escravos para Portugal. Consta parecer positivo do Conselho Ultramarino e bilhete solicitando provisão para a causa.', 629, '1757-03-14', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (177, 'Requerimento de Antonio Pires de Campos, coronel regente da conquista do gentio Caiapó, dizendo que tendo ajustado com o D. Luis Mascarenha a referida conquista e cumprindo o determinado, pede ao rei que mande passar os despachos necessários ou portarias da mercê do hábito da ordem de cristo. Consta dois despachos do conselho ultramarino, um parecer do procurador da fazenda, um parecer do procurador da coroa e um último parecer do conselho ultramarino, segundo o qual se devia deferir o pedido do requerente, de 13/01/1751.', 182, '1751-10-05', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (734, 'Requerimento de Francisco Antonio de Oliveira, comerciante de escravos e morador na comarca do Sabará, pedindo permissão para o uso do porte de arma.', 812, '1755-09-17', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (305, 'Amaro dos Santos, ermitão da capela de Nossa Senhora de Nazaré, solicita ao rei a mercê de conceder nova provisão para pedir esmolas por toda região da capitania de Minas', 298, '1750-08-25', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (209, 'Requerimento de Luis Ramos Barbas solicitando que lhe fosse passada certidão de posse das terras de sesmarias, dado por D. Luis Mascarenhas. Contém um despacho do juiz ordinário João da Costa Fialho, mandando ao escrivão e alcaide que dê a posse requerida, de 14/04/1751. Contém uma carta de sesmaria de 21/08/1748 por D. Luis Mascarenhas; além de um outro requerimento, uma certidão e um bilhete.', 200, NULL, 1752, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (246, 'O bacharel Domingos Pinheiro solicita que seja promovido à posição de provedor da fazenda das Minas Gerais já que o cargo se encontra vago devido à morte de seu antecessor.  ', 232, '1749-10-23', 1749, 5, 'R', '', '3');
INSERT INTO app_hist.provocacao VALUES (73, 'Requerimento de Jose de Macedo Souto Maior e Castro, sargento-mor dos dois terços de cavalaria auxiliar da capitania de São Paulo, a D. Jose I pedindo dois anos de licença para ir à corte tratar dos negócios de sua casa. Consta um despacho do conselho ultramarino pedindo o parecer do governo de São Paulo, de 23/10/1771.', 85, '1771-10-23', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (77, 'Requerimento de Antonio Luis de Tavora, conde de Sarzerdas, a D. João V solicitando 1. ajuda de custo de quatro mil cruzados, pelo tempo em que reside em Goiás e 2. que os gastos de seu transporte e de sua comitiva fossem a custa da Fazenda Real. O conselho ultramarino concede parecer positivo a ajuda de custo, mas afirma que não deve fazer as despesas pela fazenda real pois resultaria em diversos inconvenientes. Consta cópia da consulta, do requerimento, da carta e de um bilhete.', 89, '1739-05-16', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (270, 'Queixa do provedor da fazenda da capitania de São Paulo, Jose de Godoi Moreira, aos ouvidores e provedores dos ausentes da comarca de São Paulo, relatando os abusos, violências, roubos e vexames que são praticados pelos mesmos, que não reconhecem a autoridade do provedor da fazenda e descumprem as ordens do rei. Consta cópia de duas cartas precatórias e duas certidões, assinadas pelo comandante de destacamento de Curitiba e cabo de esquadro da infantaria da praça de Santos, Francisco Xavier Pinto e pelo escrivão da fazenda real, Angelo Xavier do Prado, onde atestam as acusações do suplicante, afirmando o não cumprimento das ordens.', 263, '1757-06-13', 1757, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (558, 'Requerimento de Jose Rebelo Pinto, cônego, pedindo a D. Jose alvará de mantimento por lhe ter sido concedida uma conezia do bispado de São Paulo. Consta um despacho favorável do conselho ultramarino, de 08/05/1756.', 568, '1756-05-08', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (42, 'Representação dos oficiais da câmara de São Paulo a D. Jose I, de 05/12/1770, expondo-lhe que os monges do mosteiro do Patriarca São Bento continuam a cobrar os foros e laudêmios por eles estabelecidos, não se abstendo de semelhante prática, esperando que o rei dê remédio justo para aquele mal. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, um dos quais mandando que Luis Antonio de Sousa Mourão, governador e capitão-general da capitania de São Paulo, informe em conformidade com a resposta do procurador da fazenda, de 12/10/1771. Contém uma cópia de um requerimento do abade do mosteiro do Patriarca São Bento, no qual dizem que Luis Antonio de Sousa Mourão, governador e capitão-general da capitania de São Paulo, D. Francisco de Sousa e a antiga câmara lhe deram o sítio para fundar o seu convento, mas como o lugar em volta era muito seco, pedem que lhes mandem dar o brejo. Sobre este requerimento recaiu um despacho da câmara, de 02/04/1667; uma carta de sesmaria concedendo o brejo, de 02/04/1667; uma cópia de uma carta dos oficiais da câmara de São Paulo, de 13/09/1764; uma cópia de uma representação dos oficiais da câmara sobre os foros e laudêmios que cobram os monges, de 19/12/1767; entre demais papéis.', 58, '1770-12-05', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (556, 'Requerimento do padre Antonio de Araujo a D. Jose I solicitando a autorização para permanecer na América, onde precisava de assistir às partilhas da herança do seu falecido pai. Conta um despacho do conselho ultramarino, indeferindo o pedido, de 13/02/1756.', 566, '1756-02-13', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (568, 'Requerimento de Antonio Fernandes dos Santos, morador de Vila Rica, solicitando ao rei a mercê de lhe conceder uma provisão para poder citar e demandar Estacio Ferreira de Sampaio, referente a dívidas de 230 oitavas de ouro por duas moradas de casas que o suplicante lhe vendeu. O requerente apresenta que não pode citar nem executar a dívida sem a provisão, pois Estacio se encontra preso na cadeia da vila. Em anexo, apresenta-se um bilhete de ordem de provisão.', 579, '1751-09-02', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (569, 'Requerimento do juiz, oficiais e irmãos da Irmandade das Almas da Matriz e Freguesia de Nossa Senhora do Pilar de Vila Rica solicitando ao rei a mercê de lhes autorizar a cobrança de esmolas para o apoio e sustento das obras da irmandade.', 580, '1751-09-02', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (64, 'Requerimento de Francisco Jose Pinto de Mendonça, ouvidor do Rio das Mortes, solicitando a mercê do pagamento dos seus ordenados. Em 27/10/1761, o pedido e o alvará de mantimentos foram providas na forma solicitada. ', 76, '1760-05-06', 1760, 5, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (407, 'Requerimento de João de Sousa Filgueiras, ouvidor de São Paulo, a D. Jose I pedindo que se lhe passe por certidão o teor da ordem régia dada aos ensaiadores e fundidores da casa da fundição de São Paulo a fim de que se cumpram as disposições da mesma ordem. Consta um despacho do conselho ultramarino, deferindo-o, de 03/12/1754. Contém provisão de D. Jose I dirigida ao governador e capitão-general da capitania do Rio de Janeiro, de 20/11/1754.', 387, '1754-12-03', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (614, 'MANDADO Carta de Alexandre Metelo de Sousa Meneses, informando ao rei sobre a necessidade de que a secretaria do conselho ultramarino lhe ceda uma das vias dos mapas, matrículas e relações das remessas do ouro, a fim de poder examinar os livros e bilhetes da capitação enviados das minas.', 643, '1751-10-27', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (661, 'Representação dos oficias da câmara da Vila Real de Nossa Senhora da Conceição de Sabará a D. Jose I, informando que o método das casas de fundição estabelecido na lei de 3 de dezembro de 1750 é o mais útil e suave aos povos das minas mas para que não haja despesas à fazenda real é necessário que não falte o solimão, pois sua ausência gera consequências à corte e aos povos das minas.', 717, '1757-07-11', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (668, 'Requerimento de Paulo Pereira de Sousa a D. Jose I, solicitando confirmação no exercício do posto de capitão da companhia da ordenança de pé da freguesia do Pouso alto. Consta referida carta patente de nomeação do governador de Minas Gerais, Jose Antonio Freire de Andrade.', 724, '1757-08-11', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (711, '***Requerimento de Estevão dos Reis Mota, morador da comarca do Rio das Mortes, informando o prejuízo que teve em suas fazendas e solicitando a mercê do hábito de Cristo e o ofício de ouvidor ou tabelião da vila de São João Del Rei. O pedido foi escusado.', 776, '1758-08-18', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (799, '**Requerimento de Antonio de Sousa Viegas, solicitando a restituição dos direitos que foi obrigado a pagar nas minas do ofício de escrivão das execuções de Vila Rica, por já tê-los pago na corte.', 945, '1760-12-09', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (728, 'Requerimento do procurador da câmara de Vila Rica, solicitando a D. Jose I uma certidão sobre uma provisão. Documento em mau estado e assinado pelo escrivão João da Costa de Azevedo.', 802, '1759-07-27', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (150, 'Requerimento de Jose Moreira Cordeiro e Andre Cursino dos Santos a D. Jose I pedindo confirmação de uma carta de sesmaria outorgada pelo governador de São Paulo e capitão-general de São Paulo, D. Luis Mascarenhas,  dando-lhe duas mil e quinhentas braças de terra com uma légua de fundo, na vila de Taubaté. Consta um parecer do procurador da fazenda e outro do procurador da coroa, três despachos do conselho ultramarino, o último dos quais diz que se passe a referida confirmação, de 07/07/1751. Contém uma carta de sesmaria dada pelo governador, de 16/06/1748, e um bilhete passado pelo secretário do conselho ultramarino dizendo para se passar aos suplicantes a confirmação da carta de sesmaria.', 155, '1750-09-11', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (86, 'Requerimento de João dos Santos Ala a D. João V solicitando que se tire nova residência do tempo que serviu na praça de Santos. Consta carta com parecer positivo do conselho ultramarino.', 96, '1739-08-01', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (192, 'Requerimento do governador da praça de Santos, Inacio Eloi de Madureira, a D. Jose I, informando as irregularidades que averiguou ao examinar as contas da provedoria de São Paulo. A despesa estaria ultrapassando a receita, e, para aumentá-la o suplicante solicita que a renda da pesca da baleia passe para São Paulo. Consta representação e cópia da provisão em anexo.', 191, '1753-10-15', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (88, 'Relação dos requerimentos apresentados pelo bispo de São Paulo, frei Manuel da Ressureição, ao Martinho de Melo e Castro, ministro e secretário de estado. Estes documentos referem-se às desordens entre os habitantes das capitanias de São Paulo e os de Minas Gerais, provocadas por um pedido do bispo de São Paulo para que lhe seja concedido o colégio dos jesuítas para sua residência e às ajudas de custo para os visitadores do bispado. Contém um ofício do governador e capitão general da capitania de São Paulo para o frei Manuel da Ressureição sobre a demarcação daquele bispado com o de Minas Gerais, de 03/12/1772; um assento sobre a divisão das duas capitanias de São Paulo e de Minas Gerais que se deve fazer pelo rio Sapucaí, de  12/10/1765.', 98, NULL, 1771, 1, '0', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (89, 'Requerimento de Manuel da Cunha Gamito pedindo a D. Jose I confirmação da carta de patente pela qual o Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, o nomeou ajudante de infantaria auxiliar da comarca de Paranaguá. Consta despacho do conselho ultramarino, mandando passar patente de confirmação, de 14/02/1772. Contém, ainda, uma carta patente de 05/09/1767, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como ajudante de infantaria auxiliar da comarca de Paranaguá.', 99, '1772-02-14', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (279, 'Sebastião Marques de Carvalho, residente no Rio das Mortes, solicita ao rei que ordene ao juiz ordinário, à câmara e ao corregedor da Vila de São José que enviem à corte os requerimentos relacionados às injustiças que haviam sido praticadas contra ele.', 273, '1750-08-13', 1750, 5, 'R', '', '2-3');
INSERT INTO app_hist.provocacao VALUES (98, 'Requerimento de João Vieira da Silva, morador na freguesia de Araritaguaba, termo da vila de Itu, pedindo justificação de alguns itens. Consta um despacho do juiz ordinário, deferindo o pedido, de 14/02/1771. Contém um termo de assentada; um instrumento de justificação; uma autuação, de 14/02/1771. Em adendo, há um requerimento de João Vieira da Silva, morador na freguesia de Araritaguaba, termo da vila de Itu, a D. Jose I, no qual, queixando-se do libelo que João de Sousa de Azevedo lhe levantou, acusando-o de devedor, pelo qual era obrigado a responder no juízo conservador da companhia do Grão Pará, pede que lhe mande passar provisão para que possa responder no juízo do seu foro. Consta um despacho do conselho ultramarino, indeferindo o pedido, de 27/06/1771. ', 108, '1771-06-27', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (306, 'João Batista Furtado, cavaleiro na ordem de Cristo, solicita ao rei a mercê de lhe promover a capitão-mor e regente da vila e comarca de Sabará.', 299, '1750-08-25', 1750, 5, 'R', '', '2-2v');
INSERT INTO app_hist.provocacao VALUES (250, 'Francisco Pereira da Costa requer ao rei a mercê de reformar a consulta relativa a sua petição de pagamento dos ordenados em uma intendência de Minas Gerais que havia vencido. ', 238, '1750-02-13', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (84, 'Requerimento do padre Lourenço Leite a D. João V solicitando provisão que demonstre a sua posse de uma fazenda em Taquari, para que assim possa expulsar Matias Alvares, que se apossou de parte das suas terras e que estava trazendo prejuízo a seu patrimônio. É informado que Lourenço já havia conseguido ordem para a retirada do dito pelo ouvidor de São Paulo, João Rodrigues Campelo, porém a resolução não se cumpriu, já que Matias tinha apoio do coronel João Coelho. Consta instrumento de justificação, certidão, resolução do ouvidor e cópia do requerimento.', 94, '1739-06-23', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (85, 'Requerimento do provincial da ordem da santíssima trindade a D. João V solicitando que se confirme ao vice-rei do Brasil e a todos os governadores e capitães generais os privilégios que a sua ordem obteve da coroa, já que as ditas autoridades ignoram. Consta cópia de provisão impressa com parecer positivo do rei.', 95, '1739-07-07', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (592, 'Requerimento de Antonio Coelho Barros, tesoureiro da fazenda real das Minas Gerais, a D. Jose I, solicitando ordenar ao provedor da fazenda real que remeta o treslado das contas apresentadas pelo suplicante para a secretaria do Conselho Ultramarino, para dar lhe quitação. Consta carta do Conselho ultramarino ordenando que remeta a conta do contador-mor para expedição e carta de Bernardo Luis Antonio de Sousa Costa atestando a necessidade do suplicante, já que todas as contas foram queimadas, impossibilitando o pagamento, e informando ao suplicante que caso seus bens tenham sido sequestrados pela falta de pagamento, que faça um levantamento.', 611, '1757-03-01', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (375, 'Requerimento de João de Sousa Figueiras, ouvidor de São Paulo, a D. Jose I pedindo que lhe seja passada provisão para vencer o seu ordenado desde o dia de embarque para esta capitania, onde vem exercer o seu cargo. Consta um despacho do conselho ultramarino, deferindo-o, de 15/02/1754. Contém outro requerimento do requerente, assim como uma certidão.', 359, '1754-02-15', 1754, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (618, 'Requerimento da mesa e mais irmãos terceiros da ordem do Seráfico Padre São Francisco de Assis, na Vila Rica, solicitando ao rei a mercê de conceder esmolas, para a construção de uma capela em honra do referido santo, e continuarem os santos exercícios.', 647, '1751-11-05', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (595, 'Requerimento do padre Antonio de Faria Mendes, vigário encomendado na igreja de Nossa Senhora da Conceição das Catas Altas, bispado de Mariana, a D. Jose I, solicitando o pagamento da congrua atrasada de dois anos, de acordo com a provisão junta. Documento incompleto.', 618, '1757-03-05', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (643, 'Requerimento de Antonio Rodrigues Frota, ao rei D. José,  solicitando confirmação no posto de sargento-mor das ordenanças do arraial de Meia Ponte e seus distritos, capitania de Goiás', 690, '1755-05-12', 1755, 28, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (203, 'Requerimento de Diogo Pinto do Rego pedindo ao juiz ordinário, João do Prado Camargo, que ordene algum dos tabeliães da cidade de São Paulo que passem o teor de uma carta de propriedade do seu ofício. Contém requerimentos do mesmo, assim como certidões, alvarás e avisos.', 183, '1750-03-20', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (210, 'Requerimento de D. Maria Paes de Jesus, viúva do tenente-coronel João Antunes Maciel e moradora de Sorocaba, a D. Jose I, solicitando a mercê do ofício de tabelião da cidade de São Paulo e a remuneração de seu marido, já que o mesmo trabalhou satisfatoriamente para o reino durante anos, nas capitanias de Minas Gerais, Rio de Janeiro e São Paulo. Consta aviso e consulta do conselho ultramarino, em que informa que o parecer do caso deve ser emitido diretamente pelo rei. ', 201, '1754-04-26', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (96, 'Requerimento de Francisco Jose de Sampaio, natural da freguesia de Santo Tirso do distrito do Porto, pedindo licença a D. Jose I para advogar naquela cidade e ocupar o escritório de seu pai que advogava na cidade de São Paulo. Consta um despacho do conselho ultramarino mandando o ouvidor-geral da comarca de São Paulo declarar quantos advogados há em São Paulo, de 08/05/1772.', 106, '1772-05-08', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (93, 'Requerimento de Manuel Pereira de Faria a D. Jose I pedindo confirmação do posto de capitão de auxiliares a pé de uma das companhias da vila de Iguape. Consta o despacho do conselho ultramarino de 06/04/1772, mandando passar a patente de confirmação. Contém, ainda, uma carta patente de 03/03/1766, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão de auxiliares de pé de uma das companhias da vila de Iguape.', 103, '1772-04-06', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (94, 'Requerimento da condessa de Sazerdas, D. Teresa da Silveira Teles, a D. João V solicitando ajuda de custo de doze mil cruzados sem que precise se dirigir a Goiás, para que possa pagar as dívidas de seu falecido marido o Conde de Sazerdas, Antonio Luis de Tavora. ', 104, '1739-09-26', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (95, 'Requerimento de João Fernandes da Costa a D. Jose I pedindo confirmação do posto de capitão das ordenanças da companhia da vila de Itu. Consta um despacho favorável do conselho ultramarino, de 08/04/1772. Contém carta patente de Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, promovendo o requerente no posto citado, de 22/10/1767.', 105, '1772-04-08', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (97, 'Requerimento de Francisco Xavier dos Santos a D. Jose I pedindo confirmação do posto de capitão de auxiliares de cavalo da companhia da freguesia da Conceição, pertencente ao corpo de dragões. Consta um despacho do conselho ultramarino mandando requerer com proposta, de 26/06/1772. Contém uma carta patente de  Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão de auxiliares de cavalo da companhia da freguesia da Conceição, de 31/01/1772.', 107, '1772-06-26', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (418, 'Representação dos oficiais da camara de Mariana queixando-se da cobiça dos contratadores e pedindo providencias para que os encargos que recaiam na população. ', 397, '1755-05-07', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (217, 'Requerimento de Maximiano de Oliveira Leite, guarda-mor, e seu socio Caetano Alves Rodrigues, coronel, solicitando ao Rei a mercê de lhes confirmar a doação, em sesmaria, de meia legua de terra em quadra, nas freguesias do Sumidouro e do Furquim.', 207, '1750-01-24', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (92, 'Requerimento de Jose da Silva Ferrão a D. João V solicitando provisão que o autorize a administrar os bens dos órfãos de Gaspar de Matos, já que se acha casado com a viúva Maria da Silva Leite. Consta certidão e instrumento de justificação.', 102, '1738-08-02', 1738, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (99, 'Requerimento do padre Inacio de Azevedo da Silva pedindo ao juiz ordinário, Joaquim Manuel da Silva Crasto, que lhe mande passar pelo tabelião Inacio Xavier de Almeida uma certidão de que tomou posse judicial das terras que lhe foram dadas de sesmaria pelo Luis Antonio de Sousa Botelho Mourão, perto do rio Tiete. Consta um despacho do juiz ordinário mandando passar a certidão pedida. Segue-se no mesmo documento uma certidão passada pelo tabelião atestando que o padre Inacio de Azevedo da Silva tomou posse das terras de sesmaria, de 05/02/1772. Contém, ainda, uma carta de sesmaria pela qual o governador concede ao padre Inacio de Azevedo da Silva um campo para pasto de animais, de 12/02/1770. Ainda, há um requerimento do referido padre pedindo a D. Jose I que lhe confirme uma carta de sesmaria pela qual Luis Antonio de Sousa Botelho Mourão lhe concedeu um campo próximo do rio Tiete. Consta dois pareceres dos procuradores da fazenda e da coroa e três despachos do conselho ultramarino, o último dos quais manda o governador declarar guerra a largura e o comprimento da sesmaria, de 16/12/1772.', 109, '1772-07-01', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (196, 'Requerimento de João Tavares de Abreu, filho do Coronel Luis de Abreu Prego, solicitando licença para casar na Comarca de Sabará onde é ouvidor com pessoa ali residente. ', 196, '1755-02-06', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (262, 'Requerimento de Francisco de Tavora a D. Jose I soliictanfo a confirmação da patente em que o governador e capitão general da capitania do Rio de Janeiro e Minas Gerais, Gomes Freire de Andrade, provera-o no posto de capitão de ordenanças de Piagoai, distrito de Guaratinguetá. Consta um despacho do conselho ultramarino, deferindo-o, de 10/02/1753. Contém uma carta patente pela qual o governador e capitão-general da capitania do Rio de Janeiro e Minas nomeou o requerente para este posto, de 04/02/1752.', 251, '1753-02-10', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (297, 'Requerimento de Francisco de Sales Lisboa, padre, a D. Jose I pedindo que ordenasse à secretaria do conselho ultramarino para lhe passar por certidão o teor da provisão em que se estabelecem as côngruas dos ministros da Sé de São Paulo. Consta um despacho do conselho ultramarino, deferindo-o, de 10/03/1753 e a certidão requerida. Contém outro requerimento, assim como um decreto de D. Jose I. ', 290, '1753-05-12', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (261, 'Requerimento dos moradores da cidade de São Paulo e seu termo a D. Jose I pedindo licença para se estabelecer pelos oficiais da câmara um partido anual de quatrocentos mil réis para poderem convidar um médico perito que morasse na dita cidade, a fim de nela prestar serviço efetivo. Consta um despacho do conselho ultramarino ordenando ao ouvidor da comarca de São Paulo que dê o seu parecer, de 01/02/1753.', 250, '1753-02-01', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (244, 'THEO TEM ALGUM DETALHE? Requerimento de Manuel dos Santos Pinto solicitando permissão para citar o juiz de orfãos, Teodosio Coelho Peres.', 230, '1755-02-25', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (575, 'Requerimento do bacharel Felix de Azevedo Fonseca, intendente de Sabará, a D. Jose I, solicitando quatro mil cruzados a cada ano do mantimento, totalizando doze mil cruzados, e quinhentos mil réis de uma devassa que os intendentes são obrigados a pagar no ofício.', 586, '1757-02-08', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (591, 'Requerimento de Paulo Pereira de Sousa, oficial da casa de fundição de Vila Rica, a D. Jose I, solicitando a mercê do hábito de Cristo.', 610, '1757-03-01', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (787, 'maisa Requerimento de Pedro Dias Paes Leme, guarda-mor das Minas, solicitando o pagamento dos ordenados pela ocupação na Provedoria da Fazenda Real.', 873, '1760-01-28', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (901, '*Daniel  Requerimento de Eugenio de Morais Rego, segundo fundidor da casa de fundição de Vila Rica, solicitando permissão para regressar ao reino devido à dificuldade que a sua mulher e filhos atravessavam.', 997, '1755-10-16', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (90, 'Requerimento de Manuel Rodrigues Jordão, natural de Figueiró dos Vinhos, casado com Ana Eufrasia da Cunha, na cidade de São Paulo, a D. Jose I dizendo que por precisar de passar ao Reino com sua família pede mercê de lhe conceder a necessária licença. Consta um despacho do conselho ultramarino. Ainda, contém uma carta de Luis Antonio de Sousa que dá o seu parecer a respeito do pedido do requerente, de 28/02/1772, no qual há em sua margem uma cópia de provisão de D. Jose I pedindo parecer do governador, de 12/08/1771. Constam dois despachos do conselho ultramarino e três pareceres, um do conselho ultramarino, outro do procurador da fazenda e outro do procurador da coroa.', 100, '1772-02-28', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (108, 'Requerimento do bacharel Agostinho Luis Ribeiro Vieira a D. João V solicitando provisão para tomar posse como juiz de fora de Itú e receber seu ordenado desde o dia de seu embarque para a capitania do Rio de Janeiro.', 118, '1740-12-05', 1740, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (111, 'Queixa do governador da praça de Santos, Jose Rodrigues de Oliveira, a D. João V mencionando os excessos e desrespeitos cometidos pelo provedor da fazenda real da praça de Santos aos oficiais da dita provedoria, que poderia causar enormes danos a fazenda real se não houvesse providências. É dito que foi tirada devassa do caso pelo almoxarife Sebastião Fernandes do Rego. Consta cópia da carta enviada ao juiz de fora Gaspar da Rocha.', 121, '1741-07-06', 1741, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (112, 'Queixa do governador de São Paulo, Luis de Mascarenhas, a D. João V relatando a rebeldia dos povos das minas de Natividade, que negavam obediência ao intendente da câmara de Goiás, Sebastião Mendes de Carvalho. Consta parecer do conselho ultramarino informando que deixa o caso nas mãos do governador.', 122, '1741-02-09', 1741, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (115, 'Requerimento do ajudante de número da praça de Santos, Jose Galvão de Moura e Lacerda, a D. João V, solicitando um cargo que estava vago em uma das companhias pelo falecimento do ocupante.', 124, '1750-02-27', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (199, 'Requerimento de Diogo Pinto do Rego, mestre-de-campo e escrivão da ouvidoria e correição da cidade de São Paulo, pedindo a Jose Luis de Brito e Melo, ouvidor-geral e corregedor da cidade de São Paulo, que lhe fizesse a mercê de mandar que o cirurgião Antonio da Mota passasse por certidão jurada as moléstias de que padecia e que o privavam de exercer qualquer ocupação. Consta um despacho do ouvidor deferindo. Contém requerimentos anteriores do mesmo, assim como certidões, alvarás e avisos. ', 183, '1750-03-10', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (113, 'Requerimento dos oficiais da câmara da vila de Santo Antônio de Guaratinguetá a D. João V, solicitando medidas de segurança para o caminho recém aberto para o Rio de Janeiro e costa do mar, feito por Antonio Gonçalves de Carvalho e outros moradores da vila de Ajuruoca, devido a facilidade de acesso dos inimigos a este novo caminho. Consta parecer do conselho ultramarino e cópia da carta enviada ao governador do Rio de Janeiro.', 123, '1742-08-31', 1742, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (109, 'Requerimento do intendente das minas de Paranapanema e Apiaí, João Coelho Duarte, a D. João V informando a necessidade de se ter uma intendência separada para essas vilas, pois devido a distância e a precariedade do caminho as matrículas não estavam sendo feitas a tempo. Consta conta da capitação, livro de matrícula e parecer do conselho ultramarino, ordenando pôr um comissário para fazer as matrículas nesses lugares.', 119, '1738-06-24', 1738, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (478, 'Alexandre Jose de Faria Lobato, escrivão das execuções da cidade de Mariana, pede para que o poder de cobrar os novos direitos lhe sejam restituídos, já que foram indevidamente tomado pelos oficiais de arrecadação da fazenda de Vila Rica ', 449, '1750-09-22', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (151, 'Requerimento do capitão engenheiro militar, Francisco Tosi Colombina, a D. Jose I, solicitando que lhe fosse confirmada a provisão do direito que lhe foi concedida de abrir caminho da vila de Santos e da cidade de São Paulo até a vila Boa de Goiás. Consta parecer positivo do conselho ultramarino. ', 156, '1752-08-09', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (479, 'Antonio Tomas da Costa, tenente dos dragões, pede ao rei o direito de voltar as suas funções de tenente com soldo integral. ', 450, '1750-09-25', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (105, 'Queixa do ouvidor e corregedor da comarca de Santos, João Rodrigues Campelo, contra o ouvidor João Gonçalves Pereira, informando as irregularidades nas eleições dos vereadores, o que provocou a convocação de uma nova eleição. Consta carta do governador de São Paulo e um edital.', 115, '1740-05-12', 1740, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (107, 'Queixa do governador de São Paulo, D. Luis de Mascarenhas, a D. João V informando que ordenou a prisão do intendente de Cuiabá, o bacharel Manuel Rodrigues Torres, que teria cometido irregularidades com o ouro da real capitação. Consta queixa do dito intendente em anexo, informando que as circunstâncias de sua prisão não foram justas, que ele não estava sendo tratado com respeito e que poderia correr risco de vida. Escreve ainda que estava sendo impedido de conseguir as certidões para seu livramento. Consta termo da junta e consulta do conselho ultramarino, cujo parecer consiste em ordenar a substituição do dito intendente e transferi-lo para a corte.', 117, '1740-12-02', 1740, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (909, 'maisa Carta do ouvidor da vila de Cuiabá, João Antonio Vaz de Morilhas a D. Jose I em que se queixa do governador de Mato Grosso, Antonio Rolim de Moura Tavares, por este ter ordenado soltar Francisco Ribeiro Machado e o fiscal da intendência Jose Ferraz, acusados por crime de perjúrio, alegando que o mesmo não deveria se intrometer em causas ordinárias do foro civil.', 1005, '1751-08-20', 1751, 27, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (496, 'Manuel Gonçalves Guimarães pede ao rei para que obrigue uma sua escrava a exibir certidão de liberdade na intendência real.', 468, '1750-10-22', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (559, 'Requerimento de Pedro Rebelo Falcão a D. Jose I solicitando o lugar de capitão-mor de Santos. Consta um despacho do conselho ultramarino, mandando juntar a oposição, de 07/04/1755. Contém documentos referentes a esse trâmite.', 569, '1755-04-07', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (596, 'RESPOSTA Bilhete de ordem de provisão passado a Antonio Jose Freire de Sousa, para exercer o cargo de escrivão da ouvidoria de Vila Rica.', 619, '1751-09-15', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (220, 'Requerimento de Antonio Galvão de França, natural de Faro e morador na vila de Guaratinguetá, a D. Jose I pedindo licença para vir ao reino. Consta um despacho do conselho ultramarino, determinando que o governador informe, de 05/04/1752.', 210, '1752-04-05', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (654, 'Queixa do governador de Minas Gerais, Jose Antonio Freire de Andrade, a D. Jose I, informando as consequências que a falta de solimão nas casas de fundição e casa da moeda vem causando, afetando a qualidade do ouro e o comércio.', 711, '1757-07-04', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (788, 'maisa Requerimento de Caetano Francisco da Costa, ajudante supra das ordenanças de pé de vila Rica, solicitando a mercê da patente de sua confirmação no exercício do referido posto. Consta carta patente em anexo, concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 874, '1760-01-28', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (902, 'Requerimento do padre Antonio Neto da Costa pedindo confirmação de sesmaria de meia légua de terra que possui na comarca de Vila Rica.  Consta a carta de sesmaria doada em 1753. Pedido deferido.', 998, '1755-10-06', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (117, 'Requerimento de Antonio Pacheco Silva, sargento-mor das ordenanças da vila de Itu, a D. Jose I pedindo a confirmação da patente. Consta um despacho do conselho ultramarino, indeferindo-o e mandando Luis Antonio de Sousa Botelho Mourão dar baixa de posto ao requerente de 30/07/1772. Contém ainda uma carta patente concedida pelo governador promovendo-o ao posto de sargento-mor, de 18/11/1766 e termo de posse e juramento dado a Antonio Pacheco Silva, de 22/11/1766.', 126, '1772-07-20', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (124, 'Requerimento de Barbara Ana da Cruz, viúva de Lourenço Rodrigues do Prado, moradora na vila de São Francisco das Chagas de Taubaté, pedindo a D. Jose I que lhe mande passar provisão autorizando-a a ser tutora dos seus oito filhos. Consta um despacho do conselho ultramarino mandando passar a provisão na forma da lei, de 04/11/1772. Contém instrumento de justificação; um requerimento da mesma requerente para o juiz de órfãos pedindo que lhe mande passar instrumento de justificação em como foi legítima mulher daquele e por sua morte ficou com oito filhos menores, pelo que pretende administrar os seus bens e filhos, anterior a 07/02/1777; e um termo de assentada. ', 133, '1772-11-04', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (670, '***Requerimento de Francisco Jose de Araujo a D. Jose I, solicitando confirmação no exercício do posto de capitão da ordenança de cavalaria da freguesia dos Camargos. Consta referida carta patente de nomeação do governador de Minas Gerais, Jose Antonio Freire de Andrade.	', 726, '1757-08-13', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (120, 'Requerimento de Jose Gomes Pinto de Morais a D. Jose I solicitando as provisões necessárias por este ter sido nomeado ouvidor da capitania de São Paulo. Consta um despacho favorável do conselho ultramarino, de 10/10/1772. Contém, ainda, um outro requerimento do mesmo solicitando uma certidão das provisões passadas ao seu antecessor, com despacho favorável do mesmo dia. Em adendo, contém uma certidão, de 09/10/1772, assinada por Joaquim Miguel Lopes de Lavre, secretário do conselho ultramarino, com o teor de dois documentos: provisão de D. Jose I na qual se ordenara que seu antecessor, o bacharel Salvador Pereira da Silva, vença o ordenado que lhe competia, de 26/02/1765; e uma segunda provisão na qual se concede a Morais uma ajuda de custo, conforme dado para seu antecessor, de 23/02/1765. Há também um bilhete de Lavre mandando passar provisão para que seja paga a quantia referente a ajuda de custo a Jose Gomes Pinto de Moaris, de 05/11/1772.', 129, '1772-10-10', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (18, 'Queixa de Salvador Pereira da Silva, ouvidor-geral da comarca de São Paulo, endereçada a Sebastião Jose de Carvalho e Melo, marquês de Pombal, contra Luis Antonio de Sousa Mourão, governador e capitão-general da capitania, que teria mandado fundir ouro e prata na oficina de Lourenço Correa de Andrade. Em anexo está uma cópia das ordens recebidas na capitania através da carta de dom Jose I de 30/07/1766 na qual se ordena que os governadores e capitães-generais proibissem o ofício de ourives e que fossem fechadas as oficinas de ourivesaria.', 33, '1770-01-15', 1770, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (121, 'Requerimento do capitão de Joaquim Cardoso Teles, capitão do extinto regimento de voluntários, a D. Maria I que, em virtude de ter pedido que o provesse no posto de sargento-mor da guarnição da praça de santos e os seus papéis terem sido mandados juntar aos demais pretendentes ao dito posto, pede que o requerimento se junte a estes documentos para que delibere como for de justiça. Consta um despacho do conselho ultramarino, deferindo-o, de 16/10/1772.  Não há o documento digitalizado, somente o resumo.', 130, '1772-10-16', 1772, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (195, 'Requerimento dos ensaiadores e fundidores da casa de fundição da comarca de São Paulo, a D. Jose I, solicitando algum rendimento para as despesas da dita casa e para o pagamento dos salários vencidos. Consta em anexo três requerimentos anteriores enviados para o intendente e o governador de São Paulo, uma solicitação de certidão dos salários vencidos pelo conselho ultramarino, uma relação do ouro que entrou na casa de fundição e um aviso do secretário para que o conselho ultramarino dê seu parecer.', 194, '1754-03-18', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (123, 'Representação dos oficiais da junta da fazenda para D. Jose I expondo que no extrato das contendas do rendimento do subsídio e novo imposto da praça de Santos remetido pelo provedor da fazenda, Jose Honorio de Valadares e Aboim, sendo injusto o sequestro feito pelo provedor dos bens do almoxarife Matias do Couto Reis. Contém uma certidão do escrivão da fazenda. ', 132, '1772-10-30', 1772, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (789, 'maisa Requerimento do capitão Domingos Alves Braga, solicitando a confirmação da carta de sesmaria de meia légua de terra em quadra, na cidade de Mariana. Consta carta de sesmaria concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 875, '1760-02-05', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (658, 'Representação dos oficiais da câmara de Vila Rica a D. Jose I, solicitando providências para a falta de solimão, para conservação da prata nas casas de fundição, e para os constantes descaminhos do quinto.', 714, '1757-07-09', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (125, 'Requerimento de Antonio Leitão de Abreu a D. Jose I pedindo a confirmação do posto de capitão de auxiliares de cavalaria da companhia da vila de Jundiaí do 1°corpo de dragões de São Paulo para que foi nomeado por Luis Antonio de Sousa Botelho Mourão como prova pela patente junta. Consta um despacho do conselho ultramarino mandando o referido governador informar sobre o motivo da demora deste requerimento, de 23/11/1772. Contém carta patente pela qual o governador nomeou o requerente para o posto citado, de 09/12/1766.', 134, '1772-11-23', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (376, 'Requerimento de João de Sousa Figueiras, ouvidor de São Paulo, a D. Jose I, pedindo que lhe sejam passadas por certidão todas as provisões e ordens que recebeu o atual ouvidor Jose Luis de Brito, quando foi tomar conta do seu cargo. Consta um despacho do conselho ultramarino, indeferindo-o, de 20/12/1753. Contém uma certidão anexa e outro requerimento do mesmo requerente.', 359, '1753-12-20', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (387, 'Requerimento do bacharel Francisco Caetano de Almeida Lobo, a D. Jose I, pedindo aumento do seu ordenado. Consta dois despachos do procurador da fazenda e três despachos do conselho ultramarino, o último dos quais ordena que o referido bacharel requeira sem demora a D. Jose I. Contém anexo um outro requerimento anterior, pedindo que lhe seja passada certidão na qual constem os aumentos de ordenado concedidos a ouvidores de Angola e de São Tomé, consta um despacho do conselho ultramarino, deferindo-o, de 13/08/1751, e um ofício. ', 368, '1752-10-16', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (122, 'Requerimento de Jose Carlos Pinto de Sousa, juiz de fora da vila de Santos, pedindo as provisões necessárias para receber a devida quantia de réis anuais para moradia e ajuda de custos. Consta um despacho favorável do conselho ultramarino, de 27/10/1772. Contém requerimento anterior pedindo certidão das provisões passadas aos seus antecessores, com despacho favorável do conselho ultramarino, de 16/10/1772 com quatro provisões.', 131, '1772-10-27', 1772, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (684, 'Requerimento do padre Amaro Gomes de Oliveira, vigário da paróquia de Guarapiranga, bispado de Mariana, solicitando ao rei que conceda alvará de mantimento para o seu pagamento da congrua.', 740, '1758-01-30', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (687, 'Requerimento dos homens pardos, irmãos da confraria de São José de Vila Rica, solicitando ao rei permissão para usarem espadim à cinta nos atos de sua irmandade, como é permitido aos homens brancos. Consta bilhete do conselho ultramarino.', 743, '1758-03-06', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (223, 'Requerimento do escrivão da ouvidoria da cidade de São Paulo, Diogo Pinto do Rego, a D. Jose I, solicitando autorização para nomear um substituto nos seus impedimentos, por achar-se doente. Consta carta do ouvidor de São Paulo, João de sousa Filgueiras, informando que o suplicante é digno deste pedido, e consulta do conselho ultramarino, cujo parecer é o mesmo dos procuradores régios, de que o pedido pode ser atendido.', 213, '1754-09-07', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (129, 'Requerimento do antigo juiz de fora da praça de Santos, João Vieira de Andrade, a D. Jose I, em que solicita a retirada de uma nova residência, já que seus inimigos da vila de Santos haviam prejudicado a primeira. Se não for possível, solicita que seja reconhecido não ser sindicante ou se reconhecerem sua culpa, que seja logo punido.', 138, '1751-10-05', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (152, 'Requerimento dos oficiais da câmara da cidade de São Paulo a D. Jose I, solicitando providências acerca das dificuldades dos mineradores de Goiás em irem fundir seu ouro em vila Boa, muito distante das minas do Carmo, de Natividade e de São Félix, além de correrem o risco de serem atacados por gentios e foragidos.', 157, '1752-08-26', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (131, 'Requerimento dos oficiais da câmara da vila de Paranaguá a D. Jose I, relatando as queixas dos moradores da vila contra os ouvidores gerais, que se intrometem na jurisdição da câmara, solicitam providências para que haja ministros que se ocupem dos despachos dos juízos desta vila. ', 140, '1751-12-18', 1751, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (132, 'Requerimento dos oficiais da câmara da vila de Iguape a D. Jose I solicitando providências contra as vexações e violências cometidas pelo corregedor da comarca, o bacharel Antonio Pires da Silva e Melo Porto Carneiro e outros ministros vem causando a vila. Consta certidão e representação assinada por Manuel Pereira de Faria.', 141, '1751-12-22', 1751, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (221, 'Requerimento do capitão de Infantaria da fortaleza de Santa Cruz Itapema da praça de Santos, João Teixeira de Carvalho, a D. Jose I, solicitando licença de quatro anos para tratar de seus negócios no reino, e acudir sua família das dívidas.', 211, '1754-05-21', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (286, 'Requerimento do juiz de fora da vila de Santos, Francisco Caetano de Almeida Lobo, a D. Jose I, solicitando que fosse passada ordem ao provedor da fazenda real, para que lhe pagassem o ordenado do mês de sua residência. Consta carta do secretário do conselho ultramarino, informando que por não constar resolução para esse prática no ultramar, deve-se excursar o suplicante.', 281, '1758-10-16', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (206, 'Requerimento de D. Luis Mascarenhas, governador e capitão-general da capitania de São Paulo, pedindo a D. Jose I que lhe conceda a mercê de lhe remunerar o trabalho excessivo que tivera na viagem a Goiás e na criação de uma vila, da mesma forma que concedeu a Rodrigo Cesar de Meneses quando governava a capitania. Diz também que não conseguiu juntar cópia das ordens que tivera de Rodrigo Cesar de Meneses conforme pedido por D. Jose I. Consta dois despachos do conselho ultramarino, um parecer do procurador da fazenda e outro do mesmo conselho, de 19/01/1752. Contém um outro requerimento do mesmo governador. ', 199, '1751-12-20', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (702, 'Requerimento de Francisco Alves de Araujo pedindo para ser provido no posto de capitação de uma das companhias da capitania de Minas Gerais, por estar vago, e por precisar amparar irmãs, pai e mãe. Solicita-se parecer do governador', 762, '1755-05-31', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (137, 'Requerimento de D. Luis de Mascarenhas a D. Jose I solicitando a mesma mercê de ajuda de custo que foi concedida ao conde de Sarzedas, Rodrigo César de Menezes, seu antecessor no governo de São Paulo, em sua viagem à Goiás, onde veio a falecer. Ficando o suplicante responsável por completar a viagem, que acabou sendo o dobro de distância da primeira. Consta parecer positivo do conselho ultramarino.', 142, '1752-02-29', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (128, 'Requerimento de Paulo de Sousa Rocha, cônego da sé catedral da cidade de São Paulo, a D. João V pedindo que lhe mandasse passar alvará de mantimentos, em virtude de o ter apresentado em um canonicato da dita sé para que ele pudesse ter a sua côngrua da fazenda real da vila de Santos. Consta um despacho do conselho ultramarino ordenando que se lhe passasse o alvará, de 21/02/1750.', 137, '1750-02-20', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (130, 'Requerimento de Antonio Francisco Barros, ajudante tenente da capitania de São Paulo, a D. Jose I solicitando provimento no cargo de governador da praça de Santos, com patente e soldo de mestre de campo, devido aos seus bons serviços prestados ao reino em cargos militares durante 47 anos. Consta em anexo fé de ofício.', 139, '1751-10-12', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (224, 'Requerimento do ex-governador da praça de Santos, fidalgo e mestre de campo Luis Antonio de Sa e Queiroga, a D. Jose I, solicitando reforma de sua consulta, por já ter se passado um ano. Na referida petição, o suplicante solicitava sua aposentadoria e o provimento de brigadeiro, por ser muito pobre e já ter mais de noventa anos de idade.', 214, NULL, 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (226, 'Requerimento do capitão da fortaleza de São João da Barra de Bertioga, Matias do Couto Reis, ao juiz de fora da vila e praça de Santos, Francisco Caetano de Almeida Lobo, pedindo-lhe que seja passada folha corrida mostrando que o requerente está livre de todo o crime. Consta no mesmo documento um alvará de folha corrida de Francisco Caetano de Almeida Lobo para que os escrivães do crime passem a referida certidão, de 27/03/1752, com resposta do escrivão no qual informa nada constar no seu cartório em desabono do suplicante. Contém um conjunto de requerimentos e certidões.', 215, '1752-04-17', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (394, 'PASSAR PARA CONSULTA MAS sem esquecer de colocar as provocações correspondentes. Consulta do conselho ultramarino, sobre a solicitação de João da Costa de Azevedo, de uma provisão para servir no cargo de escrivão da câmara da cidade de Mariana. Que por resolução de 02/12/1750, pertencia ao suplicante por cabeça de sua mulher, filha de Pedro Duarte Pereira, que foi proprietário do dito ofício. Em anexo, um bilhete de ordem de provisão a respeito do cargo.
INSERT INTO app_hist.provocacao VALUES (335, 'Requerimento do procurador da mitra do bispado de São Paulo a Jose Luis de Brito e Melo, ouvidor-geral, para que lhe seja passada uma certidão indicando o que determina o regimento do juízo dos defuntos e ausentes acerca da nomeação do escrivão e tesoureiro, quando vagar qualquer destes cargos e quem deve fazer a referida nomeação em tal caso. Consta certidão passada, de 23/10/1753. Contém documentos anexos, entre eles requerimentos. ', 314, '1753-10-23', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (581, 'Requerimento de Antonio Teixeira de Leão e Antonio Fernandes Dias, moradores do distrito do Morro de Nossa Senhora do Pilar do Mato Dentro, solicitando a mercê de conferir carta de confirmação de sesmaria referente a meia légua de terra que possuem na comarca do Serro do Frio, obtida de Gomes Freire de Andrade, governador da capitania. Em anexo um bilhete, uma carta e outros documentos.', 592, '1751-09-03', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (685, 'Requerimento de Antonia da Silva, viúva do capitão Manuel Carvalho de Azevedo e moradora da freguesia das Congonhas do Campo, termo de Vila Rica, solicitando ao rei a nomeação de tutora e administradora dos seus filhos e dos bens de seu marido.', 741, '1758-01-31', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (145, 'Requerimento de Rodrigo Felix Martins a D. Jose I pedindo que lhe mandasse passar carta de confirmação do cargo de capitão-mor da vila de Curitiba, que lhe fora dado pelo governador e capitão-general da capitania de São Paulo. Consta um despacho do conselho ultramarino ordenando que se passasse a respectiva carta de confirmação, de 13/08/1750. Contém carta patente de Luis Mascarenhas, governador e capitão-general da capitania de São Paulo, fazendo mercê de o nomear no posto requerido, de 20/06/1748 e o instrumento de posse dado pelo mesmo governador, de 31/10/1748.', 150, '1750-08-13', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (142, 'Representação dos oficias da câmara de São Paulo a D. João V pedindo proteção em virtude do ouvidor geral e corregedor da comarca ter mandado penhorar os bens da câmara por eles não terem pago mais dez mil réis além das custas do escrivão da ouvidoria e correção com o fundamento de que assim se pagava no Rio de Janeiro, de 11/03/1750. Contém uma certidão atestando não se encontrar registrado nos livros em que os ministros tomavam as contas que se desse ao escrivão da ouvidoria salário certo de dez mil réis, de 12/03/1750.', 147, '1750-03-11', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (143, 'Representação dos oficiais da câmara de São Paulo a D. João V pedindo que obtivesse do santo padre a concessão de metrópole dos bispados vizinhos e prelazias, à cidade de São Paulo, de 28/03/1750.', 148, '1750-03-28', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (139, 'Representação dos vereadores da câmara da vila de Itu a D. João V pedindo que lhes mandasse as suas ordens em virtude do bispo ter proibido que se pedisse esmola para festividades, obras pias e missas, sem sua provisão que custava quatro mil cruzados e lhes acharem que esta faculdade de conceder ou impedir que se pedisse esmola era de jurisdição real, de 01/02/1748. Constam dois despachos do conselho ultramarino, o último dos quais ordenando que se mandasse ao vigário capitular que informasse com o seu parecer. Contém duas certidões, uma do escrivão da câmara de 02/03/1750 e outra de João Floriano Ribeiro de Andrade e Silva, de 05/03/1750. Em adendo, há cópia da provisão de D. João V ao vigário capitular de São Paulo e uma carta do vigário, Lourenço Leite Penteado. ', 144, '1748-02-01', 1748, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (147, 'Requerimento de dona Maria de Madureira Morais, moradora na vila de Santos, comarca de São Paulo, viúva do coronel Antonio Francisco Lustosa, e de seus filhos, frei Manuel de Santa Gertrudes Lustosa, Ana Francisca Joaquina de Morais Lustosa e Escolástica Francisca Lustosa, a D. Jose I pedindo-lhe que, em virtude de o dito coronel ter servido como provedor da real fazenda da vila de Santos e juiz de alfândega e anexos da mesma vila, mandasse passar ordem para se tirar a residência de todo tempo que servira as ditas ocupações.  Consta dois despachos do conselho ultramarino, o último dos quais dizendo não haver embargo para tirar residência, como pediam os requerentes.', 152, '1750-09-02', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (144, 'Requerimento do frei Antonio da Madre de Deus Galvão, bispo de São Paulo, a D. Jose I pedindo que lhe concedesse as mesmas mercês que tinham sido feitos ao bispo dom Bernardo Rodrigues Nogueira. Consta três despachos do conselho ultramarino, o último dos quais determinando que lhes parecia que D. Jose I devia fazer a mercê pessoal dos mil cruzados que se tinham dado ao bispo dom Bernardo Rodrigues Nogueira, além da congrua, de 27/10/1750.', 149, '1750-07-29', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (148, 'Requerimento de João Jose da Silva a D. Jose I pedindo a confirmação da patente que lhe concedera ao governador e capitão-general da capitania de São Paulo, dom Luis Mascarenhas, e que o provera no posto de capitão de ordenança da companhia dos forasteiros da vila e praça de Santos, vago pela saída de Antonio Gaspar de Carvalho. Consta um despacho do conselho ultramarino indeferindo-o, de 10/09/1750. Contém uma carta patente do respectivo governador concedendo ao requerente o respectivo posto, de 07/10/1747 e, no verso, o termo de posse e juramento do mesmo oficial, de 12/10/1747.', 153, '1750-09-10', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (298, 'Requerimento de Francisco de Sales Lisboa a D. Jose I pedindo que mande passar pelo secretário do conselho ultramarino as ordens necessárias para poder receber a sua tença vitalícia de trinta mil réis anuais pagos nos dízimos da capitania de São Paulo como consta do alvará de mercê, que junta. Consta um parecer do procurador da fazenda e três despachos do conselho ultramarino, um deles deferindo-o. Contém um alvará de D. Jose I, um decreto de D. Jose I e outro requerimento do mesmo requerente. ', 290, '1753-05-24', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (408, 'Requerimento de Jose Luis de Brito e Melo, ouvidor da comarca da cidade de São Paulo, a D. Jose I dizendo que os ouvidores desta comarca vencem oito mil réis por cada procissão a que assistem. Não considera justo que se por alguma razão legítima a eles não possam assistir , não vençam suas propinas. Pede que seja passada ordem para que os ouvidores da comarca recebam o dinheiro equivalente às procissões da lei, mesmo que sejam impedidos de comparecerem alguma. Consta um despacho do conselho ultramarino.', 388, '1754-12-19', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (428, 'Requerimento do padre Manuel da Cunha Vilas Boas, presbítero do hábito de São Pedro, solicitando ao rei, em virtude de seu prelado, a mercê de que sejam passadas ordens ao provedor para que lhe sejam pagas suas côngruas de 200 mil réis, pelo exercício do ofício de pároco da Igreja de Nossa Senhora do Pilar da Vila de Pitangui.
INSERT INTO app_hist.provocacao VALUES (377, 'Requerimento de João Vieira de Andrade, ex-juiz de fora da vila de Santos, pedindo que mande tirar informações suas por outro ministro que não o ouvidor de Paranaguá, Antonio Pires da Silva e Melo Porto-Carreiro, visto haver três anos que o rei o encarregara dessa missão e ainda a não cumprira. Consta um despacho do conselho ultramarino. ', 360, '1754-02-16', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (207, 'Requerimento de D. Luis Mascarenhas, governador e capitão-general da capitania de São Paulo, na qual alega que Rodrigo Cesar de Meneses foi a Cuiabá formar uma vila e D. João V lhe fizera a mercê de quatro mil cruzados para os preparativos, enquanto os gastos da viagem correram por conta da fazenda real e de soldo sobrando. Situação semelhante havia ocorrido com Antonio Luis de Tavora, conde de Sarzedas, tendo os herdeiros recebido a mercê destinada a ele devido ao seu falecimento. Por não ter recebido remuneração por esse trabalho e dispêndio, pede a D. Jose I o deferimento. 
INSERT INTO app_hist.provocacao VALUES (582, 'Requerimento de Pedro Afonso de Vasconcelos, soldado de uma companhia de dragões do pé de Minas Gerais, solicitando ao rei a mercê de lhe conceder licença para se deslocar ao Reino. Em anexo uma carta e um bilhete de ordem de provisão.', 593, '1751-09-06', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (703, 'Requerimento de Francisco Alves de Araujo, capitão das ordenanças das comarcas do Serro Frio pedindo para ser provido no cargo de fiscal ou escrivão da Intendência e a merce do habito da ordem de Cristo. Pedido escusado', 763, '1755-05-31', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (790, 'maisa Requerimento de Antonio Pereira da Rocha, capitão de cavalos da companhia da ordenança de Itaubira, solicitando a confirmação no exercício do referido posto. Consta carta patente do governador de Minas Gerais, Jose Antonio Freire de Andrade.', 876, '1760-01-28', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (164, 'Queixa do ouvidor geral da comarca de São Paulo, Jose Luis de Pinto e Melo, contra o governador da praça de Santos, Luis Antonio de Sa e Queiroga. Relatando a prisão injusta do meirinho de campo, Domingos de Cubas, ordenada por esse governador, além de outras violências que o mesmo vinha praticando. Consta em anexo auto sumário, certidão, carta do capitão-mor Diogo Pinto do Rego, ordem do juiz sindicante para que se tire residência do referido governador, em observância a ordem do ouvidor geral e termo de conclusão.', 168, '1752-11-20', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (41, 'Requerimento de Antonio de Sousa Morais a D. Jose I pedindo confirmação do posto de capitão auxiliares de infantaria da companhia da vila de Mogi das Cruzes. Consta o despacho do conselho ultramarino de 30/10/1770 deferindo o pedido. Contém, ainda, uma carta patente de 11/07/1766, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando e promovendo o requerente como capitão.', 57, '1770-10-30', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (166, 'Requerimento de Francisco Rodrigues Ferreira, comerciante residente da praça de Santos, a D. Jose I, solicitando moratória por tempo de 6 anos, já que está com muitas dívidas e com risco de ser vexado judicialmente por seus credores. Consta bilhete do conselho ultramarino solicitando que os documentos inclusos do caso sejam entregues a secretaria desta repartição.', 170, '1753-04-10', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (271, 'Representação dos oficiais da câmara da vila de Curitiba  D. Jose I pedindo licença para se erigir um novo hospício e uma capela para que os irmãos da ordem terceira de São Francisco possam celebrar os santos ofícios ou conceder-lhes definitivamente a capela de nossa senhora do terço que lhes tinha sido cedida pelo bispo de São Paulo. Consta dois pareceres, um do procurador da fazenda e outro do procurador da coroa, não concordando ambos com a criação de novo hospício; e três despachos do conselho ultramarino, o último dos quais indefere o pedido, de 1754. Contém provisão do bispo de São Paulo dando autorização para que celebrassem os santos ofícios na referida capela enquanto não possuíssem capela própria, de 05/01/1752.', 264, '1753-02-21', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (167, 'Requerimento de Francisco Rodrigues Ferreira, comerciante residente da praça de Santos, a D. Jose I, solicitando uma esmola anual da provedoria real da fazenda de Santos. Consta bilhete do conselho ultramarino solicitando o parecer do caso.', 171, '1753-04-14', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (171, 'Requerimento de Manuel Rodrigues de Carvalho, tenente-general da capitania de São Paulo, a D. João V pedindo-lhe que fosse passada nova ordem para receber quatro mil cruzados referentes às despesas que fizera com a guerra dos gentios Piyagos por se ter extraviado a ordem para o dito pagamento. Consta um despacho do conselho ultramarino determinando que se passem outras vias, de 10/09/1751. Contém provisão de D. João V sobre a petição, de 11/10/1745.', 176, '1751-09-10', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (169, 'Requerimento de João Ferreira de Oliveira, sargento-mor da ordenança da cidade de São Paulo, a D. Jose I pedindo que fosse isento dos cargos da república por não ter sido nomeado para aquele posto pelo governador e capitão-general da capitania de São Paulo, D. Luis Mascarenhas, e possuir a carta patente de confirmação de D. João V. Consta um despacho do conselho ultramarino dizendo que o ouvidor, depois de ouvir os oficiais da câmara, informe com o seu parecer, de 24/04/1754. Contém outros documentos, como carta patente de confirmação do posto de sargento-mor ao requerente, requerimentos anteriores de João Ferreira de Oliveira, certidões e uma carta. ', 174, '1751-04-24', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (175, 'Requerimento de João de Gois de Araujo, natural da praça de Santos, a D. Jose I pedindo que fosse nomeado capitão da companhia de guarnição da praça de Santos. Consta um despacho do conselho ultramarino indeferindo o pedido, de 27/09/1751.', 180, '1751-09-27', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (163, 'Requerimento do tenente de Infantaria Inacio Eloi de Madureira da companhia do capitão Francisco Rosario Roquete do regimento da guarnição da praça de Elvas de que foi coronel o conde do lavradio D. Antonio de Almeida Soares e Portugal a D. Jose I pedindo para ser transferido para a praça de Santos. Consta um despacho do conselho ultramarino.', 167, '1751-01-27', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (333, 'Requerimento de Francisco Aranha Barreto ao juiz de fora pedindo que mande os tabeliões de Santos fazer a declaração do que constar sobre o requerente. Constam dois despachos do juiz autorizando. Contém documentos anexos. ', 310, '1755-05-09', 1755, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (503, 'Cosme Joaquim da Costa pede ao rei a licença para viajar ao Reino. ', 477, '1750-10-28', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (409, 'Requerimento de Jeronimo Ribeiro de Magalhães a D. João V pedindo provisão para lhe ser pago o ordenado. Consta um despacho do conselho ultramarino, deferindo, de 27/12/1754. Contém um requerimento do mesmo e uma cópia de provisão. ', 389, '1754-12-27', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (686, 'Requerimento do bacharel Felix de Azevedo da Fonseca, intendente de Sabará, solicitando ao rei que se passe ordem ao provedor da Fazenda Real para que pague ao suplicante uma ajuda de custo correspondente aos três anos em que serviu o dito lugar. Consta bilhete do conselho ultramarino.', 742, '1758-02-01', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (704, 'Requerimento do alfares Domingos Galvão pedindo licença para erguer tres moendas em seu engenho para fabricar açucar', 764, '1755-06-25', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (597, 'Requerimento de Nuno Henrique da Costa, agraciado com uma companhia de dragões em Minas Gerais, solicitando ao rei a mercê de lhe conceder o soldo de capitão enquanto aguarda a vaga. E que se pratique com Simão do Nascimento, D. Francisco Barão de Galenfles e João Batista aos quais sendo feita semelhante graça, sejam providos nos primeiros postos que vagaram. Em anexo um bilhete', 620, '1751-09-17', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (688, 'Requerimento de Manuel Duarte e Silva, morador da freguesia de São José da Barra, solicitando ao rei a confirmação de meia légua de terra de sesmaria na referida freguesia. Consta carta do governador de Minas Gerais, Jose Antonio Freire de Andrade, concedendo a dita sesmaria.', 745, '1758-03-11', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (719, '***Representação dos oficiais da câmara da cidade de Mariana, contra o excessivo pagamento aos párocos nas várias ocasiões e festas. Solicitam justiça contra esse prejuízo.', 794, '1759-05-22', 1759, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (126, 'Requerimento de Antonio Coutinho Cordeiro a D. Jose I pedindo que lhe passe a confirmação do posto de capitão de auxiliares de cavalaria da freguesia de Nossa Senhora da Conceição de Guarulhos para que foi nomeado pelo governador e capitão general da capitania de São Paulo, Luis Antonio de Sousa Botelho Mourão. Consta despacho do conselho ultramarino, ordenando ao governador que informe sobre a demora do pedido de confirmação, de 23/11/1772. Contém carta patente pela qual o governador nomeou o requerente para o posto citado, de 31/05/1766.', 135, '1772-11-23', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (662, 'DUVIDA Carta do frei João Francisco ao Senado da câmara de Mariana, queixando-se dos prejuízos que as novas concessões de sesmaria tem causado aos pobres, expulsando-os de seus sítios.', 718, '1757-07-19', 1757, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (179, 'Requerimento de Inacio Eloi de Madureira pedindo a D. Jose I que na patente que se passar para poder servir como governador da praça de Santos se declare que era coronel e governador e não mestre de campo governador, como era costume, visto que as tropas das terras conquistadas se arregimentavam como no reino. Consta um despacho do conselho ultramarino e um parecer do mesmo conselho, segundo o qual se deve deferir, de 17/12/1751.', 184, '1751-12-09', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (180, 'Requerimento de Francisco Tosi Colombina a D. Jose I, solicitando sesmarias maiores que as de meia légua, que vêm sendo concedidas no caminho entre São Paulo e Cuiabá, pois essa pequena dimensão não possibilitava aproveita da terra. Solicita também mercê para abrir um novo caminho entre a vila de Santos e Goiás. Consta sumário da consulta do conselho ultramarino, cujo parecer é o mesmo que o provedor da fazenda.', 185, '1753-04-18', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (671, 'Requerimento de Egas Carlos de Sousa e Menezes, herdeiro e testamenteiro de sua avó D. Ines Barbalho Bezerra, a D. Jose I, solicitando provisão para interpor uma ação de libelo cível e citar o desembargador e procurador-régio do Brasil, a fim de habilitar os bens deixados ao suplicante, já que o referido desembargador tenta remeter a herança a fiadores, ação não consentida por sua avó em vida.', 727, '1757-08-27', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (181, 'Requerimento do mestre de campo e ex-governador da praça de Santos, Luis Antonio de Sa Queiroga a D. Jose I, solicitando que seja provido como brigadeiro e que seja aposentado com o soldo por inteiro, porque já se encontra muito velho e pobre, tendo cumprido setenta anos do real serviço.', 186, NULL, 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (189, 'Requerimento de Francisco João ao vigário da vara, Gregorio Lopes de Oliveira, pedindo que, em virtude de lhe ser necessária uma certidão do reverendo padre vigário desta vila de Santos na qual constasse o dia, mês e ano em que falecera o alferes João Francisco Maltes, lhe mandasse passar. Consta um despacho do vigário ordenando que se passasse a respectiva certidão. Contém uma certidão, de 15/06/1734 e um grande conjunto de documentos, como certidões e requerimentos.', 146, '1734-06-15', 1734, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (183, 'Requerimento do alferes Francisco João, da companha da guarnição da praça de Santos de que era capitão Francisco de Almeida Albernas, ao provedor geral dizendo que lhe era necessária uma certidão de fé de ofícios, na qual constasse todo o tempo que tinha servido. Consta um despacho do dito provedor ordenando que se passasse a respectiva certidão. Contém um grande conjunto de documentos, como certidões e requerimentos.', 146, '1748-05-31', 1748, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (184, 'Requerimento do alferes Francisco João aos oficiais do senado da câmara do rio de São Francisco pedindo que lhe mandem passar por certidão o que constar sobre o seu procedimento e modo de viver. Segue-se no mesmo documento uma certidão, de 05/07/1746. Contém um grande conjunto de documentos, como certidões e requerimentos.', 146, '1746-07-05', 1746, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (191, 'Queixa do ouvidor geral intendente da cidade de São Paulo, Jose Luis de Brito e Melo, a D. Jose I, relatando as pessoas que passam à comarca de Paranaguá para não pagar o tributo devido, afetando nas contas da capitação que o suplicante fez com o intendente das minas de Paranapanema, Jose de Barros Leme. Consta certidão em anexo.', 190, '1752-07-30', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (562, 'Requerimento do governador de Santos e São Paulo, Alexandre Luis de Sousa Meneses pedindo a D. Jose I que mande passar patente regulando os limites dos governos do estado do Brasil do Sul declarando as terras que ficavam compreendidas no governo do Rio de Janeiro e as que dizem respeito ao governo em que se achava provido. Consta um despacho do conselho ultramarino, autorizando que se passasse a patente, de 07/01/1757. Contém consulta do conselho ultramarino.', 572, '1756-01-07', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (190, 'Requerimento dos capitães de infantaria da guarnição da praça de Santos ao governador geral de São Paulo pedindo que se registrasse a provisão e que os mesmos capitães pudessem nomear os alferes, do mesmo modo que os do Rio de Janeiro. Contém um grande conjunto de documentos, como certidões e requerimentos.', 146, '1750-03-05', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (334, 'Carta de frei Antonio da Madre de Deus Galrão, bispo de São Paulo, a D. Jose I queixando-se contra o ouvidor de São Paulo, Jose Luis e Brito e Melo, por este não observar o disposto no capítulo 25 do regimento do juízo dos defuntos e ausentes, que diz respeito à nomeação do provedor para desempenhar o cargo de tesoureiro ou escrivão do mesmo juízo no impedimento deste, mas com o parecer do bispo. ', 314, '1723-11-25', 1723, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (280, 'Simão da Silveira, vigário da freguesia de Nossa Senhora do Pilar do Pitangui, requer a mercê do pagamento de 200 mil reis e a côngrua.', 681, '1750-08-13', 1750, 5, 'R', '', '2-3 ');
INSERT INTO app_hist.provocacao VALUES (197, 'Requerimento de João Ferreira de Oliveira, sargento-mor da ordenança das cidade de São Paulo, ao provedor da casa de fundição de São Paulo, Manuel Mendes de Almeida, pedindo que lhe seja passada uma certidão acerca do ouro, que reconduziu das minas de Goiás, e dos reais quintos que pagou nesta provedoria. Consta um despacho do mesmo provedor deferindo o pedido.  Contém outros documentos, como carta patente de confirmação do posto de sargento-mor ao requerente, requerimentos de João Ferreira de Oliveira, certidões e uma carta.', 174, '1735-03-12', 1735, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (480, 'Manuel da Cunha Sotto Maior, capitão da ordenança, pede ao rei a confirmação de seu posto na dita função.', 451, '1750-09-25', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (287, 'Requerimento dos oficiais da câmara da cidade de São Paulo a D. Jose I, solicitando a elevação da freguesia de São João de Atibaia à vila. Consta carta do conselho ultramarino afirmando que concorda com o provedor da fazenda real, de que o governador da praça de Santos deveria informar seu parecer sobre o caso após ouvir o ouvidor de São Paulo, João de Sousa Filgueiras. Consta certidão do ouvidor de São Paulo e carta do governador da praça de Santos, Alexandre Luis de Sousa Meneses, informando que após analisar as certidões que os oficiais declararam sobre as características de Atibaia para se tornar uma vila foi convencido, ficando com o rei o parecer final do caso.', 282, '1759-02-17', 1759, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (272, 'Requerimento do tesoureiro da alfândega de Santos, Antonio Rodrigues da Silva, por nomeação do ex-governador da capitania de São Paulo, D. Luis Mascarenhas, pedindo a D. Jose I que sejam repetidas com salva as ordens dadas ao governador da mesma capitania para informar com o seu parecer sobre o seu anterior requerimento. Consta um despacho favorável do conselho ultramarino, de 01/05/1753.', 265, '1753-03-02', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (718, 'Requerimento do capitão Antonio de Meireles, homem pardo formado como advogado pela universidade de Coimbra e morador na vila de Nossa Senhora do bom sucesso das Minas novas do Araçuí, solicitando a mercê da concessão de uma provisão para poder advogar nos auditórios da referida vila. Consta certidão do juiz ordinário.', 790, '1759-04-27', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (712, 'Requerimento de Jose da Silva Rodarte, capitão da infantaria paga, solicitando o provimento no posto de tenente do mestre de campo general das Minas. Consta certidão em anexo.', 777, NULL, 1758, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (785, 'Requerimento do cabido da Sé de Mariana, Jose Rodrigues Cardoso, pelo seu procurador, o arcediago Giraldo Jose de Abranches, solicitando que a nova sacristia sirva somente para o cabido da Sé.', 866, '1760-01-24', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (228, 'Requerimento do bacharel João Vieira de Andrade a D. Jose I pedindo que lhe seja passada uma certidão do ano de 1747 em que prestou serviço ocupando diversos cargos. Consta um despacho do conselho ultramarino pedindo informação para o tesoureiro e executor, de 24/04/1752. Contém  algumas cartas em anexo em resposta ao despacho, nas quais se alega que não consta que se tivesse passado alguma ordem para o requerente cumprir e que pelas informações colhidas o suplicante dera inteiro cumprimento às ordens que lhe foram enviadas. ', 216, '1752-04-24', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (63, 'Requerimento de Salvador Cordeiro Gil solicitando a D. Jose I a confirmação de sesmaria de meia légua quadrada de terras que lhe foi concedida pelo Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo. Consta um parecer do procurador da fazenda, outro do procurador da coroa e três despachos do conselho ultramarino, o último dos quais o defere, de 19/02/1772. Contém, ainda, uma carta de sesmaria de 20/02/1766 pela qual Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, concedeu ao requerente a sesmaria pedida e um bilhete do secretário do conselho ultramarino ordenando que se passe carta de confirmação da sesmaria, de 25/04/1775. ', 75, '1771-06-22', 1771, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (264, 'Henrique Carlos de Sousa Magalhães, primeiro tenente dos dragões de Minas Gerais, requere ao rei por decreto ordene ao conselho ultramarino realizar o pagamento dos seus soldos. ', 254, '1750-04-16', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (338, 'Requerimento do procurador da mitra do bispado de São Paulo a Francisco Bueno Garcia, juiz ordinário da comarca de São Paulo, pedindo que o porteiro dos auditórios da comarca de São Paulo, João Rodrigues Moreira, jure se, por falecimento de Jose Pereira de Oliveira, andou apregoando o ofício de tesoureiro e se o coronel Francisco Pinto do Rego arrematou o referido ofício em praça. Consta despacho do juiz ordinário, Francisco Bueno Garcia, ordenando que viesse perante ele o escrivão e se passasse a certidão pedida. Segue-se no documento termo de juramento, 03/11/1753.', 314, '1753-11-03', 1753, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (231, 'Requerimento de Feliciana Teresa Rita a D. Jose I dizendo que Manuel Jose da Silva, marido e ajudante de ensaiador da real casa da moeda de Lisboa, fora mandado para o Rio de Janeiro por dois dias com o emprego de ensaiador de uma das casas da fundição das minas. Achando-se desamparada, pede que do ordenado de seu marido se tire a quantia de oiro tostões diários, dando ela fiança, na forma do costume. Há um outro requerimento idêntico a este, acrescentando que havia uma procuração que remetia inclusa segundo a qual podia requerer o que precisasse para bem da sua pessoa. Acerca do segundo requerimento consta um despacho do conselho ultramarino. Contém outros dois requerimentos da mesma requerente, assim como uma carta e um aviso. ', 220, '1752-04-17', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (216, 'Requerimento da abadessa do mosteiro de Monchique na cidade do Porto para o rei solicitando a mercê de se ordenar ao contador-mor das Cortes do Reino e Casa que remeta para o Brasil o traslado das contas de Lourenço Pereira da Silva, que fora tesoureiro da Fazenda Real de Minas, e em seu lugar se enviem os originais, para que, pelos mesmos, se lhe tirem as contas. ', 206, '1750-01-18', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (233, 'Requerimento do reitor do colégio de São Paulo da companhia de Jesus, padre Vitoriano da Cunha, ao governador e capitão general da capitania de Goiás, o conde de São Miguel, Alvaro Jose Xavier Botelho de Tavora, felicitando-o pelo novo cargo e solicitando que se pagassem os novos direitos para o transporte dos missionários em Goiás.', 222, NULL, 1755, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (219, 'Requerimento de Jose Luis de Brito e Melo, ouvidor-geral da comarca de São Paulo, a D. Jose I pedindo que lhe seja aumentado o ordenado, em virtude de o terem feito ao ministro do reino e de alguns estados do Brasil. Consta um despacho do conselho ultramarino.', 209, '1752-03-22', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (350, 'Requerimento do bacharel João Evangelista de Maris Sarmento, ouvidor do Serro Frio e intendente do ouro, pedindo que lhe conceda parte do ordenado de intendente. O pedido é deferido.', 328, '1755-02-25', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (482, 'Manuel Gomes Duque, capitão de ordenança, pede ao rei confirmação de seu posto. (Contem carta de patente)', 453, '1750-10-08', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (390, 'Carta do ouvidor da comarca de São Paulo, João de Sousa Filgueiras, ao secretário de Estado dos negócios da marinha e domínios ultramarinos, Tome Joaquim da Costa Corte Real, informando as diligências tomadas para prender os vereadores e procuradores que não aceitaram o novo imposto dos gêneros. Comunica também o envio das certidões, em anexo, das lotações dos ofícios, para ver a falsidade com que foram passadas com consentimento do provedor Jose de Godoi Moreira, e a má arrecadação que a provedoria de Santos fazia, recebendo indevidamente os novos direitos e dando como unidos oficiais que deviam andar separados, espera que a desordem de arrecadação da Fazenda real cesse, mandando o rei suspender o dito provedor e fazer uma devassa por um ministro integro. ', 370, '1759-05-04', 1759, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (234, 'Requerimento de Jose Ponce Diniz, morador da vila de Araritaguaba, a D. Jose I solicitando a confirmação da carta de sesmaria que recebera do governador da praça de Santos, Inacio Eloi de Madureira, de um território que fica a esquerda da vila de Itu. Consta carta do referido governador atestando as informações e solicitações do suplicante.', 223, '1755-09-15', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (232, 'Requerimento do ouvidor geral da comarca de São Paulo, João de Sousa Filgueiras, ao secretário de estado da Marinha e Ultramar, Diogo de Mendonça Corte Real, solicitando determinações reais à respeito de como prosseguir com o ouro recém descoberto no sertão do Tibagi. Consta cópia do requerimento do descobridor Angelo Pedroso Leme, solicitando tempo e licença para continuar a examinar as minas, já que estava proibido de cavar e minerar o ouro descoberto por termo do ouvidor geral até as novas ordens reais.', 221, '1755-05-22', 1755, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (263, 'Requerimento de Luis Antonio de Sa Queiroga, ex-governador da praça de Santos, a D. Jose I em que diz ter servido à coroa durante mais de 60 anos. Encontrando-se pobre e doente, pedia em recompensa de seus serviços que lhe fosse concedida a patente de brigadeiro com o respectivo soldo, convindo-lhe que se lhe desse na praça de Santos, em virtude de o seu estado de saúde não lhe permitir mudar de residência. Consta um despacho do conselho ultramarino mandando dirigir o requerimento ao rei, de 15/02/1753.', 252, '1753-02-15', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (336, 'Requerimento do procurador da mitra do bispado de São Paulo a Jose Luis de Brito e Melo, ouvidor-geral, para que lhe seja passada uma certidão atestando se depois da criação do bispado de São Paulo e estando lá a residir o bispo se deu cargo de tesoureiro e algum na tesouraria dos defuntos e ausentes da comarca de São Paulo.  Consta a certidão, de 25/10/1753.', 314, '1753-10-25', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (792, '*Requerimento de Jose Antonio Pinto Danas Boto em que solicita ao secretário de Estado um lugar na provedoria da Fazenda Real ou na intendência de Vila Rica.', 878, '1760-02-07', 1760, 5, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (872, '*LUIZA Requerimento de Manuel Gomes Duque, tesoureiro da fazenda real pedindo para continuar, por mais 3 anos, na serventia do dito ofício.', 968, '1755-10-15', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (235, 'Requerimento de Antonio Barbosa de Brito, capitão-mor do arraial e minas de São Félix, pedindo que se lhe passe confirmação do dito posto para que foi nomeado por carta patente do governador e capitão-general da capitania de São Paulo, D. Luis Mascarenhas. Consta um despacho do conselho ultramarino. Contém outro requerimento deste requerente, seguido de um alvará, de 03/12/1750; e uma carta patente pela qual o governador nomeava o requerente no devido posto, de 29/08/1740.', 224, '1752-07-29', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (240, 'Requerimento do bacharel e antigo ouvidor geral da comarca e cidade de São Paulo, Jose Luis de Brito e Melo, a D. Jose I solicitando que se passasse certidão do cumprimento das ordens que lhe foram emanadas pelo tribunal do conselho ultramarino no tempo em que serviu neste cargo. Consta carta do secretário do conselho ultramarino, informando que depois de analisar essa questão, lhe parece que o suplicante cumpriu todas as ordens que lhe foi expedido e que cabe ao rei mandar o que for servido. ', 227, '1755-09-18', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (265, 'O capitão Felisberto Caldeira Brant, caixa e administrador do contrato dos diamantes, pede a vossa majestade licença para ir ao Reino. A resposta do requerimento está em anexo. ', 255, '1750-05-09', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (268, 'Antonio Golçalves Pereira, morador do arraial de Congonhas do Sabará e casado com Josefa Cecília da Encarnação, requere a vossa majestade uma licença para viajar para o reino com sua esposa e cinco filhos: três homens e duas mulheres, todos menores de idade. ', 258, '1750-07-22', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (237, 'Representação dos oficiais da câmara de Paranaguá a D. João V pedindo que se entregue aos religiosos de São Francisco a expensas suas e dos irmãos da ordem terceira de São Francisco. Isto solucionava a falta de sacerdotes que há na vila, tornando-se mais fácil a obra, com as esmolas dos devidos, de que os referidos religiosos vivem, de 18/07/1744. Consta um despacho do conselho ultramarino ilegível. Contém, ainda, uma carta de dom Luis Mascarenhas informando ser verdade o que relatam os oficiais, de 02/08/1745; há na margem deste documento uma provisão de D. João V ordenando que Luis Mascarenhas informe, de 01/04/1745, além de despachos dizendo que se deve escrever ao bispo para dar o provimento que julgar conveniente, de 29/03/1746. Ainda há outro requerimento destes oficiais, assim como a informação do bispo.', 225, '1744-07-18', 1744, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (248, 'Queixa do provedor da fazenda real de Santos, Jose de Godoi Moreira, a D. Jose I tratando da diminuição dos rendimentos da provedoria daquela vila. Dentre os motivos para tal, cita contratos que passaram para outras provedorias e a diminuição das rendas daquele almoxarifado devido ao acréscimo das suas despesas.', 234, '1752-08-23', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (343, 'Requerimento de João Fernandes dos Santos, sargento-mor do arraial dos Corvos de São Domingos e Pega, distrito de vila Nova do Bom Sucesso das Minas Novas do Araçui pedindo lhe passe a carta de confirmação do referido posto que ficou vago porque Manuel Ribeiro Ribas ter se ausentado para Goias. O pedido foi escusado por não estar em conformidade.', 279, '1755-01-13', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (243, 'Requerimento de Rafael Freire de Azevedo, morador de Vila Rica, solicitando licença para advogar.', 229, '1752-12-09', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (239, 'Requerimento do procurador da mitra do bispado de São Paulo dizendo que para vários requerimentos lhe é necessário que Francisco Vaz Teixeira, que foi mestre de capela da vila de Itu, passe uma informação sobre quem se proibia cantar. Consta um despacho proferindo, de 05/07/1752. Segue-se no mesmo documento certidão, de 22/07/1752, com reconhecimento. Contém um outro requerimento, assim como uma informação dada por frei Antonio da Madre de Deus Galvão, de 18/08/1752.', 226, '1752-07-05', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (354, 'Requerimento do ouvidor geral e corregedor da comarca de nossa senhora do Rosário de Paranaguá, bacharel Jeronimo Ribeiro de Magalhães, a D. Jose I queixando-se do antigo ouvidor daquela comarca, Antonio Pires da Silva e Melo Porto Carneiro, por falsificar o recibo e a justificação de seu caso, obrigando o advogado do suplicante a pagar as despesas sob pretexto de ser esfera civil, mesmo que depois que o caso degenerou em crime. O suplicante pede ao rei que lhe faça justiça, mandando emendar a sentença que sofreu. Informa que possui documentos e testemunhas confirmando seu ponto, inclusive a confissão do escrivão Patricio da Silva Chaves. Consta dois pareceres dos procuradores da Fazenda e da coroa, em que informam que o rei deve dar a mesma providência que deu a Pedro Fernandes, pois se trata da mesma razão. No parecer do conselho ultramarino é mencionado que o requerente parece ser digno de que o rei mande rever os autos da casa da suplicação com os ministros que nomear.', 340, '1758-07-10', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (241, 'Requerimento do procurador da mitra do bispado de São Paulo pedindo para que o reverendo Matias Alvares Torres lhe informe, por certidão, se no tempo em que serviu de mestre de capela, por provisão do último bispo dom Bernardo Rodrigues Nogueira, chamavam qualquer pessoa para cantar nas suas funções ou se era ele mesmo que cantava; deseja também saber se havia mais do que uma escola que ensinasse solfejo. Consta um despacho proferindo, de 14/07/1752. Segue-se no mesmo documento certidão, de 22//07/1752, passada por Matias Alvares Torres. Contém um outro requerimento anterior, assim como uma informação dada por frei Antonio da Madre de Deus Galvão, de 18/08/1752.', 226, '1752-07-14', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (733, 'Requerimento de Silvestre de Lima, solicitando confirmação de carta de sesmaria de meia légua de terra junto ao rio Mutuca, na freguesia da Borda do Campo, comarca do Rio das Mortes. Consta a dita carta em anexo assinada pelo governado de Minas Gerais, Jose Antonio Freire de Andrade.', 810, '1759-11-11', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (236, 'Requerimento de Antonio Barbosa de Brito, capitão-mor do arraial de Minas de São Félix, ao juiz ordinário do arraial de Santo Antonio, Minas de São Félix de Carlos Marinho, capitão Manuel Leite Ribeiro, pedindo-lhe que seja passado alvará que mostre não ter o suplicante culpas. Segue-se um alvará de folha corrida do referido capitão Manuel Leite Ribeiro, mandando ao escrivão informar sobre as culpas do suplicante, de 03/12/1750, no qual se segue a informação do escrivão dizendo que o requerente não tinha culpa, e um reconhecimento. Contém um outro requerimento do mesmo junto com uma carta patente. ', 224, '1750-12-03', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (242, 'Requerimento a D. Jose I do ajudante de ensaiador Silverio Antonio de Matos e do primeiro fundidor da casa de fundição da capitania de São Paulo, Francisco Fernandes Pinto, solicitando alvará em que conste a nomeação do seu ofício. Consta instrumento de justificação do ouvidor geral da comarca e cidade de São Paulo, Jose Luis de Melo e Brito, informando que o escrivão não pode emitir o alvará sem a licença do rei. ', 228, '1755-10-13', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (291, 'Requerimento de Silverio Antonio de Matos pedindo certidão pelo qual fora nomeado ajudante de ensaiador da casa de fundição de São Paulo. Consta despacho deferindo o pedido, junto com a respectiva certidão. ', 285, '1753-01-26', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (110, 'Requerimento da dona Maria Inacia Faciel, mulher do intendente Manuel Rodrigues Torres, solicitando a D. João V a liberdade do seu marido, a restituição dos seus bens e a inserção do mesmo em um cargo, já que o dito estaria preso apenas por armação de seus inimigos, incluindo o ouvidor de São Paulo. Consta parecer do conselho ultramarino, ordenando soltar o dito intendente caso o mesmo seja inocente.', 120, '1741-01-23', 1741, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (205, 'Representação dos oficiais da câmara de São Paulo a D. João V expondo que não tinham recebido uma carta que o rei remetera, na qual mandara encarregar Gomes Freire de Andrade dos novos governos das capitanias de Goiás e de Mato Grosso; dizendo que a segunda via e o maço das cartas não trouxe selo nem a forma do fecho das outras que tinham sido enviadas; e que Domingos Luis da Rocha, ouvidor geral dessa comarca, não recebeu a ordem régia para guardar os bens dos órfãos daquela cidade, de 26/02/1749. Há uma nota dizendo que não foi achado na secretaria os recibos que se mandam juntar. Contém uma cópia de provisão de D. João V e uma carta do governador e capitão-general da capitania do Rio de Janeiro. ', 198, '1749-02-26', 1749, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (355, 'Requerimento de Pedro Fernandes, morador na vila de Paranaguá e mestre de uma sumaca com que viajava por todos os portos da costa do Rio de Janeiro, queixando-se a D. Jose I do antigo ouvidor daquela vila, Dr. Antonio Pires da Silva e Melo Porto Carneiro que fez um recibo falso de seu caso, obrigando-o a pagar as despesas do caso, sem que o suplicante estivesse presente, não lhe passando as vias de sua residência, confiscando seus bens e deixando o suplicante na miséria, além de o ameaçara mandar para a prisão. O requerente solicita providências para que lhe seja feita justiça, afirmando que o capitão-mor D. João Francisco Lanhas testemunhou que passou o referido documento contra a sua vontade, por ordem do mesmo ouvidor. Consta parecer do procurador da fazenda, afirmando que a sentença do suplicante é muito rigorosa e que não havia dúvidas de que ele havia sido vítima de uma acusação falsa, sendo que não deveria pagar as perdas e danos. Consta parecer do procurador da coroa, concordando que deve haver uma revisão da sentença e que ordene ao governador da relação do Rio de Janeiro que envie cópia do caso ao conselho, pois se provada a falsidade cabe castigar o acusador. Consta carta do escrivão Patricio da Silva Chaves relatando todas as irregularidades presenciadas por ele e cometidas por Antonio Pires da Silva e Melo Porto Carneiro, que havia cometido crimes de roubo, sonegação e corrupção com o ouro da real capitação.', 341, '1758-01-07', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (66, 'Requerimento de Antonio de Freitas Branco, almoxarife da fazenda real da capitania de São Paulo, a Francisco Jose Brandão, provedor da mesma fazenda, pedindo que mande passar certidão do ordenado que recebe. Consta um despacho do provedor deferindo o pedido. Segue-se a certidão atestando que o tesoureiro da fazenda real do Rio Janeiro vence anualmente om ordenado de seiscentos mil réis, de 24/03/1770. Contém, ainda, uma provisão de Luis Antonio de Sousa Botelho Mourão pela qual nomeia Antonio de Freitas Branco almoxarife da fazenda real da capitania de São Paulo, de 29/01/1770; outro requerimento de Antonio de Freitas Branco no qual pede que ateste que foi nomeado almoxarife por três anos, cargo que o impede de tratar de seus negócios e cujo ordenado não é compensador; um atestado do provedor confirmando as afirmações colocadas, de 07/02/1771; outro requerimento de Antonio de Freitas Branco ao ouvidor geral da comarca de São Paulo; um atestado do ouvidor confirmando o que foi dito; e um último requerimento de Antonio de Freitas Branco a D. Jose I pedindo que lhe alvitre ordenado equivalente ao do tesoureiro do Rio de Janeiro. Sobre este último requerimento, consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, o último dos quais manda recorrer a D. Jose I, de 06/07/1771.', 78, '1770-03-24', 1770, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (247, 'Carta do bispo de São Paulo, D. Frei Antonio da Madre de Deus Galvão a D. Jose I dizendo ter encontrado uma ermida em péssimo estado, solicitando que o rei mande concluir as obras, pois o provedor da fazenda real da vila de Santos pusera a obra a lanços. Queixa-se também que os cônegos recebem pequenas congruas e que há clérigos que não podem paroquiar algumas freguesias por serem pobres. Solicita que lhe comunique até onde devia estender a sua jurisdição, porque os bispos circunvizinhos o tinham espoliado, deixando-lhe as terras mais pobres.', 233, '1752-08-21', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (61, 'Requerimento de Manuel Lopes Viana a D. Jose I no qual pede confirmação da patente do posto de capitão-mor da vila de Jacarei. Consta despacho do conselho ultramarino, mandando passar a confirmação de patente, de 11/05/1771. Contém, ainda, uma carta patente de 20/10/1768 passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, pela qual nomeia o sargento-mor ao posto de capitão-mor da vila de Jacarei. Segue-se no verso o termo de posse lavrado pelo escrivão da câmara da vila de Nossa Senhora da Conceição da Paraíba de Jacarei. 
INSERT INTO app_hist.provocacao VALUES (283, 'Requerimento do ajudante de tenente da capitania de São Paulo, Antonio Francisco Barris, a D. Jose I, solicitando o pagamento de seu soldo pela provedoria de Santos, com o mesmo soldo das Minas Gerais, devido ao seus bons serviços prestados ao reino durante cinquenta e quatro anos. Consta parecer positivo do conselho ultramarino.', 277, '1758-01-13', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (266, 'Jose Ferreira de Veiga e Afonso Ginabel, testamenteiros de Jorge de Azevedo, arrematante dos contratos dos dízimos da fazenda real, pedem que isente a cobrança dos referidos contratos já que as mesmas se encontram pagas na fazenda real.', 256, '1755-09-07', 1755, 5, 'R', '', '3');
INSERT INTO app_hist.provocacao VALUES (308, 'Instrumento de auto cíveis a favor de Maria Gomes Pinheira, viúva de Gaspar da Rocha Pereira, lavrado no juízo da provedoria dos defuntos e ausentes, por requerimento de Gaspar da Rocha Pereira, em que Bento de Castro Carneiro era justificado.', 301, '1755-03-06', 1755, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (238, 'Representação dos oficiais da câmara de Paranaguá a D. João V pedindo licença para se fundar o convento de franciscanos. No caso de estar já concedido, pedem provisão na forma costumada, de 20/08/1748. Constam dois despachos do conselho ultramarino. Contém, ainda, uma representação destes oficiais mais recente; uma carta de dom Luis Mascarenhas informando ser verdade o que relatam os oficiais, de 02/08/1745, no qual há na margem deste documento uma provisão de D. João V ordenando que Luis Mascarenhas informe, de 01/04/1745, além de despachos dizendo que se deve escrever ao bispo para dar o provimento que julgar conveniente, de 29/03/1746. Ainda a informação do bispo.', 225, '1748-08-20', 1748, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (637, 'Requerimento de D. Ignacia Rodrigues Chaves, natural da Roça Grande do Sabará, filha de Domingos Rodrigues Chaves e casada com Felix Leite Coelho, morador de Braga. Solicita a D. Jose I que possa seguir seu marido e retornar ao reino com seus filhos, pedindo que seja passado alvará para que qualquer capitão de navio aceite-a a bordo na embarcação. Consta ordem régia do rei e confirmação da licença pelo governador Antonio Freire de Andrade.', 670, '1757-02-18', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (301, 'Requerimento de Francisco Rodrigues Ferreira, morador na vila de Santos, a D. Jose I pedindo que lhe conceda moratória por cinco anos. Consta um despacho do conselho ultramarino, ordenando ao ouvidor de São Paulo que dê o seu parecer. Contém um outro requerimento do mesmo requerente.', 291, '1753-05-11', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (721, '***Requerimento do reverendo e vigário de Ouro Preto, Antonio Jose de Moura, dando conta das despesas da festa de Corpus Christi e solicitando uma resposta sobre quem deve pagar pela mesma', 797, '1759-06-10', 1759, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (853, 'maisa Requerimento do bacharel Jose Antonio Pinto Donas Boto, solicitando certidão do teor das provisões em que conste a prorrogação do tempo de que foi proprietário dos ofícios que exerceu. O pedido foi deferido.', 944, '1760-12-06', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (273, 'Requerimento de Jose Luis de Brito e Melo, ouvidor da comarca de São Paulo, pedindo a D. Jose I provisão para poder receber o aumento do vencimento, tal como sucedeu aos ouvidores do Rio de Janeiro e do Espírito Santo. Consta três despachos do conselho ultramarino, um dos quais favorável, de 29/04/1753. Contém bilhete de Joaquim Miguel Lopes de Lavre, mandando passar a provisão pedida de 25/04/1753.', 266, '1753-03-09', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (249, 'Representação dos oficiais da câmara de São Paulo a D. Jose I dizendo que os comerciantes das minas de Goiás são prejudicados por terem que fundir o seu ouro em Vila Boa, cabeça da comarca em que está a casa da fundição, pelo risco que correm devido aos gentios e foragidos, além das despesas e distância. Constam dois despachos do conselho ultramarino e um parecer do procurador da fazenda, segundo o qual os governadores de Goiás e do Rio de Janeiro devem dar informação de onde se deva fazer a fundição com mais comodidade.', 235, '1752-08-26', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (281, 'Requerimento de Matias Alvares Vieira de Castro Crasto, sargento-mor, João de Oliveira Cardoso e Tomas Pacheco Galindo pedindo a Jose Luis de Brito e Melo, ouvidor-geral e intendente da real casa da fundição da comarca de São Paulo, que lhe mande passar certidão atestando que o primeiro requerente serve de tesoureiro da real casa da fundição da comarca de São Paulo; o segundo de escrivão da receita e despesa; e o terceiro de escrivão da fundição da mesma casa. Contém a certidão pedida, de 12/08/1752, com reconhecimento de 16/08/1752. Consta outros dois requerimentos.', 267, '1752-08-12', 1752, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (225, 'Requerimento do capitão Matias do Couto Reis ao ouvidor-geral da comarca de São Paulo e intendente, Jose Luis de Brito e Melo, para que este rubrique e sentencie os papéis de serviços pois pretende apresentar um requerimento a D. Jose I. Contém um conjunto de requerimentos e certidões.', 215, '1752-04-17', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (267, 'Jose Figueiredo, soldado da companhia de Dragões de Minas Gerais, requere a vossa majestade licença para viajar ao Reino na ocasião de ter descoberto que seus pais haviam falecidos. ', 257, '1750-07-20', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (53, 'Requerimento dos oficiais da câmara de São Paulo a D. João V solicitando a permanência do ouvidor da comarca de São Paulo, João Rodrigues Campelo, por mais três anos. ', 65, '1736-07-08', 1736, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (230, 'Requerimento de Feliciana Teresa Rita a D. Jose I dizendo que, pela brevidade que seu marido, Manuel Jose da Silva, tivera de partir por ter sido nomeado ensaiador da casa da fundição da cidade de São Paulo, não pudera deixar à requerente dinheiro para se alimentar. Assim, requereu que lhe dessem oitocentos réis por dia para seu sustento, mas como não foi atendido, pede ao rei que ordene por intermédio do conselho ultramarino que se lhe assista com este valor por conta do ordenado de seu marido, dando primeiro fiança. Consta um despacho do conselho ultramarino; um parecer do procurador da fazenda, dizendo que não tinha dúvida em meter na folha das famílias essa quantia com uma declaração garantindo que o marido havia de pagar tudo e que a requerente reporia tudo o que tivesse recebido se depois de um ano a fazenda real não tivesse sido reembolsada; um parecer dos conselheiros do conselho ultramarino no qual Fernando Jose Marques Bacalhau e Rafael Pires Pardinho concordam com a resposta do procurador da fazenda e Diogo Rangel de Almeida Castelo Branco e Antonio Freire de Andrade pontuam que a quantia pedida pela requerente era excessiva, que não se sabia se o marido destinava alguma estação pela qual sua mulher pudesse procurar o seu sustento e que a requerente deveria fazer novo requerimento com consentimento do marido, de 19/07/1752. Contém requerimentos anteriores de Feliciana, além de uma carta e um aviso.', 220, '1752-07-13', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (364, 'Representação dos oficiais da câmara da vila de Paranaguá a D. Jose I expondo a forma como é aplicada a justiça na comarca. Queixam-se do gasto nas correições anuais, assim como por estas resultarem em muitas injustiças e por isso deveriam ser feitas apenas uma correição trienal em toda a comarca. Em adendo, dizem que nenhum homem bom naquela vila e restante comarca que servia de juiz ordinário e de oficial da câmara procurando privilégios que os isentem daqueles cargos e que os ouvidores-gerais se intrometem no governo econômico, agindo apenas de acordo com seus interesses. Constam três despachos do conselho ultramarino e dois pareceres, um do procurador da fazenda no qual diz que na residência que se mandou tirar ao ouvidor se deve inquirir dos excessos que apontaram os oficiais da câmara que para os evitar se deve ordenar aos ouvidores que mandem a relação das condenações que faziam e dos motivos por que as aplicam determinando o que devem legar de aposentadoria e castigando-se asperamente todas as injustiças, outro do procurador da coroa é de teor idêntico. ', 349, '1751-12-18', 1751, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (274, 'Requerimento do tesoureiro Matias Alvares Vieira de Castro Crasto e escrivães da receita e despesa, intendência e fundição da real casa da comarca de São Paulo a D. Jose I pedindo que determine que eles vençam salários iguais aos dos oficiais das outras casas de fundição. Constam dois despachos do conselho ultramarino e um parecer do procurador da fazenda dizendo que se devem mandar conservar os ordenados antigos. Contém em anexo outros requerimento. ', 267, '1753-03-10', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (284, 'Requerimento dos moradores de Catas Altas, termo da cidade de Mariana, solicitando providências contra os vexames de que são vítimas por parte do bispo e outros padres da referidade cidade. Em anexo petição dos moradores ao senado da câmara.', 278, '1755-05-06', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (227, 'Requerimento do capitão Matias do Couto Reis ao governador da praça de Santos, Luis Antonio de Sá Queiroga, para que lhe seja passado um certificado da importância da obra que fez na reconstrução da fortaleza de São João da Barra de Bertioga. Consta um despacho do governador mandando que o provedor da fazenda real faça a avaliação da despesa, de 03/01/1752; um despacho do provedor da fazenda, Jose de Godoi Madureira, ordenando que se façam a avaliação da obra, os juízes do ofício de pedreiros, Bento de Oliveira e Jeronimo Francisco Bitencourt; e uma certidão em que Bento de Oliveira e Jeronimo Francisco Bitencourt atestam ter ido à fortaleza para avaliar a obra, achando que se dispendeu doze mil cruzados, de 19/01/1752. Contém um conjunto de requerimentos e certidões.', 215, '1752-01-03', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (299, 'Requerimento de Francisco Rodrigues Ferreira, morador na vila de Santos, a D. Jose I pedindo que lhe conceda moratória por cinco anos para que pudesse satisfazer a dívida de três contos. Consta dois despachos do conselho ultramarino e três pareceres, um do conselho, outro do procurador da fazenda e outro do procurador da coroa. Contém um outro requerimento do mesmo requerente. ', 291, '1753-05-16', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (251, 'A câmara de Mariana solicita, pela segunda vez, à vossa majestade por uma provisão que conceda permissão ao ouvidor de Vila Rica realizar o tombamento e sesmaria de um terreno sobre o qual há grandes duvidas acerca do pagamento de foros, já que as propriedades que lá existiam foram destruídas por uma inundação.', 239, '1750-03-02', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (254, 'Requerimento do ouvidor geral da comarca de São Paulo, João de Sousa Filgueiras, ao secretário do reino conde de Oeiras, Sebastião de Carvalho e Melo, relatando os problemas de jurisdição entre o governador do Rio de Janeiro e o ouvidor de Paranaguá acerca da descoberta de diamantes no sertão de Tibagi pelo italiano Francisco Tosi Colombina. Solicita igualmente que não se esqueça dele e de seu irmão caso surja um cargo na Relação do Rio de Janeiro.', 242, '1756-01-24', 1756, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (252, ' Manuel Ferreira Freire da Cruz, bispo de Mariana, se queixa a vossa majestade do contratador dos direitos reais dos registros da capitânia das Minas Gerais, Francisco Pereira da  Silva, que entendia que o dito bispo não estava isento do pagamento dos direitos. Pede também por um esclarecimento no assunto. ', 240, '1750-07-07', 1750, 5, 'R', '', '3');
INSERT INTO app_hist.provocacao VALUES (300, 'Requerimento do governador de Santos, Alexandre Luis de Sousa Meneses a D. Jose I, solicitando provisão pela provedoria do Rio de Janeiro para que lhe passem 240$000 réis de ajuda de custo, como já haviam concedido ao seu antecessor, Inacio Eloi de Madureira. Solicita também provisão para vencer soldo, desde o dia em que partiu do reino.', 292, '1757-01-17', 1757, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (253, 'Manuel de Azevedo Aragão, promovido capitão-mor da fortaleza de Gurupa, solicita a vossa majestade a mercê de se ordenar a pagar seus soldos desde o dia do embarque. ', 241, '1750-03-17', 1750, 5, 'R', '', '2');
INSERT INTO app_hist.provocacao VALUES (255, 'Representação do vigário e paroquianos da freguesia do caminho velho das minas, chamada da Conceição do Facão, da vila de Guaratinguetá, a D. Jose I soliictando um auxílio pecuniário para a sua igreja por esta ser muito pobre, de 20/09/1752. Consta um parecer do procurador da fazenda, dois despachos do conselho ultramarino, um dos quais ordena que se requeira pela mesa da consciência e ordens, de 09/05/1754.', 243, '1752-09-20', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (275, 'Queixa do governador da praça de Santos, Inacio Eloi de Madureira, ao ouvidor provedor dos ausentes da comarca de Paranaguá, Jeronimo Ribeiro de Magalhães, que deu ordens despóticas para que o embargo da arrecadação dos direitos das tropas da campanha de São Pedro do Sul fosse realizado pelo juízo dos ausentes, não cumprindo a ordem real que determinava que a arrecadação dos direitos das tropas fossem feitas pela provedoria real. Consta parecer do conselho ultramarino, mas o anexo está ilegível.', 268, '1758-02-25', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (257, 'Requerimento de João Ferreira de Oliveira, sargento-mor das ordenanças da cidade de São Paulo, a D. Jose I, pedindo que lhe seja passada certidão da sua carta patente de sargento-mor. Consta um despacho do conselho ultramarino mandando passar a certidão do que constar, de 27/01/1752, seguida de uma certidão da carta patente, de 02/02/1752. Contém um requerimento posterior a este, além de um bilhete do conselheiro Diogo Rangel de Almeida Castelo Branco, de 10/11/1752.', 244, '1752-01-27', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (258, 'Requerimento da viúva e filhos administradores da casa de Estevão Martins Torres a D. Jose I dizendo que havia sido contratado o provimento do sal da américa com a faculdade de prover os postos e sertão do Brasil e entre eles o da vila de Santos, meio por onde os moradores do sertão tiravam o sal que lhes era preciso. Era de conhecimento que o rei tinha separado as capitanias e mandara separar por contrato novo o provimento de sal para o Mato Grosso e outras partes. Pediam que lhe fosse abatido no preço do contrato este grande prejuízo. Consta um parecer do provedor da fazenda e dois despachos do conselho ultramarino, o último dos quais mandando que se passasse provisão de licença para se citar o provedor da fazenda para pelos meios competentes se lhe intimar o produto, de 28/11/1752.', 246, '1752-11-24', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (222, 'Requerimento do ouvidor da cidade de São Paulo e intendente da casa de fundição do ouro das minas da comarca de São Paulo, João de Sousa FIlgueiras, a D. Jose I, solicitando que lhe seja concedido, como intendente, o mesmo ordenado que recebiam os demais intendentes das antigas casas de fundição. Consta o regimento das casas de fundição impresso em anexo.', 212, '1754-07-13', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (269, 'Requerimento de Escolastica Maria de Matos, viúva de Manuel de Macedo e moradora da cidade de São Paulo, a D. Jose I, solicitando a mercê da tutoria de seus filhos, devido a morte de seu marido. Consta instrumento de justificação.', 261, '1756-12-16', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (285, 'Carta do ouvidor geral da comarca de São Paulo, João de Sousa Filgueiras, ao secretário do reino, o conde de Oeiras, Sebastião Jose de Carvalho e Melo, queixando-se das tentativas frustradas de prisão de Carlos de Araujo Castro, que há mais de onze anos vinha horrorizando a comarca, sendo acusado de crimes de violência. Como se encontrava escondido nos matos, não era possível prendê-lo. Consta dois autos de devassa e um auto de prisão.', 280, '1758-03-12', 1758, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (293, 'Requerimento de Inacio Eloi de Madureira, governador da praça de Santos, a D. Jose I pedindo que lhe mande dar casa em que habite, e que corresponda ao lugar que ocupa. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, o último dos quais indefere o pedido, de 15/05/1753. Contém outro requerimento do mesmo requerente.', 287, '1753-05-02', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (365, 'Carta do bispo de São Paulo, Frei Antonio da Madre de Deus Galvão, para D. Jose I alegando que, segundo o alvará de 18 de agosto de 1747, fora determinado que os processos dos tribunais eclesiásticos em que tivesse de interferir o juízo da coroa tinham de ser remetidos ao tribunal e, ainda, que os traslados dos processos, deveriam ficar no juízo eclesiástico para evitar que, perdendo-se os originais no juízo da coroa, não se pudessem reformar. Entretanto, os recorrentes não queriam pagar aos escrivães o trabalho de fazerem os ditos traslados. Além disso, os oficiais da justiça recusaram-se a devolver os processos depois de os resolverem. O bispo pede que o rei dê ordens sobre o assunto. Consta um parecer do procurador da fazenda declarando que não se faziam traslados nos casos citados e que esta carta deveria se remetida ao conselho ultramarino; recaiu um parecer do procurador geral das ordens declarando que concorda que a carta seja enviada ao conselho ultramarino; e dois despachos do conselho ultramarino, indeferindo o pedido do bispo, de 29/05/1753.', 350, '1752-08-16', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (294, 'Requerimento de Inacio Eloi de Madureira, coronel e governador da praça de Santos, a D. Jose pedindo que mande acrescentar o seu soldo de três mil cruzados, em virtude de o seu cargo o obrigar a gastar muito. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, o último dos quais indefere o pedido, de 15/05/1753. Contém outro requerimento do mesmo requerente.', 287, '1753-05-02', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (312, 'Carta de dom frei Antonio da Madre de Deus Galrão, bispo de São Paulo, informando que o ouvidor de São Paulo, Jose Luis de Brito e Melo, tinha procedido para com ele pouco corretamente e se mostrara, em muitas demonstrações de caráter religioso, pouco cristão.', 306, '1753-09-30', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (337, 'Requerimento do procurador da mitra do bispado de São Paulo a Jose de Godoi Moreira, ouvidor-geral, para que lhe seja passada uma certidão indicando a data exata em que o coronel Francisco Pinto do Rego foi provido no cargo de tesoureiro do juízo de defuntos e ausentes da cidade de São Paulo. Consta certidão, de 25/10/1753. Contém documentos anexos, como requerimentos. ', 314, '1753-10-25', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (200, 'Requerimento de Diogo Pinto do Rego, mestre-de-campo e escrivão da ouvidoria e correição da cidade de São Paulo, pedindo a Jose Luis de Brito e Melo, ouvidor-geral e corregedor da cidade de São Paulo, que lhe faça mercê de mandar que o cirurgião Domingos da Fonseca Leitão passe as moléstias de que padecia que o privavam de exercer qualquer ocupação. Consta um despacho do ouvidor a deferir. Contém requerimentos do mesmo, assim como certidões, alvarás e avisos.', 183, '1750-03-17', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (193, 'Requerimento de João de Sousa Filgueiras, recentemente nomeado a ouvidor da cidade de São Paulo, a D. Jose I, solicitando ajuda de custo para a sua viagem, como já ocorrera com outros ouvidores. Consta consulta do conselho ultramarino, cujo parecer é de que a mercê deveria ser concedida, porém deveria ser paga pela provedoria do Rio de Janeiro.', 192, '1754-02-13', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (315, 'Requerimento de Francisco Aranha Barreto, alferes de infantaria da guarnição da vila e praça de Santos, de quem é comandante Manuel Borges da Costa, no qual pede a D. Jose I para o nomear para a fortaleza de São João da Barra da Bertioga daquela praça com o posto de capitão. Consta um despacho do conselho ultramarino, mandando requerer em concurso, de 16/10/1753. Contém documentos anexos. ', 310, '1753-10-16', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (313, 'Carta do provedor da fazenda da capitania de São Paulo, Jose de Godoi Moreira, a D. Jose I dizendo que, ao fundar-se  a ouvidora da câmara de São Paulo, as câmaras da mesma se haviam comprometido a dar um donativo anual para pagamento do ordenado aos ouvidores e que a provedoria da fazenda real sempre arrecadaria aquela gratificação. Porém, o rei determinou que fossem pagos pelo rendimento da provedoria, os ouvidores nunca mais obtiveram das câmaras aquela quantia. Consta um despacho do conselho ultramarino, determinando que se juntasse a ordem pela qual se tirava às câmaras a obrigação de pagarem os ordenados e se impunha à provedoria, de 28/02/1754.', 307, '1753-10-06', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (314, 'Requerimento de Antonio Soares da Costa, solicitando a D. Jose I carta de confirmação de sesmaria de meia légua de terras de testada e meia de sertão, na paragem de Araritaguaba, que arrematou em hasta pública a Jose de Brito Leme. Consta 1. despacho do conselho ultramarino e carta de Joaquim Miguel Lopes de Lavre mandando passar certidão, 2. carta de sesmaria passada pelo governador da capitania de São Paulo, D. Luis de Mascarenhas, concedendo a Jose de Brito Leme as referidas terras, 3. auto de demarcação e medição das terras da sesmaria, 4. requerimento do mesmo suplicante solicitando certidão do termo de arrematação das terras e certidão passada pelo escrivão dos órfãos na vila de Itu, Francisco Xavier Paes.', 308, '1757-08-18', 1757, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (316, 'Requerimento de Francisco Aranha Barreto, sargento do número da companhia do capitão Andre Cursino de Matos da guarnição da praça de Santos, aos oficiais da câmara de são sebastião, pedindo que lhe passem uma certidão do seu procedimento durante o tempo que tem comandado o destacamento de que o encarregou o governador daquela praça, Jose Rodrigues de Oliveira. Consta um despacho dos oficiais da câmara, deferindo-o, de 20/03/1742. Contém documentos anexos. ', 310, '1740-03-20', 1740, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (317, 'Requerimento de Francisco Aranha Barreto aos oficiais da câmara da vila de são sebastião pedindo autorização para o escrivão da câmara passar uma certidão na qual conste que o requerente tem estado há dois anos a comandar aquela vila com um destacamento de doze soldados, assim como sobre o seu comportamento. Consta um despacho dos oficiais da câmara, deferindo, de 17/03/1742. Contém documentos anexos.', 310, '1742-03-17', 1742, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (318, 'Requerimento de Francisco Aranha Barreto, alferes, ao provedor-geral da fazenda da vila e praça de Santos, Antonio Francisco Lustosa, pedindo autorização para o escrivão da matrícula lhe passar uma certidão de fé de ofícios. Consta um despacho do provedor-geral, deferindo-o. Contém documentos anexos. ', 310, '1742-08-19', 1742, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (319, 'Requerimento de Francisco Aranha Barreto, alferes de infantaria, ao provedor-geral Jose de Godoi Moreira, pedindo que lhe mande passar uma certidão declarando estar o requerente ao serviço e há quanto tempo se encontra destacado na ilha de São Sebastião. Consta um despacho do provedor deferindo-o. Contém documentos anexos. ', 310, '1746-08-22', 1746, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (340, 'Carta do ouvidor da comarca de São Paulo, João de Sousa Filgueiras, para o ministro e secretário de Estado dos negócios do reino, Sebastião Jose de Carvalho e Melo, pedindo que lhe seja concedido o cargo de Intendente geral do ouro da Bahia com beca e um lugar na relação, visto este não ser incompatível com o da intendência geral. Informa que os cargos de ouvidor de Sabará e de Ouro Preto se encontram vagos, e afirma que qualquer daqueles cargos lhe convinha, desde que lhe fossem conferidos com beca, vencimento de ordenado e emolumentos.', 315, '1758-03-12', 1758, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (321, 'Requerimento de Jose de Brito Leme aos senadores da câmara da vila de Nossa Senhora da Candelária de Itu, explicando que lhe foi concedida a sesmaria que pediu, e solicitando que fosse nomeado piloto e arrumador da sua parte. Consta despachos da dita câmara, ordenando passar a certidão.', 308, '1748-11-16', 1748, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (339, 'Requerimento do procurador da mitra do bispado de São Paulo a Jose de Godoi Moreira, provedor da fazenda real, para que lhe seja passada uma certidão da quantia que pagou para a fazenda real o coronel Francisco Pinto do Rego nos anos em que serviu de tesoureiro do juízo de defuntos e ausentes da comarca de São Paulo. Consta a certidão, de 02/11/1753.', 314, '1753-11-02', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (311, 'Carta de Frei Antonio da Madre de Deus Galrão, bispo de São Paulo, a D. Jose I no qual pede que o provedor lhe dê a parte que lhe compete da côngrua, como todos os bispos da América usufruem, visto que o provedor, Jose de Godoi Moreira, recusou-se a cumprir a ordem no que respeitava à concessão do dinheiro. Consta um parecer do procurador da fazenda, segundo o qual se deve passar a ordem pedida na forma do alvará de D. Pedro II que está ainda em vigor. Sobre a mesma carta há dois despachos do conselho ultramarino, um dos quais declarando que seja ordenado ao dito provedor que remeta ao conselho ultramarino a terça parte da importância das côngruas destinada às despesas das bulas, de 25/05/1753.', 305, '1752-08-17', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (322, 'Requerimento de Francisco Aranha Barreto a D. Jose I no qual pede para o nomear para o posto de capitão pago da infantaria da guarnição da praça de Santos que se encontra vago por falecimento de Francisco de Almeida Albernas. Consta um despacho do conselho ultramarino. Contém documentos anexos.', 310, '1750-09-16', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (323, 'Requerimento de Francisco Aranha Barreto ao João Vieira de Andrade, juiz de fora, a pedir para lhe ser passado alvará de folha corrida.  Constam dois despachos do juiz-vereador da vila e praça de Santos, Domingos Pereira Viegas, o segundo dos quais manda passar o alvará requerido, de 08/03/1751. Contém documentos anexos. ', 310, '1751-03-08', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (324, 'Requerimento de Francisco Aranha Barreto ao Jose Godoi Moreira, provedor da fazenda, pedindo autorização para mandar o escrivão da matrícula passar-lhe uma certidão declarativa do posto que exercia. Consta um despacho do provedor, deferindo-o, de 08/03/1751. Contém documentos anexos.', 310, '1751-03-08', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (325, 'Requerimento de Francisco Aranha Barreto, da companhia do capitão Manuel Borges da Costa ao provedor da fazenda real e provedor-geral da vila e praça de Santos, Jose de Godoi Moreira, pedindo para mandar o escrivão atestar por certidão o seu tempo de serviço e ao seu comportamento. Consta um despacho do provedor, deferindo-o. Contém documentos anexos.', 310, '1751-03-10', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (326, 'Requerimento do ouvidor geral da comarca de São Paulo, João de Sousa Filgueiras, para o ministro e secretário de estado e negócios do reino, Sebastião Jose de Carvalho e Melo, solicitando que em atenção aos serviços por ele prestados, se for considerado digno de recompensa lhe seja concedido o lugar de intendente geral do ouro da Bahia ou o de ouvidor de Sabará. Consta ofício do referido ouvidor em que detalha o trabalho que fez nas prisões de criminosos e na aplicação do novo imposto em alguns gêneros, que sofreu oposição da vila de Santos.', 311, '1757-11-08', 1757, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (327, 'Requerimento de Francisco Aranha Barreto ao provedor da fazenda, Jose de Godoi Moreira, pedindo para autorizar que se lhe passe uma certidão declarando estar ao serviço. Consta um despacho do referido provedor a mandar passar o que houve sobre o requerente, de 08/03/1751. Contém documentos anexos.', 310, '1751-03-10', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (328, 'Requerimento de Francisco Aranha Barreto aos oficiais da câmara da cidade de São Paulo pedindo que lhe mandem passar uma certidão do seu comportamento nos três meses que comandou o destacamento da cidade. Contém documentos anexos. ', 310, '1753-08-18', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (329, 'Requerimento de Francisco Aranha Barreto ao juiz de fora, Francisco Caetano de Almeida Lobo, pedindo autorização para lhe ser passado alvará de folha corrida em que os tabeliões declarem o que constar sobre o requerente. Constam dois despachos do juiz de fora, deferindo-o. Contém documentos anexos.', 310, '1753-10-09', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (330, 'Requerimento de Francisco Aranha Barreto ao governador da praça de Santos, Inacio Eloi de Madureira, pedindo que lhe mande passar uma certidão do falecimento do capitão da fortaleza de São João da Bertioga, Matias do Couto Reis. Consta um despacho do governador mandando passar a certidão na forma das ordens de D. Jose I, de 20/10/1753.', 310, '1753-10-20', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (345, 'Requerimento do Padre João Carneiro de Barros, morador na Vila de São de José, comarca do Rio das Mortes, pedindo provisão para não ser obrigado a abandonar as Minas devido ao requerimento estar abaixo de seu estatuto e lhe causar prejuízos econômicos. ', 319, NULL, 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (332, 'Requerimento de Antonio Gaspar dos Reis a D. Jose I, solicitando a confirmação de sua nomeação para tenente da companhia do capitão Fernando Leite Guimarães da guarnição da praça de Santos. Consta despacho do conselho ultramarino deferindo o pedido e carta de nomeação do cargo provido pelo governador da capitania do Rio de Janeiro, Gomes Freire de Andrade.', 313, '1757-12-19', 1757, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (342, 'Requerimento de Manuel Dias de Carvalho solicitando a D. Jose I confirmação da carta patente inclusa, pela qual foi nomeado capitão das ordenanças da vila de Santo Antônio de Guaratinguetá. Consta carta patente da nomeação do suplicante no referido cargo pelo governador da capitania do Rio de Janeiro, Gomes Freire de Andrade.', 317, '1758-06-01', 1758, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (331, 'Requerimento do sargento-mor da vila de Jundiaí, Antonio Morais Pedroso, solicitando a D. Maria I confirmação de patente do referido posto. Consta despacho do conselho ultramarino, carta patente pela qual o governador de São Paulo, D. Luis de Mascarenhas, nomeou o suplicante no referido cargo, termo de posse e juramento.', 312, '1757-12-14', 1757, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (572, 'Requerimento de Alexandre Metelo de Sousa Coutinho para Tome Joaquim da Costa Corte Real solicitando ordem de novo lanço para os cinco contratos das entradas das minas.', 583, '1757-02-04', 1757, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (346, 'Requerimento de João Botelho de Carvalho Cabral, testamenteiro dos bens que ficaram por morte de seu irmão Antonio Botelho de Sampaio, pedindo que se ordene ao juiz de fora de Mariana que lhe admita a tutela, dos referidos dos bens, por seu procurador. ', 320, '1755-02-06', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (320, 'Requerimento de Francisco Aranha Barreto ao juiz de fora, João Vieira de Andrade, pedindo autorização para lhe ser passada folha dos seus serviços. Consta dois despachos do juiz de fora, deferindo-o, de 26/02/1750. Contém documentos anexos. ', 310, '1750-02-26', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (368, 'Requerimento dos oficiais da câmara de São Paulo a D. Jose I, expondo os bons serviços do ouvidor João de Sousa Filgueiras e pedindo que seu lugar seja conservado, pois o mesmo é muito ativo contra os criminosos, mencionando que remeteu a cadeia da relação do Rio de Janeiro Carlos de Araujo Castro, acusado de crimes de morte e roubos, e Antonio Raposo, que arrombou a cadeia da vila de Mogi. Constam vinte e cinco certidões assinadas por religiosos da capitania de São Paulo, atestando os bons trabalhos realizados pelo referido ouvidor no seu cargo.', 353, '1750-04-28', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (603, 'Requerimento de Manuel Medeiros Rosa, capitão de ordenanças do pé do arraial da Cachoeira, termo de Vila Rica, solicitando ao rei a mercê de ser confirmado no exercício do referido cargo, que se encontra vago devido aos abusos cometidos por Jose da Silva Rosa. Em anexo uma carta patente.', 632, '1751-09-22', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (341, 'Requerimento de Nuno da Cunha de Eça e Vasconcelos a D. Jose I dizendo que para bem de seus requerimentos lhe é necessário uma certidão, declarando-se nela o lugar do suplicante em um outro governo. Consta um despacho do conselho ultramarino, deferindo-o, de 12/01/1754.', 316, '1754-01-12', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (636, 'Queixa do desembargador João Cardoso de Azevedo solicitando a tiragem da residência de Caetano da Costa Matoso, ouvidor da comarca de Vila Rica, a D. Jose I, pois o tempo em que ficou no seu cargo implicou em diversas prisões de oficiais e acusações de crimes, como as mortes que estava sendo investigado.', 668, '1757-01-15', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (352, 'Requerimento do procurador da Mitra do Bispado de Mariana, solicitando que se lhe passe, por certidão, dia, mes e ano em que foi feito o regimento por onde se regulam os juizes seculares da comarca de Vila Rica.', 330, '1755-04-12', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (586, 'Requerimento de Domingos de Paiva Arouca, capitão de ordenança do pé do Distrito do Mato, comarca de Vila Rica, solicitando ao rei a mercê de ser confirmado no exercício do referido cargo, que ficou vago devido ao falecimento do capitão Gregorio de Macedo.
INSERT INTO app_hist.provocacao VALUES (356, 'Requerimento de Domingos Pereira Seabra da Gama, pedindo para ser provido, por 20 anos de serviços que tem efetuado, no cargo de provedor da moeda na cidade da Bahia ou em alguma secretaria das Minas Gerais, Goias ou Rio de Janeiro. Eem anexo a certidão da nomeação para o cargo de tenente dos cavalos auxiliar de 1735 pelo governador Gomes Freire de Andrade.', 334, '1755-10-01', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (385, 'Requerimento de Angelo de Siqueira, missionário e protonotário apostólico, clérigo secular do hábito de São Pedro, pedindo a D. Jose I que as recolhidas de Santa Teresa possam professar. Diz que este recolhimento fora fundado pelo bispo do Rio de Janeiro, Miguel de Barros Alarcao, no tempo em que os paulistas quando iam descobrir minas gerais, deixavam lá as suas filhas recolhidas. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino o último dos quais indefere o pedido, de 09/07/1754.', 367, '1754-07-01', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (201, 'Requerimento de Diogo Pinto do Rego, mestre-de-campo e escrivão da ouvidoria e correição da cidade de São Paulo, pedindo a Jose Luis de Brito e Melo, ouvidor-geral e corregedor da cidade de São Paulo, que mande passar alvará ordenando aos escrivães do crime que informem se têm quaisquer culpas do requerente. Consta um despacho do ouvidor a deferir. Contém requerimentos do mesmo, assim como certidões, alvarás e avisos.', 183, '1750-03-10', 1750, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (369, 'Requerimento do ouvidor geral da comarca de São Paulo, João de Sousa Filgueiras, para o secretário de estado dos negócios do reino, Sebastião Jose de Carvalho e Melo, pedindo que o atenda na promoção da relação da Bahia ou que ao menos, reconduzido com beca neste lugar, fique nele fazendo o de desembargador da dita relação ou da do Rio de Janeiro.', 354, NULL, 1759, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (382, 'Requerimento de Monica de Camargo, Ana Maria da Cunha e sua irmã, Angela Maria da Cunha a D. Jose I pedindo que lhes seja mandada passar provisão para entrarem como religiosas no convento do mecambo da cidade de Lisboa ou em qualquer outro dessa cidade, pois têm grande vocação para a vida contemplativa como o mostra o atestado junto do bispo de São Paulo. Consta um despacho do conselho ultramarino, pedindo os pareceres do bispo e do governador e capitão-general da capitania de São Paulo, de 26/04/1754; Contém atestado do bispo dizendo que estas tinham vocação religiosa, de 02/10/1753.', 364, '1754-04-26', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (378, 'Requerimento de João Vieira de Andrade, juiz de fora, a D. Jose I, queixando-se do mau procedimento do ouvidor de Paranaguá, Antonio Pires da Silva Melo Porto Carreiro, e pedindo que se lhe mandasse tirar outra residência. Consta um despacho do conselho ultramarino mandando juntar a residência que tirou ao requerimento o ouvidor de Paranaguá, de 02/02/1754. Contém uma informação dizendo que a residência não foi para a secretaria e por isso não se junta, uma provisão de D. Jose I no qual pede que se suspensa o ouvidor caso comprovado os fatos alegados contra ele e uma carta do desembargador da relação do Rio de Janeiro. ', 361, '1754-02-18', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (348, 'Requerimento de Manuel Fernandes Serra, morador na Vila de São Jose, comarca do Rio das Mortes, solicitando a propriedade dos oficios de juiz dos orfãos ou de escrivão da referida vila, a exemplo do que se praticou com Antonio Pereira Machado. Tal como este, quer ser remunerado com a dita merce por ser um grande dono de lavras e ter sofrido prejuizo. Solicita-se o parecer do governador das Minas Gerais.', 325, '1755-02-25', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (351, 'Requerimento de Estevão Rodrigues Maia, morador da Freguesia de Sabará, Minas Gerais, para poder passar com sua mulher, Rosa Maria Ferreira, e família para Lisboa. Pede-se parecer do governador. Parecer do ouvidor de Sabará é remetido e é favorável a concessão da mercê.  O requerimento é deferido.', 329, '1755-03-13', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (599, 'Requerimento do mestre de campo Agostinho Dias dos Santos a D. Jose I, solicitando para ser declarado como legítimo possuidor de umas terras que tinha na freguesia do Sumidouro, no termo da cidade de Mariana. Consta instrumento de justificação e certidão do escrivão da intendência de Vila Rica, Silverio Anacleto Vilar e Sousa.', 628, '1757-03-11', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (353, 'Requerimento do arcebispo, dignidades e conegos da Se de Mariana, pedindo que lhes passem provisão para que a provedoria de Vila Rica lhes pague o acrescentamento que lhes foi concedido em consulta da Mesa do Desembargo do Paço de forma a receberem como as dignidades e conegos da Se do Rio de Janeiro. O requerimento foi deferido.', 339, '1755-04-22', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (616, 'ULTRAMAR Cópia da carta de Jose Ferreira da Veiga, arrematante dos contratos das entradas das Minas, dirigida a Gomes Freire de Andrade, governador de Minas Gerais, solicitando que sejam observadas as condições impostas no contrato estabelecido pelo arrematante. Para prover todos os registros por seus feitores, com comissão de cobrarem pelos direitos estabelecidos da criação dos contratos.', 645, '1751-10-27', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (598, 'Requerimento dos mineiros de Minas Gerais, solicitando ao rei a mercê de ordenar a reforma do regimento dos superintendentes, guardas-mores e seus escrivães, devido aos excessivos salários que lhes eram pagos. Os requerentes expõem que, em cujo exercicio se acham em grande opressão não tirar coisa alguma em muitos anos, com razão de ficarem frustradas as suas diligências, resultando em perdas financeiras significativas por conta dos elevados salários pagos.
INSERT INTO app_hist.provocacao VALUES (481, 'Nicolau da Silva Bragança, sargento-mor de Bombarral, pede ao rei a autorização para citar e demandar o preso Manuel do Rego Tinoco, que se encontra na prisão de Mariana.', 452, '1750-10-03', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (648, 'Requerimento do frei e padre Jose das Neves Ribeiro, morador da cidade do Rio de Janeiro, a D. Jose I, pedindo para residir na comarca e vila de Sabará para que pudesse cuidar dos negócios e da partilha de bens do seu falecido pai. Consta parecer do governador de Minas Gerais, Antonio Freire de Andrade, negando o pedido.', 696, '1757-06-25', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (673, 'Requerimento de Antonio de Abreu Guimarães a D. Jose I, solicitando confirmação no exercício do posto de capitão da ordenança da companhia do distrito do Morro do Chapéu. ', 729, '1757-09-15', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (689, 'Requerimento de Manuel Henriques Ribeiro, solicitando ao rei confirmação de meia légua de terra em quadra no sítio de Capoeira e Matos virgens, na freguesia de Guarapiranga, cidade de Mariana. Consta carta concedendo a sesmaria pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 746, '1758-03-13', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (362, 'Carta do ouvidor intendente da comarca de São Paulo, João de Sousa Filgueiras a D. Jose I desobrigando-se, pela sua parte, das contas dos quintos do ouro do ano antecedente, e informando que o provedor do Rio de Janeiro devia escrever a relação da fundição do ouro da capitania, para que lhe servisse de guia. Consta termo de conferência, certificando que o ouro foi pesado e guardado nos cofres.', 346, '1757-06-28', 1757, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (574, 'Requerimento do bacharel Casimiro Teixeira Machado, intendente do ouro e casa de fundição das Minas, solicitando mercê do pagamento de 300 mil réis de ajuda de custo pela viagem que fez pelas Minas Gerais. Consta em anexo instrumento de justificação e certidão expresso por duas vias.', 585, '1757-02-07', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (794, 'maisa Requerimento de Francisco Pereira solicitando a confirmação da carta de sesmaria de meia légua de terra, na paragem do Rio das Pedras, termo da vila de São José, comarca do Rio das Mortes. Consta a referida carta de sesmaria em anexo, concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade, datada de 17 de agosto de 1758.', 880, '1760-02-12', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (855, 'Requerimento de Manuel Francisco da Costa Barros, morador de Vila Rica e casado com D. Iria Eufrasia da Silva e Avila, solicitando licença para passar ao Reino com sua família. O pedido foi deferido pelo conselho ultramarino.', 946, '1760-12-11', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (363, 'Carta do ouvidor geral da comarca de Paranaguá, Jeronimo Ribeiro de Magalhães, para o secretário de estado dos negócios do reino, Sebastião Jose de Carvalho e Melo, comunicando a descoberta dos transgressores na extração de diamantes no sertão de Tibagi. Conta que o ouvidor da comarca na época, o bacharel Antonio Pires Porto Carneiro não evitou a extração das pedras, facilitando a Angelo Pedroso Leme. Menciona ainda que o pai do referido Angelo, o frei Bento Rodrigues de São Angelo, religioso carmelita, enviou um embrulho de pedras ao ouvidor, e que pai e filho se refugiaram no mato ao saberem da devassa. O frei Fernando de Monte Carmela e Silva afirmou em carta que o referido frei Bento lhe pediu ao menos uma pedra para oferecer ao seu prelado. Consta devassa do caso.', 348, '1759-02-21', 1759, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (370, 'Requerimento do padre Faustino Xavier do Prado a D. Jose I, pedindo que lhe mande passar alvará de mantimento para poder cobrar a côngrua da sua conesia na Sé do bispado de São Paulo. Consta parecer do conselho ultramarino deferindo a solicitação.', 355, NULL, 1759, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (361, 'Queixa do provedor da fazenda real da capitania de São Paulo, Jose de Godoi Moreira, a D. Jose I expondo que o subsídio ou estanco dos molhados de Santos rendeu nos anos passados 531.360 réis, e os ouvidores roubaram nas correições e cartas de usanças 354.853 réis, como mostra na certidão junta. A câmara só pode pois dispender do dinheiro que sobrou e por isso faltou 6 mil réis para pagar a contribuição anual ao rei. O provedor afirma que esses ouvidores confiam na impunidade animados pelo sucesso de seus antecessores, e perseguem o suplicante com exigências de dinheiro, como demonstra os documentos em anexo. Consta provisão de D. Jose I ao governador da capitania do Rio de Janeiro, ordenando que dê seu parecer e cartas do ouvidor da comarca de São Paulo, João de Sousa Filgueiras, ao referido provedor acerca do pagamento dos direitos dos ofícios.', 345, '1759-04-26', 1759, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (373, 'Requerimento de João de Sousa Filgueiras a D. Jose I pedindo que lhe conceda determinada ajuda de custo, assim como idêntica quantia por conta dos seis meses de ordenado, a fim de nomear como fiador Manuel Peixoto da Silva. Consta três despachos do conselho ultramarino, o último dos quais determina que passem provisão para que o requerente receba ajuda de custo, na provedoria do Rio de Janeiro, indeferindo o adiantamento, de 23/10/1753; e um parecer do procurador da fazenda, concordando. Contém outros dois requerimentos do mesmo requerente.', 357, '1753-10-12', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (585, 'Requerimento de Antonio Lourenço de Araujo, capitão de ordenança do pé do Morro de Nossa Senhora do Pilar e Santo Antonio, solicitando ao rei a mercê de ser confirmado no referido cargo, ao qual foi nomeado por Gomes Freire de Andrade, governador da capitania. Em anexo, encontra-se uma carta patente.', 596, '1751-09-09', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (307, 'Jose de Almeida Machado, tesoureiro da fazenda real das Minas Gerais, solicita ao rei a mercê de passar ordens com salva e por mais de duas vias, para que pudessem ser remetidas as suas contas para o reino.', 300, '1750-08-26', 1750, 5, 'R', '', '2-3');
INSERT INTO app_hist.provocacao VALUES (344, 'Requerimento de João Miranda Barbosa, sargento-mor do arraial de Itacambira, do distrito de Vila de Nossa Senhora do Bom Sucesso das Minas Novas do Araçui (Minas Gerais), pedindo sua confirmação no referido posto. O pedido foi escusado por não estar em conformidade.', 318, '1755-01-13', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (396, 'Requerimento de Manuel Nunes da Silva Tojal e Jose Ferreira da Veiga, caixas na corte do 3° contrato dos diamantes do Serro do Frio, solicitando ao rei, D. José I, a mercê de mandar entregar a primeira parte dos diamantes, do contrato que remeteu Felisberto Caldeira Brant, para tratarem de sua venda. Em anexo, um parecer do conselho ultramarino, informando que se deve praticar no contrato o mesmo que se pratica nos antecedentes, não alterando em coisa alguma a entrega.', 376, '1751-01-05', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (357, 'Solicitação de providências pelos oficiais da câmara de Mariana devido à publicação de um bando sobre o ouro falso que da entrada na casa de fundição, o que prejudica os povos. ', 342, '1755-05-05', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (358, 'Representação dos oficiais da camara de Mariana solicitando a concessão de privilegios identicos aos da camara do Porto. Sendo esta uma cidade, os oficiais da camara argumentam que devem ter prioridade aos oficiais da camara de Vila Rica que solicitaram os referidos privilegios.', 343, '1755-05-06', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (609, 'Requerimento de Violente de Sousa, viúva, e seus filhos do falecido guarda-mor Francisco Machado da Silva, ao rei, solicitando autorização para, com os seus escravos, extraírem ouro nas suas lavras, devido à miséria a que se encontram reduzidos, visando ao seu sustento. Em anexo oito certidões', 638, '1751-10-02', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (360, 'Carta do provedor da fazenda real da capitania de São Paulo, Jose de Godoi Moreira, a D. Jose I informando que Torcato Teixeira de Carvalho tinha mandado construir uma fortaleza em Itapema, com a condição do rei dar o governo dela a seu filho, João Teixeira de Carvalho, com o soldo de capitão de infantaria. A obra foi feita mas necessitava de outras reformas, apesar dessa ordem dada pelo provedor ele não obteve resposta, por isso solicita ao rei que proceda como achar conveniente. Consta carta patente passada por D. Jose I a João Teixeira de Carvalho nomeando-o ao referido posto com o soldo de capitão de infantaria.', 344, '1759-04-13', 1759, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (483, 'João de Sousa Costa, tesoureiro dos defuntos e ausentes de Vila Rica, solicita ao rei que lhe devolva os pagamentos referentes aos novos direitos. ', 454, '1750-10-12', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (601, 'ULTRAMAR  Cópia do regulamento imposto por Gomes Freire de Andrade, governador de Minas Gerais, sobre as normas que deviam ser observadas pelos dois oficiais, capitão Simão da Cunha Pereira e tenente Manuel Saraiva, encarregados das patrulhas do ouro no Serro do Frio.', 630, '1751-09-18', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (419, 'Representação dos oficiais da Câmara de Mariana representam os excessos dos contratadores na cobrança dos dízimos.', 682, '1755-05-07', 1755, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (690, 'Requerimento de Antonio de Araujo Pereira solicitando ao rei confirmação de meia légua de terra na passagem do rio do Peixe, cidade de Mariana. Consta carta do governador de Minas Gerais, Jose Antonio Freire de Andrade, concedendo a dita sesmaria.', 747, '1758-03-13', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (383, 'Requerimento de Manuel Rodrigues de Carvalho a D. Jose I dizendo que fora mandado para a guerra que se fez aos gentios, na qual fez gastos tão consideráveis à custa da sua fazenda. Por isso, D. João V lhe mandara dar quatro mil cruzados pela provedoria de Santos. Mas como não recebeu, pedia que a dita quantia lhe fosse paga. Consta um despacho do conselho ultramarino, um parecer do procurador da fazenda e um parecer do conselho ultramarino favorável às pretensões do requerente, de 17/06/1754.', 365, '1754-05-27', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (854, '**Requerimento de Antonio de Sousa Viegas, solicitando a restituição dos direitos que foi obrigado a pagar nas Minas do ofício de escrivão das execuções de Vila Rica, por já tê-los pago na corte. Pede que se enviei a consulta que foi feita para caso precedente.', 945, '1760-12-09', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (379, 'Requerimento de Maria Paes de Jesus a D. Jose I expondo que seu falecido marido, João Antunes Maciel, comparara uma légua de terras, do qual sempre pagara os respectivos dízimos. Pedia para que confirmassem a compra, com a cláusula de seus herdeiros não poderem dá-la ou vendê-la. Requeria ainda que a carta fosse passada em nome de seu filho Domingos Antunes e que nela fosse lançada uma sesmaria já confirmada a favor do seu neto. Consta um despacho do conselho ultramarino, pedindo a informação do procurador da fazenda, de 29/02/1754; um parecer do procurador achando que não se devia passar a confirmação; despacho do conselho ultramarino indeferindo o requerimento, de 10/05/1755. Contém um instrumento público da escritura de venda e outro requerimento de João Machado Castanho. ', 362, '1754-03-01', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (386, 'Requerimento do bacharel Francisco Caetano de Almeida Lobo, a D. Jose I, juiz de fora da vila de Santos, pedindo que lhe seja concedida a graça do aumento das assinaturas e da terça parte do seu ordenado, como tem acontecido com alguns funcionários do ultramar e ao ouvidor de São Paulo. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino. Contém um outro requerimento anterior do mesmo requerente anexo e um ofício. ', 368, '1754-07-12', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (674, 'Requerimento de Francisco da Silva Sena a D. Jose I, solicitando a confirmação da carta de sesmaria de meia légua de terra com capoeiras e matos virgens na freguesia de São Jose da Barra Longa. Consta bilhete, carta de sesmaria do governador de Minas Gerais, Jose Antonio Freire de Andrade e mapa de contas.', 730, '1757-10-23', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (392, 'Requerimento do ouvidor da comarca de São Paulo, João de Sousa Filgueiras ao secretario de estado dos negócios da marinha e domínios ultramarinos, Tome Joaquim da Costa Corte Real, pedindo-lhe o favor da sua proteção para ser promovido na ocasião própria, num lugar da relação da Bahia ou, que ao menos, reconduzido com beca naquele lugar, fique nele fazendo o de desembargador da dita relação ou da do Rio de Janeiro, visto ter sempre merecido.', 372, '1759-05-04', 1759, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (403, 'Requerimento de Francisco Xavier de Sousa, advogado da casa da suplicação, fiador do bacharel João de Sousa Filgueiras, nomeado ouvidor de São Paulo, no qual pede a D. Jose I que lhe mande passar por certidão uma sentença cível que fez contra Bernardo de Freitas e Sampaio. Consta um despacho do conselho ultramarino, deferindo-o, de 21/10/1754.', 384, '1754-10-21', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (295, 'Requerimento de Jose Dias de Jesus, soldado da praça de Santos, pedindo a D. Jose I que lhe mande dar baixa por ter assentado praça na ilha da madeira. Atualmente, devido à idade e à doença, encontra-se impossibilitado. Consta um despacho favorável do conselho ultramarino, de 08/05/1753, com uma declaração no verso do procurador do requerente em como recebeu os documentos juntos a este requerimento., de 26/05/1753.', 288, '1753-05-08', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (289, 'Requerimento de Matias do Couto Reis a D. Jose I pedindo que, como não vencesse mais soldo que uma praça de soldado, pedia que em recompensa de seus serviços lhe fosse concedida a patente de sargento-mor da fortaleza de São João da barra de Bertioga com o soldo de sargento-mor de infantaria. Pedia também que nesse posto lhe sucedesse ou um filho ou um genro. Consta despacho do conselho ultramarino mandando informar com o parecer de D. Luis Mascarenhas, de 06/04/1753, e um despacho do conselho ultramarino, indeferindo a petição do requerente, de 23/07/1756. Consta provisões de D. Jose I e cartas de Gomes Freire de Andrade e Inacio Eloi de Madureira.', 284, '1753-04-06', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (204, 'Representação dos vereadores da câmara da vila de Santos a D. Jose I na qual pediam, como seriam citados logo que acabasse o mandado do juiz, pediam que mandasse juntar essa representação à residência dele para o obrigar a dar fiança sobre os prejuízos, de 13/03/1750. Consta um despacho do conselho ultramarino. ', 197, '1750-03-13', 1750, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (371, 'Requerimento de Maria Dias Ferreira a D. Jose I expondo que, como viúva de Antonio Francisco Lima, necessitava para o bem de sua casa e de seus filhos, de poder administrar os seus haveres, solicitava que lhe fosse passada provisão de tutela. Consta parecer do conselho ultramarino deferindo o pedido.', 356, NULL, 1759, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (374, 'Requerimento de João de Sousa Filgueiras a D. Jose I pedindo que lhe conceda ajuda de custo, conforme fora concedida aos seus antecessores. Consta um despacho do conselho ultramarino. Contém dois requerimentos anexos.', 357, '1753-10-20', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (587, 'Requerimento do padre Miguel de Castilho e Gois, como testamenteiro do vigário João da Fe de São Jeronimo Guriel de Amaral, solicitando ao rei a mercê de confirmar ao seu representado a doação em sesmaria de meia légua de terra, em quadra, na paragem do Elvas, termo da vila de São João del Rei. Em anexo um bilhete de ordem de provisão, três requerimentos e uma carta de doação.', 598, '1751-09-10', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (406, 'Requerimento de Luis Antonio de Sa Queiroga, mestre de campo e governador da praça de Santos, que serviu cinquenta anos efetivo, pedindo a D. Jose I a mercê de se reformar a sua consulta, visto que passou de um ano sem lhe ter dado resolução. Consta um despacho do conselho ultramarino, dizendo que requeresse pela secretaria das mercês, de 23/11/1754.', 386, '1754-11-23', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (398, 'isa PASSAR PARA RESPOSTA Provisão de D. Jose, para o governador de Minas Gerais, Gomes Freire de Andrade, remetendo em anexo cópia do parecer do vedor geral da corte a respeito das normas que se deveria observar. Que por falecimento de algum oficial ou soldado, se pretendia tirar fé do tempo de serviço dos oficiais, desde o inicio de suas funções até o dia de sua morte, para o procedimento na vedoria de Minas Gerais. ', 379, '1751-01-07', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (485, 'Caetano Jose de Almeida, escrivão das execuções da cidade de Mariana, pede ao rei a nomeação de Tomas Soares de Brito como seu oficial escrevente. (Contem algumas certidões)', 456, '1750-10-13', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (410, 'Requerimento de Jeronimo Ribeiro de Magalhães pedindo certidão de traslado das ordens expedidas em benefício do seu antecessor, Antonio Pires da Silva e Melo. Segue-se cópia de provisão de D. João V nomeando este como ouvidor de Paranaguá, de 04/08/1748.', 389, '1754-12-27', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (619, 'Requerimento de Manuel Jose de Brito, alferes de dragões da companhia do capitão Simão da Cunha Pereira, da guarnição de Minas, solicitando ao rei a permissão para se reformar, mantendo o soldo que recebe no exercício do referido posto. Em anexo uma carta e uma provisão', 648, '1751-11-05', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (173, 'Requerimento de Francisco Pinto do Rego a D. Jose I pedindo providências sobre o procedimento do governador da ilha de Santa Catarina que pretende despojá-lo das terras que possui. Queixa-se também do comandante da vila da Laguna, caçando na sua propriedade e danificando-a sem menor remuneração pelos animais que abate. Constam dois despachos do conselho ultramarino e um parecer do procurador da fazenda, mandando que o governador do Rio de Janeiro informe, ouvindo o da ilha de Santa Catarina e ordenando que as terras continuem na posse do proprietário até que se resolva sobre.', 178, '1751-09-17', 1751, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (416, 'Requerimento de Francisco Xavier de Sousa, advogado da casa da suplicação, a D. Jose I pedindo que lhe seja levantado o termo da fiança em que ficava responsável pelo embarque de João de Sousa Filgueiras, nomeado ouvidor de São Paulo. Consta um despacho do conselho ultramarino dizendo para o tesoureiro responder, de 17/01/1755. Consta uma certidão na qual se diz ter embarcado o requerente no navio, de 02/01/1755; e uma carta do tesoureiro no qual diz que se deve juntar a certidão do termo de fiança para que lhe seja deferido o pedido, de 10/02/1755.', 393, '1755-01-17', 1755, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (208, 'Requerimento de Luis Ramos Barbas solicitando a D. Jose I a carta de confirmação de sesmaria, dado por D. Luis Mascarenhas. Consta três despachos do conselho ultramarino e um parecer do procurador da coroa e um do provedor da fazenda, dizendo o último despacho do conselho ultramarino que se passe sesmaria na forma do estilo, de 21/06/1752. Contém uma carta de sesmaria de 21/08/1748 por D. Luis Mascarenhas; além de um outro requerimento, uma certidão e um bilhete.', 200, NULL, 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (420, 'Requerimentos dos povos de Minas Gerais expondo os excessos, sobre as vexações sofridas devido à abusiva extensão das condições dos contratos reais, que os provedores da fazenda conhecem todas as suas causas, e a levam fora dos termos da lei. Na primeira folha, um parecer de Joaquim Miguel Lopes de Lavre, secretário do conselho ultramarino, a respeito das vexações, onde informa ser necessário mandar emendar as condições e passar uma ordem geral para que todos entendam que não se concederá mais jurisdição além do que a lei permite. ', 381, '1751-01-21', 1751, 5, '0', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (421, 'Requerimento do bacharel Manuel Caetano Monteiro, intendente do ouro da comarca de Rio das Mortes, que no dito cargo solicita a graça de conceder que vençam os seus ordenados desde o dia de seu embarque.', 399, '1751-01-26', 1751, 5, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (424, 'Cópia da certidão passada por João da Costa Azevedo, escrivão da câmara de Mariana, a respeito de um acordão da referida câmara, sobre o provimento do cargo de tesoureiro da fazenda real, que foi passado por provisão do governador Gomes Freire de Andrade.', 402, '1751-03-20', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (423, 'Petição de Antonio Jose de Melo, morador de São João Del-Rei, que desejava licença para erigir um engenho de açúcar, melado e aguardente. Há informação do governador Gomes Freire de Andrade, governador de Minas Gerais, para o rei, D. Jose I.', 401, '1751-03-17', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (593, 'Requerimento do padre Henrique Pereira, vigário da Igreja de Nossa Senhora do Bom sucesso da Vila de Caeté, a D. Jose I, solicitando que o ouvidor de Vila Rica obrigue as pessoas a comparticipar com as quantias a que se tinham se obrigado a dar para a retificação da igreja.', 612, '1757-03-02', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (628, 'Requerimento de Roque de Sousa Graces, capitão de cavalaria da cidade de Mariana, solicitando ao rei a mercê de ser confirmado no referido cargo, devido à ausência de Francisco Pereira Lobo. Em anexo, uma carta patente.', 658, '1751-11-19', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (462, 'Requerimento de Angelo Xavier do Prado, escrivão da fazenda real da provedoria da vila de Santos, a D. Jose I expondo que, achando-se servindo vários ofícios, vencia unicamente o ordenado estabelecido pela provisão de 19 de julho de 1696 para o serventuário que naquele tempo servia. Mas como presentemente o dito ordenado era insuficiente, devido à carestia da vida, pedia que o mesmo lhe fosse aumentado, em paga dos seus serviços. Consta um parecer do procurador, dizendo que como a provedoria de Santos não tinha fundos para satisfazer as suas despesas ordinárias, o requerimento não podia ser deferido, além de dois despachos do conselho ultramarino, um deles indeferindo o requerimento, de 28/02/1755.', 432, '1755-10-20', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (393, 'Requerimento do bacharel Francisco Caetano de Almeida Lobo, a D. Jose I, pedindo que lhe seja passada certidão, na qual conste os aumentos de ordenado concedidos aos ouvidores de Angola e de São Tomé. Consta um despacho do conselho ultramarino, deferindo-o, de 13/08/1751. Contém provisões, certidões, ofícios e outros dois requerimentos.', 368, '1754-07-12', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (675, 'Defesa dos direitos naturais dos párocos do Brasil, contra as intenções do bispo de Mariana e do procurador do cabildo Mariano em limitar os seus proventos em favor de outrem, baseando-se na teologia, com citações em latim do evangelho. Documento incompleto e anônimo.', 731, NULL, 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (484, 'Manuel Pinto de Queiros, capitão de Vila Rica, pede ao rei para que lhe dê uma propina anual de 198.150 reis.', 455, '1750-10-12', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (602, 'Requerimento dos mineiros de Minas Gerais, solicitando a mercê de que se ordene que os mesmos não sejam executados por seus credores, exceto na terceira parte do ouro que extraírem. Os mineiros justificam que, para extrair o ouro, gastam meses e anos, empregando grande número de escravos e feitores, o que gera altos custos e pesados fardos financeiros. Esses investimentos são feitos na esperança de que os serviços de mineração produzam algum retorno suficiente para sustentar as suas famílias e pagar suas dívidas. Mas as despesas excessivas, resultam em não poderem pagar os seus credores com a prontidão necessária, por isso tal solicitação.', 631, '1751-09-20', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (627, 'Requerimento de Pedro Teixeira de Carvalho, capitão e morador nas proximidades da Vila de São Jose, solicitando ao rei a mercê de lhe conceder licença para se deslocar para o reino com a sua família.', 657, '1751-11-12', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (404, 'Requerimento do provedor e irmãos da Santa Casa de Misericórdia da cidade de São Paulo a D. Jose I supondo que a dita casa desde a sua instituição até então sempre cumpria as obrigações, motivo por que se tornara merecedora de lhe serem concedidos privilégios idênticos ao que gozava a Santa Casa de Misericórdia de Lisboa. Consta um despacho do conselho ultramarino, mandando informar o procurador da fazenda, de 18/11/1754; um parecer do procurador da fazenda dizendo que a graça era semelhante à que D. Jose I costumava conceder a todas as misericórdias; um despacho do conselho ultramarino mandando informar o procurador da coroa, de 27/12/1754; parecer do procurador da coroa, dizendo que o requerimento podia ser atendido; e um despacho do conselho ultramarino mandando o ouvidor da comarca de São Paulo informar com o seu parecer, de 02/05/1755. Contém um outro requerimento, assim como uma certidão. ', 385, '1754-11-18', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (388, 'Requerimento da duquesa camareira-mor D. Ana de Lorena Sa Almeida e Meneses e de sua filha a duquesa de Abrantes, mulher do príncipe D. João, D. Madalena de Lencastre e de seu sobrinho, o conde de vila Nova, a D. Jose I, informando que tendo sido concedida ao marquês de Abrantes, Arthur de Sa e Meneses, provisão para avocar para o juízo da ouvidoria da relação do Rio de Janeiro as causas que trazia na cidade de São Paulo com Jose de Gois, o referido marquês faleceu antes de dar execução à dita provisão, deixando por universal herdeira a duquesa de Abrantes. Solicitam que o rei lhes fizesse provisão para declarar que as outras suplicantes do caso também se tornem herdeiras e que as causas sejam remetidas ao juízo da ouvidoria do cível da relação do Rio de Janeiro. Consta parecer do procurador da Coroa e do conselho ultramarino concordando passar a referida provisão aos requerentes. ', 369, '1759-12-20', 1759, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (852, 'Representação dos oficiais da câmara de Pitangui, queixando-se sobre as violências praticadas pelo vigário Caetano Mendes de Proença e pelo capitão-mor da vila, Antonio Teixeira das Neves, solicitando que seja feita justiça.', 943, '1760-12-04', 1760, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (160, 'Requerimento de Francisca Maria Caminha e Inácia de Jesus Caminha a D. Jose I dizendo que após a morte de seu pai, sua mãe casara em segunda núpcias com o coronel Sebastião Fernandes do Rego, o qual ficou administrando as suas legítimas, com o consentimento do seu tutor e avô. Em 1745, o ouvidor da comarca ordena para embargar os bens de Sebastião, mesmo que falecido. Pedem que lhes passasse provisão no sentido de os seus bens ficarem livres de sequestro, podendo assim tomar posse das suas legítimas. Consta um despacho do conselho ultramarino, mandando o procurador da fazenda informar, de 19/11/1750; um parecer do dito procurador dizendo que os requerentes deviam usar dos meios ordinários para obterem o que pretendiam; despacho do conselho ultramarino concorde com o parecer anterior, de 23/12/1750.', 166, '1750-11-19', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (620, 'Requerimento de João Pessoa de Lemos, que exercerá o cargo de administrador dos reais contratos dos dízimos de Minas Gerais, solicitando ao rei a mercê de prorrogar por três anos o exercício do referido cargo, com o objetivo de poder cobrar todos os seus devedores.', 649, '1751-11-05', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (413, 'Requerimento de João de Sousa Filgueiras, bacharel, a D. Jose I pedindo que lhe mande passar as provisões necessárias para que pudesse exercer o cargo de ouvidor da comarca de São Paulo. Há uma réplica do mesmo pedido, solicitando as ordens que se passaram ao ouvidor seu antecessor para poder exercer o lugar de ouvidor da comarca de São Paulo e vencer o ordenado que lhe compete. Consta um despacho do conselho ultramarino, deferindo o pedido, de 30/12/1754. Contém um outro requerimento do mesmo, assim como uma certidão e uma provisão de D. João V. O requerimento aparece em duas vias.', 391, '1754-12-30', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (290, 'Requerimento de Silverio Antonio de Matos, ajudante de ensaiador da casa de fundição da cidade de São Paulo, a D. Jose I pedindo que mande que o governador-geral das Minas ordene a sua mudança para outra casa de fundição, onde ele possa servir com mais frequência. ', 285, '1753-04-21', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (405, 'Requerimento do provedor e irmãos da Santa Casa de Misericórdia da cidade de São Paulo para o juiz ordinário Francisco Bueno Garcia pedindo para que quaisquer tabeliães lhe passassem por certidão extraída dos livros da mesma Santa Casa em como desde a sua instituição até então se tinham cumprido todas as obras de misericórdia. Consta um despacho do mesmo juiz, deferindo-o. Segue-se no mesmo documento uma certidão, de 02/10/1753. ', 385, '1753-10-02', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (411, 'Requerimento em duas vias de João Franco Pedroso a D. Jose I pelo qual solicita que nomeie o ouvidor da comarca de Goiás para juiz de uma sua causa . Consta um despacho do conselho ultramarino, deferindo-o, de 19/02/1755. Contém um outro requerimento do mesmo e um bilhete passado pelo secretário do conselho ultramarino, despacho do conselho ultramarino, dando ordem ao ouvidor da comarca de Goiás para dar o seu parecer de 10/12/1754 e um bilhete do secretário do conselho ultramarino. ', 390, '1754-12-30', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (630, 'Requerimento do sargento-mor Gabriel Fernandes Aleixo, morador em Vila Rica, a D. Jose I, solicitando a mercê de expedir provisão de mantimento para receber renda da terceira parte do seu ofício que se acha depositado no cofre.', 661, '1757-05-20', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (415, 'Requerimento de João Rodrigues Pais, padre, pedindo alvará de mantimentos que lhe é necessário para ocupar a conesia vaga na sé do bispado de São Paulo. Consta um despacho do conselho ultramarino, deferindo-o.', 392, NULL, 1754, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (417, 'Representação dos oficiais da Câmara de Mariana solicitando providências contra os excessos do provedor da Fazenda na cobrança dos dízimos na freguesia de Catas Altas. Os moradores desta apresentaram queixa à câmara da Cidade de Mariana.', 395, '1755-05-07', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (706, 'maisa Requerimento do ouvidor Antonio Manuel das Povoas, nomeado para a comarca de Sabará, solicitando ao rei que passe alvará de mantimento e que lhe conceda uma ajuda de custo desde o dia em que embarcou na corte.', 767, '1758-10-16', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (422, 'Requerimento de Afonso Ginabel, contratador das entradas de Minas Gerais, pedindo que a provedoria de Santos satisfaça a parte que não alcançar o rendimento do contrato. Pretende entregar na referida provedoria o que falta para complementar, sendo que na divisão da arrematação se aplica ao Cuiabá, onde está se cobra, pois para entregar no Cuiabá ao que não chegar ao rendimento. Em anexo encontra-se um requerimento que detalha as condições do contrato, e que faça mercê de passar ordens para que os provedores da fazenda de Cuiabá e da praça de Santos pratiquem os procedimentos necessários, garantindo a segurança necessária.', 400, '1751-02-17', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (436, 'Representação dos oficiais da câmara da Vila do Príncipe solicitando ao rei providências para alterar o método de cobrança do real quinto por capitação nas caixas da intendência, dada a quantidade que os povos eram obrigados a satisfazer.', 415, '1751-05-03', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (397, 'Requerimento de João Fernandes de Oliveira, possuidor de muitas fazendas de criação de gado, que ocupam grande número de escravos, solicitando ao rei D. Jose I a mercê de poder continuar utilizando suas terras, comprometendo-se a continuar produzindo bom gado. Dado que seu antecessor, o capitão-mor Luis de Siqueira Brandão, mandou dar saída aos gados.
INSERT INTO app_hist.provocacao VALUES (211, 'Requerimento de Inacio Eloi de Madureira pedindo a D. Jose I pagamento de soldo desde o dia do embarque até chegar à praça de Santos para onde foi nomeado governador.  Consta despacho favorável do conselho ultramarino, de 05/02/1752.', 202, '1752-02-05', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (631, 'Requerimento do vigário de Vila Boa, padre Felipe da Silveira e Sousa, ao rei, D. José, solicitando esmola para reparar a igreja Senhora Santa Ana de Vila Boa.', 662, '1755-01-13', 1755, 28, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (456, 'Requerimento de Jose Rodrigues da Costa, morador no termo da vila de São Sebastião, ao juiz ordinário tenente Diogo Castanho Torres, dizendo que lhe era necessário fazer uma justificação em como sua mulher, Maria do Espirito Santo, era capaz de administrar os seus bens e por seu falecimento ser tutora e curadora dos mesmos bens pelo que pedia lhe fosse concedida a dita justificação. Consta em anexo um termo de assentada, termo de conclusão, termo de publicação, e outros dois requerimentos da referida esposa.', 429, '1755-10-08', 1755, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (457, 'Requerimento de Alexandre Simões Vieira a D. Jose I pedindo a confirmação da carta de sesmaria pela qual o governador e capitão-general da capitania de São Paulo, conde de Sarzedas, lhe concedera uma légua de terra em quadra, em Capivari, distrito de Jundiaí. Consta dois pareceres dos procuradores da fazenda e da coroa, e três despachos do conselho ultramarino, mandando informar sobre o assunto do governador das capitanias do Rio de Janeiro e anexas, de 17/11/1755. Contém uma carta de sesmaria.', 430, '1755-10-12', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (446, 'Requerimento de Antonio Jose de Azevedo, alferes, ao vigário da vara, Antonio Xavier de Matos, pedindo que se lhe passe uma certidão de como é casado na freguesia de Mogi Guaçu com Maria de Lima do Rosario, viúva de Domingos Rodrigues Chaves. Consta um despacho do mencionado vigário da vara, deferindo-o, de 27/08/1753 e a certidão pedida. Contém documentos anexos.', 404, '1753-08-27', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (453, 'Requerimento de João Teixeira de Carvalho, capitão de infantaria da fortaleza de Santa Cruz de Itapema, na barra da vila de Santos, a D. Jose I, pedindo-lhe que dê ordem ao provedor da fazenda e provedor geral da referida vila para lhe pagar os soldos referentes ao seu posto de capitão e os emolumentos do seu cargo. Consta um despacho do conselho ultramarino pedindo o parecer do governador e capitão-general da capitania do Rio de Janeiro depois de ouvir o provedor da fazenda, de 22/09/1755. Contém outro requerimento, assim como uma certidão. ', 428, '1755-09-22', 1755, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (471, 'Requerimento do vigário e fregueses da freguesia de nossa senhora do Pilar da vila do Pitangui das Minas, bispado de Mariana, a D. Jose I, solicitando alvará para cobrança de mantimentos.', 442, '1757-01-07', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (292, 'Requerimento de Jose Luis de Brito e Melo, ouvidor da comarca de São Paulo, a D. Jose I pedindo que lhe seja passada provisão em que se declare poder o requerente receber o ordenado como intendente da casa de fundição. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, um dos quais determinando que o governador e capitão-general da capitania de São Paulo dê o seu parecer. Consta uma certidão passada pelo escrivão da receita e despesa da real casa da fundição da comarca de São Paulo, João de Oliveira Cardoso, de 30/071752 e uma outra certidão com teor idêntico pelo escrivão da intendência da comarca de São Paulo, de 03/08/1752', 286, '1753-04-29', 1753, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (487, 'Francisco Jose Aguilar, solicita ao rei a concessão de uma licença para se deslocar ao reino.', 458, '1750-10-19', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (447, 'Requerimento de Antonio Jose de Azevedo, alferes, pedindo que lhe seja passada uma certidão em que conste quanto herdou de seu pai o enteado do requerente e de quanto já aumentou essa fortuna para poder apresentar o seu requerimento no Desembargo do Paço e na da Relação da Bahia. Consta um despacho, deferindo-o, de 01/04/1753. Contém documentos anexos. ', 404, '1753-04-01', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (450, 'Requerimento de Manuel Rodrigues, Manuel da Silva e João Migueis, a D. Jose I pedindo que lhes sejam pagos os salários referentes ao transporte de várias caixas de armas que os requerentes em suas fragatas. Consta um despacho do conselho ultramarino determinando que respondesse Antonio de Barros, de 08/08/1755.', 426, '1755-08-08', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (444, 'Requerimento de Antonio Jose de Azevedo, alferes, a D. Jose I pedindo que lhe seja mandada provisão para que possa obter tutela do enteado. Consta um despacho do conselho ultramarino pedindo parecer do provedor da comarca, de 21/07/1754. Contém diferentes documentos anexos.', 404, '1754-07-21', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (644, 'ULTRAMAR Carta de Diogo de Gouveia Osorio e Castro, morador das minas do Tocantins, ao rei D. Jose sobre os procedimentos corruptos e abusos de autoridade do ouvidor-geral de Goiás, Sebastiao Jose da Cunha Soares e Vasconcelos, deixando de cumprir as ordens reais, agindo despoticamente nas matérias criminais, absolvendo réus culpados, culpando os inocentes, tudo sob o aconselhamento do padre Felipe da Silveira e Sousa. ', 691, '1755-04-25', 1755, 28, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (486, 'Sancho de Andrade Castro e Lanções, intendente dos diamantes do Serro do Frio, pede ao rei a permissão para que o vencimento dos ordenados das tropas ocorra desde o dia do embarque. ', 457, '1750-08-14', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (604, 'Requerimento de Manuel de Oliveira, capitão de ordenanças do pé do distrito de Pompeu, solicitando ao rei a mercê de ser confirmado no exercício do referido cargo, que se encontra vago por ausência de Francisco Dias Leal. Em anexo uma carta patente.', 633, '1751-09-22', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (638, 'ULTRAMAR Certidão passada por Domingos Pinheiro, intendente da fazenda real de Mariana, atestando que Manuel Cardoso de Almeida exercia com zelo a função de ajudante da intendência.', 671, '1751-06-30', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (691, 'Requerimento do padre Domingos Jose Coelho e Sampaio, presbitério da igreja de Santo Antonio da vila de São Jose, solicitando ao rei a mercê do pagamento de congrua. Consta bilhete do conselho ultramarino ordenando que deve-se informar ao bispo de Mariana.', 748, '1758-03-16', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (676, 'Requerimento do capitão João Batista da Silveira a D. Jose I, solicitando o provimento de seus filhos legítimos, Joaquim Jose da Silveira e Francisco de Paulo da Silveira, no serviço militar da capitania de Minas Gerais, por serem bisnetos de militares. Consta bilhete do conselho ultramarino informando que o suplicante deve requerer ao governador da capitania.', 732, NULL, 1757, 5, 'R', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (707, 'maisa Requerimento de Domingos Jose de Campos Magro, solicitando ao rei a mercê de lhe conceder o contrato dos direitos das passagens das Minas Gerais. O pedido foi escusado.', 772, '1758-10-18', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (430, 'Carta de Manuel da Costa Coelho, procurador da câmara de Vila Rica, para o rei, dando conta dos efeitos negativos de duas ordens régias. Uma sobre os ouvidores não cobrarem para rever as licenças dos oficiais mecânicos, e outra para que dos bens do concelho não houvessem propinas, como levavam iguais dos oficiais da câmara.', 409, '1751-04-18', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (433, 'Carta de Manuel da Costa Coelho, procurador da câmara de Vila Rica, informando o rei que, durante a celebração anual da procissão de Corpus Christi na vila, vários cavaleiros das ordens militares não participam da dita procissão como são obrigados. Solicita providências para que sejam aplicadas penas que obriguem os referidos cavaleiros a assistirem a essa função.', 412, '1751-04-18', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (501, 'Sebastião Marques de Carvalho requer ao rei autorização para demandar ou ser demandado por instituições judiciais do Sabará, Vila Rica e Mariana. ', 475, '1750-10-27', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (488, 'Jose da Costa Azevedo pede ao rei  para que herde a função de seu falecido sogro, Pedro Duarte Pereira, escrivão da câmara de Mariana.', 459, '1750-10-20', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (889, 'Requerimento de Domingos da Silva Lobo, solicitando confirmação de sesmaria de meia légua de terra que possui junto ao rio do Peixe, Cidade de Mariana. Consta a referida carta concedida pelo governador das Minas Gerais, José Antonio Freire de Andrade (1749). Deferido', 984, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (664, 'Requerimento dos irmãos devotos de Nossa Senhora do Monte do Carmo, na capela de Santa Quitéria, freguesia de Nossa Senhora do Pilar do Ouro Preto e bispado de Mariana, a D. Jose I, solicitando a mercê de privilégios e graças para seu sustento. Consta certidão em anexo.', 720, '1757-07-27', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (555, 'Requerimento de Pedro Taques de Almeida Pais Leme a D. Jose I dizendo que, como herdeiro de seu pai, Bartolomeu Pais de Abreu, que se comprometeu a financiar a descoberta das minas de ouro existentes no sertão de Goiás em troca do direito dos rios que corressem entre a cidade São Paulo e as mesmas minas, viera então ao reino para obter o encarte da dita mercê, mundo dos documentos necessários, mas como estes tivessem sido destruídos pelo terremoto, recorria à clemência real para que fosse encartado na mercê. Consta despacho do conselho ultramarino mandando informar o procurador da fazenda, de 13/01/1756; parecer do procurador; do conselho ultramarino mandando informar novamente o procurador da fazenda, de 11/09/1756; do conselho ultramarino mandando informar o procurador da coroa, de 20/09/1756; e o parecer do procurador da fazenda. ', 565, '1756-01-13', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (491, 'Representação da câmara da Vila de São João Del-Rei ao rei sobre a necessidade de um hospício para os religiosos de São Francisco. Informam que pretendem construir uma igreja por meio de esmolas e rogam licença para a construção da igreja e do hospício, além da permissão para quatro ou mais religiosos. A presença dos religiosos trará grande proveito para a reforma das vidas e a salvação das almas, dado que os vigários apenas cuidam de suas conveniências.', 462, '1751-05-11', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (432, 'Carta de Manuel da Costa Coelho, procurador da câmara de Vila Rica, informando o rei acerca das perseguições que o ouvidor movia contra ele. Além disso, requer exame aos livros da câmara.', 411, '1751-04-18', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (458, 'Requerimento de João de Gois de Araujo a D. Jose I expondo que, sendo natural da praça de Santos, servia na infantaria paga da mesma praça havia 16 anos. Achando-se vago o posto de capitão da fortaleza da Bertioga, situada no mesmo distrito por falecimento do capitão-comandante dela, Matias do Couto Reis, pedia para ser nele provido, vencendo somente o soldo de alferes. Consta um despacho do conselho ultramarino, mandando-o requerer em oposição, de 16/10/1755.  Contém documentos anexos, assim como outros requerimentos.', 431, '1755-10-16', 1755, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (454, 'Requerimento de João Teixeira de Carvalho, capitão da fortaleza, ao provedor da fazenda e provedor geral da Vila de Santos, pedindo-lhe que mande passar uma certidão no qual conste o dia, mês e ano em que o requerente começou a exercer o posto de capitão da referida fortaleza. Consta um despacho do referido provedor, deferindo-o. Contém uma certidão e um outro requerimento do mesmo requerente.', 428, '1753-10-05', 1753, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (645, 'Requerimento do intendente eleito para comarca de Goiás, bacharel Luis Antonio Rosado da Cunha, ao rei D. Jose, solicitando que seja deferido seu pedido de ajuda de custo para sua viagem até Goiás.', 692, '1755-05-23', 1755, 28, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (646, 'Requerimento do ex-ouvidor-geral de Goiás, Manuel Antunes da Fonseca, solicitando alvará de fiança para que solto, possa mostrar a sua defesa. Consta carta do desembargador Jose Cardoso Castelo, ao rei D. José, sobre o requerimento.', 693, '1755-05-29', 1755, 28, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (493, 'Representação dos religiosos da ordem terceira de São Francisco, solicitando ao rei a mercê de autorizar a edificação de uma igreja e um hospício, e a presença de quatro ou mais religiosos para reforma das vidas e salvação das almas.', 464, '1751-05-12', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (492, 'Representação da câmara da Vila de São João Del-Rei, solicitando providências devido à excessiva capitação que pagam em lugar do quinto, a qual recai sobre as populações da Vila.
INSERT INTO app_hist.provocacao VALUES (672, 'Requerimento de Catarina de Neves, viúva do desembargador Francisco Pereira da Costa, a D. Jose I, solicitando o pagamento de dois anos do ordenado de seu marido.', 728, '1757-09-13', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (498, 'Francisco Xavier Correia pede ao rei a licença para se deslocar ao Reino com toda a sua família.', 471, '1750-10-23', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (431, 'Carta de Manuel da Costa Coelho, procurador da câmara de Vila Rica, informando o rei acerca das perturbações sociais ocasionadas pela situação financeira da referida câmara. Solicita medidas para evitar os contínuos clamores de vexação dos povos, causados pelas exorbitantes e injustas despesas dos bens do conselho ultramarino, que resultaram em efeitos contrários, desordenando a harmonia pública e o sossego dos povos.', 410, '1751-04-18', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (531, 'João da Costa de Azevedo, escrivão da câmara da cidade de Mariana, pede confirmação de seu cargo', NULL, '1750-12-22', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (497, 'Moradores de Brumadinho pedem ao rei para que a região fique sobre jurisdição da cidade de Mariana.', 472, '1750-10-22', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (434, 'Requerimento de Ventura Correa, morador nas Minas da Aiuruoca, solicitando ao rei a mercê de ordenar que tombe meia légua de terra que lhe foi doada em sesmaria, situada no Ribeirão do Tranas, em frente à paragem chamada Pinhal. Ele necessita tombar para evitar contendas. Em anexo, encontra-se um bilhete de ordem de provisão.', 413, '1751-04-27', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (461, 'Requerimento de João de Gois de Araujo, alferes de infantaria da praça de Santos, pedindo que lhe passasse corrida folha. Consta um despacho do vereador mais velho, juiz de fora da praça de Santos, Domingos Pereira Viegas, mandando passar o alvará de folha corrida pedido, de 04/03/1751. Contém documentos anexos e outros requerimentos do mesmo requerente.', 431, '1751-03-04', 1751, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (439, 'Requerimento do juiz e oficiais da irmandade do Patriarca de São Jose dos Homens Pardos, solicitando ao rei a mercê de ordenar que pelo escrivão da câmara de Vila Rica fosse passada uma certidão dos rendimentos da dita câmara, para o bem da justiça, a fim de instituírem um processo. Em anexo, encontram-se vários requerimentos, entre eles, a solicitação de uma certidão que comprove que são pobres e a apresentação das terras que o senado lhes concedeu.', 418, '1751-05-07', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (463, 'Carta do provedor da fazenda real da capitania de São Paulo, Jose de Godoi Moreira, a D. Jose I pedindo que ordenasse que ele vencesse três mil cruzados de ordenado cada ano, em virtude de servir com zelo no seu cargo havia dezoito anos e de seus antepassados durante mais de um século terem sempre bem servido o Reino. Contém em anexo duas cartas nas quais autoridades mencionam o bom serviço prestados pelo requerente, além de uma outra certidão. ', 433, '1755-10-20', 1755, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (441, ' Representação dos oficiais da câmara de Vila Real do Sabará, informando ao rei sobre as dificuldades enfrentadas pela capitania e solicitando alterações na tributação que recai sobre os povos. Em anexo, encontram-se duas cartas referentes ao meio mais conveniente para se pagar os quintos, garantindo a segurança da fazenda real e a suavidade para os vassalos.', 420, '1751-05-08', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (440, 'Representação da câmara de Vila Rica informando acerca da lassidão em que se encontra o juízo dos defuntos e ausentes, devido ao aumento dos vassalos, não tendo as partes para onde recorrer, senão ao tribunal da mesa da consciência, recebendo grandes perdas e incômodos. Solicitam providências para regularizar tal situação, Criada a relação do Rio de Janeiro sugerem que a mesma tome conhecimento desta situação.
INSERT INTO app_hist.provocacao VALUES (443, 'Representação da câmara da Vila de São Jose, informando ao rei acerca da opressão sofrida pelo povo das minas, resultando em gravíssimo prejuízo e inconveniente ruína, devido à cobrança dos quintos.', 422, '1751-05-08', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (442, 'Carta de João de Sousa Meneses Lobo, ouvidor de Sabará, para o rei, informando sobre a necessidade de nomear vintenários para alguns arraiais de sua comarca, devido à grande população desses locais. Destaca que a nomeação é necessária para atender a desavenças, portarem fé das feridas, e mortes.  de sorte, e, para solucionar muitas questões referentes à justiça. ', 421, '1751-05-08', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (448, 'Requerimento do padre Manuel Jose Vaz a D. José I pedindo que lhe mande passar alvará de mantimento relativo à conezia em que fora provido por falecimento do padre Lourenço Leite Penteado. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, o último dos quais manda passar o alvará pedido, de 17/05/1755.', 424, '1755-05-14', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (452, 'Requerimento de Jose de Godoi Moreira, juiz de fora, ao provedor da fazenda real de Santos, pedindo que mande passar por certidão a provisão de D. Jose I, pela qual ordena que o vencimento do ouvidor da comarca de São Paulo, Jose Luis de Brito e Melo, seja aumentado um terço.  Consta um despacho do dito provedor, deferindo o pedido. Contém uma certidão e uma provisão inclusa, assim como outro requerimento.', 427, '1755-04-21', 1755, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (459, 'Requerimento de João de Gois de Araujo dizendo que, estando destacado na cidade de São Paulo havia 9 anos, reconduzira para Santos por ordem do governador Jose Rodrigues de Oliveira os quintos vindos das minas de Itajubá e, como necessitava para seus requerimentos de uma certidão em que constasse como cumprira essa diligência, pedia para que o escrivão da mesma praça a passasse. Consta um despacho do provedor deferindo de 23/02/1751. Contém documentos anexos, assim como outros requerimentos do mesmo requerente.', 431, '1751-02-23', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (455, 'Requerimento de Maria do Espirito Santo a D. Jose I dizendo que, devido à falta de saúde de seu marido, Jose Rodrigues da Costa, e, sendo reconhecida nela capacidade para bem administrar os seus haveres, pedia para lhe ser passada provisão de tutela sobre seus filhos menores para, no caso de falecimento de seu marido, evitar o prejuízo que sofreriam os seus bens, até lhe ser concedida a dita provisão. Consta um despacho do conselho ultramarino, indeferindo. Contém instrumento de justificação, assim como outros requerimentos. Um destes requerimentos é uma cópia deste, também com pedido indeferido. ', 429, '1755-10-08', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (460, 'Requerimento de João de Gois de Araujo, alferes da infantaria da praça de Santos, pedindo para lhe ser passada uma certidão em que conste o tempo que seu avô e pai tinham servido o rei. Consta um despacho do provedor, mandando passar o que no cartório contasse, de 22/02/1751. Contém documentos anexos e outros requerimentos do mesmo requerente.', 431, '1751-02-22', 1751, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (464, 'Requerimento de Manuel da Trindade, padre, a D. Jose I pedindo que lhe mande passar alvará de mantimento, relativo ao cargo de fiscal da mesa de consciência e ordens na igreja da vila de São Sebastião, do bispado de São Paulo. Conta um despacho do conselho ultramarino, deferindo-o, de 24/10/1755. ', 434, '1755-10-24', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (476, 'Alexandre de Gusmão, escrivão da ouvidoria de Vila Rica, pede ao rei para que nomeie o capitão Antonio Jose Freire de Sousa como seu serventuário. ', 447, '1750-09-13', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (473, 'O arcediago da Sé de Mariana pede ao rei para que ordene lhe enviar as certidões com as despesas relativas as folhas seculares e eclesiásticas remetidas a Minas Gerais ', 444, '1750-09-02', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (474, 'Francisco Ribeiro, morador de Caxambu, solicita ao Rei a confirmação da sesmaria na quantia de meia légua de terra ', 445, '1750-09-02', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (475, 'Antonio Robalo Lobo, morador de Mariana, solicita ao rei a confirmação de meia légua de terra. (Contem o bilhete com a doação e ordem de confirmação)', 446, '1750-09-12', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (557, 'Requerimento de dona Maria Angela Eufrasia da Silva, viúva de Andre Alvares de Castro a D. Jose I, pedindo que lhe seja concedida provisão de tutoria que lhe permitisse administrar os bens de seus filhos menores. Consta um despacho do conselho ultramarino, deferindo-o, de 16/02/1756. ', 567, '1756-02-16', 1756, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (651, 'Requerimento do bispo de Mariana, D. Manuel da Cruz, ao secretário de Estado, solicitando licença que conceda o envio de seu sobrinho teólogo para resolver o problema do pagamento das congruas aos capelães das capelas da freguesia, além de lidar com outros assuntos religiosos. Consta carta do secretário aprovando uma junta de teólogos.', 703, '1757-06-29', 1757, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (465, 'Representação da camara de Mariana, expondo os vexames praticados pelo bispo na cobrança dos emolumentos judiciais e nos expedientes das camaras eclesiasticas.', 436, '1755-05-07', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (605, 'CONSULTA Consulta do conselho ultramarino sobre uma petição apresentada pelos mineiros de Minas Gerais, que solicitavam a reforma do regimento dos superintendentes, guarda-mores e seus escrivães, devido aos excessivos salários cobrados por esses oficiais. Os mineiros relataram que, no exercício de suas funções, encontravam-se em grande depressão, sem obter retorno de suas atividades por muitos anos, em razão das diligências frustradas. Destacaram que a maior opressão sofrida vinha dos altos salários pagos aos ocupantes desses cargos. E pelo procurador da fazenda, recaiu ser muito justo que digne mandar formar regimentos para os oficiais, buscando evitar maiores vexações.', 634, '1751-09-24', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (500, 'Pascoal Correia Lisboa, soldado de uma companhia de dragões da capitania das Minas Gerais, pede licença para se deslocar ao reino.', 474, '1750-10-26', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (229, 'Requerimento de dona Maria Paes de Jesus, moradora da vila de Sorocaba e viúva do coronel João Antunes Maciel, ex-superintendente geral das minas de Cuiabá, a D. Jose I, solicitando que o escrivão do conselho ultramarino lhe passasse cópia da provisão pela qual o então governador e capitão general da capitania de São Paulo nomeara seu marido para aquele cargo. Consta informação de serviço, atestação, provisão do governador de São Paulo, certidão, abaixo assinado, carta régia, folha de rosto auto cível e termo de assento.', 218, '1755-05-06', 1755, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (512, 'Manuel Francisco de Sousa pede licença para se deslocar ao Reino com sua família 
INSERT INTO app_hist.provocacao VALUES (841, 'maisa Requerimento de Manuel Gonçalves Casqueiro e de seu sócio, Antonio Teixeira da Cunha, solicitando a confirmação de carta de  sesmaria de meia légua de terra em quadra junto a Serra Negra, freguesia de Curral Del Rei, termo da Vila Real do Sabará, comarca do Rio das Velhas. Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 929, '1760-08-08', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (372, 'Requerimento de João de Sousa Filgueiras a D. Jose I, bacharel, expondo ter sido nomeado ouvidor da cidade de São Paulo, e que requerera ao conselho ultramarino determinada ajuda de custo. Em virtude do deferimento do requerimento, o conselho ultramarino mandou passar provisão para que lhe fosse paga a dita ajuda na provedoria do Rio de Janeiro, quando foi sempre paga em São Paulo. Assim, pede ao monarca que lhe conceda remissão a fim de o conselho ultramarino lhe consultar o requerimento. Consta uma resolução de D. Jose I determinando que seja consultado pelo conselho de 17/01/175; dois despachos do conselho ultramarino, o segundo dos quais concorda com o parecer do procurador da fazenda; e um parecer do procurador da fazenda, segundo o qual as ajudas de custo concedidas ao ministros do Brasil deviam ser pagas na provedoria do seu distrito e só tem sido pagas pelo conselho ultramarino assim como pela provedoria do Rio de Janeiro, onde o monarca mandou pagar a outro ouvidor. Contém um bilhete passado pelo secretário do conselho ultramarino, assim como outros dois requerimentos do mesmo requerente.', 357, '1754-01-17', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (634, 'ULTRAMAR Carta do juiz ordinário da comarca de Goiás, Diogo de Gouvea Osorio e Castro, ao rei sobre uma série de denúncias contra a atuação do ouvidor-geral da capitania de Goiás, bacharel Sebastião José da Cunha Soares e Vasconcelos.', 666, '1755-04-07', 1755, 28, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (622, 'Requerimento de Giraldo Jose de Abranches, arcediago da Sé de Mariana, a D. Jose I, solicitando provisão de mantimentos e o pagamento das congruas vencidas pela provedoria da Fazenda Real de Vila Rica do Ouro Preto.', 651, '1757-03-31', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (607, 'Requerimento de Manuel Lopes de Oliveira, capitão e morador na freguesia da Borda do Campo, solicitando ao rei a mercê de confirmar a doação, em sesmaria, de meia légua de terra em quadra. Em anexo: um bilhete de confirmação e uma carta de doação.', 636, '1751-09-27', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (608, 'Requerimento de Antonio da Silva, morador na freguesia da Borda do Campo, solicitando ao rei a mercê de confirmar a doação, em sesmaria, de meia légua de terra em quadra, nos sertões devolutos e matos virgens. ', 637, '1751-09-27', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (567, 'Requerimento de Domingos Martins Santiago e Tome Fernandes Santiago, moradores nas Catas Altas do Itaverava, solicitando ao rei a mercê de lhes conceder permissão para realizar os reparos necessários em um engenho de açúcar existente em sua fazenda.', 578, '1751-09-02', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (640, 'ULTRAMAR **Carta de Gomes Freire de Andrade, governador de Minas Gerais, informando ao rei sobre os emolumentos que devem ser cobrados pelos ministros e oficiais de justiça e fazenda. O governador menciona que repetidos requerimentos declaram o preço a ser pago e recebido pelos emolumentos, visto que a nova lei não determina. Em anexo duas provisões e um bando.', 673, '1751-07-30', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (606, 'Requerimento de Narciso da Costa, solicitando ao rei a mercê de confirmar a doação, em sesmaria, de meia légua de terra em quadra, localizada na freguesia da Borda do Campo. A terra foi doada em nome do rei por Gomes Freire de Andrade, governador de Minas Gerais. Em anexo: um bilhete de ordem de confirmação e uma carta de doação', 635, '1751-09-27', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (650, 'Representação dos oficiais da câmara de Vila Rica a D. Jose I, solicitando a continuação das propinas dos oficiais em caso de festividades. Consta parecer positivo do conselho ultramarino.', 698, '1757-06-27', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (649, 'Representação do governador de Minas Gerias, Jose Antonio Freire de Andrade, a Tome Joaquim da Costa Corte-Real, solicitando passar certidão às pessoas que remeterem mais de oito arrobas de ouro na Casa de Fundição, com o intuito de não haver ilegalidades.', 697, '1757-06-26', 1757, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (639, 'Requerimento de Luis Pacheco de Andrade, morador na freguesia de Guarapiranga, solicitando ao rei a mercê de confirmar a doação em sesmaria de meia légua de terra em quadra na referida freguesia. Em anexo três requerimentos, um auto de demarcação e um bilhete de ordem de confirmação.', 672, '1751-07-23', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (621, 'Requerimento de Manuel Pinto de Azevedo, professo na ordem de Cristo, porteiro e guarda livros da secretaria do conselho ultramarino a D. Jose I, solicitando provisão de nomeação no posto de secretário do governo das Minas Gerais ou no do Rio de Janeiro por tempo de três anos. Consta 1. parecer do conselho ultramarino informando que o suplicante é o principal candidato a vaga pela sua experiência como secretário do conselho, 2. certidão da ordem de Cristo e 3. provisão de nomeação como secretário do governo de Minas Gerais.', 650, '1757-03-22', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (560, 'Requerimento de Pedro Rebelo Falcão a D. Jose I solicitando a nomeação de seu sobrinho, Jose Rebelo de Macedo, para o posto de capitão-mor da capitania do Espírito Santo. Consta despacho favorável do conselho ultramarino, de 30/10/1754; decreto de D. Jose I; e relação de serviços prestados.', 423, '1754-10-30', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (632, 'Requerimento de Antonio de Queiroz Chamorro, para D. José, solicitando provisão para intentar, por seus procuradores, ações de perdas e danos e injúrias, contra Manuel de Albuquerque de Aguilhar, morador das minas de Natividade, capitania de Goiás.', 663, '1755-01-16', 1755, 28, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (653, 'Requerimento de Domingos de Moraes, capitão da companhia da ordenança de Pé do distrito do morro de Santa Ana, em Mariana, a D. Jose I, solicitando a mercê da sua confirmação no exercício do referido posto. Consta carta patente do governador de Minas Gerais, Jose Antonio Freire de Andrade, e certidão em anexo.', 705, '1757-07-06', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (655, 'Requerimento do bacharel Felix de Azevedo da Fonseca a D. Jose I, solicitando o pagamento de 500 mil réis dos ordenados, relativas a três anos pelo seu exercício como intendente da comarca de Sabará. Consta bilhete, certidão e parecer positivo do conselho ultramarino.', 710, '1757-07-06', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (508, 'Francisco Gomes da Cruz, preso na cadeia de Vila rica, pede ao rei que conceda o seu livramento enquanto não é entregue a informação ao juiz da culpa. ', 484, '1750-10-31', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (517, 'Nicolau da Silva Bragança, cavaleiro da ordem de Cristo, pede ao rei a concessão de 40 mil reis de pensão para poder dividir entre seus irmãos.', 493, '1750-11-19', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (516, 'Manuel Caetano Monteiro, nomeado intendente de capitação das Minas de Paracatu, pede ao rei para que sirva ordenar pagamento integral dos seus soldos desde o dia que embarcar para o Brasil. ', 492, '1750-11-17', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (515, 'Felix de Azevedo da Fonseca, intendente do ouro das minas de Sabará, pede duzentos e cinquenta mil reis de ajuda de custo. ', 491, '1750-11-16', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (509, 'Vicente Rodrigues da Guerra, cabo de esquadra de uma companhia de dragões de Minas gerais, pede ao rei licença para ir ao Reino. ', 485, '1750-10-31', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (532, 'João Batista Furtado, cavaleiro da ordem de Cristo, pede para que seja nomeado capitão da cavalaria da esquadra do Rio Grande', 506, '1750-12-23', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (513, 'João Pinto de Moraes Bacelar pede ao rei duzentos mil reis de ajuda de custo por ter sido recentemente provido no cargo de ouvidor do Serro do Frio. ', 489, '1750-11-06', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (514, 'Felix de Azevedo da Fonseca, intendente do ouro das minas de Sabará, pede duzentos e cinquenta mil reis de ajuda de custo', 490, '1750-11-10', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (507, 'Felix de Azevedo da Fonseca, intendente das minas do Sabará, pede ao rei para que ordene que seus soldos sejam pagos desde o dia que embarcar do Reino para o Brasil ', 483, '1750-10-30', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (530, 'Vicente Rodrigues de Guerra, cabo da esquadra, pede licença para viajar ao reino', NULL, '1750-12-22', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (511, 'João da Costa Carneiro pede ao rei que não demorem no processo de confirmação do cargo para o qual foi nomeado (segundo tabelião da Vila de São Jose) ', 487, '1750-11-02', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (524, 'João Bernardo Pereira de Seixas pede ao rei a sua nomeação para o cargo de procurador da coroa do Rio das Mortes  ', 500, '1750-12-03', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (521, 'Manuel Bernardo Sarmento, soldado de uma guarnição de Minas Gerais, pede ao rei licença para ir ao Reino', 497, '1750-11-28', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (519, 'Manuel da Guerra Leal, morador de Mariana, pede ao rei a nomeação no cargo de curador dos órfãos da mesma cidade. ', 495, '1750-11-24', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (520, 'João de Barros de Carvalho, presbítero do Habito de São Pedro, pede a concessão de um benefício simples, hábito ou pensão. ', 496, '1750-11-26', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (523, 'Caetano da Costa Matoso, ouvidor de Vila Rica, pede ao rei que lhe conceda todas as propinas que seus antecessores tiveram direito ', 499, '1750-12-02', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (43, 'Representação dos oficiais da câmara de São Paulo a D. Jose I dizendo que apesar da ordem de D. João V que determinou a construção de uma nova cadeia, mas continuara a servir a cadeia velha que de tão danificada havia ruído. Informaram que já haviam se queixado a Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, mas devido à informação do provedor da fazenda, não obtiveram deferimento, de 05/12/1770. Sobre esta representação recaíram um despacho do conselho ultramarino, de 06/08/1771, pedindo o parecer do procurador da fazenda; e um parecer do procurador em que se faz notar a necessidade de informação do Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo. Contém uma cópia da carta de D. João V ordenando a construção de uma cadeia, de 27/06/1713; um requerimento dos oficiais da câmara a cidade de São Paulo ao Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, expondo o estado da ruína da cadeia velha, com dois despachos do governador, de 23/01/1769; uma informação do provedor da fazenda real de São Paulo, de 27/01/1769; um requerimento do juiz ordinário e oficiais da câmara pedindo que o escrivão da ouvidoria passe por certidão o teror de uma ordem régia em que o rei determina que a provedoria real assista com quatrocentos mil réus anuais para construção da cadeia; e um requerimento dos oficiais da câmara requisitando um atestado em que se declare o estado de ruína da cadeia local.', 59, '1770-12-05', 1770, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (522, 'Pascoal Correia Lisboa, soldado de uma companhia de dragões das Minas Gerais, pede ao rei licença para viajar ao Reino ', 498, '1750-12-01', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (526, 'Manuel Ribeiro de Carvalho pede ao rei a confirmação da sesmaria de meia légua de terra em quadra na região do rio Bacalhou', 502, '1750-12-04', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (527, 'Domingues Nunes Vieira, intendente do Sabará, pede ao secretário do Estado que explique melhor a extensão de tempo que ficará no cargo', 503, '1750-12-05', 1750, 5, 'S', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (529, 'ULTRAMAR? Alexandre de Gusmão trata da lei das cobranças dos quintos e seus métodos utilizados. Queixa-se das novas formas de cobranças e as julga excessivas.', 505, '1750-12-18', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (506, 'Bernardo de Fonseca Lobo, capitão-mor da Vila do Príncipe, pede ao rei para que declare que durante as festas públicas tenha um assento reservado debaixo do arco da Igreja. ', 481, '1750-10-30', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (540, 'Representação dos oficiais da câmara de vila Nova da Rainha, ao rei, contestando a súplica dos moradores do Brumadinho, que pretendiam a sua desanexação da referida vila, para se agregarem a cidade de Mariana.', 524, '1751-06-05', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (533, 'Nicolau da Silva Bragança pede ao rei a concessão de graças pelos seus serviços prestados no Brasil.', 507, NULL, 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (539, 'Representação da câmara da cidade de Mariana ao rei informando sobre a desobediência de alguns ouvidores de Minas Gerais em relação ao cumprimento da provisão régia datada de 26/10/1750, que tratava das correições.', 520, '1751-06-05', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (168, 'Representação dos oficiais da câmara de Santa Ana das Cruzes de Moji a D. Jose I expondo a consternação em que se achavam os habitantes da dita vila, porque não possuíam meios e viviam, portanto, em grande pobreza. Pedem ao rei que mande proibir os fumos e sua feitura em todas as Minas para que a comarca de São Paulo fique com o seu exclusivo e assim poder aumentar o seu comércio e favorecer as condições de vida. ', 173, '1751-04-12', 1751, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (510, 'Manuel Ferreira Viana pede ao rei  para que dê provimento aos cargos a qual fora nomeado (meirinho da provedoria dos ausentes e escrivão de meirinho do campo e das execuções da cidade de Mariana) ', 486, '1750-11-02', 1750, 5, 'R', '', '1v ');
INSERT INTO app_hist.provocacao VALUES (518, 'Padre Manuel de Pinho Candido pede ao rei a graça de passar um alvará de mantimento  ', 494, '1750-11-20', 1750, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (677, 'Requerimento de Antonio Jose da Rosa Tenebres, contratador das passagens do Rio Grande e Rio das Mortes, informando as perdas e prejuízos que teve e solicitando uma esmola de mantimento ou a prorrogação do seu contrato por mais dois anos. Consta anotação do conselho ultramarino informando que o governador deve dar seu parecer ouvindo o provedor da fazenda.', 733, '1758-01-02', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (543, 'Requerimento de Bartolomeu de Sequeira Cordovil a D. Jose I solicitando certidão de confirmação de carta de sesmaria de três léguas de terra nos campus da Vacaria. Consta certidão do governador Gomes Freire de Andrade em que passa a referida sesmaria e bilhete do conselho ultramarino.', 547, '1757-01-09', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (545, 'Requerimento do provedor e mais irmãos da Santa casa de misericórdia de Vila Rica, solicitando a D. Jose I licença para pedir esmolas, devido a grande decadência que os rendimentos do hospital da mesma casa tem passado. Consta carta e provisão do ouvidor de Vila Rica e do conselho ultramarino com parecer positivo.', 549, '1757-01-10', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (544, 'Requerimento de Antonio Moniz de Medeiros, escrivão das execuções da vila de São João Del Rei, a D. Jose I solicitando o provimento de um ajudante para o seu posto.', 548, '1757-01-10', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (547, 'Requerimento de Francisco Fernandes Paradela a D. Jose I solicitando confirmação da carta de sesmaria de meia légua de terra em quadra no sitio chamado São Mateus e dos Gaviões, na freguesia do Sumidouro, território de Mariana. Consta carta do governador Jose Antonio Freire de Andrade concedendo a referida sesmaria.', 551, '1757-01-13', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (546, 'Requerimento de Manuel Francisco da Costa Barros, oficial da secretaria do governo das Minas Gerais, a D. Jose I solicitando a mercê da confirmação da provisão no referido posto.', 550, '1757-01-11', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (713, 'Requerimento do alferes Domingos Vaz de Carvalho, procurador da câmara de Vila Rica, solicitando certidões dos autos do processo que moveu aos vereadores da dita comarca no ano de 1757.', 778, NULL, 1758, 5, 'R', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (70, 'Cópia da representação do cabido e mais justiças da cidade de São Miguel de Tucumã ao D. Jose Andonezeu, comissário de Espanha, por não ter sido feita a entrega à Portugal dos sete povos do Uruguai, conforme combinado pelo tratado dos limites da América meridional, de 06/04/1752. Contém uma cópia da carta do capitão Antonio da Silveira Peixoto ao governador da colônia relatando as circunstâncias que o forçaram a chegar às posições espanholas, onde se acha encarcerado, de 16/04/1770; cópia da informação da notícia da descoberta dos fundamentos da antiga vila rica, de 13/04/1771; um ofício de Luis Antonio de Sousa, governador e capitão-general da capitania de São Paulo, ao marquês de Pombal, ministro e secretário de estado dos negócios do reino, sobre a queda do capitão em poder dos castelhanos por se ter internado em terras pertencentes à coroa de Espanha e que se encontra preso em Buenos Aires, de 04/09/1771.', 82, '1771-09-04', 1771, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (45, 'FABI ONDE ESTÁ JOÃO HONORATO? Requerimento dos devotos do mártir São Manuel da capela de Nossa Senhora da Luz aos oficiais da câmara da cidade de São Paulo dizendo que desejam construir uma capela e pedem o terreno para tanto. Consta um despacho dos oficiais da câmara indeferindo. Contém em anexo outro requerimento dos devotos do mártir São Manuel aos oficiais da câmara da cidade com despacho favorável, de 18/12/1770; uma certidão, de 12/12/1770, na qual consta uma carta de data, de 25/08/1756, indicando que as terras foram concedidas; e uma representação dos oficiais da câmara dizendo que não teve efeito a construção do convento, mas que se mandou que os bens desse colégio ficassem juntos aos bens dos mesmos jesuítas, de 12/12/1770.', 61, '1770-12-12', 1770, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (67, 'Requerimento de Jose dos Santos Rosa a D. Jose I pedindo confirmação do posto de capitão de cavalaria auxiliar da companhia da freguesia de Santo Antonio do registro de Curitiba. Consta um despacho do conselho ultramarino pedindo o parecer de Luis Antonio de Sousa Botelho Mourão e ordenando-lhe que declare a que regimento pertence a companhia mencionada ou a ordem que teve para criá-lo, de 20/07/1771. Contém, ainda, uma carta patente de 14/11/1771, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando e provendo o requerente como capitão de cavalaria auxiliar. Acerca desta carta patente também recaiu um despacho idêntico ao já citado. ', 79, '1771-07-20', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (72, 'Requerimento de Otavio Gregorio de Nebias a D. Jose I pedindo confirmação da patente do posto de capitão de auxiliares a pé da vila de São Vicente, que lhe foi concedida pelo Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo. Consta o despacho do conselho ultramarino de 23/10/1771 ordenando que o governador declare a que regimento pertence a referida companhia. Contém, ainda, uma carta patente de 03/03/1766, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão de auxiliares a pé da vila de São Vicente.', 84, '1771-10-23', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (74, 'Requerimento de Manuel Ribeiro Ribas a D. Jose I pedindo confirmação da patente do posto de capitão de auxiliares a cavalo da companhia da vila de Curitiba pertencente ao 2° corpo de infantaria da marinha de Paranaguá, que lhe foi concedida pelo Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo. Consta o despacho do conselho ultramarino de 23/10/1771 ordenando que o governador declare por que razão sendo a companhia de cavalaria fica pertencendo a infantaria e os motivos que fez tal união. Contém, ainda, uma carta patente de 20/05/1767, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente como capitão de auxiliares a cavalo da companhia da vila de Curitiba pertencente ao 2° corpo de infantaria da marinha de Paranaguá.', 86, '1771-10-23', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (79, 'Requerimento de Manuel Jose Gomes à Luis Antonio de Sousa Mourão, governador da capitania de São Paulo, pedindo alargamento do prazo para pagar a fiança que prestara a Francisco Luis de Faria. Consta despacho do governador, mandando que o provedor, Jose Honorario de Valadares e Aboim, de 08/03/1770; despacho do governador mandando dar a espera de um mês para se proceder à execução da fiança, de 12/03/1770; despacho do provedor mandando informar o almoxarife sobre o despacho anterior e perguntando se aceitava a fiança, no prazo determinado nele; uma resposta do almoxarife recusando-se a aceitar a dita espera, de 13/03/1770; representação do referido provedor, expondo ao governador as supracitadas respostas do almoxarife, e pedindo ordens, de 15/03/1770; despacho do governador reforçando o seu anterior despacho concedendo a espera, de 17/03/1770; despacho do provedor, ordenando que se não fizesse entrega de mais dinheiro, sem determinação do governador, de 18/03/1770.', 91, '1770-03-08', 1770, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (577, 'Requerimento do desembargador Estevão Galego Vidigal e sua mulher D. Luiza Antonieta a D. Jose I, solicitando provisão para poderem apelar contra as partilhas que se fizeram dos bens que ficaram de Jose Rebelo Perdigão, mestre de campo nas minas de Ouro Preto. Consta bilhete do conselho ultramarino solicitando o parecer do ouvidor da comarca.', 588, '1757-02-19', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (182, 'Requerimento de Carlos de Araujo Castro ao bispo de São Paulo, frei Antonio da Madre de Deus Galvão, em que solicita autorização para que seja enviado um sacerdote e que ele e sua comitiva recebam sacramentos. Queixa-se da perseguição de seus inimigos e relata que não cometeu os crimes de que está sendo acusado. Consta outros requerimentos do requerente, em que repete sua solicitação e relata sua condição de saúde. Consta consulta do conselho ultramarino informando que quando constar que o suplicante se põe em estado de salvação, deixando o concubinato e autorizando que seus escravos se confessem, o pedido iria ser deferido.', 188, '1753-05-21', 1753, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (82, 'Queixa de Pedro da Trindade e Silva, frei e prior do convento do Carmo, contra o ouvidor de São Paulo, João Rodrigues Campelo, relatando que o dito provoca indisciplina e desobediência aos índios da aldeia de Barueri, utilizando-os para trabalhos particulares. Solicita a D. João V que se mande averiguar e tomar providências.', 92, '1739-06-16', 1739, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (106, 'Queixa do mestre de campo e governador da praça de Santos, Jose Rodrigues de Oliveira, contra o juiz de fora de Santos, Gaspar da Rocha Pereira, que age com violência e imprudência aos oficiais da câmara, inquietando a população e estimulando um motim, provocando ruína a este povo. Em carta anexo do ouvidor João Rodrigues Campelo é informado que os oficiais da referida câmara solicitam a substituição do juiz de fora pelo escrivão da câmara, nomeado pelo governador de São Paulo. O parecer do conselho ultramarino foi de acordo com o do procurador da coroa, que se houvesse a este juiz de fora o lugar por acabado, o ordenasse tirar residência. Consta cópia da resposta e da carta do ouvidor, do juiz de fora, consulta do conselho ultramarino, treslado do pedido e termo de juramento.', 116, '1740-05-08', 1740, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (309, 'Carta do vigário da freguesia de Atibaia, padre Antonio Jose de São Francisco, para Carlos de Araujo Castro, pedindo-lhe para que abrigasse em sua casa um tal de "Amaro" que, por ter comprado uma certa porção de prata, que havia sido furtada, e por recear ser preso como ladrão, se dirigiu ao referido vigário para que ele lhe indicasse um lugar seguro, até que os acontecimentos tomassem um rumo melhor. Consta carta de reconhecimento.', 302, '1757-06-17', 1757, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (380, 'Requerimento de João Machado Castanho ao capitão Tome de Lara de Almeida, procurador bastante do conde da ilha de príncipe, dizendo que no termo da vila de Sorocaba está um capão chamado barreiro da outra parte do rio da pirapora partindo com as terras do capitão João Antunes Maciel que está devoluto. Como este terreno vai onze para doze anos lhe pertencia e dele pagava dízimos, pedia para lhe passar carta de sesmaria do mesmo. Consta um despacho do referido procurador, dando ao requerente as terras que pedia com as confrontações indicadas, de 10/12/1715. Contém carta de sesmaria, e um outro requerimento de Maria Paes de Jesus. ', 362, NULL, 1715, 1, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (588, 'ULTRAMAR? Representação dos oficiais da câmara de Vila Rica, informando ao rei D. Jose I acerca das despesas efetuadas com o luto pela morte do rei D. João V.', 599, '1751-09-11', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (564, 'Carta do bispo de Mariana ao secretário de Estado solicitando sua intercessão junto ao rei para alterar a nomeação de Tomas Rubi de Barros Barreto do cargo de ouvidor da vila de Sabará para Vila Rica. Suspendendo assim, Caetano da Costa Matoso, que vinha afrontando a jurisdição eclesiástica.', 575, '1751-08-05', 1751, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (647, 'Requerimento de Jose Antonio Freire de Andrade, governador de Minas Gerais, a Tome Joaquim da Costa Corte Real, solicitando providências para que os roubos de cargas de diamantes não aconteçam, citando o exemplo narrado por Antonio Lourenço da Costa, intendente dos diamantes, que encontrou uma dessas cargas na capitania de Goiás no ano interior.', 695, '1757-06-25', 1757, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (589, 'Requerimento de João da Costa de Azevedo, nomeado no cargo de escrivão da câmara da cidade de Mariana, solicitando ao rei a mercê de ordenar que seja passada uma carta de propriedade do referido cargo.', 600, '1751-09-13', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (610, 'Requerimento do juiz, oficiais e irmãos da mesa da irmandade do glorioso mártir São Gonçalo Garcia, ereta pelos pardos na igreja matriz de Nossa Senhora da Conceição, freguesia de Antônio Dias de Vila Rica, ao rei, solicitando a mercê de lhes permitir ter dois ermitões encarregados de pedir esmolas por todo o território. Visando arrecadar fundos para a construção de uma nova capela, uma vez que a irmandade carece dos recursos necessários para essa obra na referida vila.
INSERT INTO app_hist.provocacao VALUES (449, 'Cópia do requerimento de Angelo Pedroso Leme ao ouvidor geral da comarca de São Paulo pedindo termo de manifesto com a declaração dos ribeiros, córregos, morro que ele descobriu. Consta despacho do ouvidor geral dizendo que se tome o termo na forma que requer, de 24/04/1755. Contém termo do manifesto e uma provisão nomeando o requerente como guarda-mor das minas. ', 425, '1755-04-24', 1755, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (451, 'Requerimento de Francisco Caetano de Almeida Lobo, juiz de fora da vila e praça de Santos, a D. Jose I, no qual pede que mande passar provisão para que lhe seja concedido o aumento do ordenado que lhe foi arbitrado desde o dia em que tomou posse daquele lugar. Consta um parecer do provedor da fazenda e dois despachos do conselho ultramarino, o último dos quais manda passar a provisão requerida, de 17/09/1755. Contém um outro requerimento, assim como uma certidão.', 427, '1755-09-04', 1755, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (623, 'Requerimento do padre Antonio Jose da Silva e Sousa, tesoureiro encomendado da Igreja de Santo Antônio do Bom Retiro da Roca Grande, bispado de Mariana, a D. Jose I, solicitando provisão para cobrar a Provedoria da Fazenda Real o pagamento da congrua de um ano.', 652, '1757-04-20', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (81, 'Requerimento de Antonio Correa à Luis Antonio de Sousa Mourão, governador da capitania de São Paulo, dizendo que, tendo trazido de Viamão 20 cavalos que tinham vindo juntos com os do exército, conduzidos por Miguel Raposo de Camargo, estavam os cavalos retidos no registro de Curitiba pela demora que o dito oficial tinha em afiançar os seus. Pedia que, uma vez pagos os respectivos direitos, lhe fosse permitido retirar os seus animais. Consta um despacho do governador, deferindo, salvo inconveniente, de 23/11/1769; despacho do provedor dizendo que, para evitar enganos, devia o requerente entregar o dinheiro dos seus cavalos ao condutor da tropa, para que este pagasse a fiança toda. Há um outro requerimento posterior a este pelo mesmo suplicante dizendo que, tendo obtido despacho favorável ao seu requerimento, o apresentara ao provedor da fazenda real para que o cumprisse, mas como este se recusasse a fazê-lo, vinha de novo pedir para que fosse ordenado ao dito ministro que, uma vez recebido os reais quintos, lhe entregasse o bilhete para poder conduzir os seus animais. Consta um despacho do governador permitindo que o requerente levantasse os cavalos, de 24/11/1769; uma resposta do provedor, dizendo que o dinheiro que o requerente pretendia pagar não pertencia à fazenda real, mas sim à casa de corte real e, por esse motivo, não o podia receber; despacho do mesmo governador mandando que o procurador do dito corte real informe sobre o assunto, de 30/11/1769; informação do procurador, João Ferreira de Oliveira, dizendo que estava de acordo com o conteúdo dos despachos do mencionado provedor, de  12/12/1769; despacho do governador mandando ao requerente depositar o dinheiro na mão do inspetor do registro de Curitiba.', 90, '1769-11-23', 1769, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (83, 'Requerimento de Paulo Jose Gonçalves Coelho, capitão de auxiliares de infantaria dos moradores da freguesia da Conceição do regimento da serra acima, a D. Jose I pedindo a confirmação do seu posto, o qual lhe fora dado pelo Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, pela ordem régia de 23 de outubro de 1775. Consta um despacho do conselho ultramarino deferindo o pedido, de 09/11/1771. Contém, ainda, uma carta patente de 31/05/1776, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, fazendo saber que proveu no posto de capitão de auxiliares de infantaria da freguesia da Conceição do regimento da serra acima da capitania o suplicante.', 93, '1771-11-09', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (282, 'Denúncia feita por Jose de Aguirre Tavora contra Carlos de Araujo Castro, que resistiu contra a sua prisão que havia sido decretada por crimes de violência em um quilombo, onde estava com outras pessoas armadas. O documento é assinado pelo escrivão Simão de Toledo e Almeida.', 275, '1757-11-05', 1757, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (213, ' Requerimento de Inacio Eloi Madureira pedindo a D. Jose I ajuda de custo para ir a Santos como já se praticara com os seus antecessores. Consta um despacho do conselho ultramarino mandando juntar certidão das ajudas de custo que se tinham dado a seus últimos antecessores, de 05/02/1752.', 203, '1752-02-05', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (91, 'Requerimento de Teotonio Jose Zuzarte pedindo a D. Jose I confirmação do posto de ajudante de cavalaria de dragões da cidade de São Paulo, para o qual foi nomeado pelo governador da mesma capitania. Consta despacho favorável do conselho ultramarino, de 28/03/1772. Contém, ainda, uma carta patente de 26/12/1765, passada por Luis Antonio de Sousa Botelho Mourão, governador e capitão-general da capitania de São Paulo, nomeando o requerente no posto citado.', 101, '1772-03-28', 1772, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (100, 'Requerimento de D. Jose de Macedo Souto-Maior e Castro, sargento-mor dos dois terços de cavalaria auxiliar da capitania de São Paulo, pedindo a D. Jose I licença de dois anos para ir ao reino em recompensa dos serviços prestados. Consta um despacho do Conselho Ultramarino, mandando que o governador informe, de 07/07/1772.  Contém um requerimento do mesmo para o rei como ao anterior, do qual recaiu um despacho do conselho ultramarino, mandando o governador informar com o seu parecer, de 14/03/1772.', 110, '1772-07-07', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (310, 'Requerimento de Maria Pais de Jesus, viúva do tenente-coronel João Antunes Maciel, moradora na vila de Sorocaba, distrito da capitania de São Paulo, a D. Jose I pedindo que lhe mandasse entregar os papéis de serviços de seu marido para pedir remuneração dos ditos serviços. Constam dois despachos do conselho ultramarino, o último dos quais indefere o pedido, de 13/07/1799. Contém carta de Joaquim Miguel Lopes de Lavre dizendo que não se deve passar a dita certidão em virtude de os oficiais do conselho não terem nota alguma da entrada dos papeis dos ditos serviços, de 09/07/1753.
INSERT INTO app_hist.provocacao VALUES (391, 'Requerimento do ouvidor do ouvidor da comarca de São Paulo, João de Sousa Filgueiras, ao secretário de estado dos negócios do reino, Sebastião Jose de Carvalho e Melo, informando que após advertir o provedor Jose de Godoi Moreira o mesmo teimava em não ceder, não lhe dando os vencimentos havia três anos. Escreve que o dito provedor se intrometera a coletar dos rendeiros e a pagar a quem lhe parecia, pertencendo isto aos almoxarifes, o que motivava uma desordem na arrecadação da Fazenda real, crescendo a falência dos devedores. Solicita a suspensão do provedor e o pagamento dos seus vencimentos.', 371, '1759-05-04', 1759, 1, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (381, 'Requerimento de dona Maria Paes de Jesus, viúva do tenente-coronel João Antunes Maciel, moradora na vila de Sorocaba, dizendo que se encontra em grande necessidade. Em recompensa dos altos serviços prestados pelo seu falecido marido, vinha pedir a mercê do ofício de tabelião com seus anexos da cidade de São Paulo com quatrocentos mil réis de tença para que por sua morte passasse a seus filhos a alcaidaria-mor da cidade de São Paulo e a mercê de hábito de cristo com cem mil réis para seu filho Domingos Antunes Maciel Paes, a mercê do moço fidalgo com o hábito de cristo, que tendo sido concedida a seu falecido marido não chegara a ter efeito e a passagem de Itapetininga para seu neto João Antunes Maciel, o ofício de tabelião com seus anexos da vila de Sorocaba e de Itu e da mercê do hábito de cristo com cinquenta mil réis de tença para quem casasse com sua neta Maria Antunes Leite. Consta um parecer do conselho ultramarino em que o conselheiro Antonio Freire de Andrade Henriques diz que a supermencionada súplica não seve ser consultada sem dispensa de D. Jose I, de 26/04/1754.', 363, '1754-04-26', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (103, 'Requerimento dos oficiais da câmara de Guaratinguetá a D. João V, solicitando que seja estabelecida uma freguesia paroquial com vigário, a quem se pague pela fazenda real de São Paulo, e que seja concedido privilégios a dita câmara, que não possui remuneração do trabalho.', 113, '1739-12-29', 1739, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (402, 'Requerimento de João de Sousa Filgueiras, ouvidor da comarca de São Paulo, intendente da casa de fundição da mesma comarca, pedindo a D. Jose I que lhe mande passar ordem para fazer executar em tudo o dito regimento daquela casa, enquanto não for determinado o contrário. Consta um despacho do conselho ultramarino, indeferindo o pedido, de 10/10/1754.', 383, '1754-10-10', 1754, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (116, 'Conjunto de requerimentos de Manuel da Silva Reis e do alferes Manual de Álvares Franco, vigário da vara, referente à obras da igreja matriz da vila por conta de seu estado de ruína. Sobre o primeiro requerimento pedindo teor dos capítulos respeitantes às obras recaiu um despacho deferindo-o. Acerca do próximo requerimento em que se aponta acerca de uma concordata sobre a reedificação da igreja, recaiu um despacho, aprovando as obras e reprovando o imposto por cabeça colocado, de 13/01/11771.  Há um requerimento mais novo no qual se dizem que as promessas voluntárias estão longe de atingir a soma necessária para as obras, pedindo, então, por meio de uma provisão régia, lançar uma finta a todos os paroquianos. Sobre este requerimento recaíram um parecer do procurador da fazenda, outro do procurador da coroa e três despachos do conselho ultramarino, o último pedindo parecer do ouvidor da comarca, depois de ouvida a câmara, nobreza e povo, de 22/08/1777. Há um outro requerimento sem data ao corregedor da câmara dizendo que requereram ao vigário capitular para que aprovasse a validasse a concordata, recaindo um despacho de Salvador Pereira da Silva ordenando que requeiram ao rei. ', 125, '1772-07-21', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (119, 'Requerimento de Jose de Araujo e Silva e Julião da Silva a D, Jose I pedindo carta de confirmação de sesmaria na vila de Sorocaba. Consta um parecer do procurador da fazenda ordenando que se faça justiça, um parecer do procurador da coroa ordenando que se faça justiça e três despachos do conselho ultramarino, o último deferindo o requerimento, de 02/04/1773. Contém uma carta de sesmaria passada pelo Luis Antonio de Sousa Botelho Mourão, de 25/10/1776, um bilhete do secretário do conselho ultramarino determinando que se deve passar aos requerentes a carta de confirmação da sesmaria, 20/04/1773, uma certidão do tabelião do público judicial e notas da vila de Sorocaba, de 22/12/1767.', 128, '1772-08-08', 1772, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (127, 'Requerimento de Manuel Teixeira Coelho e dos mordomos do mártir São Manuel pelo qual pedem a D. Jose I que lhes mande passar provisão concedendo-lhes umas terras para nelas erigirem uma capela em honra do dito mártir. Constam dois despachos do conselho ultramarino, o último dos quais indefere o pedido, de 09/12/1772; e um parecer do procurador da fazenda dizendo que o requerimento não está em termos de ser deferido e que os requerentes só poderiam conseguir a graça que pretendem obtendo-a de D. Jose I, sem data definida. Contém um requerimento dos devotos do mártir São Manuel colocado na capela de Nossa Senhora da Luz pedindo aos oficiais da câmara uma certidão da carta de concessão das terras que a câmara deu aos padres jesuítas para nelas construírem um convento na passagem chamada anhagabau de baixo. Sobre este último requerimento recaiu um despacho dos oficiais da câmara mandando passar a certidão pedida, de 08/12/1770. Por fim, contém uma certidão e uma carta de data.
INSERT INTO app_hist.provocacao VALUES (138, 'Representação dos oficiais do senado da câmara de São Paulo a D. João V, pedindo que qualquer pessoa de qualquer qualidade que entrasse naquela cidade com cargos, lugares, ocupações e contratos os registrassem no senado e, se o não fizessem, fossem presos, autuados e ficasse sem efeito o cargo, lugar, ocupação ou contrato, de 30/12/1748. Consta três despachos do conselho ultramarino, o último mandando que se respondesse não parecer conveniente deferir o seu requerimento, de 23/09/1750. Contém uma cópia da provisão de D, João V ao governador da praça de Santos ordenando-lhe que o informasse com o seu parecer, de 14/10/1749; e uma carta do governador da praça de Santos, Luis Antonio de Sa Queiroga a D. João V informando que tal representação "não procurava o bem da república" e por isso se devia escusar, de 25/02/1750.', 143, '1748-12-30', 1748, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (141, 'Requerimento do alferes Francisco João, da companhia do capitão Francisco de Almeida Albernas, da praça de santos, a D. João V pedindo que lhe mandasse passar seu "intertenimento" no posto de alferes, pois sempre serviu com zelo e, naquela ocasião, se achava decrépito. Consta um despacho do conselho ultramarino, mandando que o governador e capitão-general informasse com o seu parecer, o qual não está anexado junto aos documentos. Contém um grande conjunto de documentos, como certidões e requerimentos.', 146, '1750-03-05', 1750, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (366, 'Carta do ouvidor-geral da comarca de São Paulo, Jose Luis de Brito e Melo, a D. Jose I dizendo-lhe que na vila de Guaratinguetá havia um sacerdote que era acusado de maquinar desordens da dita vila. O prelado foi informado da conduta do sacerdote e foi-lhe requerida a saída do padre da vila. Constam três despachos do conselho ultramarino e dois pareceres, um do procurador da fazenda, dizendo que se devia recomendar ao bispo de São Paulo que examinasse o procedimento do referido clérigo; outro do procurador da coroa dizendo que o ouvidor-geral deveria informar novamente. Contém certidão do escrivão atestando que o padre fomentava e era o autor das desordens da dita vila.', 351, '1753-09-03', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (611, 'Requerimento de Francisco Monteiro de Azevedo, provedor e tesoureiro da câmara de Vila Nova da Rainha, ao rei, solicitando a mercê de que sejam consideradas as despesas das referidas oitavas de ouro da rubrica dos livros, necessárias para aposentadoria e cobrir os gastos efetuados durante a correição do sindicante Domingos Nunes Vieiras. Em anexo um requerimento.', 640, '1751-10-05', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (612, 'ULTRAMAR Cópia do regulamento imposto por Gomes Freire de Andrade, governador de Minas Gerais, sobre as normas que devia observar os oficiais das patrulhas do sertão.', 641, '1751-10-12', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (78, 'FABI Requerimento de D. Teresa Jeronima Rosa de Melo e Alvim, viúva de Tome Joaquim da Costa Corte Real, ex-ministro e secretário de estado da repartição da marinha, no qual diz que, pertencendo-lhe a metade do meio direito que pagavam os gados e cavalgaduras vindos do Rio Grande de São Pedro do Sul para São Paulo, fora impedida de continuar a receber os seus rendimentos pelo governador e capitão-general da capitania que São Paulo. Pedia a D. Jose I que ordenasse ao sobredito governador para não se intrometer na administração e cobrança dos meios direitos pertencentes a sua casa, deixando aos procuradores desta  e ao provedor da fazenda a sua arrecadação. O procurador da fazenda respondeu que os documentos apresentados não bastavam para provar o direito da requerente, devendo-se ouvir o governador e aconselhá-lo a evitar sempre o prejuízo da fazenda real. Ao conselho, parece o mesmo que ao procurador da fazenda, de 06/11/1771. Contém documentos em anexo, como certidões e cartas.', 90, '1771-11-06', 1771, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (635, 'Requerimento de Simão da Silva Rabelo, ao rei D. José, solicitando a mercê do Habito da Ordem de Cristo. ', 667, '1755-04-18', 1755, 28, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (445, 'Requerimento de Antonio Jose de Azevedo ao juiz Sebastião Garcia Rocha pedindo que lhe sejam justificadas e comprovadas as duas asserções em um documento: de que é casado com Maria de Lima do Rosario, a qual tem um filho menor do primeiro matrimônio e que tem capacidade de administrar os bens do enteado. Contém documentos anexos. A petição é para um juiz local e não ao Conselho Ultramarino.', 404, '1752-09-05', 1752, 1, '0', 'N', '');
INSERT INTO app_hist.provocacao VALUES (212, 'Requerimento de Inacio Eloi Madureira, governador da praça de Santos e coronel, dizendo que, tendo pedido ajuda de de custo, D. Jose I lhe deferiu, mandando juntar certidão das ajudas de custo dos seus últimos antecessores. Pede que o rei lhe conceda a ajuda de custo e diz que não junta as certidões pedidas pois mais nenhuma pessoa do reino fora para o governo de Santos. Consta um despacho do conselho ultramarino; um parecer do procurador da fazenda, dizendo que o exemplo citado não justificava, mas que D. Jose I devia deferir como entendesse; parecer do conselho ultramarino dizendo que se devia estabelecer ao requerente e seus sucessores a ajuda de custo, de 09/03/1752.', 203, '1752-02-26', 1752, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (467, 'Requerimento de Adriano Machado Ribeiro a D. Jose I solicitando mercê de confirmação de meia légua de sesmaria na paragem chamada Palmital, na freguesia de Congonhas do Campo, no termo da vila de São Jose. Consta 1. cópia da carta de sesmaria passada pelo governador interino da capitania do Rio de Janeiro, Jose Antonio Freire de Andrade, 2. carta do escrivão Antonio de Moraes Sarmento atestando que as ditas terras foram judicialmente demarcadas e 3. bilhete do conselho ultramarino para que o suplicante pague os novos direitos que dever.', 438, '1757-01-04', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (468, 'Requerimento de Ana Maria de Madeiros, viúva de Gaspar da Costa Pacheco, morador da vila de São José, solicitando a D. Jose I provisão para ser tutora de seus filhos e administradora dos bens de seu marido. Consta instrumento de justificação.', 439, '1757-01-04', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (469, 'Requerimento de madre Antonia da Conceição, regente do recolhimento de nossa senhora da conceição dos Macaubas, comarca de Sabará, solicitando a D. Jose I mercê de trazer ermitão para pedir esmolas. Consta instrumento de testemunhas.  ', 440, '1757-01-04', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (549, 'Requerimento do regente e mais recolhidas da ordem de Nossa Senhora da Conceição dos Macaubas da freguesia de Santo Antonio da Roca Grande, comarca de Sabará, solicitando a D. Jose I a mercê de lhes permitir a continuação do peditório de esmolas.', 553, '1757-01-17', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (505, 'Francisco Xavier de Abreu e Mota pede ao rei que ordene ao conselho ultramarino confirmar os dois cargos a qual havia sido nomeado (tabelião e meirinho de campo de Paracatu).  ', 479, '1750-10-29', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (470, 'Requerimento do capitão Antonio Correia de Lacerda a D. Jose I, solicitando a confirmação de três léguas de terra de sesmaria no sertão da freguesia da Aiuruoca. Consta carta de sesmaria concedida pelo governador do Rio de Janeiro e Minas Gerais, Jose Antonio Freire de Andrade.', 441, '1757-01-04', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (641, 'Representação dos oficiais da câmara de São João Del-Rei a D. Jose I informando as desvantagens que terá para os povos das câmaras se os reais quintos do ouro for abolido. Consta termo da junta, treslado de certidão e cópia da portaria em anexo.', 674, '1757-07-09', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (680, 'Requerimento do contratador e administrador do contrato dos dízimos das Minas Gerais, João de Sousa Lisboa a D. Jose I, solicitando providências para o abuso praticado pelos mineiros relativo ao indulto concedido no decreto de 19/02/1752, na execução das suas dívidas pelos credores.', 736, '1758-01-21', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (38, 'Requerimento do padre guardião e mais religiosas do convento de São Francisco da cidade de São Paulo a D. Jose I pedindo que lhes conceda provisão para que possam fazer a procissão do enterro do Senhor na sexta-feira da paixão, privilégio que os religiosos do convento de Nossa Senhora do Monte Carmo queriam reservar para eles. Consta um parecer do procurador da fazenda e dois despachos do conselho ultramarino, o último indefere o pedido, de 14/10/1770. Contém uma certidão do arcediago e outras dignidades do cabido da Sé de São Paulo, de 03/06/1768; uma informação; um requerimento do padre guardião do convento de São Francisco da cidade de São Paulo, ao vigário capitular, pedindo licença para organizar a procissão do enterro do Senhor na sexta-feira, sobre este requerimento recaiu um despacho concedendo a licença pedida apenas por uma vez; certidão do cabido da Sé de São Paulo, de 20/05/1768.', 54, '1770-10-16', 1770, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (466, 'Representação da camara de Mariana ao Rei, queixando-se dos graves prejuizos que sofriam os moradores pela forma como os contratadores cobravam os dízimos.', 437, '1755-05-07', 1755, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (504, 'Ventura de Mota Abreu pede ao rei para que ordene ao conselho ultramarino lhe confirmar os dois cargos a qual recebeu ( o de porteiro e meirinho de campo de Paracatu) ', 478, '1750-10-29', 1750, 5, 'R', '', '1v');
INSERT INTO app_hist.provocacao VALUES (666, 'Requerimento de Domingos Pinheiro, cavaleiro professo na ordem de Cristo, a D. Jose I, solicitando mercê de confirmação no exercício do posto de sargento-mor das Ordenanças de Vila Rica e seu termo. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 722, '1757-08-08', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (570, 'Requerimento de Vicente Freire de Sousa, capitão da ordenança de pé em uma das companhias da cidade de Mariana e morador da barra do Pinheiro, na comarca de Vila Rica, a D. Jose I, solicitando o hábito da ordem de cristo.', 581, '1757-01-24', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (679, 'Requerimento do capitão e contratador João de Sousa Lisboa a D. Jose I solicitando uma certidão (provisão) que conste dos privilégios que sua majestade concedeu aos mineiros nos anos de 1751 e 1752. Consta a referida provisão em anexo.', 735, '1758-01-11', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (678, 'Requerimento de Antonio Jose da Rosa Tenebres, ex-escrivão da ouvidoria geral da vila de São João Del Rei, comarca do Rio das Mortes, a D. Jose I, solicitando ordem para que o mesmo não pague mais do que é de costume para se fazer arrematação à provedoria.', 734, '1758-01-07', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (692, 'Requerimento de Jose dos Santos de Faria, que já serviu ao cargo de secretário de governo das Minas, solicitando ao rei que lhe devolva os antigos documentos do cargo para que o suplicante possa usar em outro requerimento.', 749, '1758-06-21', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (694, 'Requerimento de Alberto Luis Ferreira de Miranda, solicitando ao rei a mercê do habito da ordem de Cristo, com tença, por ter feito entrar na casa de fundição do Sabará mais de nove arrobas de ouro, durante um ano.', 753, '1758-07-28', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (65, 'Requerimento de Maria Barbosa da Silva, viúva do sargento-mor da batalha, Domingos Teixeira de Andrade, solicitando a mercê de serem feitas partilhas dos bens que, com a morte de seu pai, estavam com a mãe, Luisa de Sousa, que havia se casado com o desembargador Manuel Ribeiro de Carvalho, também falecida. Em 26/10/1760, o rei enviou o ministro da Relação do Rio do Janeiro, nomeado-o como Juiz privativo da partilha, para fazer justiça à dita suplicante de forma sumária. Em 05/05/1760, ocorre um registro de consulta da dita provocação no Conselho Ultramarino. Há um parecer do procurador da fazenda, referendado pelo procurador da coroa, favorával ao pedido da suplicante para que o dito ministro proceda de forma sumária à execução do caso, que já levava quatorze anos. ', 77, '1760-05-05', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (695, 'Requerimento do capitão mor Jose de São Boaventura Vieira, tabelião do público judicial e notas da cidade de Mariana, solicitando ao rei cópia da provisão de provimento no seu posto. ', 754, '1758-08-03', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (472, '* Mario  REQUERIMENTO do tenente João de Sequeira, solicitando confirmação de sesmaria de meia légua de terra que possui nas matas de Paraopeba, no termo da Vila de São José. ', 987, '1755-10-14', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (576, 'Requerimento do bacharel Jose de Sousa Monteiro, cavaleiro professo na ordem de cristo e ouvidor do Rio das Mortes a D. Jose I, solicitando o levantamento de sequestro de uma das suas propriedades. Consta 1. certidão do procurador Leandro de Melo Faria e Sousa, ordenando passar o referido levantamento. 2. Requerimento anterior do suplicante, solicitando certidão do desconto que fizeram a seu ordenado, 3. Carta do conselho ultramarino informando que para responder o requerimento é necessário primeiro juntar o termo de fiança que recebeu por empréstimo e que pretende ficar desobrigado. 4. termo de fiança, 5. certidão de nomeação no ofício e de cavaleiro na ordem de cristo (impresso) e 6. recibo do alferes de dragões, Manuel Jose de Brito.', 587, '1757-02-12', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (48, 'Requerimento de Felix da Cunha Nogueira, soldado auxiliar de cavalaria da companhia do capitão Antonio de Sousa Morais, pedindo que se observem os privilégios que D. Jose I concedeu aos infantes e que os oficiais da câmara elejam outra pessoa para vereador. Consta um despacho do governador, de 07/11/1770.', 62, '1770-11-07', 1770, 1, 'S', 'N', '');
INSERT INTO app_hist.provocacao VALUES (60, 'Requerimento de Pedro Taques de Almeida Pais Leme à câmara de São Paulo pedindo certidão com o teor de uma sentença dada na Bahia a favor de Manuel Rodrigues de Morais, procurador do conde de Monsanto D. Alvaro Pires de Castro e Sousa, a respeito das cinquenta léguas que o referido conde tinha na costa do sul. Consta um despacho da referida câmara mandando passar a certidão, de 04/05/1771. Contém a certidão pedida e cinco reconhecimentos. 
INSERT INTO app_hist.provocacao VALUES (87, 'Requerimento do frei Manuel da Ressureição, bispo de São Paulo, a D. Maria I pedindo-lhe licença para nomear as dignidades, conezias, igrejas, benefícios e mais cargos eclesiásticos no seu bispado. O documento é uma consulta da mesa de consciência e ordens. Contém, ainda, um alvará de D. Jose I concedendo ao frei Manuel da Ressureição permissão de prover dignidades, conezias, vigararias e mais benefícios e cargos eclesiásticos no seu bispado, com a condição de as ditas nomeações não recaírem em indivíduos incultos e de raça impura, de 08/01/1751. O traslado em pública-forma é de Lisboa, de 07/04/1778.', 97, '1771-11-29', 1771, 1, 'R', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (566, 'Representação dos oficiais da câmara da vila de Pitangui informando o rei sobre a difícil situação econômica em que se encontram as Minas e solicitando providências para aliviar os miseráveis povos da capitania da excessiva e onerosa carga tributária que recai sobre o ouro extraído. Os oficiais ressaltam o sofrimento da população, que suporta o fardo dos tributos, mencionando que os pobres com grande dificuldade, arcam com esses pagamentos em lágrimas.', 577, '1751-08-30', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (682, 'Requerimento do padre Manuel da Silva Araujo, morador da freguesia de Nossa Senhora da Conceição, a D. Jose I, solicitando confirmação de meia légua de terra em quadra da sesmaria entre o rio Turvo e o rio Peixe, na cidade de Mariana. Consta carta da sesmaria concedida pelo governador Gomes Freire de Andrade.', 738, '1758-01-27', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (573, 'Requerimento de Francisco Dias, negociante e morador da cidade do Rio de Janeiro a D. Jose I, solicitando o pagamento de seu dinheiro pela provedoria das Minas, de que era devedor Manuel Martins Machado, assistente na Vila Rica do Ouro Preto, e seu sócio Jose.', 584, '1757-02-05', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (541, 'Requerimento de Manuel Nunes da Silva Tojal e Jose Ferreira da Veiga, caixas na corte do 2º contrato dos diamantes do Serro do Frio, solicitando ao rei que, tendo João Ferreira de Oliveira arrematado o contrato por quatro anos, seja ordenado ao tesoureiro do conselho ultramarino que tome a fiança necessária para o 3º pagamento do contrato. Isso permitirá que recebam a 3ª partida dos diamantes do referido contrato, que se encontra na casa da moeda em poder do tesoureiro.', 545, '1751-06-25', 1751, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (626, 'Requerimento de Felisberto Caldeira Brant, contratador dos diamantes e caixa no Serro do Frio, solicitando ao rei a mercê de lhe conceder a arrematação do novo contrato, referente à futura arrematação para o período de 12 anos. No pedido, Felisberto oferece pagar o valor correspondente a cada ano de forma adiantada, expressando seu desejo de continuar servindo e relatando sua experiência e as dificuldades enfrentadas no exercício da função de contratador.', 656, '1751-11-09', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (681, 'Requerimento de Gomes Freire de Andrade para o secretário de Estado da Marinha e Ultramar, Tome Joaquim da Costa Corte Real, solicitando providências para a falta de solimão na capitania, que podia resultar em prejuízos para a Fazenda Real e desordens entre os povos.', 737, '1757-08-15', 1757, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (715, 'Requerimento do tenente de Dragões, Antonio Pinto Carneiro a D. Jose I, solicitando a mercê da patente de capitão de Dragões, que se acha vaga na companhia de Minas Gerais. ', 781, '1759-02-07', 1759, 5, 'R', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (693, 'Requerimento de Andre Luis Barbosa Brandão, escrivão da câmara e almotaçaria da vila de São Jose, comarca do Rio das Mortes, solicitando ao rei que fossem mantidos os emolumentos que tinha e que lhes eram negados pelo corregedor. Consta parecer negando o dito pedido.', 751, '1758-07-12', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (697, 'Requerimento do capitão Lourenço Gomes de Macedo, morador da vila do Caeté e casado com D. Eugenia Clara do Sacramento, solicitando que se passe provisão para que consiga ir ao Reino prestar assistência na graduação de seus filhos na universidade de Coimbra.', 757, '1758-09-18', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (617, 'ULTRAMAR?
INSERT INTO app_hist.provocacao VALUES (584, 'Requerimento do alferes Leandro Machado Luis e seus sócios, Manuel Pereira Basto e João Gonçalves Linhares, solicitando ao rei a mercê de lhes confirmar a doação em sesmaria de meia légua de terra em quadra, nas cabeceiras do Onca, freguesia de São João Batista do Morro Grande e paragem no Tanque, termo da Vila Nova da Rainha. Em anexo, encontram-se um bilhete de ordem de confirmação, uma carta e um requerimento.', 595, '1751-09-09', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (118, 'Requerimento de Antonio Barbosa Pereira, capitão-mor da vila de Ubatuba, pedindo confirmação da carta patente para este posto concedida por Luis Antonio de Sousa Mourão. Consta um despacho do conselho ultramarino, indeferindo-o, de 30/07/1772. Contém carta patente concedida pelo governador e capitão-general da capitania de São Paulo provendo o requerente ao posto citado e um termo de posse e juramento dado ao Antonio Barbosa Pereira, de 07/01/1770.', 127, '1772-07-30', 1772, 1, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (667, '***Requerimento do escrivão da Fazenda Real, Contos e matrícula de gente de guerra da capitania de Minas Gerais, Constantino da Costa Leite, a D. Jose I, solicitando o pagamento de 714.573 réis pela provedoria, referente a salários do seu ofício relativos ao treslado de leis, regimentos e alvarás feito pelo suplicante. Consta parecer positivo do provedor da fazenda, do governador de Minas Gerais e do Conselho Ultramarino.', 723, '1757-08-09', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (571, 'Requerimento de Antonio Luis Souto Maior a D. Jose I solicitando o provimento no ofício de secretário de Estado da capitania de Vila Rica do Ouro Preto.', 582, '1757-01-31', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (542, 'ULTRAMAR Certidão passada por Francisco Ferreira de Azevedo, escrivão da intendência e da provedoria da fazenda de Vila Real, a respeito de uma petição de João da Fonseca da Cruz, que exercerá o cargo de intendente e provedor da fazenda da referida vila.', 546, '1751-06-25', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (429, 'RESPOSTA Cópia da provisão de D. Jose para o conde de Atouguia, vice-rei do Brasil, ordenando que suspendesse sua decisão escrita ao secretário de Estado, Marco Antonio de Azevedo Coutinho, referente a uma passagem no Rio Peroasiu, na Vila da Cachoeira, que havia sido incorporada nos bens da coroa, cobrando os direitos devidos. Em anexo, uma cópia da câmara da dita vila, em que ponderam os inconvenientes de se mandar fabricar uma ponte no rio. Além disso, encontram-se vários documentos em anexo.', 408, '1751-03-27', 1751, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (624, 'Requerimento do arcediago, dignidades e conegos da Sé da cidade de Mariana a D. Jose I, solicitando provisão de mantimento para se continuar o pagamento da congrua por tempo de um ano após a morte de cada um deles.', 653, '1757-04-26', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (578, 'Requerimento de Antonio Pereira da Rocha, ajudante da supra das ordenanças de pé de Vila Rica, a D. Jose I, solicitando sua confirmação no exercício do dito ofício. Consta carta patente do governador interino de Minas Gerais, Jose Antonio Freire de Andrade, atestando a nomeação do suplicante. ', 589, '1757-02-24', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (579, 'Requerimento de Antonio Amaro de Sousa Coutinho, secretário de governo de Minas Gerais, a D. Jose I, solicitando a mercê da restituição do dinheiro desembolsado referente ao pagamento do aluguel das casas, o qual estava sob fiança.', 590, '1757-02-25', 1757, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (580, 'Representação dos moradores da Freguesia de Nossa Senhora da Conceição de Antonio Dias, em Vila Rica, solicitando ao rei a mercê de ordenar a extinção de uma das duas imagens de sua igreja. Pois não possuem condições financeiras para realizar duas festas em menos de três meses dedicadas ao mesmo santo, além da perturbação que causa, pois alguns devotos se inclinam mais para uma imagem do que para a outra. Em anexo uma representação ', 591, '1751-09-03', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (583, 'Requerimento de Manuel Martins Nogueira e Jose Gonçalves Lima, moradores da comarca do Rio das Mortes, solicitando ao rei a mercê de lhes conceder licença para se deslocarem ao Reino com suas respectivas famílias.', 594, '1751-09-09', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (625, 'Requerimento de Luis Correa de Sousa, oficial barbeiro e sangrador da cidade de Mariana, solicitando ao rei a mercê de lhe conceder licença para se deslocar para o reino com sua família', 655, '1751-11-06', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (683, 'Requerimento de Claudio Manuel da Costa, natural de Minas Gerais e funcionário da Casa de Fundição e da Casa da Moeda, a D. Jose I, solicitando alguma premiação, concedida por mercê, pelos bons serviços que tem prestado no referido cargo.', 739, '1758-01-30', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (657, 'Requerimento de Sebastião da Cunha Coutinho Rangel, morador da vila de São Salvador da Paraiba do sul, a D. Jose I, solicitando confirmação de sesmaria de uma légua de terra na barra do rio Muriaé. Consta bilhete do conselho ultramarino e carta da sesmaria concedida pelo governador Antonio Freire de Andrade.', 713, '1757-07-08', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (700, 'Requerimento de Manuel Ferreira Torres solicitando o suprimento de idade a fim de querer a sua emancipação para obter a sua herança.', 760, '1755-09-09', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (699, 'Requerimento de Padre Henrique Pereira vigario da igreja de nossa senhora do bom sucesso da vila nova da rainha do caeté pedindo ajuda pecuniaria para reparar capela mor', 759, '1755-10-15', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (698, 'Requerimento de Antonio Manuel, cessionario de Diogo Pereira, solicitando confirmação de meia légua de sesmaria em quadra em Pedra Menina, termo da Vila de São José, Rio das Mortes. Consta carta de Jose Antonio Freire de Andrade concedendo a dita terra ao suplicante.', 758, '1758-09-27', 1758, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (737, 'maisa Representação dos oficiais da cidade de Mariana informando a solicitação do atual procurador do senado de Mariana sobre a passagem de uma certidão da forma como se costumavam dar as posses de sesmaria nas Minas Gerais. Informam o prejuízo que os moradores da capitania tiveram com a atual forma de conceção. Consta solicitação do parecer do governador de Minas Gerais e Rio de Janeiro.', 815, '1759-11-14', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (738, 'maisa duvida Requerimento do frei João de Santa Ana para Jacinto Jose Vicente da Silva solicitando a colcha que já havia pedido anteriormente.', 818, '1759-11-15', 1759, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (743, '***Requerimento do Antonio Jose de Moura solicitando a confirmação da carta de sesmaria de meia légua de terra em quadra, nos matos virgens do território da vila de São Jose, comarca do Rio das Mortes. Consta a referida carta em anexo, concedida pelo governador Jose Antonio Freire de Andrade.	', 823, '1759-11-19', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (749, '***Requerimento de Pascoal Alves Rodrigues, morador da comarca do Rio das Mortes, solicitando confirmação de sesmaria de meia légua de terra em quadra na vila de São João Del Rei. Consta a referida carta de sesmaria concedida pelo governador de Minas Gerais.', 829, '1759-11-26', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (745, 'maisa Requerimento do capitão Domingos da Silva Ferreira, fazendeiro e moedeiro das Casas de Fundição e Moeda em Cachoeira, no distrito de Gouveia, termo da vila do Príncipe do Serro Frio, solicitando a prorrogação de seu exercício naquele posto e aumento de seu soldo.', 825, '1759-11-22', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (746, '***Representação dos oficiais da câmara de Vila Rica queixando-se dos prejuízos sofridos nos concertos das calçadas, e solicitando a real atenção nesse caso.', 826, '1759-11-22', 1759, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (747, 'maisa Requerimento do capitão Silvestre Vieira de Macedo e Sousa, solicitando confirmação da carta de sesmaria de meia légua de terra em quadra, no porto de Canoas, freguesia de São José da Barra Longa, território da cidade de Mariana. Consta a referida carta de sesmaria em anexo, concedida pelo governador da capitania de Minas Gerais.', 827, '1759-11-23', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (750, 'maisa Requerimento de Antonio Dias Teixeira das Neves, solicitando a confirmação no posto de capitão mor da vila de Pitangui, comarca de Sabará. Consta carta patente em anexo, concedida pelo governador de Minas Gerais.', 830, '1759-11-27', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (716, 'Requerimento de Leandro Marçalino, meirinho em Vila Rica, solicitando a D. Jose I para passar provisão para o pagamento de seus ordenados atrasados. O conselho ultramarino informa que deve-se ouvir os oficiais da câmara da cidade de Mariana e informar ao chanceler da Relação do Rio de Janeiro. ', 782, '1759-02-23', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (742, '***Requerimento do assistente Antonio Lopes da Silva solicitando a confirmação da carta de sesmaria de meia légua de terra em quadra, no distrito da capela do Barroso, freguesia da Borda do Campo, comarca do Rio das Mortes. Consta a referida carta em anexo, concedida pelo governador Jose Antonio Freire de Andrade.', 822, '1759-11-19', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (753, '***Requerimento de Andre Vieira Cardoso de Macedo, capitão da segunda companhia da ordenança de pé da Vila do Príncipe, solicitando a confirmação do seu exercício no referido posto. Consta carta patente em anexo, concedida pelo governador Jose Antonio Freire de Andrade.', 833, '1759-12-05', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (165, 'Requerimento do dragão e seleiro de tropa, Jose Vicente Lima, a D. Jose I, solicitando que se tire devassa da contenda entre ele e Bras Seixas de Brito, cavalheiro do hábito de Cristo, que havia tentado o matar diversas vezes, utilizando-se de seu escravo e um mulato forro que tinha em sua casa, de nome Felipe, e armas de fogo. Além disso teria usado violência contra o suplicante, deixando seu rosto deformado, sem ter castigo algum, pois era protegido pelo ouvidor Manuel Antunes da Fonseca. Consta em anexo outro requerimento do mesmo suplicante, solicitando a mercê de restituição do seu real serviço e uma certidão para comprovar o tempo que serviu ao reino. Contém ainda um bilhete do conselheiro Diogo de Mendonça Corte Real solicitando tirar consulta dos referidos requerimentos.', 169, '1753-04-09', 1753, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (140, 'Queixa dos oficiais da câmara da vila de Paranaguá contra o ouvidor geral da comarca, o bacharel Antonio Pires da Silva e Melo Porto Carneiro, remetida a D. Jose I, relatando os abusos do ouvidor contra o povo da comarca. Solicitam providências para o sossego dos moradores da vila de Paranaguá. Consta parecer do conselho ultramarino, informando que se já houvesse acabado o tempo do ouvidor, que tirasse residência e nomeasse outra pessoa nesse cargo.', 145, '1752-04-15', 1752, 1, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (714, '***Requerimento de Francisco Ferreira da Silva, contratador dos caminhos das Minas Gerais, solicitando provisão para propor um libelo no juízo dos feitos da fazenda contra o procurador da Fazenda da repartição do Conselho Ultramarino.', 780, '1759-02-06', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (754, '***Requerimento de Francisco Alves do Espírito Santo solicitando a confirmação da carta de sesmaria de meia légua de terra em quadra, na paragem junto ao ribeirão chamado Taguaçu, freguesia de Piranga, território da cidade de Mariana. Consta a referida carta em anexo, concedida pelo governador Jose Antonio Freire de Andrade, onde informa que o suplicante já estava com suficiente fábrica de escravos para cultivar fazendas.', 834, '1759-12-05', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (731, 'Representação dos oficiais da câmara da Vila Real de Nossa Senhora do Sabará, a respeito da queixa do governador contra a certidão de receitas e despesas da câmara e esclarecendo o referido caso por uma certidão.', 805, '1759-09-21', 1759, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (751, 'maisa Requerimento do governador de Minas Gerais, Jose Antonio Freire de Andrade, informando que os soldados de Dragões necessitam de fardas.', 831, '1759-11-28', 1759, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (857, '*isa Requerimento de Manuel Antunes Nogueira, capitão da ordenança de pé da vila de São João del-Rei, na comarca do Rio das Mortes, solicitando a confirmação de sua nomeação para o referido posto.
INSERT INTO app_hist.provocacao VALUES (744, 'maisa Requerimento de Antonio Alves, capitão de infantaria da Ordenança do arraial do Bom sucesso do Rio Verde, solicitando a mercê da sua confirmação no referido posto conferida pelo Conde dos Arcos. ', 824, '1759-11-20', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (741, 'maisa Requerimento do padre Boaventura Ferreira Coutinho, vigário encomendado na freguesia de Nossa Senhora da Conceição dos Raposos, solicitando a mercê do pagamento de sua congrua. Consta parecer do provedor da fazenda e solicitação do parecer do bispo de Minas Gerais.', 821, '1759-11-19', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (740, 'maisa duvida Requerimento do juiz e mais oficiais da Irmandade e Matriz de Nossa Senhora do Pilar de Vila Rica do Ouro Preto, solicitando a conceção da despesa das festas anuais do Corpo de Deus, Santa Isabel e Anjo Custódio a câmara da dita vila.', 820, '1759-11-19', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (739, 'maisa Requerimento do ouvidor da comarca de Vila Rica, Jose Pio Ferreira Souto, solicitando levar propinas nas procissões e festividades que a câmara fez na dita vila. Consta parecer informando que ouvidores não devem entrar nas propinas, que só são permitidas aos oficiais das câmaras.', 819, '1759-11-17', 1759, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (717, '***Representação dos oficiais da câmara da vila Nova da Rainha do Caeté, se posicionando contra a abolição do método de cobrar os reais quintos e solicitando a continuidade do sistema anterior para poderem controlar o descaminho do ouro e a concessão da graça e doação de privilégios. O conselho ultramarino solicita o parecer do governador da capitania e do ouvidor da comarca.', 783, '1759-04-07', 1759, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (720, '***Representação dos oficiais da câmara de Vila Rica a D. Jose I, solicitando providências para o estado decadente e deplorável que se encontra os moradores da capitania de Minas Gerais.', 795, '1759-05-22', 1759, 5, 'R', '', '');
