INSERT INTO app_hist.provocacao VALUES (825, 'maisa Requerimento do padre Antonio Freire da Costa, solicitando o pagamento de congruas vencidas na igreja de Nossa Senhora da Conceição de Antonio Pereira, no bispado de Mariana e na igreja de Nossa Senhora da Conceição de Guarapiranga.', 913, '1760-05-29', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (845, 'Requerimento de D. Rosa Maria de Araujo Coutinho, viúva de Jose Furtado de Mendonça, sargento mor e vereador da câmara de Ribeirão do Carmo, e de seus filhos, Miguel Ferreira da Silva e Bento Fernandes Furtado, solicitando a mercê de esmola de lhes perdoar a parte que lhes toca da reposição que se lhes mandou fazer de sete mil cruzados para a obra da cadeia. O pedido foi escusado.', 934, '1760-08-23', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (865, '*Requerimento de Jeronimo de Sa Vilhena, presbítero do hábito de São Pedro, provido na Vigararia das Congonhas do Sabará, na igreja de Nossa Senhora do Pilar, solicitando provisão de mantimento para cobrar a sua côngrua', 958, '1752-02-04', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (847, 'maisa Requerimento de Francisco Pinto Rodrigues, morador no caminho novo que leva a Goiás, solicitando a concessão de licença para estabelecer um engenho de aguardente de cana de açúcar. Consta atestação junta do Senado da câmara da Vila de São José.', 937, '1760-08-26', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (849, 'maisa duvida Requerimento do licenciado Jose Pereira dos Santos, cirurgião da câmara de Vila Rica, solicitando a concessão de alguns subsídios para poder melhorar as condições de saúde dos presos e enfermos, que trata com seu próprio dinheiro.', 940, '1760-09-09', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (834, '*maisa Requerimento de Anacleto Pereira Coelho, capitão-mor da ordenança de pé da vila de Nossa Senhora do Bom Sucesso, comarca do Serro do Frio, solicitando a confirmação no exercício do referido posto. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 922, '1760-07-27', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (836, 'maisa Requerimento de Manuel de Sanches, morador da comarca do Rio das Mortes, solicitando a confirmação de carta de sesmaria de meia légua de terra em quadra, situada na beira do rio Pará. Consta a referida carta de sesmaria concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 924, '1760-07-29', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (828, 'maisa Requerimento de D. Antonia do Espirito Santo Gouveia, viúva de Antonio de Sousa Rocha e moradora do Rio Verde, solicitando a mercê de ser confirmada como tutora de sua filha. Consta instrumento de justificação.', 916, '1760-06-16', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (827, 'maisa duvida Requerimento de Antonio Carlos Pereira, tabelião do público judicial e notas da cidade de Mariana, solicitando a mercê de juntar a inquirição da genere ao processo de encarte do ofício de tabelião como filho primogênito do desembargador Fernando Luis Pereira. Consta certidão afirmando que o suplicante é quem diz ser pois conhece seus pais e avós, que é cristão velho limpo, sem nenhuma outra raça, e que não se sabe de nenhum rumor ou má fama associado a ele. O documento foi assinado pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 915, '1760-06-14', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (830, 'maisa Requerimento de Alexandre Nunes Cardoso, chantre da sé de Mariana, solicitando a mercê de lhe ser passada cópia da provisão pela qual lhe foi concedida congrua.', 918, '1760-06-27', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (831, 'maisa Requerimento de Domingos de Sa Rodrigues, alferes da companhia da ordenança de pé do Caquende até a Bocaína, termo de Vila Rica do Ouro Preto, solicitando a promoção no posto de capitão da referida companhia.', 919, '1760-07-08', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (832, 'maisa Requerimento de Felipe da Costa Neves e seu sócio, solicitando confirmação de carta de sesmaria de meia légua de terra em quadra na paragem de Piracicaba. Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 920, '1760-07-17', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (829, 'maisa Requerimento do sargento-mor Bernardo Figueira da Cunha, morador da comarca do Serro do Frio, solicitando licença para levantar e retificar seu engenho de cana de açúcar. O suplicante alega que seu sítio já possuí plantação de cana, e que gostaria de utiliza-la para o pagamento de suas dívidas. Além disso, pede para retificar o que já possuí afim de ocupar seus escravos. ', 917, '1760-06-18', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (835, 'maisa Requerimento de Baltasar Martins Chaves, capitão da companhia da ordenança de pé da freguesia do Inficionado, cidade de Mariana, solicitando a confirmação no exercício do referido posto. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.	', 923, '1760-07-29', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (833, 'maisa Requerimento de Antonio Pereira Coutinho e Vasconcelos, vigário da igreja de São Miguel de Piracicaba, bispado de Mariana, solicitando o pagamento da congrua vencida de dois anos.', 921, '1760-07-19', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (837, 'maisa Requerimento de Tome Soares de Brito, morador da cidade de Mariana, solicitando a confirmação de carta de sesmaria de meia légua de terra em quadra, situada próximo ao Rio do Peixe. Consta a referida carta de sesmaria concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.	', 925, '1760-07-29', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (838, 'maisa Requerimento de Manuel Pereira da Silva, capitão da companhia de ordenança da cavalaria da freguesia de Guarapiranga, cidade de Mariana, solicitando a mercê de sua confirmação no exercício do referido posto. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 926, '1760-08-01', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (839, 'maisa Requerimento de Tome Soares de Brito, capitão da ordenança de pé do distrito do Gama, cidade de Mariana, solicitando a mercê de confirmação no exercício do referido posto, que se encontrava vago após o falecimento de Manuel Cardoso de Mendonça. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 927, '1760-08-01', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (840, 'maisa Requerimento de Francisco Teixeira de Carvalho, solicitando confirmação de carta de sesmaria de meia légua de terra em quadra em um Ribeirão que faz barra no rio de São Félix da capitania de Goiás. Consta a referida carta de sesmaria concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 928, '1760-08-06', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (846, 'maisa Requerimento de Agostinho Alves Mesquita e de seu irmão e sócio, Antonio Jose de Mesquita, assistentes na comarca do Serro do Frio, solicitando a mercê de levantarem um engenho para fabricar açúcar. O pedido é deferido.', 936, '1760-08-25', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (892, '* Mario  Requerimento  do tenente João de Sequeira, solicitando confirmação de sesmaria de meia légua de terra que possui nas matas de Paraopeba, no termo da Vila de São José. ', 987, '1755-10-14', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (859, 'Requerimento do provedor e dos irmãos da santa casa de misericórdia de Vila Rica, solicitando o privilégio de obter autorização para pedir esmolas em benefício do hospital da instituição. O pedido justifica-se pelas despesas com o tratamento de enfermos e a assistência prestada aos doentes.', 952, '1752-01-25', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (861, 'Requerimento do padre Manuel da Fonseca Neves, provido na igreja de Santo Antão da Mata, solicitando a mercê de um alvará de mantimento.', 954, '1752-01-26', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (862, 'PASSAR PARA CONSULTA isa Consulta do conselho ultramarino sobre a conta de 28/07/1751, apresentada por Gomes Freire de Andrade, governador e capitão-general do Rio de Janeiro, referente à preocupação com a possível escassez de água-forte para as casas de fundição.
INSERT INTO app_hist.provocacao VALUES (866, 'isa Provisão de D. Jose I, ordenando ao ouvidor da Comarca do Sabará que informe, com seu parecer, sobre o pedido de João Fernandes de Oliveira em conservar suas fazendas na posse em que tem estado, sem prejuízo do público.', 959, '1752-02-04', 1752, 5, '0', '', '');
INSERT INTO app_hist.provocacao VALUES (871, '*Daniel- Requerimento dos irmãos da confraria da Nossa Senhora da Gloria do arraial da passagem, cidade de Mariana, pedindo permissão para pedir esmolas em caixinhas por todas as Minas para a reconstrução da capela em devoção a dita Senhora.
INSERT INTO app_hist.provocacao VALUES (848, 'maisa Requerimento de D. Teresa Maria de Jesus, viúva do capitão mor Jose de São Boaventura Vieira, solicitando a entrega das arrematações que se fizeram serventia do ofício de escrivão dos defuntos, capelas e resíduos da cidade de Mariana, que pertencia a seu marido, conforme demonstra requerimento junto por ele feito. Consta outro requerimento em anexo, onde a suplicante pede licença para voltar ao Reino com suas filhas menores, D. Ana e D. Josefa.', 938, '1760-08-27', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (761, '*Requerimento do capitão Domingos Correia Gomes, contratador dos dízimos reais nas Minas Gerais, informando que movia um processo no tribunal contra João Freire dos Santos, capitão-mor em Vila Rica, e que por óbito desse o processo seguiu com sua mulher, D. Maria Isabel de Bitencourt e seus filhos. Solicita a D. Jose I passar provisão para remeter ao tribunal da suplicação os autos, com os agravos interpostos. Para que assim tomem conhecimento de sua causa e o dispersem de suas cobranças, que já chegam ao prejuízo de seis mil cruzados.', 842, '1759-12-11', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (886, 'Requerimento de Francisco Barreto Bicudo, pedindo confirmação de meia légua de terra que possui na freguesia de São José da Barra.  Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade. (1753)', 981, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (888, 'Requerimento de Andre Ramalho Avelino, solicitando confirmação de sesmaria de meia légua de terra que possui no sítio chamado Rio do Peixe, Cidade de Mariana. Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade. (1751) Foi concedido por 6 meses.', 983, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (904, 'maisa duvida no relacionamento Representação dos oficiais Vitoriano de Freitas da Cunha, Felix Caetano Pimentel Coelho, Mauricio Jose de Abreu, Manuel da Ponte Pedreira e Francisco Xavier Dorta Tejo com os soldados de dragões da capitania de Mato Grosso a Diogo de Mendonça Corte Real, solicitando que seus soldos sejam pagos de mil réis por oitava de ouro desde o dia em que assentaram praça. ', 1000, '1751-06-21', 1751, 27, 'S', 'Y', '');
INSERT INTO app_hist.provocacao VALUES (858, 'Requerimento de João Botelho de Carvalho que ordene ao juiz dos órfãos da cidade de Mariana e ao ouvidor de Vila Rica que se abstenham de nomear novos tutores para seus sobrinhos — Januario, Marçal, Antonio, Jose, Aleixo e Manuel — filhos de seu irmão, Antonio Botelho de Sampaio. O requerente também pede que os produtos da venda dos bens pertencentes aos referidos menores sejam remetidos ao juízo dos órfãos do Reino.', 951, '1752-01-19', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (860, 'Requerimento de Pedro Dias Paes Leme, fidalgo da casa real e guarda-mor das Minas, solicitando a anexação de uma sentença proferida pelo juízo das justificações do Reino ao seu processo. O pedido visa habilitá-lo à remuneração a que julga ter direito, em razão de seus próprios serviços e dos de seus ascendentes, bem como ao cumprimento das mercês concedidas a seu pai, Garcia Rodrigues Paes.', 953, '1752-01-26', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (887, 'Requerimento de Antonio Alvares Gondim e de Luis Alvares Gondim, moradores na freguesia de Furquim, no termo de Mariana, solicitando confirmação de sesmaria de meia légua de terra que possuem nas margens do “Piranga”. Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade. (1753)', 982, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (793, 'Requerimento de Estanislau de Jesus Maria, capelão de Vila Rica, para o Conde da Bobadela, Gomes Freire de Andrade, solicitando ordens expressas para poder fazer o peditório para as obras do convento da Bahia, o qual lhe tinha sido ordenado por ordem régia.', 879, '1760-02-11', 1760, 5, 'S', '', '');
INSERT INTO app_hist.provocacao VALUES (890, 'Requerimento de Jose da Silva Zuzarte, morador nas Minas de Guarapiranga, pedindo confirmação de sesmaria de umas terras que possui junto as margens do rio Guarapiranga, Cidade de Mariana. Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade. (1754)', 985, '1755-10-24', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (885, 'Requerimento de Francisco Soares de Araujo, morador na cidade de Mariana, pedindo confirmação de sesmaria de meia légua de terra que possui ao rio do Peixe, na freguesia de São José da Barra.  Consta a referida carta concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade. (1753)', 980, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (896, 'Requerimento de João da Rocha Sobreira, morador na freguesia de São José da Barra Longa termo de Mariana, pedindo confirmação de sesmaria de meia légua de terra que possui no córrego chamado Bom Sucesso. Consta a referida carta concedida pelo governador de Minas Gerais e o termo de demarcação. Deferido', 991, '1755-10-27', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (898, 'Requerimento de Antonio Alvares de Carvalho Domingos e outros, moradores na freguesia de Guarapiranga, termo de Mariana, solicitando confirmação de sesmaria da terra que possuem no ribeirão da Pimenta.. Consta a referida carta concedida pelo governador de Minas Gerais e o termo de demarcação. Deferido', 993, '1755-10-07', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (905, 'Requerimento de Inacia Joaquina de Almeida, moradora em São João del Rei, comarca do Rio das Mortes, viúva de Antonio Ribeiro do Casal, solicitando provisão para ficar como tutora de seus filhos. Consta instrumento de justificação do juizado de órfãos. Deferido', 1001, '1755-10-15', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (907, '*Requerimento dos pretos da Irmandade de Nossa Senhora do Rosario das Minas de Pirapetinga, sita no arraial do Bacalhau, filial de Guarapiranga, pedindo permissão para continuarem a pedir esmolas a fim de poderem concluir e aparamentar a sua capela. Pedido foi escusado.', 1003, '1755-10-24', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (908, 'Requerimento de Caetano da Costa Matoso, ouvidor de Vila Rica, pedindo para que lhe seja pago pela Câmara da cidade de Mariana o que lhe devem pelos serviços prestados em correição.  Faz referência a uma petição anterior com o mesmo teor que foi indeferida. A atual foi deferida.', 1004, '1755-10-29', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (897, 'Requerimento de Antonio da Costa Santos, morador na freguesia de São José da Barra Longa, termo da cidade de Mariana, solicitando confirmação de sesmaria das terras que possui no sítio chamado Quebra Canoas, compradas a João Francisco Pimenta. Consta a referida carta concedida pelo governador de Minas Gerais e o termo de demarcação. Deferido', 992, '1755-10-29', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (870, '*Requerimento do capitão Antonio de Pinho de Azevedo solicitando o posto de capitão-mor das minas de Cuiabá com soldo e os ofícios de escrivão da ouvidoria, câmara, órfãos e tabelião para seus quatro filhos, com hábito de cristo para cada um deles com a tença de 50 mil réis. Consta certidão de batismo e dos serviços que o suplicante prestou a coroa desde 1730.', 966, '1750-07-10', 1750, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (869, 'Requerimento do padre Jose dos Anjos, religioso da reforma de São Francisco da província da Conceição do Rio de Janeiro, solicitando licença para fundar uma missão no campo do Pareci ou no rio Aporé. Consta parecer do conselho ultramarino informando que, se o que o suplicante diz é verdade e não há nenhum missionário jesuíta para acudir os índios da região, o pedido deve ser deferido.', 965, '1750-04-29', 1750, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (906, 'REQUERIMENTO do juiz e mais irmãos da Confraria de Nossa Senhora do Rosario dos Pretos da Barra de Vila Real do Sabará, pedindo provisão para serem conservados na posse de vinte e cinco braças de terra que lhes haviam sido concedidas pelos oficiais da Câmara da referida Vila em 1713. Está em causa o fato dos camaristas quererem cobrar os foros de que estavam isentos até então. Contém Consulta do Conselho Ultramarino favorável aos suplicantes', 1002, '1755-09-20', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (903, 'Não me parece que seja uma petição. 
INSERT INTO app_hist.provocacao VALUES (735, 'Requerimento de Caetano da Costa Matoso ao rei no qual pede a mercê de poder citar a câmara de Mariana que lhe deve dinheiro.', 813, '1755-09-17', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (856, '*Requerimento de Bernardo da Fonseca Lobo, capitão-mor da vila do Príncipe, na comarca do Serro do Frio, e cavaleiro professo na ordem de cristo, solicitando autorização para lavrar ouro em suas terras. O suplicante alega possuir essas terras há muitos anos, por sesmaria concedida pelo então general das Minas, Gomes Freire de Andrade, e afirma que foi comprovada a inexistência de diamantes na região. Anexa-se ao requerimento um aviso para que o rei consulte o que parecer.', 949, '1752-01-07', 1752, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (899, 'maisa Requerimento de João de Sousa de Azevedo a D. João V, solicitando mercê do foro de fidalgo e o hábito de Cristo, juntamente com tença correspondente. Pede também uma possibilidade de ser provido em um cargo na ouvidoria ou no tabelionato de Mato Grosso.', 994, '1751-04-20', 1751, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (615, 'Cópia da representação dos oficiais da câmara da cidade de Mariana, informando ao rei sobre a vexação sofrida pelos moradores da cidade em relação às capitações com o ouro quintado, ressaltando que é considerado violento o cumprimento do edital que determina tal satisfação. Em anexo quatro documentos.', 644, '1751-10-27', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (438, 'Representação da câmara de Mariana ao rei sobre a opressão causada pela lei da capitação. Nela, tecem considerações sobre a referida lei, que estabelece a cobrança dos direitos e autoriza a livre circulação do ouro em pó, em folha ou em barra, dentro e fora de Minas Gerais. Relatam que o toque do ouro só se conhece fundido, e depois não pode circular em pó; além disso, ninguém deve levar o ouro das Minas para fora sem ser quintado. Para evitar o descaminho do ouro em pó, sugerem que não haja diminuição do valor dentro do território e que não se possa comprar ou vender coisa alguma fiada.', 417, '1751-05-04', 1751, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (873, '*Mariana Requerimento dos moradores do novo arraial de são gonçalo de Catas Altas de Itaperava solicitando ao rei que os irmãos das irmandades de são gonçalo e de nossa senhora do rosário dos homens pretos possam pedir esmolas em toda a Capitania para que possam manter a igreja e o arraial é muito pobre. Escusado', 969, '1755-10-14', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (730, 'Requerimento de Antonio Francisco Pereira, segundo tabelião da vila de São Jose, solicitando a D. Jose I a compensação do salário de um ano e meio, como foi declarado pelo decreto de 15/10/1754.', 804, '1759-09-12', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (878, 'Requerimento do bacharel Francisco Xavier dos Guimarães Brito e Costa, intendente e provedor da fazenda real da vila de Cuiabá, solicitando licença para se casar por procuração com D. Ana Francisca Joaquina de Morais Lustosa, filha legítima do defunto Antonio Francisco Lustosa, que foi coronel e provedor da fazenda real, e de sua mulher D. Maria de Madureira Morais. Consta consulta do conselho ultramarino.', 973, '1750-08-21', 1750, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (874, '*Mariana Requerimento de Jose de Lima de Noronha Lobo, capitão da ordenança de Pé do distrito de Ibituruna pedindo sua confirmação no referido posto.', 970, '1755-10-09', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (876, '*Bruno  Requerimento de Domingos Moreira Granja, capitão da ordenança de pé da capela de nossa senhora de brotas de Brumado, freguesia de Congonhas do Campo pedindo que anexe ao seu pedido de confirmação do posto o Capítulo 1º do Regimento dos governadores, conforme solicitado.', 971, '1755-10-03', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (877, '*Bruno Requerimento dos homens crioulos pretos e mestiços moradores das Vilas do Real do Sabará, Vila Rica do Ouro Preto, São José e São João do Rio das Mortes, e Serro do Frio pedindo um procurador que os proteja das violências de que são vítimas', 972, '1755-10-14', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (850, 'maisa Requerimento de Felisberto Caldeira Brandão, capitão de cavalos da companhia do arraial de São Luís e Santa Ana de novo descoberto do Paracatu do Sabará, solicitando a confirmação no exercício do referido posto. Consta parecer do conselho ultramarino, ordenando passar a confirmação se não houver nenhum inconveniente.', 941, '1760-09-25', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (844, 'maisa Requerimento do desembargador Domingos Nunes Vieira, intendente da comarca de Sabará, solicitando certidão de que não faltou ao cumprimento das ordens do Tribunal do Fisco Real, para se juntar a sua residência.', 933, '1760-08-22', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (842, 'maisa Requerimento de Giraldo Jose de Abranches, arcediago da Sé de Mariana, solicitando a mercê do pagamento de sua congrua, vencida desde o dia 25/08/1759, pela provedoria da fazenda real de Vila Rica.', 930, '1760-08-09', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (826, 'maisa Requerimento de Domingos Moreira Granja, capitão da companhia de ordenança de pé do distrito da capela de Nossa Senhora das Brotas do Brumado, solicitando a confirmação no exercício do referido posto. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 914, '1760-06-10', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (796, 'maisa Requerimento do capitão Domingos Gomes Leitão, morador da Vila do Príncipe e da comarca Serro do Frio há 40 anos, solicitando licença para regressar ao reino com sua mulher, D. Francisca Rondam do Espirito Santo e seis escravos.', 882, '1760-02-15', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (795, 'maisa Requerimento de Francisco Nery Bravo, morador da vila de São João Del Rei, comarca do Rio das Mortes, e capitão da ordenança de pé do distrito da Lagoa Verde, solicitando a confirmação no exercício do referido posto, que vagou por falecimento do capitão Luis Marques das Neves. Consta carta patente concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade.', 881, '1760-02-13', 1760, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (791, 'maisa Requerimento do desembargador Francisco Jose Pinto de Mendonça, intendente dos diamantes do Serro Frio, solicitando o pagamento de seu ordenado do tempo em que serviu como ouvidor na comarca do Rio das Mortes.', 877, '1760-02-05', 1760, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (748, 'maisa Requerimento do alferes Domingos Vaz de Carvalho, procurador da câmara de Vila Rica, solicitando uma certidão dos outros de Manuel Alvares contra Jeronimo Rodrigues de Macedo. Em anexo consta a referida certidão assinada.', 828, '1759-11-24', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (732, 'maisa Requerimento de Antonio Carlos Pereira, filho legítimo e primo de Fernando Luis Pereira, solicitando a mercê da propriedade do ofício de tabelião do público judicial e notas da vila do Carmo.', 809, '1759-11-07', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (665, 'maisa Pedido de confirmação da sesmaria concedida pelo governador de Minas Gerais, Jose Antonio Freire de Andrade, a João da Cunha Rodrigues, morador de Paraopeba, termo da Vila de São José e comarca do Rio das Mortes. A meia légua de terra em quadra seria na referida região. Consta certidão do escrivão da receita e despesa da intendência da comarca do Rio das Mortes, Vicente Ferreira da Silva confirmando a demarcação das ditas terras. ', 721, '1757-08-05', 1757, 5, 'R', '', '');
INSERT INTO app_hist.provocacao VALUES (880, 'maisa Requerimento do vigário colado da freguesia de Nossa Senhora da Conceição de Vila Rica, Felix Simões de Tavora Figueiredo, solicitando ser provido na prelazia de Goiás, ou, na falta desta, na da vila de Cuiabá.', 975, '1751-01-09', 1751, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (729, 'Requerimento do capitão Valerio Xavier de Araujo, preso na cadeia da vila de Nossa Senhora de Nazare, ( ?)  solicitando a D. Jose I certidão da ordem pela qual foi preso passada pelo ouvidor geral e pelo procurador da dita vila.', 803, '1759-08-04', 1759, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (879, 'maisa Requerimento do tabelião do judicial e notas da vila de Cuiabá, Jose Calado de Lima, provido na serventia de escrivão da correição e ouvidoria, solicitando restituição dos novos direitos que pagou já que não serviu no dito ofício. O pedido foi escusado.', 974, '1750-12-06', 1750, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (881, '*Bruno Requerimento de Diogo da Silva Guimarães, morador na Comarca do Serro do Frio, pedindo permissão para levantar um engenho de moer cana. ', 976, '1755-10-23', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (882, '*Daniel Requerimento de João da Silva Mendes pedindo provisão para que os oficiais da câmara da cidade Mariana paguem o ordenado que venceu como escrivão na fatura dos tombos das propriedades dentro dos limites da sesmaria da dita câmara.', 977, '1755-10-31', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (883, '*Diogo TEMA Requerimento de Antonia Maria de Jesus, moradora na cidade do Porto, pedindo provisão para António Correia da Rosa, ser citado como testamenteiro do defunto capitão mor Rafael da Silva e Sousa falecido nas Minas de Paracatu. Foi deferido', 978, '1755-10-22', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (884, 'maisa Requerimento do juiz de fora de Itu, bacharel Teotonio da Silva Gusmão, nomeado para Mato Grosso, solicitando que seja graduado o lugar que vai criar com o título de ouvidoria, com o mesmo ordenando e aposentadoria que tem a da vila de Cuiabá. Pede também que administre as ocupações de intendente e provedor real com propina anual, ajuda de custo e que vença seu ordenado desde o dia do embarque em Itu. Consta parecer positivo do conselho ultramarino.', 979, '1751-01-21', 1751, 27, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (891, 'Requerimento de Jose Alves da Costa, morador na comarca do Rio das Mortes, solicitando provisão para ter mais tempo para seguir com a sua apelação contra a concessão de uma sesmaria.', 996, '1755-09-29', 1755, 5, 'R', 'N', '');
INSERT INTO app_hist.provocacao VALUES (663, 'Requerimento de Antonio Machado de Azevedo a D. Jose I, solicitando confirmação no exercício do posto de capitão da Ordenança de Pé do distrito do Morro de Passagem, cidade de Mariana. Consta carta patente do governador de Minas Gerais, Jose Antonio Freire de Andrade, concedendo o referido posto ao suplicante.', 719, '1757-07-21', 1757, 5, 'R', '', '');
