INSERT INTO app_hist.resposta VALUES (524, '1763-07-06', 'isa Comunica a Antonio Rolim de Moura Tavares, governador de Mato Grosso e aos oficiais da câmara de Vila Bela, ter feito mercê do governo da capitania a João Pedro da Camara.', '', NULL, 37, 1763, 28, '', '', '80');
INSERT INTO app_hist.resposta VALUES (1324, '1757-03-07', 'maisa Decreto de D. Jose I provendo Custodio Dias no posto de escrivão do meirinho de campo da comarca de Sabará. Consta recibo do escrivão Antonio de Melo e Sousa, informando que deve-se passar provisão ao suplicante.	', '', NULL, 5, 1757, 627, '', '', '');
INSERT INTO app_hist.resposta VALUES (455, NULL, 'Informe sobre o registro de uma carta que trata da remessa do alvará de 21 de fevereiro de 1765. Registrada no Livro do Rio de Janeiro f128.', '', NULL, 44, 1799, 24, '', '', '79v');
INSERT INTO app_hist.resposta VALUES (776, '1765-01-17', 'Informa ao provedor da fazenda de Mato Grosso, que a ordenação nunca concedeu privilégios aos juízes ordinários, os quais não devem estar isentos de pagar o que devem. Visto declaração de 17/08/1761, que por ocorrência de requerimento do contrato dos dizimos, passará mandado contra os devedores, incorporando quem servia de juiz ordinario não guardando privilégio da ordenação. ', '', NULL, 37, 1765, 28, '', '', '89v-90');
INSERT INTO app_hist.resposta VALUES (815, '1779-10-28', 'isa tema execução de cobrança ou herança?
INSERT INTO app_hist.resposta VALUES (781, '1760-08-22', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais da representação que fizeram os oficiais da câmara de Vila Rica 09/07/1757 e também a representação dos oficiais da câmara da Vila Real de Nossa Senhora da Conceição do Sabará em carta de 11/?/1757 onde ambos requerem a permição para estabelecimento de uma aldeia para os índios mansos para rebater os insultos dos negros salteadores. Ordena informar seu parecer ouvindo os oficiais da câmara de seu distrito e apontando os meios para realizar tal estabelecimento. ', '', NULL, 40, 1760, 20, '', '', '65');
INSERT INTO app_hist.resposta VALUES (782, '1760-08-21', '(Théo) Remete cópia aos oficiais da câmara da Vila do Príncipe de sua representação de 30/11/1756 a respeito do vexame que experimentaram os novos moradores da comarca nas execuções que a eles se mandam fazer pela provedoria da fazenda real de Vila Rica ao requerimento dos contratadores.  Pela ordem inclusa explica como deve ser feitas as diligências nas câmaras do Brasil. ', '', NULL, 40, 1760, 20, '', '', '65-65v ');
INSERT INTO app_hist.resposta VALUES (783, '1760-08-21', '(Théo) Remete ao provedor da fazenda real das Minas Gerais a representação dos oficiais da câmara da Vila do Príncipe em carta de 30/11/1756 a respeito do vexame experimentado pelos moradores daquela comarca devido devido ao fato dos contratadores obrigarem estes a pagarem mantimentos pelos preços desejado. Ordena informar seu parecer nessa matéria.  ', '', NULL, 40, 1760, 20, '', '', '65v ');
INSERT INTO app_hist.resposta VALUES (784, '1760-08-07', '(Théo) Remete cópia ao conde de Bobadela capitão general do Rio de Janeiro com o governo das Minas Gerais da conta que deu o governador interino das Minas Gerais, Jose Antonio Freire de Andrade, em carta de 26/07/1758 sobre a representação que fez o intendente do Sabará pedindo por um capitão da ordenança no Ribeirão de São Pedro, distrito de arraial de Paracatu. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '65v-66 ');
INSERT INTO app_hist.resposta VALUES (785, '1760-08-07', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerias da representação dos oficiais da câmara da Vila de São José em carta de 09/07/1757 pedindo pelo estabelecimento de uma aldeia de gentios domésticos em cada comarca dessa capitânia como meio de combater os negros escravos fugitivos aquilombados. Ordena informar seu parecer. ', '', NULL, 40, 1760, 20, '', '', '66');
INSERT INTO app_hist.resposta VALUES (786, '1760-08-23', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerias da representação dos oficiais da câmara da Vila do Príncipe em carta de 15/11/1755 onde pedem dar a providência o que me parecer mais justa para o que as juntas ordinárias daquela comarca. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '66-66v ');
INSERT INTO app_hist.resposta VALUES (787, '1760-08-20', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais representação da câmara da Vila Real do Sabará em carta de 11/07/1757 pedindo para que os povos daquela vila e comarca não paguem pelo capim e sustento dos cavalos. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '66v ');
INSERT INTO app_hist.resposta VALUES (788, '1760-08-20', '(Théo) Remete cópia ao bispo de Mariana, Manuel Ferreira Freire da Cruz, representação dos oficiais da câmara da Vila Rica em carta de 19/07/1757 sobre o excessivo direito pachorrial de meia pataca de ouro que os parrochos desse bispado. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '66v ');
INSERT INTO app_hist.resposta VALUES (789, '1760-08-07', '(Théo) Remete cópia ao bispo de Mariana, Manuel Ferreira Freire da Cruz, representação dos oficiais da câmara da vila de São José em  carta de 09/07/1757 acerca dos emolumentos das juntas. Ordena informar seu parecer dando procedimento para se evitar queixas semelhantes. ', '', NULL, 40, 1760, 20, '', '', '67 ');
INSERT INTO app_hist.resposta VALUES (790, '1760-08-25', '(Théo) Remete cópia aos oficiais da câmara da cidade de Mariana de sua carta de 22/05/1759 onde pedem mandar a providência necessária para diversos assuntos e requerimentos dessa comarca. Ordena que devido a diversidade dos assuntos, os envie em cartas separadas.', '', NULL, 40, 1760, 20, '', '', '67 ');
INSERT INTO app_hist.resposta VALUES (643, '1767-03-10', 'isa Ordem para que Luís Antônio de Sousa Mourão, governador de São Paulo, apresente um parecer em resposta ao exposto pelo provedor da fazenda da mesma capitania em 13/02/1766, referente ao que se pratica na assistência das quatro companhias de paulistas aventureiros.', '', NULL, 37, 1767, 28, '', '', '103v');
INSERT INTO app_hist.resposta VALUES (816, '1779-07-09', 'isa 
INSERT INTO app_hist.resposta VALUES (793, '1778-11-09', 'isa  Informa à junta da fazenda de Goiás e a Martim Lopes Lobo de Saldanha, governador de São Paulo, que conforme o comunicado da referida junta em 16/10/1777, em virtude do falecimento de Bartolomeu Bueno da Silva, o direito das passagens de vários rios concedido a ele foi incorporado à real fazenda, tanto nesta capitania quanto em São Paulo. Diante do constrangimento causado pela junta de São Paulo ao obrigar os negociantes, com emprego de força militar, a pagarem diretamente pelas passagens, recomenda-se ao governador retirar o feitor e o guarda para evitar tal obrigação, enquanto a junta de Goiás deve encarregar-se do registro e cobrança.
INSERT INTO app_hist.resposta VALUES (794, '1778-11-03', 'isa Ordem para que Martim Lopes Lobo de Saldanha, governador de São Paulo, remata um parecer, em resposta ao comunicado do juiz de fora de Santos, datado de 20/12/1777, que, chegando a ter que fazer os pelouros da cidade, publicou um edital. Contudo, ao chegar o momento de remeter os oficios da justiça, não devia remeter sem a confirmação dos ouvidores, porém a junta, concedeu o ofício de tabelião a Inacio Xavier de Almeida.', '', NULL, 37, 1778, 28, '', '', '134-135');
INSERT INTO app_hist.resposta VALUES (795, '1778-11-09', 'isa Ordem para que Martim Lopes Lobo de Saldanha, governador de São Paulo, providencie a verba referente ao posto de capitão de ordenança do bairro de Piratininga, em conformidade com a baixa concedida a (Francisco Antonio não consegui ler o ultimo nome), conforme determinado pela ordem de 21/01/1775, e que apresentou o governador em 17/01/1778.', '', NULL, 37, 1778, 28, '', '', '137');
INSERT INTO app_hist.resposta VALUES (796, '1739-08-18', '[LUIZ] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que registre nas cabeças das comarcas e declare aos ouvidores que nos casos sentenciados em juntas, não podem os ditos sentenciarem por si só. Devendo remeter ao conselho ultramarino as certidões dos seus procedimentos. Em função da desordem na tomada de providências contra os crimes cometidos pelos negros, mulatos e carijós. Uma vez que se frustram as sentenças devido aos ministros apelarem a Relação do Estado, não chegando nunca na execução das sentenças ou dos castigos pelos delitos cometidos. Visto que os acusados fogem das cadeias ou padecem nelas pela dificuldade da remessa dos casos.', '', NULL, 40, 1739, 19, '', '', '235');
INSERT INTO app_hist.resposta VALUES (797, '1739-06-20', '[LUIZ] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que informe com o seu parecer. Em função da carta remetida em 26/10/1738 pelos oficiais da câmara de Vila Rica, a respeito do pedido de serem servidos dos mesmos privilégios que gozam os cidadãos da cidade do Porto ou de São Paulo.', '', NULL, 40, 1739, 19, '', '', '235');
INSERT INTO app_hist.resposta VALUES (1558, '1751-02-12', 'IZA - Provisão de aposentaria concedida ao Domingos (Dr?) Francisco Xavier Pereira Brandão, nomeado juiz do crime da cidade da Bahia.', '', NULL, 39, 1751, 32, '', '', '33v-34');
INSERT INTO app_hist.resposta VALUES (799, '1739-08-18', '[LUIZ. Dúvida quanto ao tema e palavra-chave] Ordem, após escutar o procurador da coroa, ao ouvidor geral da comarca do Ouro Preto [Vila Rica], para que a representação dos oficiais de Vila Rica, a qual foi escusada, seja registrada nos livros da câmara de Vila Rica, devendo remeter certidão quando assim executar a ordem. Em função da carta enviada em 16/01/1737 pelos oficiais da câmara de Vila Rica, em que solicitavam a confirmação do ordenado de duzentos mil réis ao alcaide. Uma vez que não tinham como contribuir ou sustentar, pois o ocuparam neste serviço de sorte que não tinha lugar algum para meirinhar com mais oficiais.', '', NULL, 40, 1739, 19, '', '', '235v');
INSERT INTO app_hist.resposta VALUES (285, '1757-03-04', 'Aprova a ação apresentada por Alvaro Jose Xavier Botelho de Tavora, governador de Goiás, em 30/03/1756, por resolução de 26/02/1757, tomada em consulta no conselho ultramarino, em relação aos 06 indígenas e 25 crianças conduzidos à vila por João Godoi da Silva, capitão-mor da conquista do gentio caiapó. O governador distribuiu os indígenas mais jovens para serem criados nas casas de mulheres de leite, enquanto um bando na vila convidava os que desejassem cuidar de um indígena caiapó por um período de dois anos, instruindo-os na fé e na língua local. Estando previsto que anualmente prestem contas sobre esses indivíduos na secretaria do governo.
INSERT INTO app_hist.resposta VALUES (1557, '1751-02-13', 'IZA - Provisão para vender ordenado desde o dia do embarque concedida ao Domingos  (Dr?) Francisco Xavier Pereira Brandão, nomeado juiz do crime da cidade da Bahia.', '', NULL, 39, 1751, 32, '', '', '33v');
INSERT INTO app_hist.resposta VALUES (1219, '1761-10-27', 'LUIZ - Ordem de Francisco Xavier de Mendonça Furtado, secretário de estado, a João Manuel de Melo, governador de Goiás, para que: 1) Remeta o padre Manuel Dias e o vigário da vara, João Lopes França, ao bispo do Rio de Janeiro “com a participação urbana, e não judicial das desordens que tem feito”; 2) Os intime que estão proibidos de entrarem em qualquer comarca “onde presentemente há, ou de futuro houver terras minerais”. Devendo “participar a todos os governadores do estado do Brasil” desta proibição. Em função das informações passadas por João Manuel de Melo em carta de 29/05/1760.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 32v-33.');
INSERT INTO app_hist.resposta VALUES (791, '1778-02-11', 'isa Informa a Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, que pelo que apresentou em 03/02/1777, que falecendo com testamento Antonio Gonçalves dos Santos que se achava em correição na vila do Cuiabá, o procurador da companhia do Grão-Pará e Maranhão, fez sequestro de todos os bens, porém ponderaram a inventário com pretexto da lei que da privilégio, a feitores ou administradores que falecem, onde não podem tomar conta de seus livros juiz dos ausentes ou orfãos. Contudo, ressalta-se que a arrecadação dos bens deveria ter sido conduzida pela provedoria dos resíduos, uma vez que o privilégio da companhia se estendia somente a pessoas determinadas.', '', NULL, 37, 1778, 28, '', '', '128-129v');
INSERT INTO app_hist.resposta VALUES (800, '1739-09-20', '[LUIZ] Ordem, após escutar o procurador da fazenda e a resolução de 09/07/1739 em consulta, ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, escusando o requerimento do bacharel Manuel Dias Torres, intendente da fazenda real da comarca do Sabará. Em função da carta remetida em 30/08/1738 pelo requerente, a respeito do seu pedido em mandar um sucessor para intendência – onde embarcou do Reino em 24/03/1736 –, por achar a ocupação alheia a sua profissão e devido às diligências que realizou pelos sertões,  rompendo sua saúde, reputação e vida, na tentativa de: 1) diminuir as desobediências nos motins realizados; 2) aplicar a cobrança da capitação e; 3) arrecadar os bens dos culpados.', '', NULL, 40, 1739, 19, '', '', '235v-236');
INSERT INTO app_hist.resposta VALUES (689, '1771-06-29', 'isa Informa aos oficiais da câmara de Vila Bela e a Luis Pinto de Sousa Coutinho, governador de Mato Grosso, ter feito mercê do governo da capitania a Luis de Albuquerque de Melo Pereira e Caceres.', '', NULL, 37, 1771, 28, '', '', '113v-114');
INSERT INTO app_hist.resposta VALUES (459, NULL, 'Informe do registro de uma carta sobre a numeração dos ofícios. Registrada no Livro do Rio de Janeiro f163.', '', NULL, 44, 1799, 24, '', '', '79v');
INSERT INTO app_hist.resposta VALUES (1349, '1751-01-08', '***Provisão de licença para resgatar escravos em Benguela por uma vez concedida a Joaquim Jose, que enviaria a embarcação do Rio de Janeiro. 
INSERT INTO app_hist.resposta VALUES (802, '1739-09-22', '[LUIZ] Ordem, após escutar o procurador da coroa, ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que não altere a resolução tomada acerca do lançamento das fintas sobre o povo da câmara de Vila Rica para o pagamento dos capitães do mato. Visto que devem se contentar com o prêmio dado pelos senhores dos escravos fugidos e, quanto as fugas dos negros, cabem as milícias e paisanos contê-los, escusando-se, dessa forma, a contribuição lançada sobre o povo. Em função da carta remetida em 29/11/1738 pelo governador, Gomes Freire de Andrade, a respeito da ordem que lhe foi mandada quanto às fintas impostas aos moradores pela câmara de Vila Rica. Estando os capitães do mato obrigados, pelos governadores ou ministros, a realizarem as diligências somente com os emolumentos e as recompensas dadas pelos senhores dos negros.', '', NULL, 40, 1739, 19, '', '', '236-236v');
INSERT INTO app_hist.resposta VALUES (803, '1739-09-23', '[LUIZ. Dúvidas quanto ao tema e palavra-chave] Ordem, após resolução de 09/1739 em consulta, ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que dê o seu parecer sobre as utilidades que podem resultar da abertura de um caminho, por terra, para as minas de Cuiabá. Em função das duas representações enviadas pelo mestre de campo, Manuel Dias da Silva, sobre essa matéria.', '', NULL, 40, 1739, 19, '', '', '236v');
INSERT INTO app_hist.resposta VALUES (804, '1778-11-09', 'isa
INSERT INTO app_hist.resposta VALUES (1582, '1758-10-11', '(LUIZ) Ordem a Francisco de Atouguia Bitencourt, ouvidor de Goiás, para que tire a residência de seu antecessor, Antonio da Cunha Souto Maior e remeta os autos a sua “real presença por mão de Tome Joaquim da Costa Corte Real”.', '', NULL, 43, 1758, 21, '', '', 'Numeração 2. Fl. 4-4v');
INSERT INTO app_hist.resposta VALUES (805, '1778-11-09', 'Informa à junta da fazenda de Goiás que, por resolução de 05/08/1778, tomada em consulta no conselho ultramarino, foi ordenado abolir e cessar o privilégio que Jose de Almeida Vasconcelos Soveral e Carvalho, governador da mesma capitania solicitou em 16/10/1777, relativo ao pedido de graça de Joaquim Pereira Gaya? Pessanha para estabelecer uma fábrica de sola, considerando que na junta sua jurisdição não era capaz de conceder a graça.', '', NULL, 37, 1778, 28, '', '', '137v-138');
INSERT INTO app_hist.resposta VALUES (807, '1779-04-29', 'isa 
INSERT INTO app_hist.resposta VALUES (808, '1779-05-27', 'isa  Informa ao ouvidor de Mato Grosso, que servia de provedor dos ausentes e resíduos, pelo que apresentou em 03/02/1777, que falecendo com testamento Antonio Gonçalves dos Santos que se achava em correição na vila do Cuiabá, o procurador da companhia do Grão-Pará e Maranhão, fez sequestro de todos os bens, porém ponderaram a inventário com pretexto da lei que da privilégio, a feitores ou administradores que falecem, onde não podem tomar conta de seus livros juiz dos ausentes ou orfãos. Contudo, ressalta-se que a arrecadação dos bens deveria ter sido conduzida pela provedoria dos resíduos, uma vez que o privilégio da companhia se estendia somente a pessoas determinadas.', '', NULL, 37, 1779, 28, '', '', '139v-140v');
INSERT INTO app_hist.resposta VALUES (451, '1798-10-21', 'Informe sobre o registro de uma carta com data de 21/10/1798 para o governador do Mato Grosso, Caetano Pinto de Miranda. Registrada no livro do Rio de Janeiro f61.', '', NULL, 44, 1798, 24, '', '', '74');
INSERT INTO app_hist.resposta VALUES (810, '1779-06-18', 'isa
INSERT INTO app_hist.resposta VALUES (811, '1780-11-26', 'isa Ordem para que (barão de mossâmedes não sei qual é, crio com este nome mesmo)  remeta ao conselho o requerimento do bacharel Pedro Bernardino de Sousa Brandão, em que solicita mercê do lugar de intendente da real casa de fundição de São Félix. E sugere ainda que seria justo suprimir aos governadores e capitães-generais a autoridade de nomear os intendentes para a casa, formando duas câmaras na capitania.', '', NULL, 37, 1780, 28, '', '', '141v-142');
INSERT INTO app_hist.resposta VALUES (813, '1779-07-08', 'isa Ordem para que Joao de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, remeta um parecer em resposta à queixa dos oficiais da câmara da vila do Cuiabá, datada de 26/03/1778, sobre as bárbaras nações de gentio bororo e caiapó, constituido de agressores, resultando em mortes e roubos excessivos. E com isso, os moradores encontram-se reduzidos a pobreza, carecendo de tranquilidade e segurança em suas fazendas.
INSERT INTO app_hist.resposta VALUES (814, '1779-07-20', 'isa 
INSERT INTO app_hist.resposta VALUES (532, '1765-12-19', 'isa Ordem ao provedor da fazenda real da praça de Santos para que informe o estado da obra de reedificação da igreja matriz, a qual os oficiais da câmara da vila de Taubaté solicitaram ajuda financeira para sua conclusão em 08/05/1762. Além disso, é solicitado que o provedor informe o que mais necessita e a despesa.
INSERT INTO app_hist.resposta VALUES (283, '1757-03-03', 'Ordem ao provedor da fazenda de Goiás, por resolução de 23/06/1756 tomada em consulta no conselho ultramarino, e por um aviso emitido pelo secretário de estado Diogo de Mendonça Corte Real em 30/06, que, para evitar prejuízos enfrentados pela real fazenda devido às demoras na cobrança das dívidas, é necessário dar conta anualmente ao conselho das dívidas pendentes, sua importância e o estado das execuções, bem como uma lista dos devedores.', '', NULL, 37, 1757, 28, '', '', '42');
INSERT INTO app_hist.resposta VALUES (812, '1779-07-20', 'isaInforma a Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, que procedeu corretamente em punir um delito tão grave, que informou em 25/07/1778, de terem matado Luis de Azevedo de Sampaio, ex-ouvidor da capitania. Destaca-se também o mal exemplo na devassa, que são mal feitas as desordens do dito defunto.', '', NULL, 37, 1779, 28, '', '', '142-142v');
INSERT INTO app_hist.resposta VALUES (818, '1779-12-24', 'isa 
INSERT INTO app_hist.resposta VALUES (820, '1779-06-18', 'isa Ordem para que Martim Lopes Lobo de Saldanha, governador de São Paulo, remetaum  parecer e informe se continua a pratica que apresentou Luis Antonio de Sousa Mourão, ex-governador da capitania em 04/03/1769, de considerar conveniente colocar na presença real que os oficiais públicos, rendiam mais para a fazenda real, sendo arrematados na junta que se mandou criar na cidade, em comparação com a do Rio de Janeiro, onde costumavam ir realizar as arrematações.', '', NULL, 37, 1779, 28, '', '', '145-145v');
INSERT INTO app_hist.resposta VALUES (821, '1779-06-17', 'isa Ordem para que (Manuel da Ressurreição não consegui colocar o oficio na hora de criar estava dando erro), bispo de São Paulo, envie um parecer sobre o incômodo dos moradores da freguesia de Araraitaguaba, apresentado em 04/03/1765, pelos oficiais da câmara de Itu, referente ao pagamento anual de meia pataca de conhecença a seu pároco por cada pessoa.', '', NULL, 37, 1779, 28, '', '', '145v');
INSERT INTO app_hist.resposta VALUES (822, '1760-08-23', '(Théo) Remete cópia ao conde de bobadela capitão general do Rio de Janeiro com o governo das Minas Gerais da representação dos oficiais da câmara da Vila Real do Sabará em carta de 20/11/1754 onde pedem que mande declarar em lei que cada descoberta de córrego, ribeirão ou morro de capacidade de 20 datas sejam dadas ao descobridor a quarta parte de quarenta datas até cem oitavas com preferência na agoada principal do descoberto. Vendo-se o que apresentou o conde dos arcos, vice rei e capitão general de mar e terra dos Estado do Brasil em carta de 07/05/1756 ordena que forme um parecer ouvindo por escrito as quatro câmaras das comarcas dessa capitânia, e seus respectivos capitães mor. ', '', NULL, 40, 1760, 20, '', '', '67-67v ');
INSERT INTO app_hist.resposta VALUES (824, '1760-08-20', '(Théo) Remete cópia ao capitão general do Rio de Janeiro com o governo das Minas Gerais da representação que fizeram os oficiais da câmara da cidade de Mariana em carta de 14/11/1759 onde abordam a utilidade de se inventarem novos descobertos nos dilatados dos sertões que se acham incógnitos em todas as comarcas correndo as câmaras de cada comarca onde os exploradores recebem ajuda de custo. Ordena que informe seu parecer ouvindo as câmaras do distrito das Minas.', '', NULL, 40, 1760, 20, '', '', '67v ');
INSERT INTO app_hist.resposta VALUES (825, '1760-08-07', '(Théo) Remete cópia aos oficiais da câmara de Vila Nova da Rainha de sua representação em 03/03/1755 sobre a necessidade da criação de um juiz de fora nessa vila. Explica que não parece conveniente aceitar esse requerimento no momento. ', '', NULL, 40, 1760, 20, '', '', '68 ');
INSERT INTO app_hist.resposta VALUES (823, '1779-06-18', 'isa 
INSERT INTO app_hist.resposta VALUES (819, '1779-07-24', 'isa Ordem para que Manuel da Ressurreição, bispo de São Paulo, informe sobre o estado atual da dependência, considerando o que foi informado pelo ex-vigário capitular, Manuel de Jesus Pereira, em 11/12/1766, sobre a consternação causada pela falta de pagamento dos clérigos destinados a atender às aldeias. Além disso, é ordenado a Martim Lopes Lobo de Saldanha, governador de São Paulo que assista as congruas que o bispo anterior havia arbitrado pagar, pelo rendimento dos bens dos jesuitas.', '', NULL, 37, 1779, 28, '', '', '144v-145; 146-146v');
INSERT INTO app_hist.resposta VALUES (826, '1779-03-27', 'isa Informa aos oficiais da câmara de Cuiabá que foi ordenado a Manuel da Ressurreição, bispo de São Paulo, que obrigue os clérigos assistentes a participarem das procissões e estabelecerem sua assistência sem cobrar emolumento algum, visando evitar as calamidades públicas. Isso ocorreu em resposta à representação dos mencionados oficiais em 18/02/1764, na qual observaram que, em conformidade com as ordens reais, as procissões e festividades com missa cantada eram realizadas, mas em algumas ocasiões os eclesiásticos demonstravam pouco zelo e devoção, chegando a não comparecer na igreja matriz.', '', NULL, 37, 1779, 28, '', '', '148v-149');
INSERT INTO app_hist.resposta VALUES (829, '1780-10-20', 'isa 
INSERT INTO app_hist.resposta VALUES (950, '1806-06-06', 'Rayssa. Licença concedida a  Antonio Jose Rodrigues, sargento mor com exercício de engenheiro, para se recolher ao reino.', '', NULL, 44, 1806, 24, '', '', '102');
INSERT INTO app_hist.resposta VALUES (837, '1782-08-03', 'isa Informa a Luis da Cunha Meneses, governador de Goiás e aos oficias da câmara da mesma capitania, ter feito mercê do governo da capitania a Tristão da Cunha Meneses.', '', NULL, 37, 1782, 28, '', '', '158v');
INSERT INTO app_hist.resposta VALUES (325, '1795-10-28', 'É remetido a petição de Jose Zeferino Monteiro de Mendonça, professor régio de gramática latina, ao governador  de Mato Grosso, Caetano Pinto de Miranda, para que informe interpondo o seu parecer. ', '', NULL, 44, 1795, 24, '', '', '67v');
INSERT INTO app_hist.resposta VALUES (830, '1760-08-27', '(Théo) Remete cópia ao intendente da comarca do Sabará, Jose Gomes de Araujo, de sua carta de 14/03/1759 onde aborda a sua ordem de tirar o ouvidor João Tavares de Abreu e que também havia feito uma devasse dos oficiais da câmara dos que o serviram e servirão o intendente. Aborda que devido ao número de testemunhas é impossível completar o processo no período de 30 dias. Ordena que tire as testemunhas referidas e no que se refere a livrar os oficiais culpados nas residências, não  inove nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '68-68v ');
INSERT INTO app_hist.resposta VALUES (831, '1760-08-28', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com as Minas Gerais sobre a representação que fez o governador interino das Minas Gerais, Jose Antonio Freire de Andrade, em carta de 06/07/1757 onde representa pedido dos oficiais da câmara de Pitangueira que requerem a criação de uma companhia das ordenanças em um arraial dessa vila. Por resolução de 02/07/1758 tomada em conformidade com o conselho ultramarino permite a criação da dita companhia. ', '', NULL, 40, 1760, 20, '', '', '68v-69 ');
INSERT INTO app_hist.resposta VALUES (925, '1803-06-13', 'Rayssa. Relação dos despachos com que foi servido remunerar os oficiais que distinguiram na defesa da capitania de Mato Grosso
INSERT INTO app_hist.resposta VALUES (832, '1760-08-28', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais da representação que fez o governador interino das Minas Gerais, Jose Antonio Freire de Andrade, em carta de 25/04/1757 onde aborda que em maio do ano antecedente recebeu um requerimento dos oficiais da câmara da cidade de Mariana onde pediam para que criasse uma companhia da ordenança no distrito dos porcos. Por resolução de 17/08/1757 em conformidade com o conselho ultramarino, ordena que dê procedimento a criação da companhia.', '', NULL, 40, 1760, 20, '', '', '69 ');
INSERT INTO app_hist.resposta VALUES (833, '1782-02-10', 'isa Ordem para que o provedor e intendente de Mato Grosso,  envie um parecer e especifique a quantidade de livros e folhas necessários anualmente. Isso decorre do que apresentou em 09/12/1769, onde foi mencionado que os livros foram impressos para as entradas pelos registros, indicando que não são necessários os livros de papel em branco para o ouro das fundições, mas são essenciais os livros utilizados pela provedoria da fazenda real.', '', NULL, 37, 1782, 28, '', '', '157');
INSERT INTO app_hist.resposta VALUES (834, '1782-02-10', 'isa Ordem para que Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, envie um parecer sobre as dúvidas apresentadas pelo ouvidor da comarca em 08/02/1781, as quais dizem respeito à integência da carta régia de 12/08/1771, devido a junta da justiça e assentos, que sobre ela tomou o decreto de 24/09/1778.', '', NULL, 37, 1782, 28, '', '', '157-157v');
INSERT INTO app_hist.resposta VALUES (835, '1782-02-23', 'isa Ordem para que o ouvidor de Goiás, remeta um parecer sobre o apresentado pelo juiz dos orfãos trienal do arraial das trairas Francisco ? dos Reis em 15/11/1780, que
INSERT INTO app_hist.resposta VALUES (836, '1782-02-16', 'isa Ordem para que Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, remeta um parecer sobre o que foi apresentado pelo provedor da fazenda da capitania, servindo como intendente e ouvidor, em 16/12/1779. Onde destacou que, segundo o regimento das intendências e casas de fundição, os fiscais das casas deveriam ser nomeados pelas câmaras e aprovados pelos ouvidores. E neste termo não parecia necessario tirar provimento pela a secretaria do governo para a nomeação, pois pelo ordenado no mesmo regimento não eram verdadeiramente oficiais, mas sim, procuradores dos povos.', '', NULL, 37, 1782, 28, '', '', '158-158v');
INSERT INTO app_hist.resposta VALUES (1162, '1751-03-01', 'isa palavra chave testemunha?
INSERT INTO app_hist.resposta VALUES (1325, '1760-01-23', '(Théo) Dom José remete carta ao provedor da fazenda real das Minas Gerais onde é servido ordena por resolução de 27 /01 /1756 em conformidade do conselho ultramarino a todos os anos obrigar os oficiais de recebimento a prestar conta das despesas, receitas e também de suas próprias rendas.  ', '', NULL, 40, 1760, 20, '', '', '79');
INSERT INTO app_hist.resposta VALUES (841, '1760-08-29', '(Théo) Remete cópia ao conde de bobadela capitão general do Rio de Janeiro com o governo das Minas Gerais que por resolução de 25/?/1760 tomado em consulta com o conselho ultramarino, ordena passar as ordens necessárias para se arrematarem nessa cidade por três anos os contratos do Rio de Janeiro e das Minas expressos na relação. ', '', NULL, 40, 1760, 20, '', '', '69v ');
INSERT INTO app_hist.resposta VALUES (842, '1760-08-29', '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais representação que fez o governador interino das Minas Gerais, Jose Antonio de Andrade, em carta de 10/03/1757 de que em novembro do ano antecedente recebeu carta da câmara de Vila de São José sobre a necessidade de criar uma companhia das ordenanças na região de Passatempo e na Capela de Nossa Senhora da Glória. Por resolução de 16/08/1757 ordena a criação da companhia nos referidos distritos. ', '', NULL, 40, 1760, 20, '', '', '70');
INSERT INTO app_hist.resposta VALUES (843, NULL, '(Théo) Remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais a representação dos oficiais da câmara de Mariana em carta de 07/03/1762 nas quais se queixam dos ouvidores e guardas mor fazerem as concessões das terras. Ordena informar seu parecer nessa matéria. ( Resposta incompleta, não há assinatura ou data). ', '', NULL, 40, 1762, 20, '', 'Y', '70 ');
INSERT INTO app_hist.resposta VALUES (280, '1756-03-31', 'Ordem a Antonio Rolim de Moura Tavares, governador de Mato Grosso, por resolução tomada em consulta no conselho ultramarino em 20/03/1756, para que o ouvidor João Antonio Vaz Morilhas seja imediatamente suspenso, a fim de proteger as comunidades contra as violações que estava cometendo. Isso ocorre devido ao desembargador designado para sucedê-lo, Fernando Caminha de Castro, nomeado como ouvidor de Cuiabá, não ter conseguido tomar posse devido ao seu falecimento, como relatou o governador em 09/05/1755.
INSERT INTO app_hist.resposta VALUES (839, '1782-08-25', 'isa Remete conhecimento ao provedor da fazenda do Rio de Janeiro para que receba de Inacio dos Santos, mestre da nau de guerra nossa senhora dos prazeres, seis baús encorados contendo uma botica com medicamentos, a fim de que sejam entregues ao governo de Goiás.
INSERT INTO app_hist.resposta VALUES (1569, '1751-02-20', 'Provisão para vencimento de ordenado desde o dia do embarque concedida ao bacharel Manoel Luis de Melo, provido pelo Desembargo do Paço no lugar de ouvidor geral da capitania do Grão-Pará.', '', NULL, 39, 1751, 32, '', '', '35v-36');
INSERT INTO app_hist.resposta VALUES (844, '1760-08-28', '(Théo) Remete cópia ao conde de bobadela capitão general do Rio de Janeiro com o governo das Minas Gerais da representação que fez o vice rei do Brasil, Dom Marcos de Noronha, em carta de 17/05/1758 sobre o decreto de 10/05/1757 sobre separar do governo da Bahia a região de  Minas Novas do Fanado e que esta região, passasse a fazer parte da comarca de Serro do Frio, e que também ampliasse os poderes do intendente geral dos diamantes. Discorre também sobre o povo daquela região ter aceitado o ouvidor do Serro do Frio o que provocou queixa do ouvidor de Jacobina por achar que estava sendo usurpado de administrar aquela região.  Por resolução de 26/?/1760 em conformidade com o conselho ultramarino, ordena que seja repreendido o ouvidor de Jacobina e que posteriormente dê posse ao ouvidor do Serro do Frio. Também ordena que declarem que toda a região de Fanados é de administração e parte de Serro do Frio. ', '', NULL, 40, 1760, 20, '', '', '71-72');
INSERT INTO app_hist.resposta VALUES (931, NULL, 'Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre as licenças concedidas à tropa paga para irem ao reino.
INSERT INTO app_hist.resposta VALUES (1356, '1757-04-06', 'Provisão de D. Jose I ordenando o governador de Minas Gerais, Antonio Freire de Andrade, que execute as instruções acerca dos provimentos das serventias, expressa no decreto de vinte e três de março de 1756. Consta carta do referido governador confirmando a execução da dita ordem.', '', NULL, 5, 1757, 676, '', '', '');
INSERT INTO app_hist.resposta VALUES (930, NULL, 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre dois decretos a respeito do crime de deserção. ', '', NULL, 44, 1803, 24, '', '', '97v');
INSERT INTO app_hist.resposta VALUES (275, '1754-07-10', '( théo- verificar resumo: menage e corredor) Remete ao governador das Minas Gerais, Jose Antonio Freire de Andrade, cópia da carta dos oficiais da câmara de Vila Rica de 05/10/1753 na qual discorriam sobre o regimento das comarcas feita no ano de 1642 sobre a questão dos capitães e sargentos mores, assim como alferes deverem serem eleitos pelos oficiais das respectivas câmaras, tendo seu pleito e menagem rubricados pelo corredor da mesma câmara em um livro separado. Nessa matéria, defende com base no regimento dos governadores de armas que os mesmos não deveriam interferir nas questões dos oficiais da ordenança, já que os mesmos não eram juramentados. Ordena que faça um parecer dando como entendido o que se deve observar do regimento das ordenanças do reino.', '', NULL, 40, 1754, 20, '', '', '9-9v');
INSERT INTO app_hist.resposta VALUES (846, '1782-07-07', 'isa
INSERT INTO app_hist.resposta VALUES (847, '1782-12-02', 'isa Ordem para que Martim Lopes Lobo de Saldanha, governador de São Paulo examine a verdade do requerimento de Jose Antonio de Morais e Castro, devendo solta-lo logo, visto a queixa datada de 01/08/1781, relatando a injusta prisão realizada pelo dito governador, estando na cadeia de barra grande da Vila de Santos.', '', NULL, 37, 1782, 28, '', '', '160v');
INSERT INTO app_hist.resposta VALUES (1308, '1751-01-07', 'Provisão de D. Jose, para o governador de Minas Gerais, Gomes Freire de Andrade, remetendo em anexo cópia do parecer do vedor geral da corte a respeito das normas que se deveria observar. Que por falecimento de algum oficial ou soldado, se pretendia tirar fé do tempo de serviço dos oficiais, desde o inicio de suas funções até o dia de sua morte, para o procedimento na vedoria de Minas Gerais. ', '', NULL, 5, 1751, 378, '', '', '');
INSERT INTO app_hist.resposta VALUES (938, '1804-09-04', 'Rayssa. Licença concedida a Antonio Jose Felix de Avelar para se recolher ao reino com sua família', '', NULL, 44, 1804, 24, '', '', '99');
INSERT INTO app_hist.resposta VALUES (849, '1739-09-23', '[LUIZ. Dúvida tema] Ordem, após resolução de 06/09/1739 em consulta do conselho ultramarino, ao governador e capitão general da capitania de Minas Gerais, como do Rio de Janeiro, Gomes Freire de Andrade, para que embarque para o reino as peças de artilharia que se acharem incapazes nas naus de “Comboy”, para que possam ser refundidas. Em função da carta remetida pelo mesmo governador em 25/01/1739, com um mapa junto informando sobre: 1) a artilharia que se tem na fortaleza dessa praça; 2) a conta das peças que mais necessita; 3) conta das peças que se encontram incapazes.', '', NULL, 40, 1739, 19, '', '', '237');
INSERT INTO app_hist.resposta VALUES (850, '1739-10-10', '[LUIZ. Dúvidas palavra-chave] Ordem ao ouvidor geral da comarca do Sabará, Jose Teles da Silva, para que observe o seu regimento e ordens enquanto não for mandado o contrário. Em função da carta remetida pelo mesmo em 12/09/1738, no qual expunha os motivos que deveria servir o rei em mandar aumentar os salários das vistorias realizadas pelos ouvidores da comarca do Sabará.', '', NULL, 40, 1739, 19, '', '', '237');
INSERT INTO app_hist.resposta VALUES (1333, '1763-04-20', '(Théo) Dom José remete cópia ao governadorcapitão general do Rio de Janeiro com o governo das Minas Gerais, (junta de governo), do que representou o governador interino das Minas Gerais, Jose Antonio Freire de Andrade, em carta de 16/07/1760 onde expunha que o ouvidor da comarca do Serro do Frio recebeu vários requerimentos dos moradores da Barra do Rio das Mortes pedindo a criação de um juiz ordinário junto com um escrivão para estabelecer lei e ordem na região. Por resolução de 27/11/1761 tomada em consulta com o conselho ultramarino aprova a criação dos ditos cargos na região. ', '', NULL, 40, 1763, 20, '', '', '88-88v ');
INSERT INTO app_hist.resposta VALUES (853, '1782-11-20', 'isa Ordem para que João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, remeta um parecer sobre o que apresentou João Alves Torres, vigário da missa de Santana do Sacramento da vila do Cuiabá, em 23/06/1780. A respeito da criação, por ordem do rei, da humanização de índios, e que, estando distantes da vila do Cuiabá, se dividiam em freguesias e celebravam os ofícios divinos em casas indecentes até 1779. Mas Jose Carlos Pereira, juiz de fora da vila, ergueu, às suas custas, um majestoso templo para abrigar o santíssimo aacramento, necessário para as funções do divino culto.', '', NULL, 37, 1782, 28, '', '', '161');
INSERT INTO app_hist.resposta VALUES (855, '1782-07-15', 'isa Informa a Felipe Jose Nogueira Coelho, provedor da fazenda de Mato Grosso, que é mais decente e conveniente ao decoro e governo que se celebrem na casa da câmara, e estranha-se levar tão adiante, atos similarmente ao que informou em 22/03/1781, quando foi necessário convocar uma junta de justiça para decidir um recurso interposto pelo juiz de fora. Na ocasião, Joaquim Jose de Morais, ouvidor da capitania, foi convocado para participar, porém recusou-se a ir a qualquer lugar que não fosse a casa da câmara.
INSERT INTO app_hist.resposta VALUES (854, '1782-07-15', 'isa Informa a Joaquim Jose de Morais, ouvidor de Mato Grosso, que é mais decente e conveniente ao decoro e governo que se celebrem na casa da câmara, e estranha-se levar tão adiante, atos similarmente ao que informou em 23/03/1781, quando foi necessário convocar uma junta de justiça para decidir um recurso interposto pelo juiz de fora. Na ocasião, o provedor da fazenda da capitania, Felipe Jose Nogueira Coelho, foi convocado para participar, porém recusou-se a ir a qualquer lugar que não fosse a casa da câmara.
INSERT INTO app_hist.resposta VALUES (856, '1783-10-20', 'isa Informa ao provedor intendente de Mato Grosso que mantenha a avaliação dos novos direitos, visto o que apresentou em 06/06/1780, de que por ordem do governador Luis Pinto de Sousa Coutinho, os emolumentos dos advogados não formados e que obtêm provimentos do governador foram avaliados. E mandava observar na capitania o regimento dos novos direitos de 11/04/1661, que estabelece que esses emolumentos devem ser avaliados apenas em determinadas circunstâncias, pagariam em outras partes do reino, exceto em Lisboa e no posto.', '', NULL, 37, 1783, 28, '', '', '162-162v');
INSERT INTO app_hist.resposta VALUES (928, NULL, 'Rayssa. Informe de uma carta regia enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre não constranger os índios que servem os contratadores dos dízimos.', '', NULL, 44, 1803, 24, '', '', '97v');
INSERT INTO app_hist.resposta VALUES (932, NULL, 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre a restituição do território anteriormente da Espanha.', '', NULL, 44, 1803, 24, '', '', '98');
INSERT INTO app_hist.resposta VALUES (937, '1804-08-06', 'Rayssa. Sob o parecer do Conselho Ultramarino em 23/09/1803 a respeito do ofício do ex-governador Caetano Pinto de Miranda em 02/06/198 é ordenado que o atual governador, Manuel Carlos de Abreu Meneses intime o Intendente e Provedor da Fazenda para regular os salários e emolumentos pelo alvará de 10/10/1754.', '', NULL, 44, 1804, 24, '', '', '98v-99');
INSERT INTO app_hist.resposta VALUES (955, '1806-08-11', 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, sobre não dar patente de oficiais de marinha ad honorem.', '', NULL, 44, 1806, 24, '', '', '103v');
INSERT INTO app_hist.resposta VALUES (934, '1804-01-07', 'Rayssa. Licença concedida a Jose Felix de Araujo, provedor da fazenda da capitania de Mato Grosso, para se recolher ao reino, ainda que seu sucessor não tenha chegado, deve o ouvidor da comarca servir interinamente no cargo.', '', NULL, 44, 1804, 24, '', '', '98');
INSERT INTO app_hist.resposta VALUES (863, '1784-02-20', 'isa Ordem para que Francisco da Cunha e Meneses, governador de São Paulo, envie um parecer sobre a solicitação dos oficiais da câmara de Paranaguá datada em 27/07/1783, de abolir o cargo de sargento-mor e seu ajudante, devido aos prejuízos e inconvenientes que sua existência causa à população. No caso de optar pela manutenção desses cargos, que disponha ao tesoureiro os excedentes das contribuições para custear seus pagamentos.', '', NULL, 37, 1784, 28, '', '', '166v');
INSERT INTO app_hist.resposta VALUES (859, '1783-01-27', 'isa Informa aos oficiais da câmara de Vila Bela que, com base na solicitação dos próprios datada de 23/02/1777, que, diante da vila estar infestada de ares impuros e reconhecendo a capacidade financeira da câmara, planejavam contratar o cirurgião Antonio da Silva Coutinho. Mas não devem comprometer-se com o pagamento do dito cirurgião, pois as rendas da câmara não são suficientes para cobrir seu pagamento. No entanto, em prol da necessidade pública e do benefício da população, poderão considerar essa medida para o futuro.', '', NULL, 37, 1783, 28, '', '', '163v');
INSERT INTO app_hist.resposta VALUES (860, '1782-12-20', 'isa Informa aos oficiais da câmara da vila de Sorocaba, que foi ordenado que Francisco da Cunha e Meneses, governador de São Paulo, ordene ao ouvidor Estevão Gomes Ferreira para assistir à eleição e presidir a mesma, conforme solicitado pelos ditos oficiais em 30/01/1782. Onde, após o falecimento do capitão-mor da vila, Jose de Almeida Leme, foi comunicado o cargo vago e propostos três homens capacitados para assumir a posição. No entanto, o dito ouvidor não compareceu nem respondeu à solicitação, conforme as reais ordens.', '', NULL, 37, 1782, 28, '', '', '163v-164');
INSERT INTO app_hist.resposta VALUES (1388, '1764-12-22', '***Dom José remete cópia ao governador das Minas Gerais, Luis Diogo Lobo da Silva, da representação que fizeram os oficiais da câmara da cidade de Mariana em carta de 27/03/1762 onde pedem que seja servido ter dinheiro provincial nessa capitania. Ordena informar seu parecer nessa matéria.  ', '', NULL, 40, 1764, 20, '', '', '95v ');
INSERT INTO app_hist.resposta VALUES (857, '1783-07-05', 'isa 
INSERT INTO app_hist.resposta VALUES (861, '1784-03-09', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, zele pela conservação da paz e trate benevolentemente aqueles que vivem sob sua jurisdição, buscando atrair mais indígenas para viverem sob o domínio real. Isso em conformidade com o informado por Luis da Cunha Meneses, enquanto governador da capitania, em 10/01/1783, seguindo as reais ordens, onde solicitou que a nação caiapó, vivesse no grêmio da igreja para cessar as hostilidades, estabelecendo a aldeia maria primeira, onde foram batizados 113 indígenas.', '', NULL, 37, 1784, 28, '', '', '164v-165');
INSERT INTO app_hist.resposta VALUES (862, '1784-02-13', 'isa 
INSERT INTO app_hist.resposta VALUES (867, '1785-10-25', 'isa Ordem para que o ouvidor de Mato Grosso prossiga com a devassa que estava aberta, para identificar o responsável pelo insulto que apresentou em 26/08/1784, cometido contra o juiz de fora da vila de Cuiabá, Antonio Rodrigues Gayoso. E, caso o culpado seja encontrado, que remeta a devassa.
INSERT INTO app_hist.resposta VALUES (864, '1784-03-30', 'isa Ordem para que Francisco da Cunha e Meneses, governador de São Paulo, envie um parecer sobre a representação feita pelos oficiais da câmara de Cuiabá em 19/11/1782, referente ao comportamento do ouvidor Joaquim Jose de Moraes, que declarou nulos os pelouros realizados pelo juiz de fora na sua ausência e procedeu a novos pelouros.', '', NULL, 37, 1784, 28, '', '', '167-167v');
INSERT INTO app_hist.resposta VALUES (865, '1785-02-22', 'isa Ordem para que o ouvidor de Mato Grosso remeta um parecer e esclareça o procedimento adotado em relação à ordem expedida ao provedor da fazenda, Felipe Jose de Nogueira Coelho. Pois se declarou que ao ouvidor Joaquim Jose de Morais foi concedida uma ajuda de custo pelo erário real, com a condição de que não fosse repetida. Em 07/01/1782, o provedor descontou o valor, indicando que de fato não repetiu a ajuda, mas realizou o pagamento pelo transporte.
INSERT INTO app_hist.resposta VALUES (866, '1785-10-06', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, envie um parecer sobre as queixas de vexações e desordens apresentadas por Manuel Pereira de Carvalho em 20/06/1785, as quais foram praticadas por João Pereira Guimarães, assistente na comarca.
INSERT INTO app_hist.resposta VALUES (851, '1739-10-08', '[LUIZ. Obs.: Manuel Caetano é secretário do Conselho Ultramarino. Seria preciso criar esse ofício para adicionar no preenchimento. Dúvidas tema e palavra-chave] Ordem ao ouvidor geral da comarca de Vila Rica, para que envie as certidões que constatem o dia, mês e ano em que os serventuários entraram em seus ofícios. Em função da representação enviada por Manuel Caetano Lopes de Lavre, no qual, em razão do decreto de 01/02/1738, foi ordenado a nomeação de vários ofícios pelos donativos da real fazenda, como: inquiridor, contador e distribuidor, escrivão da almotaçaria, escrivão das execuções e meirinho geral. Sendo, assim, preciso saber, pelo bem da real fazenda, quando os serventuários passaram a exercer os seus ofícios.', '', NULL, 40, 1739, 19, '', '', '237');
INSERT INTO app_hist.resposta VALUES (868, '1739-10-01', '[LUIZ. Dúvida do requerente. Alguém já o criou. Acho, pelos ofícios, que seja a mesma pessoa. No entanto, o ideal não seria complementar as informações com o ofício que recebe dessa resposta? Além disso, dúvida quanto a palavra-chave] Ordem ao provedor da fazenda real das Minas Gerais, para que dê os devidos descontos nos soldos de Alexandre Luis de Sousa e Meneses. Remetendo as quantias ao reino conforme ordenado em semelhantes remessas. Em razão da nomeação do dito suplicante ao posto de 2º tenente da companhia de dragões e o adiantamento de seis meses de seu soldo.', '', NULL, 40, 1739, 19, '', 'Y', '237v');
INSERT INTO app_hist.resposta VALUES (869, '1739-10-21', '[LUIZ] Ordem ao ouvidor geral da comarca do Rio das Mortes, para que faça a avaliação do ordenado anual, como também dos emolumentos e propinas, do ofício de escrivão da vara de meirinho da fazenda dos defuntos, ausentes, capelas e resíduos. Em função da serventia provida a Antonio Carneiro de Barros, por tempo de um ano, na dita ocupação, sendo mandada tal avaliação para que o mesmo possa pagar os novos direitos na chancelaria.', '', NULL, 40, 1739, 19, '', '', '237v');
INSERT INTO app_hist.resposta VALUES (870, '1737-08-29', '[LUIZ] Ordem incompleta ao provedor da fazenda real das Minas do Goiás sobre ter atendido ao pedido de acompanhar o Conde de Sarzedas, Antonio Luis de Tavora, governador e capitão General da capitania de São Paulo, na jornada que fez por essas minas.', '', NULL, 40, 1737, 19, '', 'N', '237v');
INSERT INTO app_hist.resposta VALUES (871, '1739-10-26', '[LUIZ. Criar a Vila de São José] Ordem ao ouvidor geral da comarca do Rio das Mortes, para que informe, com o seu parecer, sobre o ordenado e emolumentos que o ofício de escrivão dos órfãos recebe anualmente. Devendo essa informação ser feita por inquirição de testemunhas e sem a intervenção das partes, para que se possa realizar na chancelaria mor da corte e reino a dita avaliação. Em função do dito ofício, na Vila de São José, não se encontrar avaliado.', '', NULL, 40, 1739, 19, '', '', '239');
INSERT INTO app_hist.resposta VALUES (872, '1739-10-27', '[LUIZ. Dúvidas quanto a tema e palavra-chave] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que não consinta que irmãos ou parentes, dentro do quarto grau, trabalhem em uma mesma intendência, devendo os oficiais servirem em intendências diferentes. Em função de conter inconvenientes que possam surgir ao real serviço e fazenda.', '', NULL, 40, 1739, 19, '', '', '239');
INSERT INTO app_hist.resposta VALUES (1208, '1761-10-26', 'LUIZ - Ordem do rei, dom José I, a João Manuel de Melo, governador de Goiás, para que, “com todo o segredo”, compute o arbítrio médio de cinco arrematações. A fim de que a fazenda “fique utilizada” e os serventuários providos do necessário. Além disso, solicita que dê o seu parecer à secretaria de estado dos negócios da marinha e domínios ultramarinos, para que possa ser estabelecida as pensões e o provimento das pessoas mais adequadas, evitando as faltas de administração da justiça. Em função da carta firmada pelo rei, em 24/10/1761, a respeito da arrematação trienal das serventias dos ofícios.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 22v-23. ');
INSERT INTO app_hist.resposta VALUES (882, '1786-11-10', 'isa Ordem para que João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, remeta um parecer ouvindo o ouvidor, sobre os procedimentos do ouvidor da comarca na fatura dos inventários e a forma como procede nas juntas, conforme apresentado pelo provedor da fazenda real e intendente do ouro em 07/01/1786.', '', NULL, 37, 1786, 28, '', '', '174-174v');
INSERT INTO app_hist.resposta VALUES (875, '1739-10-29', '[LUIZ] Ordem ao provedor da fazenda real das Minas Gerais, para que dê os devidos descontos nos soldos de Manuel Nogueira de Abreu Homem, e os remetendo ao reino conforme ordenado em semelhantes remessas. Em função da nomeação do dito suplicante ao posto de 2º tenente da companhia de dragões e o adiantamento de seu soldo.', '', NULL, 40, 1739, 19, '', '', '239v');
INSERT INTO app_hist.resposta VALUES (874, '1739-10-31', '[LUIZ. Dúvida se encaixa o "privilégios" e "regresso ao reino (licença". Como também nas palavra-chave] Ordem, após resolução de 27/10/1739 em consulta, louvando o que obrou Rafael Pires Pardinho, desembargador e intendente do distrito dos diamantes da comarca do Serro do Frio, e mandando que retorne ao reino e escolha alguém para ficar em seu lugar, enquanto não for enviado um sucessor. Em função da carta remetida pelo mesmo, em 31/08/1738, sobre seus procedimentos no referido ministério.', '', NULL, 40, 1739, 19, '', '', '239-239v');
INSERT INTO app_hist.resposta VALUES (876, '1739-10-31', '[LUIZ. Dúvida tema e palavra-chave] Ordem, após resolução de 27/10/1739 em consulta, aprovando as despesas realizadas pelo provedor da fazenda real da capitania de Minas Gerais, exceto aquela dada a Domingos Pinheiro, por se entender que a condução de uma maior quantidade de ouro não implicava em maior trabalho ou despesa. Em função das cartas remetidas pelo dito provedor em 15/12/1738 e 10/01/1739, em que prestava conta das ajudas de custos mandadas pelo governador.', '', NULL, 40, 1739, 19, '', '', '239v');
INSERT INTO app_hist.resposta VALUES (935, NULL, 'Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre a inoculação da bexiga.', '', NULL, 44, 1804, 24, '', '', '98');
INSERT INTO app_hist.resposta VALUES (877, '1739-10-31', '[LUIZ] Ordem, após resolução de 27/10/1739 em consulta, ao governador e capitão general, Gomes Freire de Andrade, como também ao intendente do distrito dos diamantes da comarca do Serro do Frio, determinando que o intendente deve ter o conhecimento de todas as causas minerais dos distritos da comarca do Serro do Frio, como também dos soldados presentes na guarnição. Devendo remeter essa informação ao auditor geral de Vila de Rica, para que possa sentenciar na forma que dispõe o seu regimento e pelas ordens reais. Além disso, como o intendente também têm a jurisdição econômica, as remetendo às justiças ordinárias a quem pertenciam, é declarado que não fique separado da jurisdição ordinária dos ouvidores daquele distrito, sendo esses avisados para executarem a ordem. Em função das informações dadas em carta de 02/05/1736 pelo governador e capitão general, Gomes Freire de Andrade, a respeito da jurisdição que deveria ter o intendente dos diamantes da comarca do Serro do Frio.', '', NULL, 40, 1739, 19, '', '', '240');
INSERT INTO app_hist.resposta VALUES (736, '1772-04-09', '*Ordem para que Antonio Carlos Xavier Furtado de Mendonça, governador de Goiás, remeta um parecer informando o rendimento atual do contrato das passagens. Visto a representação do ex governador da capitania, João Manuel Melo, datada de 26/05/1763, com 
INSERT INTO app_hist.resposta VALUES (901, '1760-09-01', '(Théo) Remete cópia ao governador das Minas Gerais, Gomes Freire de Andrade, carta do governador interino das Minas Gerais, Jose Antonio Freire de Andrade, de 10 /03 /1757 em que dera conta da representação da câmara da Vila de São João Del-Rei de ?  /05 /1754 sobre a necessidade da criação de duas companhias da ordenança na região devido ao número de pessoas lá residindo. Ordena que proceda nessa matéria na forma do regimento das ordenanças que se remete-o ao reino. ', '', NULL, 40, 1760, NULL, '', '', '76v-77 ');
INSERT INTO app_hist.resposta VALUES (880, '1786-01-27', 'isa
INSERT INTO app_hist.resposta VALUES (1326, '1761-11-09', '(Théo) Dom José remete cópia ao ouvidor da comarca da Vila do Sabará da representação que fez o juiz mais velho dessa vila, José Ferreira do Vale, em carta de 27/11/1760 onde remete cópia inclusa a respeito de se  ter um médico, como também um cirurgião e um boticário para melhor assistir aos presos doentes. Ordena informar seu parecer nessa matéria.   ', '', NULL, 40, 1761, 20, '', '', '84 ');
INSERT INTO app_hist.resposta VALUES (898, '1760-04-02', '(Théo) Remete cópia a Gomes Freire de Andrade da carta do governador interino das Minas Gerais, Jose Antonio Freire de Andrade, de 12 /01 /1756 em que aborda sobre aa questão da forma como os intendentes são eleitos,  sendo nomeados pelas câmaras e tendo mandatos de três meses. Questiona se não seria melhor se os mineiros não fossem nomeados para essa ocupação devido ao seu conhecimento em ouro. Ordena que nenhum intendente proposto pela câmara seja aceito se não cumprir os requisitos do regimento das intendências e casas de fundição. ', '', NULL, 40, 1760, 20, '', '', '75v-76 ');
INSERT INTO app_hist.resposta VALUES (936, NULL, 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre o uniforme da tropa de milícias.', '', NULL, 44, 1804, 24, '', '', '98v');
INSERT INTO app_hist.resposta VALUES (957, '1806-11-19', 'Rayssa. É perdoado o crime de deserção de Paulo Fernandes de Siqueira.', '', NULL, 44, 1806, 24, '', '', '104');
INSERT INTO app_hist.resposta VALUES (883, '1787-01-11', 'isa 
INSERT INTO app_hist.resposta VALUES (893, '1760-08-29', 'Remete cópia a Gomes Freire de Andrade que por resolução de 25 /08 /1760 ordena que faça as movimentações necessárias para que durante o tempo de três anos, se remetam a essa cidade os contratos de Minas Gerais e do Rio de Janeiro assinadas pelo secretário do conselho. ', '', NULL, 40, 1760, 20, '', '', '75-75v ');
INSERT INTO app_hist.resposta VALUES (885, '1784-02-26', 'isa Informa aos oficiais da câmara de Cuiabá que comunicou o ouvidor da comarca a respeito da benfeitoria da casa e do matadouro de bois, que relataram os oficiais em 14/11/1787, realizada por Antonio Jose de Oliveira. Esse trabalho está em observância com um capítulo da correição, no qual, em 1767, o ouvidor anterior da comarca, Miguel Pereira Pinto Teixeira. ', '', NULL, 37, 1784, 28, '', '', '165-166');
INSERT INTO app_hist.resposta VALUES (1583, '1758-10-14', '(LUIZ) Ordem para que João Manuel de Melo, governador de Goiás, tenha inteligência sobre as diligências a serem realizadas pelo novo ouvidor, Francisco de Atouguia Bitencourt, em tirar residência do ex-ouvidor, Antonio da Cunha Souto Maior, como também do ex-governador, Alvaro Jose Xavier Botelho de Tavora, conde de São Miguel.', '', NULL, 43, 1758, 21, '', '', 'Numeração 2. Fl. 4v.');
INSERT INTO app_hist.resposta VALUES (887, '1760-08-29', '(Théo) Remete cópia ao ouvidor da comarca do Sabará, Jose Gomes de Araujo,  resposta a sua carta de 04 / 05 / 1759 onde escreve ao secretário do Estado, Joaquim da Costa Corte Real, sobre a ordem de 13 / 10 / 1758 sobre tirar residência ao desembargador Domingos Nunes Vieira no tempo que foi intendente da casa de fundição dessa comarca. Informa que a ordem citada não foi cumprida. ', '', NULL, 40, 1760, 20, '', '', '73-73v ');
INSERT INTO app_hist.resposta VALUES (888, '1760-08-28', '(Théo) Remete cópia ao conde de Bobadela, Gomes Freire de Andrade, da representação dos oficiais da câmara de Mariana sobre o bando nessa capitânia informou o ouro perdido que o povo tem quando vai fundi-lo nas casas de fundição já que lá ele é misturado com outros metais e por consequência acaba perdendo seu valor. Por resolução de 25 / 08 / 1760 ordena que o ouro levado as casas de fundição não seja dado como perdido, e que examinem o ouro metido na casa de fundição para poder realizar o desconto tributário mas também para poder realizar procedimento contra os falsificadores. ', '', NULL, 40, 1760, 20, '', '', '73v-74');
INSERT INTO app_hist.resposta VALUES (889, '1760-08-28', '(Théo) Remete cópia a Gomes Freire de Andrade resolução de 25 / 08 / 1760 tomada em conformidade com o conselho ultramarino ordenar ao mesmo conselho que examine a quantidade de prata que costuma sair dos registros em cada ano e as faça introduzir o mesmo valor anualmente para assim suprir a falta de prata. ', '', NULL, 40, 1760, 20, '', '', '74 ');
INSERT INTO app_hist.resposta VALUES (890, '1760-08-28', '(Théo) Remete cópia aos oficiais da câmara de Mariana acerca do ouro perdido que tinham aqueles que iam na casa de fundição e tinham seu ouro misturado com outros metais durante o processo de fundição resultando na perda de seu valor. Por resolução de 25 / 08 / 1760 informa e ordena que o ouro não deve se dar por perdido e que todo o ouro que for metido nas casas de fundição devem ser examinados para obter os valores tributários mas também para dar procedimento contra os falsificadores. ', '', NULL, 40, 1760, 20, '', '', '74v-75 ');
INSERT INTO app_hist.resposta VALUES (891, '1760-08-29', '(Théo) Remete cópia ao bispo de Mariana, Manuel Ferreira Freire da Cruz, sobre o fato dos vigários receberem desobrigas no valor de três mil reis por pessoa além de excessivos emolumentos de covas e enterros que extorquem dos clientes. Ordena informar seu parecer tomando as medidas necessárias para se encerrar esses clamores. ', '', NULL, 40, 1760, 20, '', '', '74v-75 ');
INSERT INTO app_hist.resposta VALUES (892, '1760-08-29', '(Théo) Remete cópia ao provedor da fazenda real das Minas de sua carta de 17 /02 /1759 onde informara que na terceira condição do contrato das entradas e diante da chegada de uma nau que entrou no porto do Rio de Janeiro em 13 /10 /1759 recolheu ao cofre o que se achava vencido, dando o valor total de cento e sessenta e dois contos quinhentos e quatro mil cento e sessenta e treis reis com os quais foram cobrados apenas cem contos de reis e o restante foi aprendido. Informa que agiu de forma correta, e deve continuar com os mesmos procedimentos.   ', '', NULL, 40, 1760, 20, '', '', '75 ');
INSERT INTO app_hist.resposta VALUES (884, '1786-11-10', 'Ordem para que João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, pratique cuidado e zelo no trabalho que deseja prosseguir, que informou 23/05/1786, a respeito da recepção da real ordem de 20/07/1782, que instrui o ouvidor a elaborar registros de todos os novos estabelecimentos, eventos e casos mais notáveis desde a fundação da capitania. ', '', NULL, 37, 1786, 28, '', '', '175');
INSERT INTO app_hist.resposta VALUES (899, '1760-10-09', '(Théo) Remete cópia a Gomes Freire de Andrade, governador das Minas Gerais, acerca da resolução de 23 /08 /1760 tomada em consulta com o conselho ultramarino que estabeleça os soldos para os auxiliares do Brasil no valor de dez mil reis enquanto os auxiliares supra deverão receber oito mil reis. ', '', NULL, 40, 1760, 20, '', '', '76 ');
INSERT INTO app_hist.resposta VALUES (900, '1760-09-03', '(Théo) Remete cópia a Gomes Freire de Andrade sobre a representação dos oficiais da câmara da Vila do Principe em carta de 15 /11 /1755 sobre não ter dado oberservância quanto a declaração a respeito das pessoas que  estabeleciam loges publicas em no distrito das terras diamantinas, e que eles só deveriam ser expulsos mediante aprovação da câmara. ', '', NULL, 40, 1760, 20, '', '', '76-76v ');
INSERT INTO app_hist.resposta VALUES (1229, '1761-10-29', 'LUIZ - Informação de Sebastião Jose de Carvalho e Melo, conde de Oeiras, a Joaquim Pedro de Campos, escrivão da intendência da casa de fundição de Vila Boa, estimando as notícias dadas em suas duas cartas – em 30/05 e 30/12/1761 –, sobre as desordens naquela capitania. No qual, o governador, João Manuel de Melo, executou as providências para que cessassem esses distúrbios. Sendo assim, o conde de Oeiras salienta que “a todas as ocasiões, que tiver de dar-lhe gosto, não faltarei com a mais obsequiosa vontade”.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 38v.');
INSERT INTO app_hist.resposta VALUES (1357, '1757-03-10', 'maisa Provisão de D. Jose I ordenando ao intendente dos diamantes da Serra do Frio que informe, com seu parecer, sobre a queixa que foi formulada contra a sua pessoa pelos oficias da câmara da Vila do Príncipe. ', '', NULL, 5, 1757, 677, '', '', '');
INSERT INTO app_hist.resposta VALUES (1509, '1778-01-26', '(Théo) Dona Maria remete cópia a Dom Antonio de Noronha governador das Minas Gerais,, requerimento de João de Queiros pedindo a confirmação do posto de tenente-coronel do regimento de cavalaria auxiliar Ordena que confirme o posto para o dito suplicante.', '', NULL, 40, 1778, 20, '', '', '161');
INSERT INTO app_hist.resposta VALUES (902, '1786-08-30', 'isa Informa ouvindo o procurador da fazenda, a Diogo Miguel Frary da Silva No, ouvidor da comarca de Goiás, e a Tristão da Cunha Meneses, governador da mesma capitania, que em caso de dúvida deve-se representar ao conselho. Essa orientação foi dada com base no comunicado do governador em 12/01/1786, no qual Luis da Cunha Meneses, governador antecessor, ordenou a observância das provisões reais em favor da câmara de Vila Boa, referentes à arrecadação das rendas. O ouvidor Diogo Miguel, se intrometeu buscando um novo provimento, contestando a ordem estabelecida por considerá-la mal compreendida.', '', NULL, 37, 1786, 28, '', '', '175; 176');
INSERT INTO app_hist.resposta VALUES (904, '1787-05-31', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, observe o regimento que solicitou o intendente da real casa de fundição, em 16/08/1786.', '', NULL, 37, 1787, 28, '', '', '176v');
INSERT INTO app_hist.resposta VALUES (913, '1790-09-07', 'isa Informa a Bernardo Jose Maria Lorena e Silveira, governador de São Paulo, que a respeito do pedido de 12/02/1785 dos oficiais da câmara de Paranaguá, para aprovação do arbítrio tomado em colocar franca à venda das carnes, subsistência e conservação das lenhas, bem como sobre a nova obra da casa da cadeia. Primeiramente, é mais benéfico que a venda das carnes seja realizada por contrato em vez de ser colocada franca. Quanto à conservação das lenhas, essa medida deve ser mantida enquanto a povoação durar. Quanto à obra da escada da cadeia, recomenda-se que seja executada de acordo com o segundo risco.', '', NULL, 37, 1790, 28, '', '', '181v');
INSERT INTO app_hist.resposta VALUES (905, '1787-12-10', 'isa Ordem ouvindo o procurador da fazenda, para que Manuel da Ressureição, reverendo bispo de São Paulo, remeta um parecer informando sobre a necessidade de um pároco na freguesia e também a quantia das congruas, em resposta ao que informaram os oficiais da câmara de Pindamonhangaba em 16/12/1786. Os oficiais relataram a duplicação da obrigação de pagar os dízimos simultaneamente aos párocos, e com isso solicitando a nomeação de um vigário colado com as congruas necessárias. ', '', NULL, 37, 1787, 28, '', '', '176v-177');
INSERT INTO app_hist.resposta VALUES (906, '1788-06-07', 'isa Informa, ouvindo o procurador da fazenda, a Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, que ordenou aos oficiais da câmara de Cuiabá que observassem os pelouros feitos pelo ouvidor Joaquim Jose de Morais, considerando a irregularidade encontrada nos do juiz de fora anterior, Antonio Rodrigues Gayoso, os quais o ouvidor declarou nulos. Essa decisão foi tomada em resposta à queixa datada de 27/06/1786, apresentada pelos mencionados oficiais, sobre a dita anulação realizada pelo ouvidor.', '', NULL, 37, 1788, 28, '', '', '177-177v');
INSERT INTO app_hist.resposta VALUES (908, '1787-10-19', 'isa  Adverte ao intendente do ouro de Vila Bela sobre o engano relatado por Felipe Jose de Faria, desembargador e intendente do ouro da Bahia, em 12/02/1787. Segundo ele, em conformidade com as leis e registros das intendências, havia passado a casa da moeda, guias e registros das barras de ouro, e durante a conferência das que entraram em 1766, constatou um número duplicado. Alerta que a repetição desse erro poderá resultar em culpa, e é necessário investigar para identificar e punir os responsáveis.', '', NULL, 37, 1787, 28, '', '', '179');
INSERT INTO app_hist.resposta VALUES (909, '1790-07-03', 'isa Ordem para que João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, tome as medidas necessárias para que a câmara de Vila Bela pague as propinas devidas a Joaquim Miguel Lopes de Lavre, secretário do conselho ultramarino. Esta ordem é emitida com base na resposta datada de 15/07/1789, fornecida pelo mencionado governador em conformidade com a real ordem de 24/07/1781, que exige que as câmaras da capitania paguem imediatamente o referido secretário.
INSERT INTO app_hist.resposta VALUES (910, '1790-09-06', 'isa Informa aos oficiais da câmara de Paranaguá, que sobre o pedido de 12/02/1785, para aprovação do arbítrio tomado em colocar franca à venda das carnes, subsistência e conservação das lenhas, bem como sobre a nova obra da casa da cadeia. Primeiramente, é mais benéfico que a venda das carnes seja realizada por contrato em vez de ser colocada franca. Quanto à conservação das lenhas, essa medida deve ser mantida enquanto a povoação durar. Quanto à obra da escada da cadeia, recomenda-se que seja executada de acordo com o segundo risco.
INSERT INTO app_hist.resposta VALUES (912, '1790-10-05', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, remeta um parecer sobre a solicitação datada de 16/09/1789, dos oficiais da câmara de Vila Boa, referente às propinas. Com objetivo de garantir que a câmara fiquem na certeza sobre as graças que imploram, além de evitar o desprazer de ser excluída da proprina dos lutos, que não se nega.
INSERT INTO app_hist.resposta VALUES (879, '1785-10-06', 'isa Ordem para que o ouvidor de Paranaguá remeta um parecer ouvindo os oficiais da câmara, a nobreza e o povo, sobre a solicitação dos oficiais da câmara da mesma vila, datada de 12/02/1785, para aprovação do arbítrio tomado em colocar franca à venda das carnes, subsistência e conservação das lenhas, bem como sobre a nova obra da casa da cadeia.', '', NULL, 37, 1785, 28, '', '', '172v');
INSERT INTO app_hist.resposta VALUES (914, '1760-08-30', '(Théo) Remete cópia ao governador das Minas Gerais da carta do governador interino da mesma capitânia de 09 /03 /1757 em que da conta da representação que fizeram os moradores de Mariana se queixando de que o escrivão cobrava uma oitava de ouro para cada pedido de sesmaria enviada ao rei e se o escrivão teria ordens reais para cobrar tal quantia. Ordena que exponha e relembre o escrivão da câmara a ordem do governador  interino de não haver salário algum como pagamento por informações dadas ao governador. ', '', NULL, 40, 1760, 20, '', '', '77-78 ');
INSERT INTO app_hist.resposta VALUES (915, '1760-09-05', '(Théo) Remete cópia aos oficiais da câmara da Vila do Principe acerca de sua carta de 15 /11 /1755 onde se queixam de que as casas das moedas podem estar desviando parte do ouro das barras já que a quantidade pós processo é menor ou mais leve do que a quantidade original. Informa que as casas estão ordenadas a seguir as guias e marcos, assim como aqueles que entregam e recebem o ouro. ', '', NULL, 40, 1760, 20, '', '', '77v-78 ');
INSERT INTO app_hist.resposta VALUES (916, '1760-04-05', '(Théo) Remete cópia ao governador das Minas Gerais, Gomes Freire de Andrade, do que abordaram os oficiais da câmara de Vila do Principe em carta de 15 /11 /1755 acerca sobre as diferenças de peso e valor do ouro nas casas de moeda e casas de fundição. Ordena que informe seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '78');
INSERT INTO app_hist.resposta VALUES (917, '1760-04-03', '(Théo) Remete ordem ao Bispo de Mariana para que remete a cópia inclusa ao Bispo do Rio de Janeiro a respeito das desordens que comete-o o oficial Jose Ayres enquanto estava no Mato Grosso. ', '', NULL, 40, 1760, 20, '', '', '78-79');
INSERT INTO app_hist.resposta VALUES (1310, '1757-02-08', 'maisa Decreto de D. Jose I provendo Alexandre da Silva no posto de inquiridor, contador e distribuidor da vila do Caeté, Comarca de Sabará. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 605, '', '', '');
INSERT INTO app_hist.resposta VALUES (956, '1806-10-27', 'Rayssa. Prorrogação da licença de Jose Joaquim Correia de Mendonça para ficar em Cuiabá até novembro de 1807, no posto de capitão do regimento das ordenanças de minas do Cuiabá.', '', NULL, 44, 1806, NULL, '', '', '103v');
INSERT INTO app_hist.resposta VALUES (963, '1807-05-30', 'Rayssa. É perdoado o crime de deserção de Manuel Ribeiro da Silva, cabo de esquadra, contanto que se recolha sem demora ao seu corpo para continuar o real serviço no posto de esquadra.', '', NULL, 44, 1807, 24, '', '', '106v');
INSERT INTO app_hist.resposta VALUES (845, '1760-08-28', '(Théo) Remete cópia ao ouvidor do Serro do Frio José Pereira Sarmento, de sua carta de 06/08/1759 sobre o decreto e provisão do conselho ultramarino de 13/05/1757 sobre separar as Minas Novas do Finado do governo da Bahia  a região de Minas Novas do Finado e que ficassem unidas as tropas que nelas se achavam as do Serro do Frio, e que também ampliasse os poderes do intendente geral dos diamantes. Discorre também sobre o povo daquela região ter aceitado o ouvidor do Serro do Frio o que provocou queixa do ouvidor de Jacobina por achar que estava sendo usurpado de administrar aquela região.  Por resolução de 26/?/1760 em conformidade com o conselho ultramarino, ordena que passe a relação da Bahia, as informações necessárias para a repreensão do dito ouvidor de Jacobina . Também declara que toda a região de Finados é de administração e parte de Serro do Frio. ', '', NULL, 40, 1760, 20, '', '', '72-73 ');
INSERT INTO app_hist.resposta VALUES (920, '1798-04-19', '(Rayssa) É ordenado que o governador do Mato Grosso, Caetano Pinto de Miranda Monte Negro, execute tudo que for possível do arbítrio de Diogo de Toledo Lara, que se encontra em anexo entre os fólios 69v-70, sobre economias para a capitania, e que procure facilitar a comunicação entre o Pará e Mato Grosso pelos rios Amazonas, Madeira, Guaporé e Mamoré.', '', NULL, 44, 1798, 24, '', '', '70v');
INSERT INTO app_hist.resposta VALUES (940, NULL, 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, para caçar patente dos oficiais de Malta.', '', NULL, 44, 1804, 24, '', '', '99v');
INSERT INTO app_hist.resposta VALUES (922, '1799-04-22', 'Rayssa. Resposta aos ofícios de número 03 ao número 06: É recomendado ao governador do Mato Grosso, Caetano Pinto de Miranda que faça estabelecimentos para civilização dos índios com a maior economia da fazenda real, introduzindo-os a cultura e mineração. Além disso, recomenda que atraia o maior número de vassalos espanhóis sem esforços da fazenda real para o território português. Por fim, alega que remeteu o último ofício ao Luis Pinto de Sousa, embaixador entre Madrid, para que o mesmo faça reclamações a respeito da conduta dos governadores espanhóis.', '', NULL, 44, 1799, 24, '', '', '76-76v');
INSERT INTO app_hist.resposta VALUES (939, '1804-10-03', 'Rayssa. Licença concedida a Antonio Jose Felix de Avelar para se recolher ao reino com sua família e seus criados', '', NULL, 44, 1804, 24, '', '', '99-99v');
INSERT INTO app_hist.resposta VALUES (958, '1806-11-29', 'Rayssa. É ordenado sob parecer do conselho ultramarino em 20/10/1804  que a junta da justiça estabelecida na capitania de Mato Grosso por carta régia de 12/08/1771, realize processos simplesmente verbais em uma só instancia, tendo voto decisivo de juízes, e os casos militares sejam regulados pelas leis militares devendo subir a presença real em casos de réus de maiores patentes.', '', NULL, 44, 1806, 24, '', '', '104-105v');
INSERT INTO app_hist.resposta VALUES (923, '1799-04-23', 'Rayssa. Resposta aos ofícios de número 23 ao número 39: É louvado o trabalho que a câmara de Vila Bela tem realizado, é ordenado que o governador do Mato Grosso, Caetano Pinto de Miranda, demore o tenente coronel engenheiro Ricardo Franco e Almeida Serra na capitania para cumprir os serviços e depois o permita regressar ao reino livremente. É recomendado que se melhore a comunicação com as capitanias do Pará, Goiás e São Paulo para melhor comércio entre elas. Além disso, recomenda-se que todas as reclamações dos governadores espanhóis sejam enviadas ao embaixador de Madrid, Luis Pinto de Sousa, e reforça a importância de manter toda a defesa aos ataques espanhóis. Por fim, ordena que atenda o pedido de Miguel Jose Rodrigues, morador da vila do Cuiabá, que serviu o estado por anos, oferecendo um ofício militar ou de fazenda, ou uma pensão para que possa se sustentar e ficar no sossego nos últimos dias de vida.', '', NULL, 44, 1799, 24, '', '', '76v-79');
INSERT INTO app_hist.resposta VALUES (924, '1803-06-13', 'Rayssa. Resposta a diferentes ofícios: Louva-se o trabalho de todos os oficiais que se distinguiram na defesa da capitania de Mato Grosso. É concedido o regresso para o reino do engenheiro Ricardo Franco e Almeida Serra. É desejado que os reais serviços do governador Caetano Pinto de Miranda se mantenham na mesma qualidade na capitania de Pernambuco.', '', NULL, 44, 1803, 24, '', '', '92v-94');
INSERT INTO app_hist.resposta VALUES (921, '1798-08-03', '(Rayssa) Resposta a diferentes ofícios: É recomendado que o governador do Mato Grosso, Caetano Pinto de Miranda descubra novos meios para a capitania se sustentar e segurar sua defesa, sem enormes gastos da coroa, sugere-se que amplie o trabalho das minas de ouro e das fazendas de gado, e abra mais ainda a comunicação e comércio com a capitania do Pará. Além disso, recomenda-se que incomode os governadores espanhóis para que se vejam abrigados a ir para o centro e não incomodar mais. Por fim, é recomendado que adiante a civilização dos índios, afim de crescer a força contra a Espanha', '', NULL, 44, 1798, 24, '', '', '71-73');
INSERT INTO app_hist.resposta VALUES (959, '1807-02-06', 'Rayssa. É ordenado ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, conceda a herança de João Pereira Leite. ', '', NULL, 44, 1807, 24, '', '', '105v');
INSERT INTO app_hist.resposta VALUES (962, '1807-05-29', 'Rayssa. É ordenado ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, permita que o suplicante Jose Ribeiro de Almeida, cônego da catedral do Pará, regresse para a capitania do Pará.', '', NULL, 44, 1807, 24, '', '', '106');
INSERT INTO app_hist.resposta VALUES (964, '1807-07-17', 'Rayssa. É ordenado que seja entregue os recibos e quitações legais da herança de João de Albuquerque que se encontra com o juiz de defuntos e ausentes Jose Manuel Carlezo da Cunha ao procurador Amaro Velho da Silva, no Rio de Janeiro, do suplicante Manuel Albuquerque de Melo, ou seja entregue ao seu substabelecido em Vila Bela.', '', NULL, 44, 1807, 24, '', '', '106v-107');
INSERT INTO app_hist.resposta VALUES (968, NULL, 'Informe de uma carta enviada ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, para por a capitania em estado de defesa contra qualquer ataque hostil.', '', NULL, 44, 1807, 24, '', '', '108');
INSERT INTO app_hist.resposta VALUES (989, '1800-02-07', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, remeta um parecer sobre a criação do vigário da vara, que solicitaram os oficiais da câmara de Sorocaba em 28/12/1798.', '', NULL, 37, 1800, 28, '', '', '193v');
INSERT INTO app_hist.resposta VALUES (966, '1807-09-24', 'Rayssa. É ordenado que o governador do Mato Grosso, João Carlos Augusto de Oyenhausen, pague cem mil réis de ajuda de custo ao sargento mor de infantaria, Alexandre Jose Leite Chaves e Melo.', '', NULL, 44, 1807, 24, '', '', '107v-108');
INSERT INTO app_hist.resposta VALUES (969, '1807-10-30', 'Rayssa. É solicitado que o governador do Mato Grosso, João Carlos Augusto de Oyenhausen, interponha o seu parecer sobre a representação anônima feita entre o juiz de fora de Vila Bela, Gaspar Pereira da Silva Navarro.', '', NULL, 44, 1807, 24, '', '', '108v');
INSERT INTO app_hist.resposta VALUES (1390, '1764-12-20', '(Théo) Remete cópia ao governador das Minas Gerais, Diogo Lobo da Silva, representação que fizeram os oficiais da câmara da cidade de Mariana em carta de 16/03/1763 acerca das dilações que se admitem nas causas das contendas mineiras. Ordena informar seu parecer nessa matéria.  ', '', NULL, 40, 1764, 20, '', '', '96 ');
INSERT INTO app_hist.resposta VALUES (1230, '1761-10-29', 'LUIZ - Ordem de Francisco Xavier de Mendonça Furtado, secretário de estado, a Jose Ribeiro da Fonseca, tesoureiro da casa de fundição de Vila Boa, informando que todas as providências têm sido dadas e que as rendas da câmara sejam reduzidas a uma regular administração. Em função da carta de 30/12/1759 de Jose Ribeiro da Fonseca, a respeito da má administração dos bens da câmara dessa vila [Vila Boa] e das demais materiais.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 39.');
INSERT INTO app_hist.resposta VALUES (1584, '1766-07-21', '(LUIZ) Ordem a Joaquim Pedro de Campos a respeito da falta de um molhador, “a qual cessará com a liberdade da navegação”, prevista pela nova lei que deixará “os portos bem providos e abundantes”. Em função de uma carta remetida em 22/07/1765.', '', NULL, 43, 1766, 21, '', '', 'Numeração 2. Fl. 52v-53.');
INSERT INTO app_hist.resposta VALUES (926, '1803-06-19', 'Rayssa. Instruções e recomendações para Manuel Carlos de Abreu de Meneses, novo governador da capitania de Mato Grosso. Espera-se que durante seu real serviço aumente a população e indústria da capitania para aumento da riqueza e poderio do império português no continente americano. É recomendado que desanime o comercio entre a capitania do Mato Grosso e Pará pelos rios Amazonas, Madeira e Guaporé devido ao encarecimento do frete, e que juntamente com o governador do Pará remova os obstáculos e motivos de encarecimento, para que possa ser viável navegação pelos rios. recomenda-se que utilize dos homens livres ou escravos para promoção da defesa, cultura e produção do preciosos gêneros do terreno. Além disso, recomenda-se a promoção da civilização dos índios, anime a povoação e aumente as fortificações da capitania. Por fim, é ordenado que utilize o subsídio enviado anualmente no valor de doze arrobas de ouro pela capitania de Goiás, juntamente com o valor fornecido pela fazenda real para abertura das minas do Cachepó e Paraguai.', '', NULL, 44, 1803, 24, '', '', '95-97');
INSERT INTO app_hist.resposta VALUES (947, '1802-01-11', 'Rayssa. É ordenado que seja entregue os recibos e quitações legais da herança de João de Albuquerque que se encontra com o testamenteiro juiz de defuntos e ausentes Jose Manuel Carlezo da Cunha ao procurador Amaro Velho da Silva, no Rio de Janeiro, do suplicante Manuel Albuquerque de Melo. ', '', NULL, 44, 1802, 24, '', '', '100v-101');
INSERT INTO app_hist.resposta VALUES (990, '1800-02-07', 'isa Ordem para que Mateus de Abreu Pereira, bispo de São Paulo declare se há ou não vigário na vila de Sorocaba, considerando o pedido dos oficiais da câmara da dita vila em 28/12/1798, referente à criação do vigário da vara, que ao não deferir, fizeram.', '', NULL, 37, 1800, 28, '', '', '193v-194');
INSERT INTO app_hist.resposta VALUES (949, '1806-06-06', 'Rayssa. Ordem ao governador interino da capitania de Mato Grosso para que entregue toda a herança do Manuel Carlos de Abreu Meneses ao seu irmão desembargador Francisco de Abreu Pereira de Meneses.', '', NULL, 44, 1806, 24, '', '', '101v');
INSERT INTO app_hist.resposta VALUES (1002, '1803-07-19', '+confirmar Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo dê baixa a Antonio de Padua, na confirmação do posto de ajudante de sargento da infantaria de milicias dos úteis da cidade.', '', NULL, 37, 1803, 28, '', '', '209v');
INSERT INTO app_hist.resposta VALUES (991, '1800-12-04', 'isa Informa Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, e aos oficiais da câmara da capitania, ter feito mercê do governo a Bernardim Freire de Andrade.', '', NULL, 37, 1800, 28, '', '', '194');
INSERT INTO app_hist.resposta VALUES (952, '1806-07-14', 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, sobre o padrão dos uniformes da tropa', '', NULL, 44, 1806, 24, '', '', '102v');
INSERT INTO app_hist.resposta VALUES (953, '1806-07-09', 'Rayssa. É ordenado ao governador interino da capitania de Mato Grosso que dispense de juramento de preito e homenagem e entregue o governo de Mato Grosso a João Carlos Augusto de Oyenhausen', '', NULL, 44, 1806, 24, '', '', '102v-103');
INSERT INTO app_hist.resposta VALUES (954, '1806-07-09', 'Rayssa. É ordenado ao juiz, vereadores e procurador da câmara de Vila Bela que suspenda a entrega de carta regia de patente e dispense o juramento de preito e homenagem de João Carlos Augusto de Oyenhausen, e que o reconheças como governador de Mato Grosso.', '', NULL, 44, 1806, 24, '', '', '103');
INSERT INTO app_hist.resposta VALUES (960, '1807-02-19', 'Rayssa. Informe de uma carta enviada ao governador do Mato Grosso, João Carlos Augusto de Oyenhausen, sobre comutar pena de 10 anos de degredo dos militares de crimes de terceira e simples deserção para tempo de trabalho em obras públicas.', '', NULL, 44, 1807, 24, '', '', '106');
INSERT INTO app_hist.resposta VALUES (970, '1792-09-28', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, remeta um parecer juntamente com a informação datada de 28/01/1790, referente à queixa apresentada pelo ouvidor de Goiás e intendente do ouro, Jose Carlos Pereira, em 12/07/1791. A queixa diz respeito à falta de cumprimento das reais ordens, especialmente aquelas relacionadas as redigidas sobre a conta do intendente, que não estava recebendo a sentinela ou guarda particular em sua porta.', '', NULL, 37, 1792, 28, '', '', '183');
INSERT INTO app_hist.resposta VALUES (971, '1792-10-08', 'isa Ordem para que Bernardo Jose Maria Lorena e Silveira, governador de São Paulo, envie um parecer em resposta à obrigação imposta pelas leis reais, que apresentaram os oficiais da câmara de Taubaté em 12/10/1790. Onde solicitam que seja providenciado outro pároco para a igreja matriz, que não seja o padre Francisco Luis Braseiros, devido ao seu comportamento considerado horroroso durante o período em que serviu como vigário encomendado.', '', NULL, 37, 1792, 28, '', '', '183v');
INSERT INTO app_hist.resposta VALUES (972, '1792-11-08', 'isa Ordem para que Tristão da Cunha Meneses, governador de Goiás, envie um parecer sobre a questão levantada pelo intendente da casa de fundição em 09/06/1790. Onde relata que, na criação da referida casa, tanto ele quanto outros oficiais foram contemplados com os mesmos ordenados concedidos à casa de fundição de Vila Boa. No entanto, a partir de uma portaria de 1784, todos os oficiais passaram a receber apenas meio ordenado.', '', NULL, 37, 1792, 28, '', '', '184');
INSERT INTO app_hist.resposta VALUES (973, '1793-08-29', 'isa Informa a Bernardo Jose Maria Lorena e Silveira, governador de São Paulo, que confia no zelo da remessa dos documentos, que apresentou na carta datada de 07/03/1793, em resposta à real ordem de 20/04/1792. Esta ordem instrui sobre a remessa dos documentos legais relacionados a cada um dos onze artigos mencionados na mesma ordem. Onde deve-se interpor a cobrança que está fazendo indevidamente a casa doada de Tomé Joaquim da Costa Corte Real pelos meios direitos dos animais.', '', NULL, 37, 1793, 28, '', '', '184-184v');
INSERT INTO app_hist.resposta VALUES (974, '1794-04-09', 'isa Ordena a João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, que envie os livros conforme a resolução que está sendo enviada, considerando que os livros anteriormente enviados em 26/04/1791 para o expediente da receita e despesa da real fazenda e intendência do ouro se encontravam findos.', '', NULL, 37, 1794, 28, '', '', '186-186v');
INSERT INTO app_hist.resposta VALUES (1442, '1769-03-05', '(Théo) Dom José remete carte ao conde de Valadares, Governador Capitão das Minas Gerais, representação de João Pedro da Correia em carta de 06/09/1765 sobre queixa de vários funcionários da comarca de Sabará em relação a propor Jose do Livramento como capitão de uma companhia de soldados dragão da capitânia. Ordena que informe seu parecer nessa matéria.   ', '', NULL, 40, 1769, 20, '', '', '126 ');
INSERT INTO app_hist.resposta VALUES (1073, NULL, 'Ordem para que o intendente e provedor da fazenda de Cuiabá, proceda ao desconto de 10 mil reis nos soldos de Manuel de Abreu Furriel, que foi provido na companhia de dragões de Mato Grosso, para o sustento de sua mulher e filhos.', '', NULL, 37, 1749, 27, '', '', '379-379v');
INSERT INTO app_hist.resposta VALUES (948, '1806-03-28', 'Informe de uma carta enviada ao governador do Mato Grosso, Manuel Carlos de Abreu Meneses, sobre o envio de exemplares a respeito da inoculação da bexiga.', '', NULL, 44, 1806, 24, '', '', '101v');
INSERT INTO app_hist.resposta VALUES (984, '1798-04-23', 'isa Informa Tristão da Cunha Meneses, governador de Goiás e aos oficiais da câmara de Vila Boa, ter feito mercê do governo a João Manuel de Meneses.', '', NULL, 37, 1798, 28, '', '', '192');
INSERT INTO app_hist.resposta VALUES (980, '1797-03-27', 'isa Informa a Caetano Pinto de Miranda Montenegro, governador de Mato Grosso, que é determinado revogar a portaria emitida pelo governador antecessor, João de Albuquerque de Melo Pereira 
INSERT INTO app_hist.resposta VALUES (911, '1790-11-06', 'isa Informa ao ouvidor geral de São Paulo que é aprovado o procedimento de ter realizado o inventário dos bens do falecido reverendo bispo frei Manuel da Ressurreição. O ouvidor apresentou em 24/02/1790 que tais bens estavam sob posse do cônego Manuel da Cruz Lima, com o propósito de entregá-los ao sucessor do bispado. Portanto, orienta-se conservar esses bens em depósito seguro até que a entrega seja realizada.
INSERT INTO app_hist.resposta VALUES (981, '1797-05-26', 'isa palavra chave: eleição, suborno ? Ordem para que o intendente do ouro de Vila Boa remeta um parecer sobre o exposto pelo juiz presidente da câmara dessa vila, Francisco Andrade da Fonseca, em 30/03/1796, referente ao conteúdo da eleição realizada para novos oficiais da câmara em 1756, e o suborno com o qual ela foi feita, pelo capitão Pedro Monteiro da Silva e outros aliados do governador. Solicita-se que, para o futuro, a câmara esteja presente quando os corregedores realizarem as eleições.', '', NULL, 37, 1797, 28, '', '', '189v-190');
INSERT INTO app_hist.resposta VALUES (982, '1797-06-07', 'isa Informa a Caetano Pinto de Miranda Montenegro, governador de Mato Grosso, que é determinado que o conselho ultramarino consulte o regulamento individual específico que deve ser estabelecido, bem como a quantidade de soldo que pode ser devido aos suplicantes. Visto o requerimento dos tenentes-coronéis do real corpo de engenheiros, Joaquim Jose Ferreira e Ricardo Francisco de Almeida Serra, para pagamento do valor mínimo recebido de acordo com as reais ordens para os postos de capitães e sargentos-mores que ocuparam, a respeito do mesmo triplo do soldo, que mandará pagar ao posto.', '', NULL, 37, 1797, 28, '', '', '190-190v');
INSERT INTO app_hist.resposta VALUES (983, '1798-05-08', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, dê baixa a Domingos Alvares do Rio no posto de capitão agregado à companhia da freguesia da conceição dos Guarulhos, visto que a nomeação foi feita contra as reais ordens.', '', NULL, 37, 1798, 28, '', '', '191v-192');
INSERT INTO app_hist.resposta VALUES (1418, '1767-03-10', '(Théo) Dom José remete cópia aos oficiais da câmara da Vila de Pitangui, sua representação de 21/08/1765 os altos prejuizos que tem o povo dessa vila ao lider com testamentos. Informa que não parece válido no momento alterar o emolumentos dos juízes ordinários. ', '', NULL, 40, 1767, 20, '', '', '118v-119 ');
INSERT INTO app_hist.resposta VALUES (985, '1798-09-26', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, remeta um parecer sobre a solicitação apresentada pelos oficiais da câmara da capitania em 29/07/1797, a respeito da graça de confirmar a Jose Joaquim da Costa Gavião no posto de capitão da cavalaria de voluntários reais com exercício de ajudante de ordens.', '', NULL, 37, 1798, 28, '', '', '192v');
INSERT INTO app_hist.resposta VALUES (986, '1798-09-27', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, remeta um parecer sobre o que foi apresentado pelo juiz presidente, vereadores e procuradores do senado da câmara de São Sebastião em 03/02/1798, referente à opressão e ao vexame que os moradores da vila têm enfrentado desde 1798, devido à ordem emitida pelo antecessor, Bernardo Jose Maria Lorena e Silveira, de todas as vilas do mar terem suprimido a liberdade de levar os moradores a qualquer porto do Brasil, sob pena de prisão, e onde solicitam a restituição da antiga liberdade de comércio.', '', NULL, 37, 1798, 28, '', '', '192v-193');
INSERT INTO app_hist.resposta VALUES (987, '1798-09-29', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, remeta um parecer sobre as decadências que a vila de Guaratuba enfrenta, devido à falta de homens diligentes que cultivem as terras desertas, conforme apresentado pelo juiz ordinário e pelos oficiais da câmara da dita vila em 10/03/1797.', '', NULL, 37, 1798, 28, '', '', '193');
INSERT INTO app_hist.resposta VALUES (988, '1799-02-21', 'isa Informa que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, deve dar baixa ao pedido de Jose Manuel de Oliveira para a confirmação do posto de tenente da oitava companhia do segundo regimento das milícias da cavalaria ligeira da cidade, pois não há faculdade para formar.', '', NULL, 37, 1799, 28, '', '', '193-193v');
INSERT INTO app_hist.resposta VALUES (1180, '1750-11-10', 'Ordem para que Marcos Jose de Noronha e Brito, governador de Goiás, informe se a província da companhia de Jesus tem enviado missionários para o distrito de Goiás, conforme recomendado por resolução de 10/07/1747. Agradece o cumprimento da ordem para realizar o empréstimo das oitavas de ouro do coronel Antonio Pires de Campos. Conforme informado pelo ex-governador de São Paulo, D. Luis Mascarenhas, em 12/09/1748, acerca do que ocorreu com o dito coronel sobre o ajuste e modo de "desinfestar" os bororos do caminho das minas dos gentios caiapós, conforme a ordem de 08/05/1746, incluindo também informações sobre os párocos que cobravam oferendas excessivas aos bororos e o empréstimo para as oitavas de ouro do referido coronel.', '', NULL, 37, 1750, 27, '', '', '393v-394');
INSERT INTO app_hist.resposta VALUES (1231, '1761-10-29', 'LUIZ - Informação de Francisco Xavier de Mendonça Furtado, secretário de estado, a Diogo de Gouveia Osorio de Castro dizendo que “o mesmo senhor [Jose I] servido mandar conhecer as desordens”. Em função das cartas do mesmo Diogo de Castro, morador do “arraial do cocal das minas de Tocantins”, de 25/03 e 15/05/1760.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 39-39v.');
INSERT INTO app_hist.resposta VALUES (454, '1799-05-09', 'Rayssa. Informe sobre o registro de uma carta que trata sobre dízimos. Registrada no Livro do Rio de Janeiro f10.', '', NULL, 44, 1799, 24, '', '', '79');
INSERT INTO app_hist.resposta VALUES (1311, '1757-02-08', 'maisa Decreto de D. Jose I provendo Manuel Antonio da Silva no posto de escrivão dos órfãos da vila do Caeté, comarca de Sabará. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 606, '', '', '');
INSERT INTO app_hist.resposta VALUES (1312, '1757-02-09', 'maisa Decreto de D. Jose I provendo Francisco Moreira da Silva no posto de primeiro tabelião do judicial e notas da vila do Caeté, comarca de Sabará. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 607, '', '', '');
INSERT INTO app_hist.resposta VALUES (993, '1801-03-31', 'isa Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, restitua Tomas da Costa Correa da Silva,  no exercício de seu posto de sargento-mor comandante da brigada de artilharia da legião dos voluntários reais da capitania, bem como o cargo de lente de matemática, artilharia e fortificação na mesma legião. Isso se deve ao fato de que o suplicante foi intimado a continuar no exercício de seu posto, recebendo ordens para intimar o governador nomeado, Bernardim Freire de Andrade, sem partir da cidade do Rio de Janeiro, antes que tal determinação fosse efetivada, e estava agora o suplicante perplexo visto que o general partiu.', '', NULL, 37, 1801, 28, '', '', '195');
INSERT INTO app_hist.resposta VALUES (994, '1801-05-15', 'Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, responda e esclareça os fatos mencionados nas representações do reverendo bispo da mesma capitania, Mateus de Abreu Pereira, e de outros diversos indivíduos, os quais respondestes 
INSERT INTO app_hist.resposta VALUES (996, '1801-04-29', 'isa Ordem para que Mateus de Abreu Pereira, bispo de São Paulo observe e adote as mesmas práticas religiosas que são seguidas nas sé e igrejas do Rio de Janeiro. Isso vem em resposta às representações recebidas no conselho ultramarino sobre a desonra ocorrida na igreja da sé e a questão do repique dos sinos nesta e em outras cidades do recôncavo.', '', NULL, 37, 1801, 28, '', '', '196');
INSERT INTO app_hist.resposta VALUES (997, '1801-04-28', 'isa Ordem para que João Manuel de Meneses, governador de Goiás, informe sobre o conteúdo transcrito das queixas e violências apresentadas pelos oficiais da câmara da vila do Príncipe em 02/10/1800. Que referem-se às preocupações dos lavradores e habitantes do arraial de Arasauai, conforme documentado no papel intitulado ''memorial'' dirigido à câmara, especialmente em relação à cobrança dos dízimos de suas lavouras pelos rendeiros.', '', NULL, 37, 1801, 28, '', '', '196');
INSERT INTO app_hist.resposta VALUES (998, '1802-01-15', 'isa Ordem para que Caetano Pinto de Miranda Montenegro, governador de Mato Grosso, prenda o o ouvidor Manuel Joaquim Ribeiro Freire, visto a denuncia de João Manuel de Meneses, governador de Goiás. Em ter o ouvidor indiscreta e criminosa condescendência ao aceitar uma comissão ilegal, para investigar, por meio de uma devassa, o suborno que Manuel Joaquim de Aguiar Mourão, ouvidor atual teria praticado durante a residência de seu antecessor, Antonio do Liz.', '', NULL, 37, 1802, 28, '', '', '196v');
INSERT INTO app_hist.resposta VALUES (1181, '1749-11-12', 'isa 
INSERT INTO app_hist.resposta VALUES (1232, '1761-10-29', 'LUIZ - Ordem de Francisco Xavier de Mendonça Furtado, secretário de estado, a Tome Inacio da Costa Mascarenhas, fiscal da casa de fundição de Goiás, informando que “o mesmo senhor [Jose I] dar ao dito respeito a necessária providência”. Em função da carta enviada em 31/05/1760 pelo mesmo Tome Mascarenhas, no qual dava conta da falta de observação do regimento e das demais desordens na casa de fundição da vila [Vila Boa]. ', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 39v.');
INSERT INTO app_hist.resposta VALUES (1233, '1762-12-23', 'LUIZ - Carta do rei, Jose I, a João Manuel de Melo, governador de Goiás, enviando as cópias das cartas enviadas a todos os governadores e capitães generais dos portos do Brasil, Maranhão e Angola. As quais servirão de instrução, “para não ignorar o que se tem passado quanto à guerra, que declararam contra este reino [Portugal] as cortes de Madri e Paris, e o estado em que esta se acha”.', '', NULL, 43, 1762, 21, '', '', 'Numeração 2. Fl. 40.');
INSERT INTO app_hist.resposta VALUES (1247, '1765-02-11', 'LUIZ - Informação de Francisco Xavier de Mendonça Furtado, secretário de estado, a João Manuel de Melo, governador de Goiás, em resposta a carta de 09/06/1764, a respeito: 1) Dos mapas e do ouro que entrou na casa de fundição dessa vila [Vila Boa]; 2) Da importância dos quintos e escovilhas, além da remessa feita ao Rio de Janeiro; 3) Das somas remetidas para a “capitania do Cuiabá” para prestar socorro ao governador do Mato Grosso.', '', NULL, 43, 1765, 21, '', '', 'Numeração 2. Fl. 47v-48.');
INSERT INTO app_hist.resposta VALUES (1296, '1755-09-11', 'LUIZ - Ordem para que Antonio da Cunha Souto Maior, ouvidor de Goiás: 1) tome o direito e o ouro que se achassem junto com o frei Jose de Nossa Senhora dos Anjos; 2) O prenda e conduza ao porto com segurança, a fim de que seja remetida ao seu prelado no reino; 3) Realize essas diligências e “escrevendo ao prelado franciscano do Rio de Janeiro para que o tenha em prisão até se embarcar e ao sindico da mesma cidade, Manuel dos Santos Pinto, para que ajuste o seu transporte. Em função da representação do provincial, São Francisco da observância da província de Portugal, a respeito do frei Jose de Nossa Senhora dos Anjos, estar há 10 anos pedindo esmolas para edificar um convento. Sem, contudo, realizar remessa alguma.', '', NULL, 43, 1755, 21, '', '', 'Numeração 1. Fl. 12-12v.');
INSERT INTO app_hist.resposta VALUES (467, '1800-02-14', 'Rayssa. Informe de uma carta enviada ao governador do Pará, para que o governador do Mato Grosso, Caetano Pinto de Miranda. siga as mesmas reais ordens contida na carta.', '', NULL, 44, 1800, 24, '', '', '82v-83');
INSERT INTO app_hist.resposta VALUES (1313, '1757-02-12', 'maisa duvida Decreto de D. Jose I provendo Jose Bezerra Seixas no posto de inquiridor, contador e distribuidor da vila de São João del Rei. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 608, '', '', '');
INSERT INTO app_hist.resposta VALUES (1000, '1803-03-21', '*Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, dê baixa na nomeação de Antonio Silveira da Costa para o posto de tenente-coronel agregado do segundo regimento de infantaria de milícias, uma vez que tal nomeação não está autorizada.', '', NULL, 37, 1803, 28, '', '', '197-197v');
INSERT INTO app_hist.resposta VALUES (1004, '1804-04-22', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, envie um parecer sobre o que os oficiais da câmara da Vila de Cunha apresentaram em 01/02/1803 sobre os acontecimentos relacionados à proposta de uma nova eleição para o provimento do posto de capitão-mor. Alegam que Jose Vaz da Silva foi provido para o cargo e estava exercendo-o satisfatoriamente, mas que Jose Alves de Oliveira foi preterido por meio de um requerimento feito em nome de Jose Vaz, com o seu consentimento, para sua demissão, mas ele contesta a veracidade desse requerimento e, como resultado, teve baixa no posto, solicitando que Jose Vaz seja reinstaurado no posto de capitão-mor, invalidando assim a patente concedida a Jose Alves.', '', NULL, 37, 1804, 28, '', '', '214');
INSERT INTO app_hist.resposta VALUES (1309, '1757-02-08', '***Decreto de D. Jose I provendo Antonio Luis de Abreu no posto de segundo tabelião do judicial e notas da vila do Caeté, comarca de Sabará. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 604, '', '', '');
INSERT INTO app_hist.resposta VALUES (1009, '1804-08-08', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, remeta um parecer declarando se o suplicante, Candido Xavier de Almeida, tenente-coronel de infantaria do regimento da praça de Santos e atual comissário da divisão de São Paulo na demarcação de limites com o Paraguai, está atualmente em exercício da comissão ou se está servindo em seu regimento, a fim de esclarecer a solicitação dos 400 réis que ele apresentou.', '', NULL, 37, 1804, 28, '', '', '215v-216');
INSERT INTO app_hist.resposta VALUES (1001, '1803-03-24', '*Ordem  para que Antonio Jose da Franca e Horta, governador de São Paulo, dê baixa e a nomeação de Manuel Joaquim Azevedo, que solicitou confirmação da patente do posto de coronel agregado à primeira companhia do segundo regimento de infantaria miliciana, pois foi provido de forma incompetente.', '', NULL, 37, 1803, 28, '', '', '197v');
INSERT INTO app_hist.resposta VALUES (1005, '1804-03-14', 'Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo confirme Manuel Domingues Salgueiro no posto que solicitou de capitão-mor da vila de Lorena. Deve também enviar uma cópia da ordem pela qual foi criado o posto e explicar o motivo pelo qual o mesmo permaneceu tantos anos sem ser provido.', '', NULL, 37, 1804, 28, '', '', '214v');
INSERT INTO app_hist.resposta VALUES (1011, '1804-08-16', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, dê baixa a Jose Morato do Canto, que exerceu o posto de capitão da companhia da ordenança da vila de Paranaguá, visto que Francisco Ferreira de Oliveira foi confirmado no posto.', '', NULL, 37, 1804, 28, '', '', '217');
INSERT INTO app_hist.resposta VALUES (1012, '1804-08-30', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, remeta um parecer sobre a queixa dos oficiais da câmara da capitania de 26/10/1799, referente a Mateus de Abreu Pereira, bispo da capitania, que obriga escravos e nascidos no país a tirarem provisão de casamento, o que é contra o antigo costume.', '', NULL, 37, 1804, 28, '', '', '217-217v');
INSERT INTO app_hist.resposta VALUES (1008, '1804-06-27', '**Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, que por ordem de 15/03/1802, dê baixa ao capitão-mor da vila de Cunha, Jose Gomes de Siqueira e Mota, devido aos crimes horríveis e escandalosos provados contra ele. Além disso, o governador deve observar com muita vigilância as ações de homens semelhantes a ele. Isso se deu pelo requerimento de Jose Gomes, solicitando a nomeação de um sindicante para investigar as violências cometidas contra ele pelo coronel Antonio Jose de Macedo e Francisco Xavier Leite.', '', NULL, 37, 1804, 28, '', '', '215-215v');
INSERT INTO app_hist.resposta VALUES (1006, '1804-06-07', '*Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, remeta um parecer sobre as consequencias da queixa apresentada pelos oficiais da câmara da Vila Antonina em 31/05/1803. A queixa refere-se ao pároco da matriz da Vila, que recebe uma congrua paga pela real fazenda, o que vai contra o costume do bispado, e está exigindo que seus fregueses lhe paguem conhecenças.', '', NULL, 37, 1804, 28, '', '', '214v');
INSERT INTO app_hist.resposta VALUES (1007, '1804-06-16', '*Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, faça uma advertência ao capitão Jose dos Santos Cruz, comandante da vila de Mogi Mirim, sobre os excessos que tem sido atribuídos a ele e que estão evidenciados nas queixas apresentadas em 09/12/1803.', '', NULL, 37, 1804, 28, '', '', '215');
INSERT INTO app_hist.resposta VALUES (1013, '1804-09-17', 'isa Ordem para que o ouvidor de São Paulo remeta ao conselho ultramarinio uma nota específica das propinas que até agora foram pagas, conforme a representação que fez em 12/01/1804, em cumprimento da ordem de 15/03/1802 referente às propinas que as câmaras devem pagar. Aquelas que não estiverem em conformidade deverão ser arbitrárias, levando em consideração o rendimento de cada uma. Além disso, deve enviar um mapa do atual rendimento de todas as câmaras.', '', NULL, 37, 1804, 28, '', '', '217v');
INSERT INTO app_hist.resposta VALUES (1014, '1804-09-15', 'isa Manda que Antonio Jose da Franca e Horta, governador de São Paulo,  conceda a patente de confirmação a Jose Pedro da Cunha no posto de capitão da primeira companhia da freguesia de Juqueri, conforme sua solicitação. Ao mesmo tempo, determina-se que seja revogada e anulada a patente de Jose Pires de Almeida, e que sejam registradas as devidas observações nas margens dos livros.', '', NULL, 37, 1804, 28, '', '', '217v-218');
INSERT INTO app_hist.resposta VALUES (1015, '1804-09-15', 'isa Manda que Antonio Jose da Franca e Hoorta, governador de São Paulo , conceda a patente de confirmação a Luis Castanho de Moraes no posto de sargento-mor das ordenanças da vila de Jundiaí, conforme sua solicitação. Ao mesmo tempo, determina-se que seja revogada a patente de Gabriel da Fonseca Sousa para o mesmo posto, e que sejam registradas as devidas observações nas margens do registro da sua patente.', '', NULL, 37, 1804, 28, '', '', '218');
INSERT INTO app_hist.resposta VALUES (1016, '1804-08-06', 'isa Informa a Antonio Jose da Franca e Horta, governador de São Paulo, que repreende a inconsequência do governador antecessor, Antonio Manuel de Melo e Castro de Mendonça, por ter nomeado Manuel Domingos Salgueiro para o posto de capitão-mor, conforme informou em 27/03/1804. Isso se deu  em cumprimento da ordem de 18/07/1803, que atende ao requerimento de Francisco Berlamino de Assunção, que se queixou de ter sido preterido no posto de capitão-mor da vila de Lorena, quando deveria ter sido proposto em primeiro lugar pela câmara. ', '', NULL, 37, 1804, 28, '', '', '218v-219');
INSERT INTO app_hist.resposta VALUES (1297, '1750-11-02', '(Théo) Dom Jose nomeia Maximo Barbosa Pinto Pereira de Matos ao cargo de escrivão do provedor de registros de Paraíbuna. ', '', NULL, 5, 1750, 556, '', '', '');
INSERT INTO app_hist.resposta VALUES (1299, '1750-11-04', '(Théo) Dom Jose nomeia Francisco Dias Leal ao cargo de escrivão de órfãos da cidade de Mariana ', '', NULL, 5, 1750, 560, '', '', '1v');
INSERT INTO app_hist.resposta VALUES (1017, '1804-10-23', 'isa Declara a Antonio Jose da Franca e Horta, governador de São Paulo, que o referido posto de sargento-mor das ordenanças, conforme a ordem de 28/05/1795, não inclui oficiais previamente providos. Isso se evidencia na confirmação solicitada por Manuel Correa de Mesquita para o posto de sargento-mor das ordenanças da vila de São Sebastião, proposto pela câmara após baixa no referido posto, o qual ele exerceu por 22 anos sem confirmação.', '', NULL, 37, 1804, 28, '', '', '219');
INSERT INTO app_hist.resposta VALUES (1182, '1749-11-02', 'isaOrdem para que Marcos Jose de Noronha e Brito, governador de Goiás, informe com parecer sobre a comunicação do intendente e provedor da fazenda de Cuiabá, datada de 19/10/1746. Nessa comunicação, é mencionado que o ouvidor daquela comarca, como superintendente das terras minerais, possui, pelo regimento, a faculdade de obter preferência nos descobrimentos da data, o que se pagava pelo trabalho na arrecadação dos reais quintos. Contudo, essa causa cessou devido à nova capitação que reduziu os quintos do ouro, e, com novos descobrimentos, o superintendente não tem mais trabalho adicional relacionado à real fazenda. Assim, solicita que se considere conceder aos provedores da fazenda o mesmo tratamento dado ao superintendente, em reconhecimento pelo trabalho realizado.', '', NULL, 37, 1749, 27, '', '', '387-387v');
INSERT INTO app_hist.resposta VALUES (1234, '1761-12-02', 'LUIZ - Ordem de Francisco Xavier de Mendonça Furtado, secretário de estado, a João Manuel de Melo, governador de Goiás, para que informe se Miguel da Costa Pereira encontra-se nos termos para receber a mercê do hábito de Cristo. Em função do requerimento do dito ao conselho ultramarino e de ter mandado a casa de fundição [de Vila Boa] uma significativa quantia em 1756, em consequência da lei de 03/12/1750.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 40-40v.');
INSERT INTO app_hist.resposta VALUES (1235, '1761-12-02', 'LUIZ - Ordem de Francisco Xavier de Mendonça Furtado, secretário de estado, a João Manuel de Melo, governador de Goiás, para que informe se Tomas João Rufo de Aquino encontra-se nos termos para receber a mercê do hábito de Cristo. Em função do requerimento do dito ao conselho ultramarino e de ter mandado a casa de fundição [de Vila Boa] uma significativa quantia, em consequência da lei de 03/12/1750.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 40v-41.');
INSERT INTO app_hist.resposta VALUES (1298, '1750-11-04', '(Théo) Dom Jose nomeia Jose Alvares Maciel ao cargo de escrivão das execuções de Vila Rica', '', NULL, 5, 1750, 558, '', '', '1v');
INSERT INTO app_hist.resposta VALUES (1291, '1754-02-03', 'LUIZ - Nomeação de Marcos Jose de Noronha e Brito, conde dos Arcos e governador de Goiás, como vice-rei do estado do Brasil. No entanto, que ele não saia da capitania antes da chegada de seu sucessor, o conde de São Miguel, Alvaro Jose Xavier Botelho de Tavora, a fim de que o possa instruir. Em função dos pedidos de Luis Peregrino de Ataide, 10º conde de Atouguia e vice-rei do Brasil, que mandasse um sucessor.', '', NULL, 43, 1754, 21, '', '', 'Numeração 1. 6v-7.');
INSERT INTO app_hist.resposta VALUES (1249, '1766-07-21', 'LUIZ [DÚVIDA: Devo colocar essa resposta?] - Ordem de Sebastião Jose de Carvalho e Melo, conde de Oeiras, a Antonio Jose de Araujo e Sousa, ouvidor e provedor da fazenda, informando que “o mesmo senhor [ Jose I] me manda declarar a vossa mercê, que fica na sua real lembrança para o atender”. Em função da carta enviada pelo ouvidor e provedor da fazenda em 24/07/1765, recordando a forma do despacho em que foi mandado para Goiás e as razões para esperar o seu adiantamento.', '', NULL, 43, 1766, 21, '', '', 'Numeração 2. Fl. 50v-51.');
INSERT INTO app_hist.resposta VALUES (1334, '1763-04-12', '*Dom José remete cópia aos oficiais da câmara da cidade de Mariana do que representaram em carta de 07/02/1761 sobre os preços altos nos medicamentos que levam os boticarios por todas a região das Minas Gerais. Ordena que observe o regimento dos boticarios para ver a taxa pela qual os mesmos devem cobrar para os medicamentos. ', '', NULL, 40, 1763, 20, '', '', '88v-89 ');
INSERT INTO app_hist.resposta VALUES (1314, '1757-02-23', 'maisa Decreto de D. Jose I provendo Caetano Jose Viegas no posto de escrivão da fazenda real de Vila Rica do Ouro Preto. Consta recibo do escrivão Antonio de Melo e Sousa.', '', NULL, 5, 1757, 609, '', '', '');
INSERT INTO app_hist.resposta VALUES (1327, '1761-11-09', '(Théo) Dom José remete cópia ao governador capitão general do Rio de Janeiro com o governo das Minas Gerais, Gomes Freire de Andrade, da representação que fizeram os oficiais da câmara da Vila Real do Sabará em carta de 03/09/1760 sobre o fato de haver providência pronta para reperar as pontes do Rio das Velhas por serem muito utilizadas e pedem para que declare uma anta geral na capitânia para que não demore o processo de reedificação das ditas pontes. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1761, 20, '', '', '84 ');
INSERT INTO app_hist.resposta VALUES (1335, '1763-04-15', '(Théo) Dom José remete cópia aos oficiais da câmara da Vila de São José resposta a sua representação em carta de 09/03/1759 suplicando a criação de um lugar de juiz de fora para essa vila. Informa que não parece ser conveniente deferir esse requerimento. ', '', NULL, 40, 1763, 20, '', '', '89');
INSERT INTO app_hist.resposta VALUES (216, '1755-04-21', 'Ordem a Antonio Rolim de Moura Tavares, governador de Mato Grosso, para que remeta um parecer referente às informações apresentadas pelo ouvidor geral de Cuiabá em 10/05/1754. O ouvidor relata a recorrência de invasões e hostilidades por parte dos indígenas, destacando a necessidade de fomentar o comércio e a tranquilidade das Minas. Com isso, é recomendada cautela na pacificação dos índios e, em virtude do desejo de empreender uma nova guerra, similar à ocorrida em 1744, sugere explorar os meios de reduzi-los pela suavidade, recorrendo à guerra somente em situações de última necessidade.', '', NULL, 37, 1755, 28, '', '', '32');
INSERT INTO app_hist.resposta VALUES (218, '1753-05-29', 'Remete a carta do intendente da casa de fundição do Rio das Mortes de 05/09/1752 na qual o mesmo aborda o capítulo 12, paragrafo 3 do determinado regimento que dizia que os juízos ordinários e seus escrivãos deveriam servir as terras aonde estavam estabelecidas as reais casas de fundição. Nessa matéria, os intendentes haviam encontrado uma dúvida se os oficiais da ouvidoria se enquadrariam nas determinações do dito parágrafo. Informa que o mencionado regimento fala dos oficiais dos juízes ordinários e não dos oficiais da ouvidoria, os quais deveriam servir diante das ordens do intendente, podendo então ordena-los a cumprir suas ordens e puni-los em caso de descumprimento. Ordena também que registre tais ordens nos livros da intendência e da câmara. ', '', NULL, 40, 1753, 20, '', '', '8v');
INSERT INTO app_hist.resposta VALUES (702, '1771-11-20', 'isa Solicita-se que Luis Antonio de Sousa Mourão, governador de São Paulo declare a importância dos rendimentos em justificação da utilidade das sesmarias dos monges beneditinos e esclareça se houve alguma ordem que permitiu essas concessões. Isso decorre da representação dos oficiais da câmara da capitania de 05/12/1770, na qual expõem que, após a doação das terras para fundarem o convento, receberam foros e laudêmios, além de roubarem as pensões pelos pastos dos animais cavaleiros.', '', NULL, 37, 1771, 28, '', '', '116-116v');
INSERT INTO app_hist.resposta VALUES (1559, '1751-02-15', 'IZA - Provisão para vencer o mesmo lugar e ordenado que tem o juiz dos órfãos da cidade da Bahia concedida ao Domingos (Dr?) Francisco Xavier Pereira Brandão, nomeado juiz do crime da cidade da Bahia. A provisão, embora expedida por expediente, é fundamentada em resolução de 05/12/1741 tomada em consulta do conselho ultramarino. 
INSERT INTO app_hist.resposta VALUES (1018, '1804-11-22', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, remeta um parecer sobre a queixa dos oficiais da câmara de Porto Feliz em 02/04/1803, referente à imposição do governador antecessor, Antonio Manuel de Melo e Castro de Mendonça, de 40 réis em cada arroba de açúcar para a nova contribuição literária, solicitando a isenção desse gênero da referida contribuição.', '', NULL, 37, 1804, 28, '', '', '219v-219v');
INSERT INTO app_hist.resposta VALUES (1586, '1766-07-21', '(LUIZ) Resposta a Manuel da Fonseca Brandão, desembargador da relação do Rio de Janeiro, informando que a devassa enviada já encontra-se com os juízes nomeados, os quais “darão a providência que julgarem necessária, segundo as provas da mesma devassa”. Referindo-se a carta enviada por Manuel Brandão em 23/07/1765 a respeito de terem tirado uns depósitos que haviam na intendência e a “dificuldade que encontrara para a averiguação do dito fato”.', '', NULL, 43, 1766, 21, '', '', 'Numeração 2. Fl. 54');
INSERT INTO app_hist.resposta VALUES (1020, '1804-11-26', 'isa Manda que Antonio Jose da Franca e Horta, governador de São Paulo, conceda a patente de confirmação a Antonio do Rego para o posto da décima companhia do regimento de infantaria miliciana da vila de Cunha, que ficou vago devido à promoção de João da Costa para tenente-coronel agregado. Além disso, deve informar se o dito João da Costa, foi provido pelo seu antecessor, Antonio Manuel de Melo e Castro de Mendonça a tal posto.', '', NULL, 37, 1804, 28, '', '', '220');
INSERT INTO app_hist.resposta VALUES (1021, '1804-11-26', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo dê baixa a Francisco Leite de Siqueira, no posto de tenente da quinta companhia do segundo regimento de milicia de infantaria, que pediu confirmação em 02/05/1804 em observancia da ordem de 05/11/1803.', '', NULL, 37, 1804, 28, '', '', '220-220V');
INSERT INTO app_hist.resposta VALUES (1023, '1805-02-19', 'isa Ordem para que Manuel Carlos de Abreu e Meneses, governador de Mato Grosso, remeta um parecer e seja remetido e providenciadas as prisões dos réus pronunciados na devassa, tomando as medidas necessárias em resposta à queixa apresentada pelo juiz ordinário da vila, João Francisco dos Guimarães, referente à falta de execução das leis diante dos procedimentos criminosos do capitão-mor, Joaquim Leite Paes, que está pronunciado na devassa.', '', NULL, 37, 1805, 28, '', '', '225v');
INSERT INTO app_hist.resposta VALUES (1025, '1805-02-28', 'isa Ordem para que Antonio Jose da Franca e Horta, governador de São Paulo, remeta um parecer sobre o pedido feito pelo auditor da justiça militar em 30/05/1804, solicitando a presença real dos conselhos de guerra, que foram por ele processados, para que sejam revisados e examinados pelo conselho de justiça das sentenças, a autoridade legítima e competente recentemente estabelecida.', '', NULL, 37, 1805, 28, '', '', '226');
INSERT INTO app_hist.resposta VALUES (1037, '1805-05-14', 'isa Informa ao ouvidor geral da comarca de São Paulo sobre as queixas do capitão Bento Jose Leite Penteado e outros herdeiros do falecido Manuel Joaquim Leite Penteado, a respeito do desembargador Joaquim Jose de Almeida e do ouvidor, pelos procedimentos na herança do falecido.', '', NULL, 37, 1805, 28, '', '', '227v-228');
INSERT INTO app_hist.resposta VALUES (1022, '1804-12-15', 'isa Informa a Antonio Jose da Franca e Horta, governador de São Paulo, que, por ordem de 15/12/1804, considera estranha a carta apresentada pelo ouvidor, Joaquim Procopio Picão Salgado, em 10/05/1804, referente ao procedimento adotado na exclusão de Manuel Jose de Castro para o cargo de vereador da câmara. Determina que seja realizada uma nova eleição pelos oficiais para o referido cargo, na qual nem mesmo o governador deveria interferir em tais eleições.', '', NULL, 37, 1804, 28, '', '', '221v-222; 222v-224');
INSERT INTO app_hist.resposta VALUES (1038, '1760-06-27', 'isa Informa ao provedor da fazenda de Goiás que, em virtude de suas responsabilidades no cargo e do salário que recebe, deve assumir a tarefa de fazer e ordenar o recenseamento das contas dos oficiais sob sua jurisdição. Isso se deve ao que apresentou em 13/04/1759, no qual expressava que, em relação ao mapa da receita e despesa da referida provedoria, estava em conformidade com a provisão de 1756, que exigia o envio anual ao conselho ultramarino de certidões de recenseamento das receitas e despesas. No entanto, ao assumir o cargo, verificou-se que o tesoureiro, capitão Guilherme Jose Pereira, não havia realizado o recenseamento de suas contas durante 14 anos.', '', NULL, 37, 1760, 28, '', '', '52v-53');
INSERT INTO app_hist.resposta VALUES (1042, '1782-10-12', 'Informa a Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso que encarrega o cuidado com que deve satisfazer a real ordem, conforme detalhou 13/12/1780. Que recebeu a ordem real datada de 09/11/1778, que solicita um parecer sobre as questões administrativas abordadas na comunicação de 08/03/770, durante o governo do antecessor Luis Pinto de Sousa, relacionadas às dúvidas surgidas na administração das minas.', '', NULL, 37, 1782, 28, '', '', '169');
INSERT INTO app_hist.resposta VALUES (1392, '1764-12-20', '***Remete cópia ao ouvidor da comarca de Vila Rica do que deu conta a camara da cidade de Mariana, em carta de 27/03/1762, de não se achar cumprida a lei de 19/03/1756 pelos corregedores dessa capitania. Ordena informar seu parecer nessa matéria.', '', NULL, 40, 1764, 20, '', '', '96v ');
INSERT INTO app_hist.resposta VALUES (1024, '1805-03-05', 'isa Ordem para que Manuel Carlos de Abreu e Meneses, governador de Mato Grosso, remeta um  parecer sobre a queixa apresentada pelo juiz ordinário João Francisco dos Guimarães em 20/09/1805, relatando os procedimentos praticados pelo ouvidor Manuel Joaquim Ribeiro Freire, na qualidade de ministro de governo interino, e pelo comissário Antonio Felipo da Cunha, colega de governo.', '', NULL, 37, 1805, 28, '', '', '225v-226');
INSERT INTO app_hist.resposta VALUES (1019, '1804-11-22', 'Manda que o ouvidor da comarca de São Paulo remeta o alvará de 24/11/1645 e o decreto de 22/03/1751, conforme o que representou em 30/04/1804 sobre a impossibilidade de cumprir o regimento que autorizava a confirmação e provimento das justiças da governança da comarca, devido à maioria das pessoas destinadas à administração da justiça estarem empregadas em postos de milícias e ordenanças.', '', NULL, 37, 1804, 28, '', '', '219v-220');
INSERT INTO app_hist.resposta VALUES (1039, '1760-08-30', 'isa Informa aos oficiais da câmara de Vila Boa, que após as disposições estabelecidas no regimento de 10/10/1754, não é necessário tomar novas providências quanto o que os ouvidores devem levar para as eleições das justiças. Isso se baseia no que foi apresentado por Teotonio da Silva Gusmão, juiz de fora que atuou na vila, em 01/06/1751, que sendo ordenado que o referido cargo fosse criado com os mesmos emolumentos estabelecidos para o juízo de fora de Mariana, para a criação dessa vila, foi determinado que os auditores deveriam observar o regimento das Minas Gerais, apesar da objeção do ouvidor, que deveria receber apenas o mesmo que é atribuído ao ouvidor de Vila Rica. ', '', NULL, 37, 1760, 28, '', '', '65');
INSERT INTO app_hist.resposta VALUES (1301, '1750-11-18', '(Théo) Dom Jose nomeia Antonio de Sousa Viegas ao cargo de escrivão da fazenda real de Vila Rica ', '', NULL, 5, 1750, 562, '', '', '');
INSERT INTO app_hist.resposta VALUES (1300, '1750-11-14', 'D. Jose nomeia João Carlos Vieira ao cargo de inquiridor, distribuidor e contador da Vila Nova da Rainha do Caeté.', '', NULL, 5, 1750, 561, '', '', '1v');
INSERT INTO app_hist.resposta VALUES (873, '1739-10-27', '[LUIZ] Ordem, após escutar o procurador da fazenda, ao intendente da fazenda real da Vila do Carmo [Mariana], Antonio Rodrigues de Macedo, para que faça: 1) defira a denúncia que deram os filhos do sargento mor, Andre Gonçalves Chaves, de ter sonegado a capitação de alguns escravos. Devendo dar apelação e agravo “para onde pertencer”; 2) Avise ao governador e capitão general, Gomes Freire de Andrade, que não consinta o serviço de parentes, dentro do quarto grau, em uma mesma Intendência. Em função da carta enviada pelo mesmo intendente em 10/01/1739, a respeito da denúncia admitida ao padre, João Gonçalves Chaves, e Patricio da Silva, filhos do sargento mor, Andre Gonçalves Chaves, de que este havia, como citado, a sonegado a capitação de alguns escravos. Os quais, devendo ser entregues aos denunciantes, se opusera um Jacinto de Figueiredo, que tinha a posse deles por venda que lhe fizera o dito sargento.', '', NULL, 40, 1739, 19, '', '', '239');
INSERT INTO app_hist.resposta VALUES (1027, '1740-02-13', '[LUIZ] Ordem, após resolução de 08/02/1740 em consulta, ao governador e capitão general, Gomes Freire de Andrade, avisando sobre a nomeação de Placido de Almeida Matoso para o cargo de intendente das terras minerais dos diamantes do Serro do Frio. Em função da licença concedida ao desembargador e intendente dos diamantes, Rafael Pires Pardinho, para passar ao reino. ', '', NULL, 40, 1740, 19, '', '', '241');
INSERT INTO app_hist.resposta VALUES (1026, '1739-11-20', '[LUIZ] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, após ouvir o juiz dos órfãos da vila do Sabará, dando culpa a residência do ouvidor do Sabará, como também ordenando que seja registrada a dita ordem com a de 05/12/1730 nas ouvidorias e câmaras. Devido ao ouvidor ter excedido a sua jurisdição ao tratar dos bens que estavam sobre a jurisdição do juízo dos órfãos, desrespeitando as disposições da provisão da Mesa da Consciência de 01/02/1730, no qual os provedores da fazenda dos defuntos e ausentes não devem admitir habilitações dos filhos ilegítimos dos defuntos, dado que devem remeter as fazendas à corte, a fim de evitar os descaminhos. Em função da carta do governador, Gomes de Andrade, em 07/12/1738, informando sobre o falecimento de Martinho Cardoso, em que, deixando um filho menor, o juiz dos órfãos da vila de Sabará realizou um inventário dos bens por ordem real expedida em 05/12/1730. No entanto, intrometendo-se o juízo dos defuntos e ausentes da comarca, realizou-se um novo inventário e foram passadas ordens para que todos que portarem algum bem do defunto fossem presos.', '', NULL, 40, 1739, 19, '', '', '240v-241');
INSERT INTO app_hist.resposta VALUES (1028, '1740-05-05', '[LUIZ. Como posso criar o requerente? É preciso tirar o "Dom" da pessoa citada, não?] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que envie o seu parecer. Em função da carta enviada em 01/04/1739 pelo mestre do campo e governador da praça de Santos, sobre a dúvida que moveu Luis Mascarenhas, governador e capitão general da capitania de São Paulo, a respeito do poder de abrir a carta de [crença]. Uma vez que, conforme a carta patente, na ausência do dito Governador, caberia aos oficiais da câmara de São Paulo essa diligência.', '', NULL, 40, 1740, 19, '', '', '241-241v');
INSERT INTO app_hist.resposta VALUES (1029, '1740-12-07', '[LUIZ. Dúvida quanto a utilização do termo "esmola"] Ordem, após resolução de 03/12/1740 em consulta, ao provedor da fazenda real da capitania de Minas Gerais, para que remeta três mil cruzados à Matriz de Nossa Senhora do Pilar, em Vila Rica. A fim de que possam adquirir um retábulo. Em função da representação enviada pelos oficiais da câmara de Vila Rica sobre essa necessidade e o parecer dado pelo Provedor da Fazenda.', '', NULL, 40, 1740, 19, '', '', '241v');
INSERT INTO app_hist.resposta VALUES (1030, '1740-04-05', '[LUIZ] Ordem ao governador e capitão general da capitania de Minas Gerais, Gomes Freire de Andrade, para que o intendente da vila do Carmo [Mariana] defira, dando apelação e agravo às partes, sobre a matéria. Em função da carta remetida em 30/01/1739 pelo governador, a respeito da denúncia realizada pelo padre João Gonçalves Chaves, na intendência da Vila do Carmo [Mariana] sobre a matrícula dos escravos. Uma vez que, devido seu pai, Andre Gonçalves Chaves, não ter pago a capitação deles nas matrículas antecedentes, e naquela altura pertencerem, por compra ou tomados por dívida, a Jacinto de Figueiredo Freire, desconsiderando as despesas feitas aos ditos escravos.', '', NULL, 40, 1740, 19, '', '', '242v');
INSERT INTO app_hist.resposta VALUES (1031, '1740-12-12', '[LUIZ. Errei a comarca do requerente ao criar a pessoa. Em vez de ser "Vila Rica", deveria ser Rio das Mortes] Ordem para que o ouvidor geral da comarca do Rio das Mortes informe o rendimento anual do ofício de tesoureiro da fazenda dos defuntos e ausentes da vila de São João Del-Rei. Em função do requerimento realizado pelo provido ao cargo, Simão Fogaça dos Santos, na junta dos três estados.', '', NULL, 40, 1740, 19, '', '', '242v-243');
INSERT INTO app_hist.resposta VALUES (1032, '1804-10-09', 'isa Informa a Antonio Jose da Franca e Horta, governador de São Paulo, que em todos os registros da patente emitida para o sargento-mor Jeronimo Gonçalves Pereira, que a cláusula injuriosa dirigida ao sargento-mor antecessor seja eliminada. Isso se deve à confirmação no posto de sargento-mor das ordenanças da vila de Bragança, que requereu o dito Jeronimo, onde no corpo da patente, a mencionada cláusula estava presente.', '', NULL, 37, 1804, 28, '', '', '218-218v');
INSERT INTO app_hist.resposta VALUES (995, '1801-05-15', 'Ordem para que Antonio Manuel de Melo e Castro de Mendonça, governador de São Paulo, dê baixa a Jose Gomes Mota do cargo de capitão-mor da vila de Cunha, e ao capitão reservado conceder direito salvo. Isso se deu pela representação presente no conselho ultramarino, de ser o dito Jose Gomes Mota um "monstro de defeitos e culpas", tendo prostituido a filha do filha do capitão Francisco Xavier Leite, merecendo castigos pela irregularidade de seus procedimentos.', '', NULL, 37, 1801, 28, '', '', '195v-196');
INSERT INTO app_hist.resposta VALUES (1034, '1805-03-27', 'isa Informa ao ouvidor de São Paulo que, pelo apresentado em 22/05/1803, que destacava a importância de os ouvidores estarem cientes de todas as deliberações das respectivas câmaras, a fim de evitar conflitos de jurisdição, deve observe estritamente as leis, permitindo que as partes recorram das câmaras quando a relação negar os agravos que apresentar.', '', NULL, 37, 1805, 28, '', '', '226-226v');
INSERT INTO app_hist.resposta VALUES (919, '1760-08-30', 'Remete cópia ao governador das Minas Gerais, Gomes Freire de Andrade, da representação do governador interino da mesma capitânia em carta de 06 /07 /1757 aonde aborda o requerimento dos moradores de Nossa Senhora do Nazaré e Vila Nova da Rainha para criar o arraial de Catas Altas. Ordena informar seu parecer nessa matéria. ', '', NULL, 40, 1760, 20, '', '', '79 ');
INSERT INTO app_hist.resposta VALUES (1574, '1751-08-04', 'Provisão de Gomes Freire de Andrade, governador de Minas Gerais, nomeando Manuel Francisco da Costa Barros para o cargo de oficial ajuramentado da secretaria de governo de Minas, considerando ser necessário para o melhor funcionamento da referida secretaria. O nomeado deverá atuar com verdade, sigilo e prontidão em suas funções.', '', NULL, 5, 1751, 574, '', '', '');
INSERT INTO app_hist.resposta VALUES (1036, '1805-05-09', 'isa Ordem para que Francisco de Assis Mascarenhas, governador de Goiás, remeta um parecer e junte as ordens que autorizavam o antecessor, Antonio Manuel de Melo e Castro de Mendonça, a prover o posto de capitão da primeira companhia das ordenanças de Vila Boa a Jose Ribeiro da Costa, que requereu confirmação.', '', NULL, 37, 1805, 28, '', '', '227v');
INSERT INTO app_hist.resposta VALUES (370, '1760-08-19', 'Ordem para que o governador da praça de Santos, remeta um parecer ouvindo os oficiais da câmara e o ouvidor, sobre o pagamento do ordenados dos ouvidores, e também sobre a falta da cobrança para contribuição do pagamento, que comunicou o provedor da fazenda real da praça em 16/12/1757. ', '', NULL, 37, 1760, 28, '', '', '54v');
INSERT INTO app_hist.resposta VALUES (1202, '1761-10-27', '*Carta de Francisco Xavier de Mendonça Furtado, secretário de estado, a João Manuel de Melo, governador de Goiás, o informando sobre as ordens passadas ao ouvidor, Manuel da Fonseca Brandão, em 27/10/1761, para que registre as ordens nos livros da secretaria, a fim de que não haja mais falta dos livros na provedoria, na casa da intendencia dessa vila [Vila Boa], no arraial de São Félix e nos registros de entradas. Além disso, informa sobre a devassa tirada contra a fraude praticada na carta da intendência, no qual denuncia o dito governador em carta de 28/03 - a qual não especifica o ano. Toda essa resolução em função das copias inclusas de tres cartas régias enviadas a Manoel da Fonseca Brandão, nas quais dá o conhecimento das resoluções tomadas sobre as matérias expostas pelo governador nas cartas remetidas em 22/03/1760 e 22/05/1760.', '', NULL, 43, 1761, 21, '', '', 'Numeração 2. Fl. 17-17v-18.');
