INSERT INTO app_hist.resposta VALUES (1045, '1794-02-01', 'isa Ordem para que João de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso pague a Luis Manuel de Moura Cabral, juiz de fora de Cuiabá, os ordenados desde o
INSERT INTO app_hist.resposta VALUES (1394, '1764-11-22', '(Théo) Remete cópia ao ouvidor da comarca do Serro do Frio da representação que fizeram os oficiais da câmara da Vila do Principe em carta de 28/04/1762 onde dão razões e defendem a criação de dois almotaces, junto com seus privilégios. Ordena informar seu parecer nessa matéria.  ', '', NULL, 40, 1764, 20, '', '', '97 ');
INSERT INTO app_hist.resposta VALUES (1049, '1804-12-05', 'Informa a Antonio Jose da Franca e Horta, governador de São Paulo, que por real resolução de 08/11/1804, determina-se que a sentença cujo efeito está sendo erroneamente mantido pela junta da fazenda seja devidamente executada, respeitando-se a competência e jurisdição apropriadas. Isso se dá em resposta à representação que questionava a legalidade das sentenças proferidas no caso de liberdade de seis escravos - Timoteo, Joaquim, Antonio, Felipe, Margarida e Jacinta - pelo antigo ouvidor da capitania, Joaquim Jose de Almeida, e pela relação do Rio de Janeiro. E o atual ouvidor, Joaquim Procopio Picão Salgado, alertou a junta sobre a ilegalidade e imprudência de suspender a execução da sentença da relação.', '', NULL, 37, 1804, 28, '', '', '220v-221');
INSERT INTO app_hist.resposta VALUES (1043, '1785-02-22', 'Ordem para que Luis de Albuquerque de Melo Pereira e Caceres, governador de Mato Grosso, remeta um parecer e esclareça o procedimento adotado em relação à expedição de uma ordem ao provedor da fazenda, Felipe Jose de Nogueira Coelho, onde menciona que ao ouvidor Joaquim Jose de Morais foi concedida uma ajuda de custo pelo erário real, com a condição de que não a repetisse. E em 07/01/1782, o provedor descontou o valor dessa ajuda, indicando que não a havia repetido, mas pagou o transporte que o ouvidor havia importado, consistindo em um grande valor em gêneros de roupas dos armazéns reais.', '', NULL, 37, 1785, 28, '', '', '170v');
INSERT INTO app_hist.resposta VALUES (1048, '1797-07-05', 'Informa a Tristão da Cunha Meneses, governador de Goiás, que por resolução de 11/05/1797, é ordenado que os moradores da capitania contribuam para a indispensável despesa de subsistência dos índios, levando em consideração o relato de 10/04/1788, que informava sobre a chegada de 3 mil índios da nação chavante à capitania.', '', NULL, 37, 1797, 28, '', '', '191');
INSERT INTO app_hist.resposta VALUES (739, '1772-07-30', '*Informa a Luis Antonio de Sousa Mourão, governador de São Paulo, que o posto de capitão-mor da vila de Ubatuba solicitado por Antonio Barbosa Pereira foi escusado. 
INSERT INTO app_hist.resposta VALUES (386, '1760-08-07', 'Ordem para que Antonio Rolim de Moura Tavares, governador de Mato Grosso, remeta um parecer ouvindo os oficiais da câmara, a nobreza e o povo, sobre o meio adotado pelos oficiais da câmara da vila de Cuiabá para contribuírem com o donativo destinado à reconstrução da Capital, apresentado em 31/12/1756. Além disso, requerem permissão para minerarem nas terras, alegando a possível presença de diamantes.', '', NULL, 37, 1760, 28, '', '', '55-55v');
INSERT INTO app_hist.resposta VALUES (399, '1760-08-31', 'isa Ordem para que Antonio Rolim de Moura Tavares, governador de Mato Grosso, remeta um parecer ouvindo o provedor da fazenda e os oficiais da câmara, quanto à carta de sesmaria que solicitou confirmação Agostinho de Faria e Castro, de um sítio com três léguas de testada, situado entre os rios Aricá Osu e Aricá Mirim. Ordena que conceda sesmarias apenas em áreas de até três léguas de comprimento e uma de largura. Adicionalmente, considerando que o local possui um rio navegável, é necessário que haja disponibilidade de canoa ou barco para a travessia, e junto a isso uma faixa de terra com meia légua seja reservada para uso público.', '', NULL, 37, 1760, 28, '', '', '59v-60');
